/* eslint-disable @typescript-eslint/no-explicit-any */
import { NextRequest, NextResponse } from "next/server"
import { hashPassword } from "@/lib/auth"
import { prisma } from '@/lib/prisma'
import { AuditLogger } from "@/lib/audit/logger"
import { getClientIP } from "@/lib/utils/ip"
import { rateLimit } from "@/lib/rate-limit"

export async function POST(request: NextRequest) {
  try {
    // Rate limiting: 5 req/hora por IP (prevenir spam de registros)
    const ip = getClientIP(request);
    const rateLimitResult = await rateLimit(ip, 5, 3600); // 5 requisições por 3600 segundos (1 hora)

    if (!rateLimitResult.success) {
      return NextResponse.json(
        { error: 'Muitas tentativas de registro. Tente novamente mais tarde.' },
        {
          status: 429,
          headers: {
            'X-RateLimit-Remaining': '0',
            'X-RateLimit-Reset': rateLimitResult.reset?.toString() || '',
          }
        }
      );
    }

    const body = await request.json()

    const {
      nomeCompleto,
      email,
      whatsapp,
      crm,
      estado,
      senha,
      plan,
      aceitoTermos,
      acceptedTermsOfService,
      aceitoNovidades,
    } = body

    // Validate required fields
    if (!nomeCompleto || !email || !whatsapp || !crm || !estado || !senha) {
      return NextResponse.json(
        { error: "Todos os campos obrigatórios devem ser preenchidos" },
        { status: 400 }
      )
    }

    if (!aceitoTermos) {
      return NextResponse.json(
        { error: "Você deve aceitar os termos de uso" },
        { status: 400 }
      )
    }

    // Validação OBRIGATÓRIA dos Termos de Uso completos
    if (!acceptedTermsOfService) {
      return NextResponse.json(
        { error: "Você deve aceitar os Termos de Uso e Política de Privacidade para usar a plataforma" },
        { status: 400 }
      )
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (!emailRegex.test(email)) {
      return NextResponse.json(
        { error: "Email inválido" },
        { status: 400 }
      )
    }

    // Validate WhatsApp format
    const whatsappRegex = /^\+55 \(\d{2}\) \d{4,5}-\d{4}$/
    if (!whatsappRegex.test(whatsapp)) {
      return NextResponse.json(
        { error: "Formato de WhatsApp inválido. Use: +55 (XX) XXXXX-XXXX" },
        { status: 400 }
      )
    }

    // Validate password strength
    if (senha.length < 8) {
      return NextResponse.json(
        { error: "Senha deve ter no mínimo 8 caracteres" },
        { status: 400 }
      )
    }

    if (!/[A-Z]/.test(senha)) {
      return NextResponse.json(
        { error: "Senha deve conter pelo menos uma letra maiúscula" },
        { status: 400 }
      )
    }

    if (!/[a-z]/.test(senha)) {
      return NextResponse.json(
        { error: "Senha deve conter pelo menos uma letra minúscula" },
        { status: 400 }
      )
    }

    if (!/[0-9]/.test(senha)) {
      return NextResponse.json(
        { error: "Senha deve conter pelo menos um número" },
        { status: 400 }
      )
    }

    // Check if email already exists
    const existingUser = await prisma.user.findUnique({
      where: { email },
    })

    if (existingUser) {
      return NextResponse.json(
        { error: "Este email já está cadastrado" },
        { status: 400 }
      )
    }

    // Check if CRM already exists
    const existingCRM = await prisma.user.findFirst({
      where: {
        crm,
        estado,
      },
    })

    if (existingCRM) {
      return NextResponse.json(
        { error: "Este CRM já está cadastrado" },
        { status: 400 }
      )
    }

    // Hash password with bcrypt
    const hashedPassword = await hashPassword(senha)

    // Validate plan
    const validPlans = ["founding", "professional"]
    const selectedPlan = validPlans.includes(plan) ? plan : "professional"

    // Set pricing based on plan
    const pricing = {
      founding: {
        basePrice: 400,
        additionalPatientPrice: 150,
        isLifetimePrice: true,
      },
      professional: {
        basePrice: 500,
        additionalPatientPrice: 180,
        isLifetimePrice: false,
      },
    }

    const planPricing = pricing[selectedPlan as keyof typeof pricing]

    // Create user in database
    const user = await prisma.user.create({
      data: {
        nomeCompleto,
        email,
        whatsapp,
        crm,
        estado,
        senha: hashedPassword,
        plan: selectedPlan,
        basePrice: planPricing.basePrice,
        additionalPatientPrice: planPricing.additionalPatientPrice,
        isLifetimePrice: planPricing.isLifetimePrice,
        aceitoTermos,
        aceitoNovidades: aceitoNovidades || false,
        firstLogin: true,

        // NOVO: Termos de Uso completos (OBRIGATÓRIO)
        acceptedTermsOfService: true,
        termsOfServiceAcceptedAt: new Date(),
        termsOfServiceVersion: "1.0",
        termsOfServiceAcceptedFromIP: ip,

        // NOVO: Automaticamente ativa Inteligência Coletiva (via Termos)
        collectiveIntelligenceOptIn: true,
        collectiveIntelligenceDate: new Date(),

        createdAt: new Date(),
      },
    })

    // Remove password from response
    const { senha: _senha, ...userWithoutPassword } = user

    // Audit log: novo usuário registrado
    await AuditLogger.userRegistered({
      userId: user.id,
      email: user.email,
      role: "medico",
      ipAddress: ip,
      userAgent: request.headers.get('user-agent') || 'unknown',
    })

    // In production, you would:
    // 1. Send welcome email
    // 2. Set up Twilio WhatsApp connection
    // 3. Create session/JWT token
    // 4. Log registration event

    return NextResponse.json(
      {
        message: "Conta criada com sucesso",
        user: userWithoutPassword,
      },
      { status: 201 }
    )
  } catch (error: any) {
    console.error("Registration error:", error)

    return NextResponse.json(
      {
        error: "Erro ao criar conta. Por favor, tente novamente.",
        details: process.env.NODE_ENV === "development" ? error.message : undefined,
      },
      { status: 500 }
    )
  } finally {
    await prisma.$disconnect()
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';

export async function POST() {
  try {
    // Verificar se já existe
    const existing = await prisma.patient.findFirst({
      where: { phone: { contains: '99866' } }
    });

    if (existing) {
      return NextResponse.json({
        success: true,
        message: 'Patient already exists',
        patient: {
          name: existing.name,
          phone: existing.phone,
        }
      });
    }

    // Criar ou buscar usuário médico
    let user = await prisma.user.findFirst();

    if (!user) {
      user = await prisma.user.create({
        data: {
          email: 'joao@teste.com',
          nomeCompleto: 'Dr. João Vítor',
          senha: '$2a$12$hashedpassword',
          role: 'medico',
          plan: 'professional',
          maxPatients: 100,
          basePrice: 950,
          additionalPatientPrice: 350,
          isLifetimePrice: false,
          firstLogin: false,
          aceitoTermos: true,
        }
      });
    }

    // Criar paciente
    const patient = await prisma.patient.create({
      data: {
        userId: user.id,
        name: 'João Vítor da Cunha Lima Viana',
        phone: '83998663089', // SEM código de país
        email: 'paciente@teste.com',
        age: 30,
        sex: 'male',
        cpf: '12345678900',
      }
    });

    // Criar cirurgia
    const surgery = await prisma.surgery.create({
      data: {
        patientId: patient.id,
        userId: user.id,
        type: 'hemorroidectomia',
        date: new Date('2025-11-18'),
        hospital: 'Hospital Teste',
      }
    });

    // Criar follow-ups
    const followUpDays = [1, 2, 3, 5, 7, 10, 14];
    const followUps = [];

    for (const day of followUpDays) {
      const scheduledDate = new Date('2025-11-18');
      scheduledDate.setDate(scheduledDate.getDate() + day);
      scheduledDate.setHours(10, 0, 0, 0);

      const followUp = await prisma.followUp.create({
        data: {
          surgeryId: surgery.id,
          patientId: patient.id,
          userId: user.id,
          dayNumber: day,
          scheduledDate,
          status: 'pending',
        }
      });
      followUps.push(followUp);
    }

    return NextResponse.json({
      success: true,
      message: 'Test patient created successfully',
      patient: {
        name: patient.name,
        phone: patient.phone,
      },
      surgery: {
        type: surgery.type,
        date: surgery.date,
      },
      followUps: followUps.length,
    });

  } catch (error) {
    console.error('Error creating test patient:', error);
    return NextResponse.json(
      { error: String(error) },
      { status: 500 }
    );
  }
}

/**
 * API para marcar todas as notificações como lidas
 *
 * POST /api/notifications/mark-all-read
 */

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/lib/auth';
import { prisma } from '@/lib/prisma';

export async function POST(request: NextRequest) {
  try {
    const session = await auth();

    if (!session?.user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const userId = session.user.id;

    // Marcar todas as notificações não lidas como lidas
    const result = await prisma.notification.updateMany({
      where: {
        userId,
        read: false,
      },
      data: {
        read: true,
        readAt: new Date(),
      },
    });

    return NextResponse.json({
      success: true,
      count: result.count,
    });
  } catch (error) {
    console.error('Error marking all notifications as read:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to mark all notifications as read' },
      { status: 500 }
    );
  }
}

/**
 * API Endpoint: Push Notification Subscription
 * Gerencia subscriptions de push notifications
 */

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/lib/auth';
import { prisma } from '@/lib/prisma';

/**
 * POST - Salvar nova subscription
 */
export async function POST(request: NextRequest) {
  try {
    // Verificar autenticação
    const session = await auth();
    if (!session?.user?.id) {
      return NextResponse.json(
        { error: 'Não autenticado' },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { endpoint, keys, userAgent } = body;

    // Validar dados
    if (!endpoint || !keys?.p256dh || !keys?.auth) {
      return NextResponse.json(
        { error: 'Dados de subscription inválidos' },
        { status: 400 }
      );
    }

    // Verificar se já existe essa subscription
    const existingSubscription = await prisma.pushSubscription.findFirst({
      where: {
        userId: session.user.id,
        endpoint,
      },
    });

    if (existingSubscription) {
      // Atualizar subscription existente
      await prisma.pushSubscription.update({
        where: { id: existingSubscription.id },
        data: {
          p256dh: keys.p256dh,
          auth: keys.auth,
          userAgent: userAgent || null,
          isActive: true,
          updatedAt: new Date(),
        },
      });

      return NextResponse.json({
        success: true,
        message: 'Subscription atualizada',
      });
    }

    // Criar nova subscription
    await prisma.pushSubscription.create({
      data: {
        userId: session.user.id,
        endpoint,
        p256dh: keys.p256dh,
        auth: keys.auth,
        userAgent: userAgent || null,
        isActive: true,
      },
    });

    return NextResponse.json({
      success: true,
      message: 'Subscription criada com sucesso',
    });
  } catch (error) {
    console.error('Error saving push subscription:', error);
    return NextResponse.json(
      { error: 'Erro ao salvar subscription' },
      { status: 500 }
    );
  }
}

/**
 * DELETE - Remover subscription
 */
export async function DELETE(request: NextRequest) {
  try {
    // Verificar autenticação
    const session = await auth();
    if (!session?.user?.id) {
      return NextResponse.json(
        { error: 'Não autenticado' },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { endpoint } = body;

    if (!endpoint) {
      return NextResponse.json(
        { error: 'Endpoint não fornecido' },
        { status: 400 }
      );
    }

    // Desativar subscription (soft delete)
    await prisma.pushSubscription.updateMany({
      where: {
        userId: session.user.id,
        endpoint,
      },
      data: {
        isActive: false,
      },
    });

    return NextResponse.json({
      success: true,
      message: 'Subscription removida',
    });
  } catch (error) {
    console.error('Error removing push subscription:', error);
    return NextResponse.json(
      { error: 'Erro ao remover subscription' },
      { status: 500 }
    );
  }
}

/**
 * GET - Listar subscriptions do usuário
 */
export async function GET(request: NextRequest) {
  try {
    // Verificar autenticação
    const session = await auth();
    if (!session?.user?.id) {
      return NextResponse.json(
        { error: 'Não autenticado' },
        { status: 401 }
      );
    }

    // Buscar subscriptions ativas
    const subscriptions = await prisma.pushSubscription.findMany({
      where: {
        userId: session.user.id,
        isActive: true,
      },
      select: {
        id: true,
        endpoint: true,
        userAgent: true,
        createdAt: true,
        updatedAt: true,
      },
      orderBy: {
        createdAt: 'desc',
      },
    });

    return NextResponse.json({
      success: true,
      subscriptions,
      count: subscriptions.length,
    });
  } catch (error) {
    console.error('Error fetching push subscriptions:', error);
    return NextResponse.json(
      { error: 'Erro ao buscar subscriptions' },
      { status: 500 }
    );
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any */
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { z } from 'zod';
import {
  buildErrorResponse,
  isValidCuid,
  calculateCompleteness,
} from '@/lib/api-utils';
import { fromBrasiliaTime } from '@/lib/date-utils';
import { AuditLogger } from '@/lib/audit/logger';
import { getClientIP } from '@/lib/utils/ip';

// ============================================
// VALIDATION SCHEMAS
// ============================================

const updatePatientSchema = z.object({
  // Patient basic data
  name: z.string().min(1).optional(),
  cpf: z.string().optional().nullable(),
  dateOfBirth: z.string().datetime().optional().nullable(),
  age: z.number().int().min(0).max(120).optional().nullable(),
  sex: z.string().optional().nullable(),
  phone: z.string().optional(),
  email: z.string().email().optional().nullable(),

  // Surgery data
  surgery: z
    .object({
      id: z.string().optional(),
      type: z.string().optional(),
      date: z.string().datetime().optional(),
      hospital: z.string().optional().nullable(),
      durationMinutes: z.number().int().optional().nullable(),
      status: z.string().optional(),
    })
    .optional(),

  // Surgery details
  details: z
    .object({
      // Hemorrhoid fields
      hemorrhoidTechnique: z.string().optional().nullable(),
      hemorrhoidEnergyType: z.string().optional().nullable(),
      hemorrhoidNumMamillae: z.number().int().optional().nullable(),
      hemorrhoidPositions: z.string().optional().nullable(),
      hemorrhoidType: z.string().optional().nullable(),
      hemorrhoidInternalGrade: z.string().optional().nullable(),
      hemorrhoidExternalDetails: z.string().optional().nullable(),

      // Fistula fields
      fistulaType: z.string().optional().nullable(),
      fistulaTechnique: z.string().optional().nullable(),
      fistulaNumTracts: z.number().int().optional().nullable(),
      fistulaSeton: z.boolean().optional().nullable(),
      fistulaSetonMaterial: z.string().optional().nullable(),

      // Fissure fields
      fissureType: z.string().optional().nullable(),
      fissureLocation: z.string().optional().nullable(),
      fissureTechnique: z.string().optional().nullable(),

      // Pilonidal fields
      pilonidalTechnique: z.string().optional().nullable(),

      // Common fields
      fullDescription: z.string().optional().nullable(),
      complications: z.string().optional().nullable(),
      recoveryRoomMinutes: z.number().int().optional().nullable(),
      sameDayDischarge: z.boolean().optional(),
      hospitalizationDays: z.number().int().optional().nullable(),
    })
    .optional(),

  // Anesthesia
  anesthesia: z
    .object({
      type: z.string().optional(),
      anesthesiologist: z.string().optional().nullable(),
      observations: z.string().optional().nullable(),
      pudendoBlock: z.boolean().optional(),
      pudendoTechnique: z.string().optional().nullable(),
      pudendoAccess: z.string().optional().nullable(),
      pudendoAnesthetic: z.string().optional().nullable(),
      pudendoConcentration: z.string().optional().nullable(),
      pudendoVolumeML: z.number().optional().nullable(),
      pudendoLaterality: z.string().optional().nullable(),
      pudendoAdjuvants: z.string().optional().nullable(),
      pudendoDetails: z.string().optional().nullable(),
    })
    .optional(),

  // Pre-op preparation
  preOp: z
    .object({
      botoxUsed: z.boolean().optional(),
      botoxDate: z.string().datetime().optional().nullable(),
      botoxDoseUnits: z.number().int().optional().nullable(),
      botoxLocation: z.string().optional().nullable(),
      botoxObservations: z.string().optional().nullable(),
      intestinalPrep: z.boolean().optional(),
      intestinalPrepType: z.string().optional().nullable(),
      otherPreparations: z.string().optional().nullable(),
    })
    .optional(),

  // Post-op prescription
  postOp: z
    .object({
      ointments: z.string().optional().nullable(),
      medications: z.string().optional().nullable(),
    })
    .optional(),

  // Comorbidities (array of IDs or objects)
  comorbidities: z
    .array(
      z.object({
        comorbidityId: z.string(),
        details: z.string().optional().nullable(),
        severity: z.string().optional().nullable(),
      })
    )
    .optional(),

  // Medications (array of IDs or objects)
  medications: z
    .array(
      z.object({
        medicationId: z.string(),
        dose: z.string().optional().nullable(),
        frequency: z.string().optional().nullable(),
        route: z.string().optional().nullable(),
      })
    )
    .optional(),
});

// ============================================
// GET - LOAD COMPLETE PATIENT DATA
// ============================================

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    console.log(`[GET Patient] Fetching patient with ID: ${id}`);

    // Validate ID format
    if (!isValidCuid(id)) {
      console.log(`[GET Patient] Invalid CUID: ${id}`);
      return NextResponse.json(
        buildErrorResponse('Invalid patient ID format', 'ID must be a valid CUID'),
        { status: 400 }
      );
    }

    // Fetch patient with all relations
    console.log(`[GET Patient] Executing Prisma query...`);
    const patient = await prisma.patient.findUnique({
      where: { id },
      include: {
        comorbidities: {
          include: {
            comorbidity: true,
          },
        },
        medications: {
          include: {
            medication: true,
          },
        },
        surgeries: {
          include: {
            details: true,
            anesthesia: true,
            preOp: true,
            postOp: true,
          },
          orderBy: {
            date: 'desc',
          },
        },
        followUps: {
          include: {
            responses: true,
          },
          orderBy: {
            scheduledDate: 'desc',
          },
        },
        consentTerms: {
          orderBy: {
            createdAt: 'desc',
          },
        },
      },
    });

    if (!patient) {
      console.log(`[GET Patient] Patient not found in DB.`);
      return NextResponse.json(
        buildErrorResponse('Patient not found', `No patient found with ID: ${id}`),
        { status: 404 }
      );
    }
    console.log(`[GET Patient] Patient found: ${patient.name}`);

    // Calculate completeness for the latest surgery
    let dataCompleteness = 20;
    if (patient.surgeries.length > 0) {
      const latestSurgery = patient.surgeries[0];
      dataCompleteness = calculateCompleteness({
        comorbidities: patient.comorbidities,
        medications: patient.medications,
        surgery: latestSurgery,
      });
    }

    // Audit log: visualização de dados do paciente
    try {
      await AuditLogger.patientViewed({
        userId: patient.userId,
        patientId: patient.id,
        patientName: patient.name,
        ipAddress: getClientIP(request),
        userAgent: request.headers.get('user-agent') || 'unknown',
      });
    } catch (auditError) {
      console.error('[GET Patient] Audit log failed (non-fatal):', auditError);
    }

    return NextResponse.json({
      success: true,
      data: {
        ...patient,
        dataCompleteness,
      },
    });
  } catch (error) {
    console.error('[GET Patient] Critical error fetching patient:', error);
    return NextResponse.json(
      buildErrorResponse(
        'Failed to fetch patient',
        error instanceof Error ? error.message : 'Unknown error'
      ),
      { status: 500 }
    );
  }
}

// ============================================
// PATCH - UPDATE PATIENT DATA
// ============================================

export async function PATCH(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;

    // Validate ID format
    if (!isValidCuid(id)) {
      return NextResponse.json(
        buildErrorResponse('Invalid patient ID format', 'ID must be a valid CUID'),
        { status: 400 }
      );
    }

    // Parse and validate request body
    const body = await request.json();
    const validatedData = updatePatientSchema.parse(body);

    // Check if patient exists
    const existingPatient = await prisma.patient.findUnique({
      where: { id },
      include: {
        surgeries: {
          orderBy: { date: 'desc' },
          take: 1,
        },
      },
    });

    if (!existingPatient) {
      return NextResponse.json(
        buildErrorResponse('Patient not found', `No patient found with ID: ${id}`),
        { status: 404 }
      );
    }

    // Update patient basic data
    const patientUpdateData: any = {};
    if (validatedData.name) patientUpdateData.name = validatedData.name;
    if (validatedData.cpf !== undefined) patientUpdateData.cpf = validatedData.cpf;
    if (validatedData.dateOfBirth !== undefined)
      patientUpdateData.dateOfBirth = validatedData.dateOfBirth
        ? new Date(validatedData.dateOfBirth)
        : null;
    if (validatedData.age !== undefined) patientUpdateData.age = validatedData.age;
    if (validatedData.sex !== undefined) patientUpdateData.sex = validatedData.sex;
    if (validatedData.phone) patientUpdateData.phone = validatedData.phone;
    if (validatedData.email !== undefined)
      patientUpdateData.email = validatedData.email;

    // Start transaction
    const result = await prisma.$transaction(async (tx) => {
      // Update patient
      const updatedPatient = await tx.patient.update({
        where: { id },
        data: patientUpdateData,
      });

      let surgeryId = existingPatient.surgeries[0]?.id;

      // Update or create surgery
      if (validatedData.surgery) {
        const surgeryData: any = {};
        if (validatedData.surgery.type) surgeryData.type = validatedData.surgery.type;
        if (validatedData.surgery.date)
          surgeryData.date = fromBrasiliaTime(new Date(validatedData.surgery.date));
        if (validatedData.surgery.hospital !== undefined)
          surgeryData.hospital = validatedData.surgery.hospital;
        if (validatedData.surgery.durationMinutes !== undefined)
          surgeryData.durationMinutes = validatedData.surgery.durationMinutes;
        if (validatedData.surgery.status)
          surgeryData.status = validatedData.surgery.status;

        if (surgeryId) {
          // Update existing surgery
          await tx.surgery.update({
            where: { id: surgeryId },
            data: surgeryData,
          });
        } else {
          // Create new surgery
          const newSurgery = await tx.surgery.create({
            data: {
              ...surgeryData,
              patientId: id,
              type: validatedData.surgery.type || 'hemorroidectomia',
              date: validatedData.surgery.date
                ? fromBrasiliaTime(new Date(validatedData.surgery.date))
                : fromBrasiliaTime(new Date()),
            },
          });
          surgeryId = newSurgery.id;
        }
      }

      // Update surgery details
      if (validatedData.details && surgeryId) {
        await tx.surgeryDetails.upsert({
          where: { surgeryId },
          create: {
            surgeryId,
            userId: existingPatient.userId,
            ...validatedData.details,
          },
          update: validatedData.details,
        });
      }

      // Update anesthesia
      if (validatedData.anesthesia && surgeryId) {
        await tx.anesthesia.upsert({
          where: { surgeryId },
          create: {
            surgeryId,
            type: validatedData.anesthesia.type || 'geral_IOT',
            ...validatedData.anesthesia,
          },
          update: validatedData.anesthesia,
        });
      }

      // Update pre-op
      if (validatedData.preOp && surgeryId) {
        const preOpData: any = { ...validatedData.preOp };
        if (preOpData.botoxDate) {
          preOpData.botoxDate = new Date(preOpData.botoxDate);
        }

        await tx.preOpPreparation.upsert({
          where: { surgeryId },
          create: {
            surgeryId,
            ...preOpData,
          },
          update: preOpData,
        });
      }

      // Update post-op prescription
      if (validatedData.postOp && surgeryId) {
        await tx.postOpPrescription.upsert({
          where: { surgeryId },
          create: {
            surgeryId,
            ...validatedData.postOp,
          },
          update: validatedData.postOp,
        });
      }

      // Update comorbidities
      if (validatedData.comorbidities) {
        // Delete existing comorbidities
        await tx.patientComorbidity.deleteMany({
          where: { patientId: id },
        });

        // Create new comorbidities
        if (validatedData.comorbidities.length > 0) {
          await tx.patientComorbidity.createMany({
            data: validatedData.comorbidities.map((c) => ({
              patientId: id,
              comorbidityId: c.comorbidityId,
              details: c.details,
              severity: c.severity,
            })),
          });
        }
      }

      // Update medications
      if (validatedData.medications) {
        // Delete existing medications
        await tx.patientMedication.deleteMany({
          where: { patientId: id },
        });

        // Create new medications
        if (validatedData.medications.length > 0) {
          await tx.patientMedication.createMany({
            data: validatedData.medications.map((m) => ({
              patientId: id,
              medicationId: m.medicationId,
              dose: m.dose,
              frequency: m.frequency,
              route: m.route,
            })),
          });
        }
      }

      // Recalculate completeness
      const patientWithRelations = await tx.patient.findUnique({
        where: { id },
        include: {
          comorbidities: true,
          medications: true,
          surgeries: {
            where: { id: surgeryId },
            include: {
              details: true,
              anesthesia: true,
              preOp: true,
              postOp: true,
            },
          },
        },
      });

      if (patientWithRelations && surgeryId) {
        const newCompleteness = calculateCompleteness({
          comorbidities: patientWithRelations.comorbidities,
          medications: patientWithRelations.medications,
          surgery: patientWithRelations.surgeries[0],
        });

        await tx.surgery.update({
          where: { id: surgeryId },
          data: { dataCompleteness: newCompleteness },
        });
      }

      return updatedPatient;
    });

    // Fetch updated patient with all relations
    const updatedPatient = await prisma.patient.findUnique({
      where: { id },
      include: {
        comorbidities: {
          include: {
            comorbidity: true,
          },
        },
        medications: {
          include: {
            medication: true,
          },
        },
        surgeries: {
          include: {
            details: true,
            anesthesia: true,
            preOp: true,
            postOp: true,
          },
          orderBy: {
            date: 'desc',
          },
        },
      },
    });

    // Audit log: paciente atualizado
    const fieldsUpdated = Object.keys(validatedData);
    await AuditLogger.patientUpdated({
      userId: existingPatient.userId,
      patientId: id,
      patientName: updatedPatient?.name || existingPatient.name,
      fieldsUpdated,
      ipAddress: getClientIP(request),
      userAgent: request.headers.get('user-agent') || 'unknown',
    });

    return NextResponse.json({
      success: true,
      message: 'Patient updated successfully',
      data: updatedPatient,
    });
  } catch (error) {
    console.error('Error updating patient:', error);

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        buildErrorResponse(
          'Validation error',
          error.issues.map((e) => `${e.path.join('.')}: ${e.message}`).join(', ')
        ),
        { status: 400 }
      );
    }

    return NextResponse.json(
      buildErrorResponse(
        'Failed to update patient',
        error instanceof Error ? error.message : 'Unknown error'
      ),
      { status: 500 }
    );
  }
}

// ============================================
// DELETE - DELETE PATIENT
// ============================================

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;

    // Validate ID format
    if (!isValidCuid(id)) {
      return NextResponse.json(
        buildErrorResponse('Invalid patient ID format', 'ID must be a valid CUID'),
        { status: 400 }
      );
    }

    // Check if patient exists
    const existingPatient = await prisma.patient.findUnique({
      where: { id },
    });

    if (!existingPatient) {
      return NextResponse.json(
        buildErrorResponse('Patient not found', `No patient found with ID: ${id}`),
        { status: 404 }
      );
    }

    // Delete patient (cascade will handle related records)
    await prisma.patient.delete({
      where: { id },
    });

    return NextResponse.json({
      success: true,
      message: 'Patient deleted successfully',
    });
  } catch (error) {
    console.error('Error deleting patient:', error);
    return NextResponse.json(
      buildErrorResponse(
        'Failed to delete patient',
        error instanceof Error ? error.message : 'Unknown error'
      ),
      { status: 500 }
    );
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any */
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { buildErrorResponse } from '@/lib/api-utils';
import {
  performANOVAAnalysis,
  calculateChiSquare,
  calculateExpectedFrequencies,
  calculateFisherExact,
  interpretCramerV,
} from '@/lib/research-export-utils';
import { auth } from '@/lib/auth';

// ============================================
// GET - RESEARCH STATISTICS AND ANALYTICS
// ============================================

interface RouteParams {
  params: Promise<{ id: string }>;
}

export async function GET(
  request: NextRequest,
  { params }: RouteParams
) {
  try {
    const resolvedParams = await params;
    const researchId = resolvedParams.id;

    const session = await auth();
    if (!session?.user?.id) {
      return NextResponse.json(
        buildErrorResponse('Unauthorized', 'You must be logged in'),
        { status: 401 }
      );
    }
    const userId = session.user.id;

    // Get research with all related data
    const research = await prisma.research.findFirst({
      where: {
        id: researchId,
        userId,
      },
      include: {
        groups: {
          orderBy: {
            groupCode: 'asc',
          },
        },
      },
    });

    if (!research) {
      return NextResponse.json(
        buildErrorResponse('Not found', 'Research not found'),
        { status: 404 }
      );
    }

    // Get all patients in this research
    const patients = await prisma.patient.findMany({
      where: {
        userId,
        isResearchParticipant: true,
        researchGroup: {
          in: research.groups.map((g) => g.groupCode),
        },
      },
      include: {
        surgeries: {
          include: {
            details: true,
            anesthesia: true,
            followUps: {
              include: {
                responses: true,
              },
            },
          },
        },
        comorbidities: {
          include: {
            comorbidity: true,
          },
        },
        medications: {
          include: {
            medication: true,
          },
        },
      },
    });

    // Calculate statistics by group
    const groupStats = research.groups.map((group) => {
      const groupPatients = patients.filter(
        (p) => p.researchGroup === group.groupCode
      );

      // Demographics
      const ages = groupPatients
        .filter((p) => p.age !== null)
        .map((p) => p.age as number);
      const avgAge =
        ages.length > 0 ? ages.reduce((a, b) => a + b, 0) / ages.length : 0;

      const sexDistribution = groupPatients.reduce(
        (acc, p) => {
          const sex = p.sex || 'Não informado';
          acc[sex] = (acc[sex] || 0) + 1;
          return acc;
        },
        {} as Record<string, number>
      );

      // Surgery types
      const surgeryTypes = groupPatients
        .flatMap((p) => p.surgeries)
        .reduce(
          (acc, s) => {
            acc[s.type] = (acc[s.type] || 0) + 1;
            return acc;
          },
          {} as Record<string, number>
        );

      // Data completeness
      const completeness = groupPatients
        .flatMap((p) => p.surgeries)
        .map((s) => s.dataCompleteness);
      const avgCompleteness =
        completeness.length > 0
          ? completeness.reduce((a, b) => a + b, 0) / completeness.length
          : 0;

      // Comorbidities
      const comorbidities = groupPatients
        .flatMap((p) => p.comorbidities)
        .reduce(
          (acc, c) => {
            const name = c.comorbidity.name;
            acc[name] = (acc[name] || 0) + 1;
            return acc;
          },
          {} as Record<string, number>
        );

      // Medications
      const medications = groupPatients
        .flatMap((p) => p.medications)
        .reduce(
          (acc, m) => {
            const name = m.medication.name;
            acc[name] = (acc[name] || 0) + 1;
            return acc;
          },
          {} as Record<string, number>
        );

      // Follow-up response rates
      const followUps = groupPatients.flatMap((p) =>
        p.surgeries.flatMap((s) => s.followUps)
      );
      const totalFollowUps = followUps.length;
      const respondedFollowUps = followUps.filter(
        (f) => f.status === 'responded'
      ).length;
      const responseRate =
        totalFollowUps > 0 ? (respondedFollowUps / totalFollowUps) * 100 : 0;

      // Pain scores over time
      const painScores = followUps
        .filter((f) => f.status === 'responded')
        .flatMap((f) => f.responses)
        .map((r) => {
          try {
            const data = JSON.parse(r.questionnaireData);
            return {
              day: followUps.find((f) =>
                f.responses.some((resp) => resp.id === r.id)
              )?.dayNumber || 0,
              painLevel: data.painLevel || 0,
            };
          } catch {
            return null;
          }
        })
        .filter((p) => p !== null);

      // Complications
      const complications = followUps
        .flatMap((f) => f.responses)
        .map((r) => {
          try {
            const flags = JSON.parse(r.redFlags || '[]');
            return flags;
          } catch {
            return [];
          }
        })
        .flat();

      const complicationRate =
        followUps.length > 0 ? (complications.length / followUps.length) * 100 : 0;

      return {
        groupCode: group.groupCode,
        groupName: group.groupName,
        patientCount: groupPatients.length,
        avgAge: Math.round(avgAge * 10) / 10,
        ageRange: ages.length > 0 ? [Math.min(...ages), Math.max(...ages)] : [0, 0],
        sexDistribution,
        surgeryTypes,
        avgCompleteness: Math.round(avgCompleteness),
        comorbidities,
        medications,
        responseRate: Math.round(responseRate * 10) / 10,
        painScores,
        complicationRate: Math.round(complicationRate * 10) / 10,
        totalFollowUps,
        respondedFollowUps,
      };
    });

    // Overall statistics
    const totalPatients = patients.length;
    const allAges = patients.filter((p) => p.age !== null).map((p) => p.age as number);
    const overallAvgAge =
      allAges.length > 0 ? allAges.reduce((a, b) => a + b, 0) / allAges.length : 0;

    const overallSexDistribution = patients.reduce(
      (acc, p) => {
        const sex = p.sex || 'Não informado';
        acc[sex] = (acc[sex] || 0) + 1;
        return acc;
      },
      {} as Record<string, number>
    );

    const allSurgeries = patients.flatMap((p) => p.surgeries);
    const overallCompleteness =
      allSurgeries.length > 0
        ? allSurgeries.reduce((a, b) => a + b.dataCompleteness, 0) / allSurgeries.length
        : 0;

    // Chi-square test for categorical variables
    const chiSquareTest = (observed: number[][], expected: number[][]) => {
      let chiSquare = 0;
      for (let i = 0; i < observed.length; i++) {
        for (let j = 0; j < observed[i].length; j++) {
          const obs = observed[i][j];
          const exp = expected[i][j];
          if (exp > 0) {
            chiSquare += Math.pow(obs - exp, 2) / exp;
          }
        }
      }
      return chiSquare;
    };

    // T-test for continuous variables (simplified)
    const tTest = (group1: number[], group2: number[]) => {
      if (group1.length === 0 || group2.length === 0) return null;

      const mean1 = group1.reduce((a, b) => a + b, 0) / group1.length;
      const mean2 = group2.reduce((a, b) => a + b, 0) / group2.length;

      const variance1 =
        group1.reduce((acc, val) => acc + Math.pow(val - mean1, 2), 0) /
        (group1.length - 1);
      const variance2 =
        group2.reduce((acc, val) => acc + Math.pow(val - mean2, 2), 0) /
        (group2.length - 1);

      const pooledVariance =
        ((group1.length - 1) * variance1 + (group2.length - 1) * variance2) /
        (group1.length + group2.length - 2);

      const tStatistic =
        (mean1 - mean2) /
        Math.sqrt(pooledVariance * (1 / group1.length + 1 / group2.length));

      return {
        tStatistic: Math.round(tStatistic * 1000) / 1000,
        mean1: Math.round(mean1 * 10) / 10,
        mean2: Math.round(mean2 * 10) / 10,
        difference: Math.round((mean1 - mean2) * 10) / 10,
      };
    };

    // Statistical comparisons
    let statisticalTests = null;

    if (groupStats.length === 2) {
      // T-test for 2 groups
      const group1Ages = patients
        .filter((p) => p.researchGroup === groupStats[0].groupCode && p.age)
        .map((p) => p.age as number);
      const group2Ages = patients
        .filter((p) => p.researchGroup === groupStats[1].groupCode && p.age)
        .map((p) => p.age as number);

      statisticalTests = {
        testType: 't-test',
        ageTTest: tTest(group1Ages, group2Ages),
      };
    } else if (groupStats.length >= 3) {
      // ANOVA for 3+ groups
      const groupAges = groupStats.map((groupStat) =>
        patients
          .filter((p) => p.researchGroup === groupStat.groupCode && p.age)
          .map((p) => p.age as number)
      );

      const groupNames = groupStats.map((g) => `${g.groupCode}: ${g.groupName}`);

      // Age ANOVA
      const ageANOVA = performANOVAAnalysis(groupAges, groupNames);

      // Pain scores ANOVA (Day 7)
      const groupPainScores = groupStats.map((groupStat) => {
        const painScores = groupStat.painScores
          .filter((p) => p.day === 7)
          .map((p) => p.painLevel);
        return painScores;
      });

      const painANOVA = groupPainScores.every(g => g.length > 0)
        ? performANOVAAnalysis(groupPainScores, groupNames)
        : null;

      statisticalTests = {
        testType: 'ANOVA',
        ageANOVA,
        painANOVA,
      };
    }

    // ============================================
    // CATEGORICAL DATA ANALYSIS (Chi-square tests)
    // ============================================

    const categoricalAnalysis: any[] = [];

    if (groupStats.length >= 2) {
      // 1. SEX DISTRIBUTION
      const sexCategories = ['Masculino', 'Feminino', 'Outro'];
      const sexObserved: number[][] = groupStats.map(groupStat => {
        return sexCategories.map(sex => groupStat.sexDistribution[sex] || 0);
      });

      if (sexObserved.some(row => row.some(val => val > 0))) {
        const sexExpected = calculateExpectedFrequencies(sexObserved);
        const sexChiSquare = calculateChiSquare(sexObserved, sexExpected);

        categoricalAnalysis.push({
          variable: 'sex',
          label: 'Distribuição de Sexo',
          categories: sexCategories,
          groupLabels: groupStats.map(g => `Grupo ${g.groupCode}`),
          contingencyTable: {
            observed: sexObserved,
            expected: sexExpected,
            rowTotals: sexObserved.map(row => row.reduce((a, b) => a + b, 0)),
            colTotals: sexCategories.map((_, j) => sexObserved.reduce((sum, row) => sum + row[j], 0)),
            total: sexObserved.flat().reduce((a, b) => a + b, 0),
          },
          chiSquareTest: sexChiSquare,
          fisherExactTest: (sexObserved.length === 2 && sexObserved[0].length === 2)
            ? calculateFisherExact(sexObserved)
            : null,
          interpretation: `${sexChiSquare.significant ? 'Diferença significativa' : 'Sem diferença significativa'} na distribuição de sexo entre grupos (p=${sexChiSquare.pValue.toFixed(3)}). Tamanho do efeito (Cramér's V): ${interpretCramerV(sexChiSquare.cramerV, sexChiSquare.degreesOfFreedom)}.`,
        });
      }

      // 2. COMPLICATIONS (Yes/No)
      const complicationsObserved: number[][] = groupStats.map(groupStat => {
        const withComplications = Math.round((groupStat.complicationRate / 100) * groupStat.patientCount);
        const withoutComplications = groupStat.patientCount - withComplications;
        return [withComplications, withoutComplications];
      });

      if (complicationsObserved.some(row => row.some(val => val > 0))) {
        const complicationsExpected = calculateExpectedFrequencies(complicationsObserved);
        const complicationsChiSquare = calculateChiSquare(complicationsObserved, complicationsExpected);

        categoricalAnalysis.push({
          variable: 'complications',
          label: 'Presença de Complicações',
          categories: ['Com Complicações', 'Sem Complicações'],
          groupLabels: groupStats.map(g => `Grupo ${g.groupCode}`),
          contingencyTable: {
            observed: complicationsObserved,
            expected: complicationsExpected,
            rowTotals: complicationsObserved.map(row => row.reduce((a, b) => a + b, 0)),
            colTotals: ['Com Complicações', 'Sem Complicações'].map((_, j) =>
              complicationsObserved.reduce((sum, row) => sum + row[j], 0)
            ),
            total: complicationsObserved.flat().reduce((a, b) => a + b, 0),
          },
          chiSquareTest: complicationsChiSquare,
          fisherExactTest: (complicationsObserved.length === 2 && complicationsObserved[0].length === 2)
            ? calculateFisherExact(complicationsObserved)
            : null,
          interpretation: `${complicationsChiSquare.significant ? 'Diferença significativa' : 'Sem diferença significativa'} na taxa de complicações entre grupos (p=${complicationsChiSquare.pValue.toFixed(3)}). ${complicationsChiSquare.useFisherExact ? 'Recomenda-se usar Teste Exato de Fisher devido ao tamanho amostral pequeno.' : ''}`,
        });
      }

      // 3. COMORBIDITIES (Present/Absent)
      const uniqueComorbidities = Array.from(
        new Set(
          groupStats.flatMap(g => Object.keys(g.comorbidities))
        )
      );

      uniqueComorbidities.forEach(comorbidity => {
        const comorbidityObserved: number[][] = groupStats.map(groupStat => {
          const withComorb = groupStat.comorbidities[comorbidity] || 0;
          const withoutComorb = groupStat.patientCount - withComorb;
          return [withComorb, withoutComorb];
        });

        if (comorbidityObserved.some(row => row.some(val => val > 0))) {
          const comorbidityExpected = calculateExpectedFrequencies(comorbidityObserved);
          const comorbidityChiSquare = calculateChiSquare(comorbidityObserved, comorbidityExpected);

          categoricalAnalysis.push({
            variable: `comorbidity_${comorbidity.toLowerCase().replace(/\s+/g, '_')}`,
            label: `Comorbidade: ${comorbidity}`,
            categories: ['Presente', 'Ausente'],
            groupLabels: groupStats.map(g => `Grupo ${g.groupCode}`),
            contingencyTable: {
              observed: comorbidityObserved,
              expected: comorbidityExpected,
              rowTotals: comorbidityObserved.map(row => row.reduce((a, b) => a + b, 0)),
              colTotals: ['Presente', 'Ausente'].map((_, j) =>
                comorbidityObserved.reduce((sum, row) => sum + row[j], 0)
              ),
              total: comorbidityObserved.flat().reduce((a, b) => a + b, 0),
            },
            chiSquareTest: comorbidityChiSquare,
            fisherExactTest: (comorbidityObserved.length === 2 && comorbidityObserved[0].length === 2)
              ? calculateFisherExact(comorbidityObserved)
              : null,
            interpretation: `${comorbidityChiSquare.significant ? 'Diferença significativa' : 'Sem diferença significativa'} na prevalência de ${comorbidity} entre grupos (p=${comorbidityChiSquare.pValue.toFixed(3)}).`,
          });
        }
      });

      // 4. ANESTHESIA TYPE
      const anesthesiaTypes = Array.from(
        new Set(
          patients
            .flatMap(p => p.surgeries)
            .map(s => s.anesthesia?.type)
            .filter(type => type != null)
        )
      ) as string[];

      if (anesthesiaTypes.length > 0) {
        const anesthesiaObserved: number[][] = groupStats.map(groupStat => {
          const groupPatients = patients.filter(p => p.researchGroup === groupStat.groupCode);
          return anesthesiaTypes.map(type =>
            groupPatients
              .flatMap(p => p.surgeries)
              .filter(s => s.anesthesia?.type === type).length
          );
        });

        if (anesthesiaObserved.some(row => row.some(val => val > 0))) {
          const anesthesiaExpected = calculateExpectedFrequencies(anesthesiaObserved);
          const anesthesiaChiSquare = calculateChiSquare(anesthesiaObserved, anesthesiaExpected);

          categoricalAnalysis.push({
            variable: 'anesthesia_type',
            label: 'Tipo de Anestesia',
            categories: anesthesiaTypes,
            groupLabels: groupStats.map(g => `Grupo ${g.groupCode}`),
            contingencyTable: {
              observed: anesthesiaObserved,
              expected: anesthesiaExpected,
              rowTotals: anesthesiaObserved.map(row => row.reduce((a, b) => a + b, 0)),
              colTotals: anesthesiaTypes.map((_, j) => anesthesiaObserved.reduce((sum, row) => sum + row[j], 0)),
              total: anesthesiaObserved.flat().reduce((a, b) => a + b, 0),
            },
            chiSquareTest: anesthesiaChiSquare,
            fisherExactTest: null,
            interpretation: `${anesthesiaChiSquare.significant ? 'Diferença significativa' : 'Sem diferença significativa'} na distribuição de tipos de anestesia entre grupos (p=${anesthesiaChiSquare.pValue.toFixed(3)}).`,
          });
        }
      }

      // 5. PUDENDAL NERVE BLOCK (Yes/No)
      const pudendoObserved: number[][] = groupStats.map(groupStat => {
        const groupPatients = patients.filter(p => p.researchGroup === groupStat.groupCode);
        const withPudendo = groupPatients
          .flatMap(p => p.surgeries)
          .filter(s => s.anesthesia?.pudendoBlock === true).length;
        const withoutPudendo = groupPatients.flatMap(p => p.surgeries).length - withPudendo;
        return [withPudendo, withoutPudendo];
      });

      if (pudendoObserved.some(row => row.some(val => val > 0))) {
        const pudendoExpected = calculateExpectedFrequencies(pudendoObserved);
        const pudendoChiSquare = calculateChiSquare(pudendoObserved, pudendoExpected);

        categoricalAnalysis.push({
          variable: 'pudendal_block',
          label: 'Bloqueio do Nervo Pudendo',
          categories: ['Realizado', 'Não Realizado'],
          groupLabels: groupStats.map(g => `Grupo ${g.groupCode}`),
          contingencyTable: {
            observed: pudendoObserved,
            expected: pudendoExpected,
            rowTotals: pudendoObserved.map(row => row.reduce((a, b) => a + b, 0)),
            colTotals: ['Realizado', 'Não Realizado'].map((_, j) =>
              pudendoObserved.reduce((sum, row) => sum + row[j], 0)
            ),
            total: pudendoObserved.flat().reduce((a, b) => a + b, 0),
          },
          chiSquareTest: pudendoChiSquare,
          fisherExactTest: (pudendoObserved.length === 2 && pudendoObserved[0].length === 2)
            ? calculateFisherExact(pudendoObserved)
            : null,
          interpretation: `${pudendoChiSquare.significant ? 'Diferença significativa' : 'Sem diferença significativa'} no uso de bloqueio do nervo pudendo entre grupos (p=${pudendoChiSquare.pValue.toFixed(3)}).`,
        });
      }
    }

    return NextResponse.json({
      success: true,
      data: {
        research: {
          id: research.id,
          title: research.title,
          description: research.description,
          surgeryType: research.surgeryType,
          isActive: research.isActive,
          startDate: research.startDate,
          endDate: research.endDate,
        },
        overview: {
          totalPatients,
          avgAge: Math.round(overallAvgAge * 10) / 10,
          sexDistribution: overallSexDistribution,
          dataCompleteness: Math.round(overallCompleteness),
          totalGroups: research.groups.length,
        },
        groups: groupStats,
        statisticalTests,
        categoricalAnalysis,
      },
    });
  } catch (error) {
    console.error('Error getting research statistics:', error);
    return NextResponse.json(
      buildErrorResponse(
        'Failed to get research statistics',
        error instanceof Error ? error.message : 'Unknown error'
      ),
      { status: 500 }
    );
  }
}

