'use client';

import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { CheckCircle2, XCircle, Loader2, MessageSquare, Bot } from 'lucide-react';

interface ApiStatus {
  configured: boolean;
  connected: boolean | null;
  loading: boolean;
  error: string | null;
  details?: any;
}

export default function ApiConfigPage() {
  const [anthropicStatus, setAnthropicStatus] = useState<ApiStatus>({
    configured: false,
    connected: null,
    loading: false,
    error: null,
  });

  const [whatsappStatus, setWhatsappStatus] = useState<ApiStatus>({
    configured: false,
    connected: null,
    loading: false,
    error: null,
  });

  // Testar API Anthropic
  const testAnthropicConnection = async () => {
    setAnthropicStatus({ ...anthropicStatus, loading: true, error: null });

    try {
      const response = await fetch('/api/test/anthropic', {
        method: 'POST',
      });

      const data = await response.json();

      if (response.ok) {
        setAnthropicStatus({
          configured: true,
          connected: true,
          loading: false,
          error: null,
          details: data,
        });
      } else {
        setAnthropicStatus({
          configured: data.configured || false,
          connected: false,
          loading: false,
          error: data.error || 'Erro ao testar conexão',
        });
      }
    } catch (error) {
      setAnthropicStatus({
        configured: false,
        connected: false,
        loading: false,
        error: 'Erro ao conectar com o servidor',
      });
    }
  };

  // Testar API WhatsApp
  const testWhatsAppConnection = async () => {
    setWhatsappStatus({ ...whatsappStatus, loading: true, error: null });

    try {
      const response = await fetch('/api/test/whatsapp', {
        method: 'POST',
      });

      const data = await response.json();

      if (response.ok) {
        setWhatsappStatus({
          configured: true,
          connected: true,
          loading: false,
          error: null,
          details: data,
        });
      } else {
        setWhatsappStatus({
          configured: data.configured || false,
          connected: false,
          loading: false,
          error: data.error || 'Erro ao testar conexão',
        });
      }
    } catch (error) {
      setWhatsappStatus({
        configured: false,
        connected: false,
        loading: false,
        error: 'Erro ao conectar com o servidor',
      });
    }
  };

  const getStatusBadge = (status: ApiStatus) => {
    if (status.loading) {
      return <Badge variant="outline">Testando...</Badge>;
    }
    if (status.connected === null) {
      return <Badge variant="outline">Não testado</Badge>;
    }
    if (status.connected) {
      return <Badge className="bg-green-500">Conectado</Badge>;
    }
    if (!status.configured) {
      return <Badge variant="destructive">Não configurado</Badge>;
    }
    return <Badge variant="destructive">Erro na conexão</Badge>;
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <div className="max-w-4xl mx-auto space-y-6">
        <div>
          <h1 className="text-3xl font-bold mb-2">Configuração de APIs</h1>
          <p className="text-muted-foreground">
            Configure e teste as integrações com APIs externas
          </p>
        </div>

        {/* Anthropic API */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-purple-100 dark:bg-purple-900 rounded-lg">
                  <Bot className="h-6 w-6 text-purple-600 dark:text-purple-300" />
                </div>
                <div>
                  <CardTitle>Anthropic API (Claude AI)</CardTitle>
                  <CardDescription>
                    IA para análise inteligente de respostas dos pacientes
                  </CardDescription>
                </div>
              </div>
              {getStatusBadge(anthropicStatus)}
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <h4 className="font-semibold">Como configurar:</h4>
              <ol className="list-decimal list-inside space-y-1 text-sm text-muted-foreground">
                <li>Acesse <a href="https://console.anthropic.com/settings/keys" target="_blank" rel="noopener noreferrer" className="text-primary hover:underline">console.anthropic.com/settings/keys</a></li>
                <li>Crie uma nova API Key</li>
                <li>Adicione a chave no arquivo .env como <code className="bg-muted px-1 py-0.5 rounded">ANTHROPIC_API_KEY</code></li>
                <li>Reinicie o servidor de desenvolvimento</li>
              </ol>
            </div>

            {anthropicStatus.error && (
              <Alert variant="destructive">
                <XCircle className="h-4 w-4" />
                <AlertDescription>{anthropicStatus.error}</AlertDescription>
              </Alert>
            )}

            {anthropicStatus.connected && anthropicStatus.details && (
              <Alert>
                <CheckCircle2 className="h-4 w-4" />
                <AlertDescription>
                  Conexão bem-sucedida! Modelo: {anthropicStatus.details.model}
                </AlertDescription>
              </Alert>
            )}

            <Button
              onClick={testAnthropicConnection}
              disabled={anthropicStatus.loading}
              className="w-full sm:w-auto"
            >
              {anthropicStatus.loading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Testando...
                </>
              ) : (
                'Testar Conexão'
              )}
            </Button>
          </CardContent>
        </Card>

        {/* WhatsApp API */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-green-100 dark:bg-green-900 rounded-lg">
                  <MessageSquare className="h-6 w-6 text-green-600 dark:text-green-300" />
                </div>
                <div>
                  <CardTitle>WhatsApp Business API</CardTitle>
                  <CardDescription>
                    Envio de mensagens e questionários automáticos
                  </CardDescription>
                </div>
              </div>
              {getStatusBadge(whatsappStatus)}
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <h4 className="font-semibold">Como configurar:</h4>
              <ol className="list-decimal list-inside space-y-1 text-sm text-muted-foreground">
                <li>Acesse <a href="https://developers.facebook.com/apps/" target="_blank" rel="noopener noreferrer" className="text-primary hover:underline">developers.facebook.com/apps</a></li>
                <li>Crie um novo app e adicione o produto &quot;WhatsApp Business API&quot;</li>
                <li>Configure um número de telefone de teste</li>
                <li>Obtenha o Phone Number ID e Access Token</li>
                <li>Adicione no arquivo .env:
                  <ul className="list-disc list-inside ml-4 mt-1">
                    <li><code className="bg-muted px-1 py-0.5 rounded">WHATSAPP_PHONE_NUMBER_ID</code></li>
                    <li><code className="bg-muted px-1 py-0.5 rounded">WHATSAPP_ACCESS_TOKEN</code></li>
                    <li><code className="bg-muted px-1 py-0.5 rounded">DOCTOR_PHONE_NUMBER</code></li>
                  </ul>
                </li>
                <li>Reinicie o servidor de desenvolvimento</li>
              </ol>
            </div>

            {whatsappStatus.error && (
              <Alert variant="destructive">
                <XCircle className="h-4 w-4" />
                <AlertDescription>{whatsappStatus.error}</AlertDescription>
              </Alert>
            )}

            {whatsappStatus.connected && whatsappStatus.details && (
              <Alert>
                <CheckCircle2 className="h-4 w-4" />
                <AlertDescription>
                  Conexão bem-sucedida! Phone Number ID: {whatsappStatus.details.phoneNumberId?.slice(0, 10)}...
                </AlertDescription>
              </Alert>
            )}

            <Button
              onClick={testWhatsAppConnection}
              disabled={whatsappStatus.loading}
              className="w-full sm:w-auto"
            >
              {whatsappStatus.loading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Testando...
                </>
              ) : (
                'Testar Conexão'
              )}
            </Button>
          </CardContent>
        </Card>

        {/* Info adicional */}
        <Alert>
          <AlertDescription>
            <strong>Nota:</strong> As APIs são necessárias para o funcionamento completo do sistema.
            A API Anthropic analisa as respostas dos pacientes e a API WhatsApp envia os questionários automaticamente.
          </AlertDescription>
        </Alert>
      </div>
    </div>
  );
}

"use client"

import { useState, useEffect } from "react"
import { useParams, useRouter } from "next/navigation"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Progress } from "@/components/ui/progress"
import { ArrowLeft, Save, CheckCircle2, FileText, Download } from "lucide-react"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Badge } from "@/components/ui/badge"
import { useToast } from "@/hooks/use-toast"
import { ApplyTemplateDialog } from "@/components/ApplyTemplateDialog"
import { SaveAsTemplateDialog } from "@/components/SaveAsTemplateDialog"
import { ResearchCompletionProgress } from "@/components/ResearchCompletionProgress"
import { validateResearchFields, type ValidationResult } from "@/lib/research-field-validator"
import { SurgeryRiskDisplay, SurgeryRiskNotAvailable } from "@/components/ml/surgery-risk-display"

// Section components
import { DadosBasicosSection } from "@/components/edit/DadosBasicosSection"
import { ComorbidadesSection } from "@/components/edit/ComorbidadesSection"
import { MedicacoesSection } from "@/components/edit/MedicacoesSection"
import { DetalhesCirurgicosSection } from "@/components/edit/DetalhesCirurgicosSection"
import { PreOperatorioSection } from "@/components/edit/PreOperatorioSection"
import { AnestesiaSection } from "@/components/edit/AnestesiaSection"
import { PrescricaoSection } from "@/components/edit/PrescricaoSection"
import { DescricaoCompletaSection } from "@/components/edit/DescricaoCompletaSection"

// New patient components
import { ConversationTimeline } from "@/components/patient/ConversationTimeline"
import { PainEvolutionChart } from "@/components/patient/PainEvolutionChart"

interface PatientData {
  id: string
  name: string
  cpf?: string
  dateOfBirth?: Date
  age?: number
  sex?: string
  phone: string
  email?: string
  isResearchParticipant?: boolean
  researchGroup?: string
  surgery: {
    id: string
    type: string
    date: Date
    hospital?: string
    durationMinutes?: number
    dataCompleteness: number
    // ML Prediction fields
    predictedRisk?: number
    predictedRiskLevel?: 'low' | 'medium' | 'high'
    mlModelVersion?: string
    mlPredictedAt?: Date
    mlFeatures?: string
  }
}

export default function EditPatientPage() {
  const params = useParams()
  const router = useRouter()
  const { toast } = useToast()
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [patient, setPatient] = useState<PatientData | null>(null)
  const [activeTab, setActiveTab] = useState("basicos")
  const [completedSections, setCompletedSections] = useState<Set<string>>(new Set())
  const [lastSaved, setLastSaved] = useState<Date | null>(null)
  const [applyDialogOpen, setApplyDialogOpen] = useState(false)
  const [saveDialogOpen, setSaveDialogOpen] = useState(false)
  const [researchValidation, setResearchValidation] = useState<ValidationResult | null>(null)

  // Auto-save every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      if (patient) {
        autoSave()
      }
    }, 30000) // 30 seconds

    return () => clearInterval(interval)
  }, [patient])

  // Load patient data
  useEffect(() => {
    async function loadInitialData() {
      setLoading(true)
      await loadPatient()
      setLoading(false)
    }

    loadInitialData()
  }, [params.id])

  // Validate research fields whenever patient data changes
  useEffect(() => {
    if (patient && patient.isResearchParticipant) {
      const validation = validateResearchFields(patient)
      setResearchValidation(validation)
    } else {
      setResearchValidation(null)
    }
  }, [patient])

  function calculateCompletedSections(data: PatientData): Set<string> {
    const completed = new Set<string>()

    // Dados básicos sempre completo (vem do express)
    completed.add("basicos")

    // Check other sections based on data presence
    if (data.surgery.hospital || data.surgery.durationMinutes) {
      completed.add("detalhes")
    }

    // Add more logic for other sections

    return completed
  }

  async function autoSave() {
    if (!patient) return

    try {
      await fetch(`/api/paciente/${params.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(patient)
      })
      setLastSaved(new Date())
    } catch (error) {
      console.error("Auto-save failed:", error)
    }
  }

  async function handleSave() {
    setSaving(true)
    try {
      await fetch(`/api/paciente/${params.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(patient)
      })

      toast({
        title: "Dados salvos com sucesso",
        description: "As informações do paciente foram atualizadas"
      })

      setLastSaved(new Date())
    } catch (error) {
      toast({
        title: "Erro ao salvar",
        description: "Não foi possível salvar os dados",
        variant: "destructive"
      })
    } finally {
      setSaving(false)
    }
  }

  function markSectionComplete(sectionId: string) {
    setCompletedSections(prev => new Set([...prev, sectionId]))
  }

  function handleTemplateApplied() {
    // Reload patient data after template is applied
    loadPatient()
  }

  function handleTemplateSaved() {
    toast({
      title: "Template salvo",
      description: "Você pode aplicá-lo a outros pacientes na página de Templates"
    })
  }

  async function loadPatient() {
    try {
      const response = await fetch(`/api/paciente/${params.id}`)
      if (!response.ok) throw new Error("Paciente não encontrado")

      const data = await response.json()
      setPatient(data)

      // Calculate completed sections
      const completed = calculateCompletedSections(data)
      setCompletedSections(completed)
    } catch (error) {
      console.error("Error loading patient:", error)
      toast({
        title: "Erro ao carregar dados",
        description: error instanceof Error ? error.message : "Não foi possível carregar os dados do paciente",
        variant: "destructive"
      })
    }
  }

  const sections = [
    { id: "basicos", label: "Dados Básicos", component: DadosBasicosSection },
    { id: "comorbidades", label: "Comorbidades", component: ComorbidadesSection },
    { id: "medicacoes", label: "Medicações", component: MedicacoesSection },
    { id: "detalhes", label: "Detalhes Cirúrgicos", component: DetalhesCirurgicosSection },
    { id: "preop", label: "Pré-Operatório", component: PreOperatorioSection },
    { id: "anestesia", label: "Anestesia", component: AnestesiaSection },
    { id: "prescricao", label: "Prescrição Pós-Op", component: PrescricaoSection },
    { id: "descricao", label: "Descrição Completa", component: DescricaoCompletaSection }
  ]

  const completionPercentage = (completedSections.size / sections.length) * 100

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Carregando dados do paciente...</p>
        </div>
      </div>
    )
  }

  if (!patient) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="max-w-md">
          <CardHeader>
            <CardTitle>Paciente não encontrado</CardTitle>
            <CardDescription>
              Não foi possível encontrar os dados deste paciente
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Button onClick={() => router.push("/dashboard")}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Voltar ao Dashboard
            </Button>
          </CardContent>
        </Card>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#F5F7FA] to-[#E2E8F0] p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="mb-6">
          <Button
            variant="ghost"
            onClick={() => router.push("/dashboard")}
            className="mb-4"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            Voltar ao Dashboard
          </Button>

          <Card>
            <CardHeader>
              <div className="flex items-start justify-between">
                <div>
                  <CardTitle className="text-2xl">{patient.name}</CardTitle>
                  <CardDescription className="mt-2">
                    {patient.surgery.type.charAt(0).toUpperCase() + patient.surgery.type.slice(1)} - {" "}
                    {new Date(patient.surgery.date).toLocaleDateString("pt-BR")}
                  </CardDescription>
                </div>
                <div className="text-right">
                  <Badge
                    variant={completionPercentage >= 80 ? "default" : "secondary"}
                    className="mb-2"
                  >
                    {Math.round(completionPercentage)}% Completo
                  </Badge>
                  {lastSaved && (
                    <p className="text-xs text-gray-500">
                      Salvo às {lastSaved.toLocaleTimeString("pt-BR")}
                    </p>
                  )}
                </div>
              </div>

              <div className="mt-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm text-gray-600">
                    {completedSections.size} de {sections.length} seções completas
                  </span>
                </div>
                <Progress value={completionPercentage} className="h-2" />
              </div>
            </CardHeader>
          </Card>
        </div>

        {/* Research Completion Progress */}
        {patient?.isResearchParticipant && researchValidation && (
          <ResearchCompletionProgress
            validation={researchValidation}
            isResearchParticipant={true}
            className="mb-6"
            showDetails={true}
            onFieldClick={(fieldId) => {
              // Auto-scroll to field
              const element = document.getElementById(fieldId)
              if (element) {
                element.scrollIntoView({ behavior: 'smooth', block: 'center' })
                element.focus()
              }
            }}
          />
        )}

        {/* ML Risk Prediction */}
        {patient?.surgery?.predictedRisk !== null &&
          patient?.surgery?.predictedRisk !== undefined &&
          patient?.surgery?.predictedRiskLevel ? (
          <SurgeryRiskDisplay
            risk={patient.surgery.predictedRisk}
            level={patient.surgery.predictedRiskLevel}
            features={patient.surgery.mlFeatures}
            modelVersion={patient.surgery.mlModelVersion}
            predictedAt={patient.surgery.mlPredictedAt}
            showDetails={true}
            showFactors={true}
            className="mb-6"
          />
        ) : (
          <SurgeryRiskNotAvailable className="mb-6" />
        )}

        {/* Pain Evolution Chart */}
        <div className="mb-6">
          <PainEvolutionChart patientId={patient.id} />
        </div>

        {/* WhatsApp Conversation Timeline */}
        <div className="mb-6">
          <ConversationTimeline patientId={patient.id} />
        </div>

        {/* Main Form */}
        <Card>
          <CardContent className="pt-6">
            <Tabs value={activeTab} onValueChange={setActiveTab}>
              <TabsList className="grid grid-cols-4 lg:grid-cols-8 mb-6">
                {sections.map(section => (
                  <TabsTrigger
                    key={section.id}
                    value={section.id}
                    className="relative"
                  >
                    {section.label}
                    {completedSections.has(section.id) && (
                      <CheckCircle2 className="absolute -top-1 -right-1 h-4 w-4 text-green-600" />
                    )}
                  </TabsTrigger>
                ))}
              </TabsList>

              {sections.map(section => {
                const SectionComponent = section.component
                return (
                  <TabsContent key={section.id} value={section.id}>
                    <SectionComponent
                      patient={patient}
                      onUpdate={setPatient}
                      onComplete={() => markSectionComplete(section.id)}
                      isResearchParticipant={patient?.isResearchParticipant || false}
                    />
                  </TabsContent>
                )
              })}
            </Tabs>

            {/* Template Actions */}
            <div className="flex justify-center gap-3 mt-6 pt-6 border-t">
              <Button
                variant="outline"
                onClick={() => setApplyDialogOpen(true)}
                className="flex-1 max-w-xs"
              >
                <Download className="mr-2 h-4 w-4" />
                Aplicar Template
              </Button>

              <Button
                variant="outline"
                onClick={() => setSaveDialogOpen(true)}
                className="flex-1 max-w-xs"
              >
                <FileText className="mr-2 h-4 w-4" />
                Salvar como Template
              </Button>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-between mt-4 pt-4 border-t">
              <Button
                variant="outline"
                onClick={() => router.push("/dashboard")}
              >
                Cancelar
              </Button>

              <div className="flex gap-3">
                <Button
                  variant="outline"
                  onClick={autoSave}
                  disabled={saving}
                >
                  Salvar Rascunho
                </Button>

                <Button
                  onClick={handleSave}
                  disabled={saving}
                >
                  <Save className="mr-2 h-4 w-4" />
                  {saving ? "Salvando..." : "Salvar e Concluir"}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Template Dialogs */}
        {patient && (
          <>
            <ApplyTemplateDialog
              open={applyDialogOpen}
              onOpenChange={setApplyDialogOpen}
              surgeryType={patient.surgery.type}
              patientId={patient.id}
              surgeryId={patient.surgery.id}
              onSuccess={handleTemplateApplied}
            />

            <SaveAsTemplateDialog
              open={saveDialogOpen}
              onOpenChange={setSaveDialogOpen}
              patientData={patient}
              onSuccess={handleTemplateSaved}
            />
          </>
        )}
      </div>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { useRouter } from "next/navigation"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { useToast } from "@/hooks/use-toast"
import {
  ArrowLeft,
  Plus,
  FileText,
  Trash2,
  Star,
  StarOff,
  Eye,
  Calendar,
  Stethoscope
} from "lucide-react"
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog"
import { SURGERY_TYPE_LABELS, getTemplateSummary, TemplateData } from "@/lib/template-utils"

interface Template {
  id: string
  createdAt: Date
  updatedAt: Date
  name: string
  surgeryType: string
  templateData: string
  templateDataParsed?: TemplateData
  isDefault: boolean
}

export default function TemplatesPage() {
  const router = useRouter()
  const { toast } = useToast()
  const [templates, setTemplates] = useState<Template[]>([])
  const [loading, setLoading] = useState(true)
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false)
  const [templateToDelete, setTemplateToDelete] = useState<string | null>(null)
  const [previewTemplate, setPreviewTemplate] = useState<Template | null>(null)
  const [selectedType, setSelectedType] = useState<string>("all")

  useEffect(() => {
    loadTemplates()
  }, [selectedType])

  async function loadTemplates() {
    try {
      const url = selectedType === "all"
        ? "/api/templates"
        : `/api/templates?surgeryType=${selectedType}`

      const response = await fetch(url)
      if (!response.ok) throw new Error("Erro ao carregar templates")

      const data = await response.json()
      setTemplates(data)
    } catch (error) {
      toast({
        title: "Erro ao carregar templates",
        description: "Não foi possível carregar os templates",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  async function handleDelete(id: string) {
    try {
      const response = await fetch(`/api/templates?id=${id}`, {
        method: "DELETE"
      })

      if (!response.ok) throw new Error("Erro ao excluir template")

      toast({
        title: "Template excluído",
        description: "O template foi removido com sucesso"
      })

      loadTemplates()
    } catch (error) {
      toast({
        title: "Erro ao excluir",
        description: "Não foi possível excluir o template",
        variant: "destructive"
      })
    } finally {
      setDeleteDialogOpen(false)
      setTemplateToDelete(null)
    }
  }

  async function toggleDefault(template: Template) {
    try {
      const response = await fetch("/api/templates", {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: template.id,
          isDefault: !template.isDefault
        })
      })

      if (!response.ok) throw new Error("Erro ao atualizar template")

      toast({
        title: template.isDefault ? "Removido como padrão" : "Definido como padrão",
        description: template.isDefault
          ? "Este template não será mais aplicado automaticamente"
          : "Este template será aplicado automaticamente a novos pacientes"
      })

      loadTemplates()
    } catch (error) {
      toast({
        title: "Erro ao atualizar",
        description: "Não foi possível atualizar o template",
        variant: "destructive"
      })
    }
  }

  const surgeryTypes = [
    { value: "all", label: "Todos os Tipos" },
    { value: "hemorroidectomia", label: "Hemorroidectomia" },
    { value: "fistula", label: "Fístula" },
    { value: "fissura", label: "Fissura" },
    { value: "pilonidal", label: "Pilonidal" }
  ]

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Carregando templates...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#F5F7FA] to-[#E2E8F0] p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="mb-6">
          <Button
            variant="ghost"
            onClick={() => router.push("/dashboard")}
            className="mb-4"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            Voltar ao Dashboard
          </Button>

          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Templates de Procedimentos
              </h1>
              <p className="text-gray-600">
                Salve e aplique suas configurações padrão de cirurgias
              </p>
            </div>

            <Button onClick={() => router.push("/templates/novo")}>
              <Plus className="mr-2 h-4 w-4" />
              Novo Template
            </Button>
          </div>
        </div>

        {/* Filters */}
        <Card className="mb-6">
          <CardContent className="pt-6">
            <div className="flex gap-2 flex-wrap">
              {surgeryTypes.map(type => (
                <Button
                  key={type.value}
                  variant={selectedType === type.value ? "default" : "outline"}
                  onClick={() => setSelectedType(type.value)}
                  size="sm"
                >
                  {type.label}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Templates List */}
        {templates.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center">
              <FileText className="mx-auto h-12 w-12 text-gray-400 mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                Nenhum template encontrado
              </h3>
              <p className="text-gray-600 mb-4">
                {selectedType === "all"
                  ? "Crie seu primeiro template para agilizar cadastros"
                  : `Nenhum template para ${SURGERY_TYPE_LABELS[selectedType]}`}
              </p>
              <Button onClick={() => router.push("/templates/novo")}>
                <Plus className="mr-2 h-4 w-4" />
                Criar Template
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {templates.map(template => {
              const summary = template.templateDataParsed
                ? getTemplateSummary(template.templateDataParsed)
                : []

              return (
                <Card key={template.id} className="hover:shadow-lg transition-shadow">
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <CardTitle className="text-lg flex items-center gap-2">
                          {template.name}
                          {template.isDefault && (
                            <Star className="h-4 w-4 text-yellow-500 fill-yellow-500" />
                          )}
                        </CardTitle>
                        <CardDescription className="mt-1">
                          <Badge variant="outline" className="mt-1">
                            <Stethoscope className="h-3 w-3 mr-1" />
                            {SURGERY_TYPE_LABELS[template.surgeryType] || template.surgeryType}
                          </Badge>
                        </CardDescription>
                      </div>
                    </div>
                  </CardHeader>

                  <CardContent>
                    {/* Summary */}
                    {summary.length > 0 && (
                      <div className="mb-4 space-y-1">
                        {summary.slice(0, 3).map((item, index) => (
                          <p key={index} className="text-sm text-gray-600">
                            • {item}
                          </p>
                        ))}
                        {summary.length > 3 && (
                          <p className="text-sm text-gray-500 italic">
                            +{summary.length - 3} mais...
                          </p>
                        )}
                      </div>
                    )}

                    {/* Metadata */}
                    <div className="flex items-center gap-2 text-xs text-gray-500 mb-4">
                      <Calendar className="h-3 w-3" />
                      Criado em {new Date(template.createdAt).toLocaleDateString("pt-BR")}
                    </div>

                    {/* Actions */}
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        className="flex-1"
                        onClick={() => setPreviewTemplate(template)}
                      >
                        <Eye className="h-4 w-4 mr-1" />
                        Ver
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => toggleDefault(template)}
                        title={template.isDefault ? "Remover como padrão" : "Definir como padrão"}
                      >
                        {template.isDefault ? (
                          <StarOff className="h-4 w-4" />
                        ) : (
                          <Star className="h-4 w-4" />
                        )}
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          setTemplateToDelete(template.id)
                          setDeleteDialogOpen(true)
                        }}
                      >
                        <Trash2 className="h-4 w-4 text-red-500" />
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              )
            })}
          </div>
        )}

        {/* Delete Confirmation Dialog */}
        <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Confirmar Exclusão</AlertDialogTitle>
              <AlertDialogDescription>
                Tem certeza que deseja excluir este template? Esta ação não pode ser desfeita.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancelar</AlertDialogCancel>
              <AlertDialogAction
                onClick={() => templateToDelete && handleDelete(templateToDelete)}
                className="bg-red-600 hover:bg-red-700"
              >
                Excluir
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>

        {/* Preview Dialog */}
        {previewTemplate && (
          <AlertDialog open={!!previewTemplate} onOpenChange={() => setPreviewTemplate(null)}>
            <AlertDialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
              <AlertDialogHeader>
                <AlertDialogTitle>{previewTemplate.name}</AlertDialogTitle>
                <AlertDialogDescription>
                  <Badge variant="outline">
                    {SURGERY_TYPE_LABELS[previewTemplate.surgeryType]}
                  </Badge>
                  {previewTemplate.isDefault && (
                    <Badge variant="default" className="ml-2">
                      Template Padrão
                    </Badge>
                  )}
                </AlertDialogDescription>
              </AlertDialogHeader>

              <div className="space-y-4 text-sm">
                {previewTemplate.templateDataParsed && (
                  <>
                    {/* Anestesia */}
                    {previewTemplate.templateDataParsed.anesthesia && (
                      <div>
                        <h4 className="font-semibold mb-2">Anestesia</h4>
                        <div className="bg-gray-50 p-3 rounded space-y-1">
                          {previewTemplate.templateDataParsed.anesthesia.type && (
                            <p>Tipo: {previewTemplate.templateDataParsed.anesthesia.type}</p>
                          )}
                          {previewTemplate.templateDataParsed.anesthesia.pudendoBlock && (
                            <p>Bloqueio Pudendo: Sim ({previewTemplate.templateDataParsed.anesthesia.pudendoTechnique})</p>
                          )}
                        </div>
                      </div>
                    )}

                    {/* Prescrição */}
                    {previewTemplate.templateDataParsed.prescription && (
                      <div>
                        <h4 className="font-semibold mb-2">Prescrição</h4>
                        <div className="bg-gray-50 p-3 rounded space-y-2">
                          {previewTemplate.templateDataParsed.prescription.ointments && (
                            <div>
                              <p className="font-medium">Pomadas:</p>
                              {previewTemplate.templateDataParsed.prescription.ointments.map((o, i) => (
                                <p key={i} className="text-sm ml-2">
                                  • {o.name} - {o.frequency} por {o.durationDays} dias
                                </p>
                              ))}
                            </div>
                          )}
                          {previewTemplate.templateDataParsed.prescription.medications && (
                            <div>
                              <p className="font-medium">Medicações:</p>
                              {previewTemplate.templateDataParsed.prescription.medications.map((m, i) => (
                                <p key={i} className="text-sm ml-2">
                                  • {m.name} {m.dose} - {m.frequency} por {m.durationDays} dias
                                </p>
                              ))}
                            </div>
                          )}
                        </div>
                      </div>
                    )}
                  </>
                )}
              </div>

              <AlertDialogFooter>
                <AlertDialogCancel>Fechar</AlertDialogCancel>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        )}
      </div>
    </div>
  )
}

'use client';

import { useEffect, useState, Suspense } from 'react';
import Link from 'next/link';
import { useParams, useSearchParams } from 'next/navigation';
import { termoTemplates, TermoData } from '@/lib/termo-templates';

function TermoPage() {
  const params = useParams();
  const searchParams = useSearchParams();
  const tipo = params.tipo as string;

  const [data, setData] = useState<TermoData>({
    pacienteNome: searchParams.get('nome') || '',
    pacienteCPF: '',
    data: new Date().toLocaleDateString('pt-BR'),
    cidade: 'João Pessoa'
  });

  const [autoPrint, setAutoPrint] = useState(false);

  useEffect(() => {
    // Auto-print se o parâmetro print=true estiver na URL
    if (searchParams.get('print') === 'true') {
      setTimeout(() => setAutoPrint(true), 0);
      setTimeout(() => {
        window.print();
      }, 500);
    }
  }, [searchParams]);

  const termo = termoTemplates[tipo as keyof typeof termoTemplates];

  if (!termo) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
