  { threshold: 41, label: "Bom progresso", color: "#EAB308", bgColor: "#FEF3C7", emoji: "🟡" },
  { threshold: 61, label: "Quase completo", color: "#84CC16", bgColor: "#ECFCCB", emoji: "🟢" },
  { threshold: 81, label: "Excelente!", color: "#22C55E", bgColor: "#DCFCE7", emoji: "✨" },
  { threshold: 100, label: "Cadastro perfeito!", color: "#FFD700", bgColor: "#FEF9C3", emoji: "🏆" }
]

const motivationalMessages = {
  0: "Vamos começar! Cada informação conta.",
  21: "Ótimo começo! Continue assim.",
  41: "Você está indo muito bem!",
  61: "Quase lá! Não desista agora.",
  81: "Incrível! Falta muito pouco.",
  100: "Perfeito! Cadastro 100% completo!"
}

export function CompletenessIncentive({
  currentCompletion,
  onContinue,
  showConfetti = true
}: CompletenessIncentiveProps) {
  const [previousCompletion, setPreviousCompletion] = useState(currentCompletion)
  const [showMilestone, setShowMilestone] = useState(false)

  // Determine current level
  const currentLevel = [...levels].reverse().find(level => currentCompletion >= level.threshold) || levels[0]

  // Get motivational message
  const message = Object.entries(motivationalMessages)
    .reverse()
    .find(([threshold]) => currentCompletion >= parseInt(threshold))?.[1] || motivationalMessages[0]

  const triggerConfetti = () => {
    const duration = 3000
    const end = Date.now() + duration

    const frame = () => {
      confetti({
        particleCount: 3,
        angle: 60,
        spread: 55,
        origin: { x: 0 },
        colors: ['#0066CC', '#00A3E0', '#FFD700', '#FFA500']
      })
      confetti({
        particleCount: 3,
        angle: 120,
        spread: 55,
        origin: { x: 1 },
        colors: ['#0066CC', '#00A3E0', '#FFD700', '#FFA500']
      })

      if (Date.now() < end) {
        requestAnimationFrame(frame)
      }
    }
    frame()
  }

  // Check for milestone reached
  useEffect(() => {
    const milestones = [40, 60, 80, 100]
    const reachedMilestone = milestones.find(
      m => currentCompletion >= m && previousCompletion < m
    )

    if (reachedMilestone) {
      setTimeout(() => setShowMilestone(true), 0)

      // Trigger confetti for 100%
      if (reachedMilestone === 100 && showConfetti) {
        triggerConfetti()
      }

      setTimeout(() => setShowMilestone(false), 3000)
    }

    setPreviousCompletion(currentCompletion)
  }, [currentCompletion, previousCompletion, showConfetti])

  // Get unlocked and locked benefits
  const allBenefitLevels = [20, 40, 60, 80, 100]
  const unlockedBenefits: { level: number; items: string[] }[] = []
  const nextBenefit = allBenefitLevels.find(level => currentCompletion < level)

  allBenefitLevels.forEach(level => {
    if (currentCompletion >= level) {
      unlockedBenefits.push({ level, items: benefits[level as keyof typeof benefits] })
    }
  })

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="relative"
    >
      {/* Milestone Celebration Overlay */}
      <AnimatePresence>
        {showMilestone && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="absolute inset-0 z-50 flex items-center justify-center bg-black/20 backdrop-blur-sm rounded-2xl"
          >
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: [0, 1.2, 1] }}
              transition={{ duration: 0.5 }}
              className="bg-white rounded-2xl p-8 shadow-2xl text-center"
            >
              <motion.div
                animate={{ rotate: [0, 360] }}
                transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              >
                <Award className="w-20 h-20 mx-auto mb-4" style={{ color: currentLevel.color }} />
              </motion.div>
              <h3 className="text-2xl font-bold mb-2">Parabéns!</h3>
              <p className="text-gray-600">Você alcançou {currentCompletion}% de completude!</p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main Card */}
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-2xl shadow-lg p-6 border border-gray-200">
        <div className="flex items-start gap-6">
          {/* Progress Ring */}
          <div className="flex-shrink-0">
            <div className="relative w-32 h-32">
              {/* Background circle */}
              <svg className="w-32 h-32 transform -rotate-90">
                <circle
                  cx="64"
                  cy="64"
                  r="56"
                  stroke="#E5E7EB"
                  strokeWidth="8"
                  fill="none"
                />
                {/* Progress circle */}
                <motion.circle
                  cx="64"
                  cy="64"
                  r="56"
                  stroke={currentLevel.color}
                  strokeWidth="8"
                  fill="none"
                  strokeLinecap="round"
                  initial={{ strokeDasharray: "0 352" }}
                  animate={{
                    strokeDasharray: `${(currentCompletion / 100) * 352} 352`,
                  }}
                  transition={{ duration: 1, ease: "easeOut" }}
                  style={{
                    filter: currentCompletion === 100 ? 'drop-shadow(0 0 8px rgba(255, 215, 0, 0.8))' : 'none'
                  }}
                />
              </svg>

              {/* Center content */}
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                <motion.div
                  key={currentCompletion}
                  initial={{ scale: 0.5, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  className="text-3xl font-bold"
                  style={{ color: currentLevel.color }}
                >
                  {currentCompletion}%
                </motion.div>
                <motion.div
                  animate={{
                    scale: currentCompletion === 100 ? [1, 1.2, 1] : 1,
                  }}
                  transition={{
                    duration: 0.5,
                    repeat: currentCompletion === 100 ? Infinity : 0,
                    repeatDelay: 1
                  }}
                  className="text-2xl"
                >
                  {currentLevel.emoji}
                </motion.div>
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 min-w-0">
            {/* Level Badge */}
            <motion.div
              key={currentLevel.label}
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className="inline-flex items-center gap-2 px-3 py-1 rounded-full text-sm font-semibold mb-3"
              style={{
                backgroundColor: currentLevel.bgColor,
                color: currentLevel.color
              }}
            >
              <Sparkles className="w-4 h-4" />
              {currentLevel.label}
            </motion.div>

            {/* Motivational Message */}
            <h3 className="text-xl font-bold text-gray-900 mb-2">
              {message}
            </h3>

            {/* Progress to next milestone */}
            {nextBenefit && currentCompletion < 100 && (
              <div className="flex items-center gap-2 text-sm text-gray-600 mb-4">
                <TrendingUp className="w-4 h-4" />
                <span>
                  Faltam {nextBenefit - currentCompletion}% para desbloquear novos recursos
                </span>
              </div>
            )}

            {/* Unlocked Benefits */}
            {unlockedBenefits.length > 0 && (
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center gap-2">
                  <CheckCircle2 className="w-4 h-4 text-green-500" />
                  Recursos Desbloqueados
                </h4>
                <div className="space-y-1">
                  {unlockedBenefits.flatMap(b => b.items).map((benefit, idx) => (
                    <motion.div
                      key={idx}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: idx * 0.1 }}
                      className="flex items-center gap-2 text-sm text-gray-600"
                    >
                      <div className="w-1.5 h-1.5 rounded-full bg-green-500" />
                      {benefit}
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            {/* Next Benefits Preview */}
            {nextBenefit && currentCompletion < 100 && (
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center gap-2">
                  <Lock className="w-4 h-4 text-gray-400" />
                  Próximos Desbloqueios ({nextBenefit}%)
                </h4>
                <div className="space-y-1">
                  {benefits[nextBenefit as keyof typeof benefits].map((benefit, idx) => (
                    <motion.div
                      key={idx}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: idx * 0.1 }}
                      className="flex items-center gap-2 text-sm text-gray-400"
                    >
                      <div className="w-1.5 h-1.5 rounded-full bg-gray-300" />
                      {benefit}
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            {/* CTA Button */}
            {currentCompletion < 100 && onContinue && (
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={onContinue}
                className="w-full bg-gradient-to-r from-[#0066CC] to-[#00A3E0] text-white font-semibold py-3 px-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow flex items-center justify-center gap-2"
              >
                <Sparkles className="w-5 h-5" />
                Continue preenchendo
                <motion.span
                  animate={{ x: [0, 4, 0] }}
                  transition={{ duration: 1, repeat: Infinity }}
                >
                  →
                </motion.span>
              </motion.button>
            )}

            {/* Perfect completion message */}
            {currentCompletion === 100 && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-gradient-to-r from-yellow-50 to-orange-50 border-2 border-yellow-300 rounded-xl p-4 text-center"
              >
                <div className="flex items-center justify-center gap-2 text-lg font-bold text-yellow-700 mb-1">
                  <Star className="w-6 h-6 fill-yellow-500 text-yellow-500" />
                  Cadastro Perfeito!
                  <Star className="w-6 h-6 fill-yellow-500 text-yellow-500" />
                </div>
                <p className="text-sm text-yellow-600">
                  Você desbloqueou todos os recursos da plataforma!
                </p>
              </motion.div>
            )}
          </div>
        </div>

        {/* Progress Bar (mobile fallback) */}
        <div className="mt-6 md:hidden">
          <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
            <motion.div
              initial={{ width: 0 }}
              animate={{ width: `${currentCompletion}%` }}
              transition={{ duration: 1, ease: "easeOut" }}
              className="h-full rounded-full"
              style={{ backgroundColor: currentLevel.color }}
            />
          </div>
        </div>
      </div>
    </motion.div>
  )
}

"use client"

import { useState } from "react"
import { generateConsentTermHTML, type TermData } from "@/lib/consent-term-template"
import { Button } from "@/components/ui/button"
import { Checkbox } from "@/components/ui/checkbox"
import { Label } from "@/components/ui/label"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Download, Printer, Upload, Check, AlertCircle } from "lucide-react"
import { useRouter } from "next/navigation"
import { toast } from "sonner"

interface ConsentTermViewerProps {
  data: TermData
  patientId: string
}

export function ConsentTermViewer({ data, patientId }: ConsentTermViewerProps) {
  const router = useRouter()
  const [physicalSignatureConfirmed, setPhysicalSignatureConfirmed] = useState(false)
  const [uploading, setUploading] = useState(false)
  const [uploadedFile, setUploadedFile] = useState<File | null>(null)
  const [confirming, setConfirming] = useState(false)

  const termHtml = generateConsentTermHTML(data)

  const handlePrint = () => {
    // Abre janela de impressão
    const printWindow = window.open("", "_blank")
    if (printWindow) {
      printWindow.document.write(termHtml)
      printWindow.document.close()
      printWindow.focus()
      printWindow.print()

      toast.success("Termo pronto para impressão")
    }
  }

  const handleDownloadPDF = () => {
    // Abre o termo em nova aba para salvar como PDF
    const pdfWindow = window.open("", "_blank")
    if (pdfWindow) {
      pdfWindow.document.write(termHtml)
      pdfWindow.document.close()

      // Aguarda carregamento e dispara ctrl+p
      setTimeout(() => {
        pdfWindow.focus()
        pdfWindow.print()
      }, 250)

      toast.success("Use 'Salvar como PDF' na janela de impressão")
    }
  }

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0]
    if (file) {
      // Valida tipo de arquivo
      const allowedTypes = ["application/pdf", "image/jpeg", "image/png", "image/jpg"]
      if (!allowedTypes.includes(file.type)) {
        toast.error("Apenas PDF ou imagens (JPG, PNG) são permitidos")
        return
      }

      // Valida tamanho (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast.error("Arquivo muito grande. Máximo: 5MB")
        return
      }

      setUploadedFile(file)
      toast.success(`Arquivo "${file.name}" selecionado`)
    }
  }

  const handleConfirm = async () => {
    if (!physicalSignatureConfirmed) {
      toast.error("Você precisa confirmar que obteve a assinatura física")
      return
    }

    setConfirming(true)

    try {
      // Se há arquivo, faz upload primeiro
      let fileUrl = null
      if (uploadedFile) {
        const formData = new FormData()
        formData.append("file", uploadedFile)
        formData.append("patientId", patientId)

        const uploadRes = await fetch("/api/consent-term/upload", {
          method: "POST",
          body: formData,
        })

        if (!uploadRes.ok) {
          throw new Error("Erro ao fazer upload do arquivo")
        }

        const uploadData = await uploadRes.json()
        fileUrl = uploadData.url
      }

      // Registra o consentimento no banco
      const res = await fetch("/api/consent-term/confirm", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          patientId,
          fileUrl,
          termData: data,
        }),
      })

      if (!res.ok) {
        throw new Error("Erro ao confirmar consentimento")
      }

      toast.success("Consentimento registrado com sucesso!")

      // Redireciona de volta ao dashboard do paciente
      setTimeout(() => {
        router.push(`/pacientes/${patientId}`)
      }, 1500)

    } catch (error) {
      console.error("Erro ao confirmar:", error)
      toast.error("Erro ao registrar consentimento. Tente novamente.")
    } finally {
      setConfirming(false)
    }
  }

  return (
    <div className="container mx-auto py-8 space-y-6">
      {/* Instruções */}
      <Card className="border-blue-200 bg-blue-50">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertCircle className="h-5 w-5 text-blue-600" />
            Como Obter o Consentimento
          </CardTitle>
          <CardDescription>
            Siga os passos abaixo para formalizar o consentimento do paciente
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="flex gap-3">
            <div className="flex-shrink-0 w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center font-bold">
              1
            </div>
            <div>
              <p className="font-medium">Imprima o termo</p>
              <p className="text-sm text-muted-foreground">
                Use o botão &quot;Imprimir Termo&quot; abaixo para gerar uma cópia física
              </p>
            </div>
          </div>

          <div className="flex gap-3">
            <div className="flex-shrink-0 w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center font-bold">
              2
            </div>
            <div>
              <p className="font-medium">Obtenha a assinatura física</p>
              <p className="text-sm text-muted-foreground">
                Apresente o termo ao paciente, explique e colete a assinatura manuscrita
              </p>
            </div>
          </div>

          <div className="flex gap-3">
            <div className="flex-shrink-0 w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center font-bold">
              3
            </div>
            <div>
              <p className="font-medium">Upload do termo assinado (opcional)</p>
              <p className="text-sm text-muted-foreground">
                Escaneie ou fotografe o termo assinado e faça upload para arquivo digital
              </p>
            </div>
          </div>

          <div className="flex gap-3">
            <div className="flex-shrink-0 w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center font-bold">
              4
            </div>
            <div>
              <p className="font-medium">Confirme no sistema</p>
              <p className="text-sm text-muted-foreground">
                Marque a checkbox de confirmação e clique em &quot;Confirmar Consentimento&quot;
              </p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Botões de ação */}
      <Card>
        <CardHeader>
          <CardTitle>Ações do Termo</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex flex-wrap gap-3">
            <Button onClick={handlePrint} variant="default" size="lg">
              <Printer className="mr-2 h-4 w-4" />
              Imprimir Termo
            </Button>

            <Button onClick={handleDownloadPDF} variant="outline" size="lg">
              <Download className="mr-2 h-4 w-4" />
              Salvar como PDF
            </Button>
          </div>

          {/* Upload opcional */}
          <div className="border-t pt-4">
            <Label htmlFor="file-upload" className="block mb-2 font-medium">
              Upload do Termo Assinado (Opcional)
            </Label>
            <div className="flex items-center gap-3">
              <Button asChild variant="outline">
                <label htmlFor="file-upload" className="cursor-pointer">
                  <Upload className="mr-2 h-4 w-4" />
                  Escolher Arquivo
                </label>
              </Button>
              <input
                id="file-upload"
                type="file"
                accept=".pdf,image/jpeg,image/png,image/jpg"
                onChange={handleFileSelect}
                className="hidden"
              />
              {uploadedFile && (
                <span className="text-sm text-green-600 flex items-center gap-1">
                  <Check className="h-4 w-4" />
                  {uploadedFile.name}
                </span>
              )}
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              Formatos aceitos: PDF, JPG, PNG (máx. 5MB)
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Confirmação */}
      <Card className="border-green-200">
        <CardHeader>
          <CardTitle>Confirmar Consentimento</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-start space-x-3">
            <Checkbox
              id="physical-signature"
              checked={physicalSignatureConfirmed}
              onCheckedChange={(checked) => setPhysicalSignatureConfirmed(checked as boolean)}
            />
            <Label
              htmlFor="physical-signature"
              className="text-sm font-normal cursor-pointer leading-relaxed"
            >
              <span className="font-medium">Confirmo que obtive a assinatura física do paciente</span>
              <br />
              <span className="text-muted-foreground">
                O termo foi impresso, apresentado ao paciente, devidamente explicado, e a assinatura manuscrita foi coletada no documento físico.
              </span>
            </Label>
          </div>

          <Button
            onClick={handleConfirm}
            disabled={!physicalSignatureConfirmed || confirming}
            size="lg"
            className="w-full"
          >
            {confirming ? (
              <>Registrando...</>
            ) : (
              <>
                <Check className="mr-2 h-4 w-4" />
                Confirmar Consentimento
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {/* Preview do termo */}
      <Card>
        <CardHeader>
          <CardTitle>Preview do Termo</CardTitle>
          <CardDescription>
            Visualização de como o termo será impresso
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div
            className="border rounded-lg p-6 bg-white max-h-[600px] overflow-y-auto"
            dangerouslySetInnerHTML={{ __html: termHtml }}
          />
        </CardContent>
      </Card>
    </div>
  )
}

