            ) : (
              <span className="flex items-center gap-2">
                ATIVAR ACOMPANHAMENTO
              </span>
            )}
          </Button>

          <p className="text-sm text-muted-foreground text-center">
            Tempo estimado: 30 segundos
          </p>
        </form>
      </CardContent>
    </Card>
  )
}

