interface AnalyticsData {
  totalPatients: number
  patients: {
    id: string
    name: string
    age: number | null
    sex: string | null
    surgeries: {
      id: string
      type: string
      date: string
      followUps: {
        dayNumber: number
        status: string
        responses: {
          questionnaireData: string
          riskLevel: string
          redFlags: string | null
        }[]
      }[]
    }[]
  }[]
}

interface AnalyticsDashboardProps {
  data: AnalyticsData
  userName: string
}

const COLORS = {
  primary: "#0A2647",
  secondary: "#D4AF37",
  success: "#10B981",
  warning: "#F59E0B",
  error: "#EF4444",
  info: "#3B82F6",
  purple: "#8B5CF6",
  pink: "#EC4899",
}

export function AnalyticsDashboard({ data, userName }: AnalyticsDashboardProps) {
  const [surgeryFilter, setSurgeryFilter] = useState<string>("all")
  const [periodFilter, setPeriodFilter] = useState<string>("30")

  // ========== PROCESSA DADOS ==========

  const processedData = useMemo(() => {
    let filtered = data.patients

    // Filtra por tipo de cirurgia
    if (surgeryFilter !== "all") {
      filtered = filtered.filter((p) => p.surgeries.some((s) => s.type === surgeryFilter))
    }

    // Filtra por período
    const periodDays = parseInt(periodFilter)
    const cutoffDate = new Date()
    cutoffDate.setDate(cutoffDate.getDate() - periodDays)
    filtered = filtered.filter((p) =>
      p.surgeries.some((s) => new Date(s.date) >= cutoffDate)
    )

    // 1. Curva de Dor ao Longo do Tempo
    const painOverTime: Record<number, number[]> = {}
    filtered.forEach((patient) => {
      patient.surgeries.forEach((surgery) => {
        surgery.followUps.forEach((fu) => {
          if (fu.responses[0]) {
            try {
              const data = JSON.parse(fu.responses[0].questionnaireData)
              if (data.painLevel) {
                if (!painOverTime[fu.dayNumber]) painOverTime[fu.dayNumber] = []
                painOverTime[fu.dayNumber].push(Number(data.painLevel))
              }
            } catch {}
          }
        })
      })
    })

    const painChartData = Object.entries(painOverTime)
      .map(([day, values]) => ({
        day: `D+${day}`,
        dayNumber: Number(day),
        média: (values.reduce((a, b) => a + b, 0) / values.length).toFixed(1),
        n: values.length,
      }))
      .sort((a, b) => a.dayNumber - b.dayNumber)

    // 2. Distribuição de Tipos de Cirurgia
    const surgeryTypes: Record<string, number> = {}
    filtered.forEach((patient) => {
      patient.surgeries.forEach((surgery) => {
        surgeryTypes[surgery.type] = (surgeryTypes[surgery.type] || 0) + 1
      })
    })

    const surgeryDistribution = Object.entries(surgeryTypes).map(([name, value]) => ({
      name: name.charAt(0).toUpperCase() + name.slice(1),
      value,
    }))

    // 3. Níveis de Risco
    const riskLevels = { low: 0, medium: 0, high: 0, critical: 0 }
    filtered.forEach((patient) => {
      patient.surgeries.forEach((surgery) => {
        surgery.followUps.forEach((fu) => {
          if (fu.responses[0]) {
            const level = fu.responses[0].riskLevel as keyof typeof riskLevels
            if (level in riskLevels) {
              riskLevels[level]++
            }
          }
        })
      })
    })

    const riskDistribution = [
      { name: "Baixo", value: riskLevels.low, color: COLORS.success },
      { name: "Médio", value: riskLevels.medium, color: COLORS.warning },
      { name: "Alto", value: riskLevels.high, color: COLORS.error },
      { name: "Crítico", value: riskLevels.critical, color: "#991B1B" },
    ]

    // 4. Taxa de Resposta por Dia
    const responseRates: Record<number, { total: number; responded: number }> = {}
    filtered.forEach((patient) => {
      patient.surgeries.forEach((surgery) => {
        surgery.followUps.forEach((fu) => {
          if (!responseRates[fu.dayNumber]) {
            responseRates[fu.dayNumber] = { total: 0, responded: 0 }
          }
          responseRates[fu.dayNumber].total++
          if (fu.status === "responded") {
            responseRates[fu.dayNumber].responded++
          }
        })
      })
    })

    const responseRateData = Object.entries(responseRates)
      .map(([day, stats]) => ({
        day: `D+${day}`,
        dayNumber: Number(day),
        taxa: ((stats.responded / stats.total) * 100).toFixed(1),
      }))
      .sort((a, b) => a.dayNumber - b.dayNumber)

    // 5. Correlação Idade vs Dor
    const ageVsPain: { idade: number; dor: number }[] = []
    filtered.forEach((patient) => {
      if (patient.age) {
        patient.surgeries.forEach((surgery) => {
          const d1 = surgery.followUps.find((fu) => fu.dayNumber === 1)
          if (d1?.responses[0]) {
            try {
              const data = JSON.parse(d1.responses[0].questionnaireData)
              if (data.painLevel && patient.age !== null) {
                ageVsPain.push({
                  idade: patient.age,
                  dor: Number(data.painLevel),
                })
              }
            } catch {}
          }
        })
      }
    })

    // 6. Distribuição por Sexo
    const sexDistribution = [
      {
        name: "Masculino",
        value: filtered.filter((p) => p.sex === "Masculino").length,
        color: COLORS.info,
      },
      {
        name: "Feminino",
        value: filtered.filter((p) => p.sex === "Feminino").length,
        color: COLORS.pink,
      },
    ]

    // 7. KPIs
    const totalFollowUps = filtered.reduce(
      (acc, p) => acc + p.surgeries.reduce((a, s) => a + s.followUps.length, 0),
      0
    )
    const respondedFollowUps = filtered.reduce(
      (acc, p) =>
        acc +
        p.surgeries.reduce(
          (a, s) => a + s.followUps.filter((fu) => fu.status === "responded").length,
          0
        ),
      0
    )
    const responseRate = totalFollowUps > 0 ? ((respondedFollowUps / totalFollowUps) * 100).toFixed(1) : "0"

    const criticalAlerts = filtered.reduce(
      (acc, p) =>
        acc +
        p.surgeries.reduce(
          (a, s) =>
            a +
            s.followUps.filter((fu) => fu.responses[0]?.riskLevel === "critical").length,
          0
        ),
      0
    )

    return {
      painChartData,
      surgeryDistribution,
      riskDistribution,
      responseRateData,
      ageVsPain,
      sexDistribution,
      kpis: {
        totalPatients: filtered.length,
        totalFollowUps,
        responseRate,
        criticalAlerts,
      },
    }
  }, [data, surgeryFilter, periodFilter])

  // Tipos de cirurgia disponíveis
  const availableSurgeryTypes = Array.from(
    new Set(data.patients.flatMap((p) => p.surgeries.map((s) => s.type)))
  )

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Analytics Avançado</h1>
          <p className="text-muted-foreground">
            Análises e insights dos seus pacientes, Dr. {userName}
          </p>
        </div>
      </div>

      {/* Filtros */}
      <Card>
        <CardHeader>
          <CardTitle>Filtros</CardTitle>
        </CardHeader>
        <CardContent className="flex gap-4">
          <div className="flex-1">
            <label className="text-sm font-medium mb-2 block">Tipo de Cirurgia</label>
            <Select value={surgeryFilter} onValueChange={setSurgeryFilter}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todas</SelectItem>
                {availableSurgeryTypes.map((type) => (
                  <SelectItem key={type} value={type}>
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div className="flex-1">
            <label className="text-sm font-medium mb-2 block">Período</label>
            <Select value={periodFilter} onValueChange={setPeriodFilter}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="7">Últimos 7 dias</SelectItem>
                <SelectItem value="30">Últimos 30 dias</SelectItem>
                <SelectItem value="90">Últimos 90 dias</SelectItem>
                <SelectItem value="365">Último ano</SelectItem>
                <SelectItem value="9999">Todos</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* KPIs */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between pb-2">
            <CardTitle className="text-sm font-medium">Total de Pacientes</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{processedData.kpis.totalPatients}</div>
            <p className="text-xs text-muted-foreground mt-1">pacientes ativos</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between pb-2">
            <CardTitle className="text-sm font-medium">Taxa de Resposta</CardTitle>
            <Activity className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{processedData.kpis.responseRate}%</div>
            <p className="text-xs text-muted-foreground mt-1">
              {processedData.kpis.totalFollowUps} follow-ups
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between pb-2">
            <CardTitle className="text-sm font-medium">Alertas Críticos</CardTitle>
            <AlertTriangle className="h-4 w-4 text-error" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-error">{processedData.kpis.criticalAlerts}</div>
            <p className="text-xs text-muted-foreground mt-1">requerem atenção</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between pb-2">
            <CardTitle className="text-sm font-medium">Dor Média D+1</CardTitle>
            <TrendingDown className="h-4 w-4 text-success" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {processedData.painChartData[0]?.média || "-"}/10
            </div>
            <p className="text-xs text-muted-foreground mt-1">primeiro dia pós-op</p>
          </CardContent>
        </Card>
      </div>

      {/* Gráficos */}
      <Tabs defaultValue="pain" className="space-y-4">
        <TabsList className="grid w-full grid-cols-2 lg:grid-cols-5">
          <TabsTrigger value="pain">Curva de Dor</TabsTrigger>
          <TabsTrigger value="surgery">Tipos de Cirurgia</TabsTrigger>
          <TabsTrigger value="risk">Níveis de Risco</TabsTrigger>
          <TabsTrigger value="response">Taxa de Resposta</TabsTrigger>
          <TabsTrigger value="correlation">Idade vs Dor</TabsTrigger>
        </TabsList>

        {/* 1. Curva de Dor ao Longo do Tempo */}
        <TabsContent value="pain">
          <Card>
            <CardHeader>
              <CardTitle>Evolução da Dor Pós-Operatória</CardTitle>
              <CardDescription>
                Média de dor (escala 0-10) ao longo dos dias de acompanhamento
              </CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <AreaChart data={processedData.painChartData}>
                  <defs>
                    <linearGradient id="colorPain" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor={COLORS.error} stopOpacity={0.8} />
                      <stop offset="95%" stopColor={COLORS.error} stopOpacity={0} />
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="day" />
                  <YAxis domain={[0, 10]} />
                  <Tooltip />
                  <Legend />
                  <Area
                    type="monotone"
                    dataKey="média"
                    stroke={COLORS.error}
                    fillOpacity={1}
                    fill="url(#colorPain)"
                  />
                </AreaChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </TabsContent>

        {/* 2. Distribuição de Tipos de Cirurgia */}
        <TabsContent value="surgery">
          <Card>
            <CardHeader>
              <CardTitle>Distribuição por Tipo de Cirurgia</CardTitle>
              <CardDescription>Quantidade de procedimentos realizados por tipo</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <PieChart>
                  <Pie
                    data={processedData.surgeryDistribution}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, percent }) => `${name}: ${((percent || 0) * 100).toFixed(0)}%`}
                    outerRadius={120}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {processedData.surgeryDistribution.map((entry, index) => (
                      <Cell
                        key={`cell-${index}`}
                        fill={Object.values(COLORS)[index % Object.values(COLORS).length]}
                      />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </TabsContent>

        {/* 3. Níveis de Risco */}
        <TabsContent value="risk">
          <Card>
            <CardHeader>
              <CardTitle>Distribuição de Níveis de Risco</CardTitle>
              <CardDescription>
                Quantidade de follow-ups por nível de risco detectado
              </CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={processedData.riskDistribution}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="value" name="Quantidade">
                    {processedData.riskDistribution.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </TabsContent>

        {/* 4. Taxa de Resposta */}
        <TabsContent value="response">
          <Card>
            <CardHeader>
              <CardTitle>Taxa de Resposta aos Follow-ups</CardTitle>
              <CardDescription>
                Percentual de pacientes que responderam por dia pós-operatório
              </CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={processedData.responseRateData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="day" />
                  <YAxis domain={[0, 100]} />
                  <Tooltip />
                  <Legend />
                  <Line
                    type="monotone"
                    dataKey="taxa"
                    name="Taxa de Resposta (%)"
                    stroke={COLORS.success}
                    strokeWidth={2}
                    dot={{ fill: COLORS.success, r: 4 }}
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </TabsContent>

        {/* 5. Correlação Idade vs Dor */}
        <TabsContent value="correlation">
          <Card>
            <CardHeader>
              <CardTitle>Correlação Idade vs Dor (D+1)</CardTitle>
              <CardDescription>
                Relação entre idade do paciente e nível de dor no primeiro dia pós-operatório
              </CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <ScatterChart>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis type="number" dataKey="idade" name="Idade" unit=" anos" />
                  <YAxis type="number" dataKey="dor" name="Dor" domain={[0, 10]} />
                  <Tooltip cursor={{ strokeDasharray: "3 3" }} />
                  <Legend />
                  <Scatter
                    name="Pacientes"
                    data={processedData.ageVsPain}
                    fill={COLORS.primary}
                    opacity={0.6}
                  />
                </ScatterChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  )
}

'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Slider } from '@/components/ui/slider';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import {
  Settings2,
  Grid3x3,
  Palette,
  Tag,
  Maximize2,
  RefreshCw,
} from 'lucide-react';

export interface ChartSettings {
  showConfidenceIntervals: boolean;
  showGridLines: boolean;
  showDataLabels: boolean;
  showLegend: boolean;
  colorScheme: 'telos' | 'viridis' | 'categorical' | 'monochrome';
  axisScaleX: 'linear' | 'log';
  axisScaleY: 'linear' | 'log';
  opacity: number;
  fontSize: number;
}

interface ChartControlsProps {
  settings: ChartSettings;
  onSettingsChange: (settings: ChartSettings) => void;
  onReset?: () => void;
  showScaleControls?: boolean;
  showColorControls?: boolean;
}

const STORAGE_KEY = 'chart-settings';

export function ChartControls({
  settings,
  onSettingsChange,
  onReset,
  showScaleControls = true,
  showColorControls = true,
}: ChartControlsProps) {
  const [localSettings, setLocalSettings] = useState<ChartSettings>(settings);

  // Load settings from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setTimeout(() => setLocalSettings({ ...settings, ...parsed }), 0);
        onSettingsChange({ ...settings, ...parsed });
      } catch (e) {
        console.error('Failed to parse saved chart settings', e);
      }
    }
  }, []);

  // Save settings to localStorage when they change
  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(localSettings));
  }, [localSettings]);

  const updateSetting = <K extends keyof ChartSettings>(key: K, value: ChartSettings[K]) => {
    const newSettings = { ...localSettings, [key]: value };
    setLocalSettings(newSettings);
    onSettingsChange(newSettings);
  };

  const handleReset = () => {
    const defaultSettings: ChartSettings = {
      showConfidenceIntervals: true,
      showGridLines: true,
      showDataLabels: false,
      showLegend: true,
      colorScheme: 'telos',
      axisScaleX: 'linear',
      axisScaleY: 'linear',
      opacity: 100,
      fontSize: 12,
    };
    setLocalSettings(defaultSettings);
    onSettingsChange(defaultSettings);
    localStorage.removeItem(STORAGE_KEY);
    if (onReset) onReset();
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline" size="sm">
          <Settings2 className="h-4 w-4 mr-2" />
          Controles do Gráfico
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80" align="end">
        <div className="space-y-4">
          <div>
            <h4 className="font-semibold mb-3 flex items-center gap-2">
              <Settings2 className="h-4 w-4" />
              Configurações do Gráfico
            </h4>
          </div>

          {/* Display Options */}
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <Label htmlFor="ci-toggle" className="text-sm flex items-center gap-2">
                <Grid3x3 className="h-3.5 w-3.5" />
                Intervalos de Confiança
              </Label>
              <Switch
                id="ci-toggle"
                checked={localSettings.showConfidenceIntervals}
                onCheckedChange={(checked: boolean) => updateSetting('showConfidenceIntervals', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="grid-toggle" className="text-sm flex items-center gap-2">
                <Grid3x3 className="h-3.5 w-3.5" />
                Linhas de Grade
              </Label>
              <Switch
                id="grid-toggle"
                checked={localSettings.showGridLines}
                onCheckedChange={(checked: boolean) => updateSetting('showGridLines', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="labels-toggle" className="text-sm flex items-center gap-2">
                <Tag className="h-3.5 w-3.5" />
                Rótulos de Dados
              </Label>
              <Switch
                id="labels-toggle"
                checked={localSettings.showDataLabels}
                onCheckedChange={(checked: boolean) => updateSetting('showDataLabels', checked)}
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="legend-toggle" className="text-sm">
                Mostrar Legenda
              </Label>
              <Switch
                id="legend-toggle"
                checked={localSettings.showLegend}
                onCheckedChange={(checked: boolean) => updateSetting('showLegend', checked)}
              />
            </div>
          </div>

          {/* Color Scheme */}
          {showColorControls && (
            <div className="space-y-2">
              <Label className="text-sm flex items-center gap-2">
                <Palette className="h-3.5 w-3.5" />
                Esquema de Cores
              </Label>
              <Select
                value={localSettings.colorScheme}
                // eslint-disable-next-line @typescript-eslint/no-explicit-any
                onValueChange={(value: any) => updateSetting('colorScheme', value)}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="telos">
                    <div className="flex items-center gap-2">
                      <div className="flex gap-0.5">
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#0A2647' }} />
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#144272' }} />
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#205295' }} />
                      </div>
                      Telos.AI (Azul)
                    </div>
                  </SelectItem>
                  <SelectItem value="viridis">
                    <div className="flex items-center gap-2">
                      <div className="flex gap-0.5">
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#440154' }} />
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#31688e' }} />
                        <div className="w-3 h-3 rounded-full" style={{ backgroundColor: '#35b779' }} />
                      </div>
                      Viridis (Científico)
                    </div>
                  </SelectItem>
                  <SelectItem value="categorical">
                    <div className="flex items-center gap-2">
                      <div className="flex gap-0.5">
                        <div className="w-3 h-3 rounded-full bg-red-500" />
                        <div className="w-3 h-3 rounded-full bg-blue-500" />
                        <div className="w-3 h-3 rounded-full bg-green-500" />
                      </div>
                      Categórico (Distinto)
                    </div>
                  </SelectItem>
                  <SelectItem value="monochrome">
                    <div className="flex items-center gap-2">
                      <div className="flex gap-0.5">
                        <div className="w-3 h-3 rounded-full bg-gray-700" />
                        <div className="w-3 h-3 rounded-full bg-gray-500" />
                        <div className="w-3 h-3 rounded-full bg-gray-300" />
                      </div>
                      Monocromático (P&B)
                    </div>
                  </SelectItem>
                </SelectContent>
              </Select>
            </div>
          )}

          {/* Axis Scales */}
          {showScaleControls && (
            <div className="space-y-3">
              <div className="space-y-2">
                <Label className="text-sm flex items-center gap-2">
                  <Maximize2 className="h-3.5 w-3.5" />
                  Escala do Eixo X
                </Label>
                <Select
                  value={localSettings.axisScaleX}
                  // eslint-disable-next-line @typescript-eslint/no-explicit-any
                  onValueChange={(value: any) => updateSetting('axisScaleX', value)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="linear">Linear</SelectItem>
                    <SelectItem value="log">Logarítmica</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label className="text-sm flex items-center gap-2">
                  <Maximize2 className="h-3.5 w-3.5 rotate-90" />
                  Escala do Eixo Y
                </Label>
                <Select
                  value={localSettings.axisScaleY}
                  // eslint-disable-next-line @typescript-eslint/no-explicit-any
                  onValueChange={(value: any) => updateSetting('axisScaleY', value)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="linear">Linear</SelectItem>
                    <SelectItem value="log">Logarítmica</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          )}

          {/* Opacity Slider */}
          <div className="space-y-2">
            <Label className="text-sm">
              Opacidade: {localSettings.opacity}%
            </Label>
            <Slider
              value={[localSettings.opacity]}
              onValueChange={([value]) => updateSetting('opacity', value)}
              min={20}
              max={100}
              step={10}
              className="w-full"
            />
          </div>

          {/* Font Size Slider */}
          <div className="space-y-2">
            <Label className="text-sm">
              Tamanho da Fonte: {localSettings.fontSize}px
            </Label>
            <Slider
              value={[localSettings.fontSize]}
              onValueChange={([value]) => updateSetting('fontSize', value)}
              min={8}
              max={16}
              step={1}
              className="w-full"
            />
          </div>

          {/* Reset Button */}
          <Button
            variant="outline"
            size="sm"
            onClick={handleReset}
            className="w-full"
          >
            <RefreshCw className="h-3.5 w-3.5 mr-2" />
            Restaurar Padrões
          </Button>
        </div>
      </PopoverContent>
    </Popover>
  );
}

export function getColorPalette(scheme: ChartSettings['colorScheme']): string[] {
  switch (scheme) {
    case 'telos':
      return ['#0A2647', '#144272', '#205295', '#2C74B3', '#3A8BC9', '#4EA5D9'];
    case 'viridis':
      return ['#440154', '#31688e', '#35b779', '#fde724'];
    case 'categorical':
      return ['#ef4444', '#3b82f6', '#22c55e', '#f59e0b', '#a855f7', '#ec4899'];
    case 'monochrome':
      return ['#1f2937', '#4b5563', '#6b7280', '#9ca3af', '#d1d5db', '#e5e7eb'];
    default:
      return ['#0A2647', '#144272', '#205295', '#2C74B3'];
  }
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { toast } from 'sonner';
import {
  Download,
  FileImage,
  FileCode,
  Copy,
  FileText,
  Check,
} from 'lucide-react';
import html2canvas from 'html2canvas';

interface ChartExportMenuProps {
  chartRef: React.RefObject<HTMLDivElement | null>;
  chartName?: string;
  data?: any[]; // Chart data for CSV export
  onExport?: (format: 'png' | 'svg' | 'csv') => void;
}

export function ChartExportMenu({ chartRef, chartName = 'chart', data, onExport }: ChartExportMenuProps) {
  const [isExporting, setIsExporting] = useState(false);

  const exportAsPNG = async (resolution: 'standard' | 'high' = 'standard') => {
    if (!chartRef.current) {
      toast.error('Gráfico não encontrado');
      return;
    }

    setIsExporting(true);

    try {
      const scale = resolution === 'high' ? 3 : 2; // 300 DPI vs 150 DPI

      const canvas = await html2canvas(chartRef.current, {
        scale,
        backgroundColor: '#ffffff',
        logging: false,
        useCORS: true,
        allowTaint: true,
      } as any);

      const link = document.createElement('a');
      const timestamp = new Date().toISOString().split('T')[0];
      link.download = `${chartName}-${resolution === 'high' ? '300dpi' : '150dpi'}-${timestamp}.png`;
      link.href = canvas.toDataURL('image/png');
      link.click();

      toast.success(`Gráfico exportado em ${resolution === 'high' ? 'alta' : 'padrão'} resolução!`);

      if (onExport) onExport('png');
    } catch (error) {
      console.error('Error exporting PNG:', error);
      toast.error('Erro ao exportar gráfico');
    } finally {
      setIsExporting(false);
    }
  };

  const exportAsSVG = async () => {
    if (!chartRef.current) {
      toast.error('Gráfico não encontrado');
      return;
    }

    setIsExporting(true);

    try {
      // Clone the chart element
      const svgElements = chartRef.current.querySelectorAll('svg');

      if (svgElements.length === 0) {
        toast.error('Nenhum gráfico SVG encontrado');
        return;
      }

      // Get the first (main) SVG
      const svgElement = svgElements[0];
      const svgData = new XMLSerializer().serializeToString(svgElement);

      // Add XML declaration and proper encoding
      const svgBlob = new Blob(
        ['<?xml version="1.0" encoding="UTF-8"?>\n' + svgData],
        { type: 'image/svg+xml;charset=utf-8' }
      );

      const link = document.createElement('a');
      const timestamp = new Date().toISOString().split('T')[0];
      link.download = `${chartName}-${timestamp}.svg`;
      link.href = URL.createObjectURL(svgBlob);
      link.click();

      toast.success('Gráfico exportado como SVG!');

      if (onExport) onExport('svg');
    } catch (error) {
      console.error('Error exporting SVG:', error);
      toast.error('Erro ao exportar gráfico como SVG');
    } finally {
      setIsExporting(false);
    }
  };

  const copyToClipboard = async () => {
    if (!chartRef.current) {
      toast.error('Gráfico não encontrado');
      return;
    }

    setIsExporting(true);

    try {
      const canvas = await html2canvas(chartRef.current, {
        scale: 2,
        backgroundColor: '#ffffff',
        logging: false,
      } as any);

      canvas.toBlob(async (blob) => {
        if (!blob) {
          toast.error('Erro ao criar imagem');
          return;
        }

        try {
          await navigator.clipboard.write([
            new ClipboardItem({
              'image/png': blob,
            }),
          ]);

          toast.success('Gráfico copiado para área de transferência!');
        } catch (clipboardError) {
          console.error('Clipboard error:', clipboardError);
          toast.error('Erro ao copiar para área de transferência');
        } finally {
          setIsExporting(false);
        }
      });
    } catch (error) {
      console.error('Error copying to clipboard:', error);
      toast.error('Erro ao copiar gráfico');
      setIsExporting(false);
    }
  };

  const exportData = () => {
    if (!data || data.length === 0) {
      toast.error('Nenhum dado disponível para exportação');
      return;
    }

    setIsExporting(true);

    try {
      // Convert data to CSV format
      const headers = Object.keys(data[0]);
      const csvRows = [headers.join(',')];

      for (const row of data) {
        const values = headers.map(header => {
          const value = row[header];
          // Handle nested objects and arrays
          if (typeof value === 'object' && value !== null) {
            return JSON.stringify(value).replace(/,/g, ';');
          }
          // Escape commas and quotes in strings
          if (typeof value === 'string') {
            return `"${value.replace(/"/g, '""')}"`;
          }
          return value;
        });
        csvRows.push(values.join(','));
      }

      const csvContent = csvRows.join('\n');
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      const timestamp = new Date().toISOString().split('T')[0];
      link.download = `${chartName}-data-${timestamp}.csv`;
      link.href = URL.createObjectURL(blob);
      link.click();

      toast.success('Dados exportados com sucesso!');

      if (onExport) onExport('csv');
    } catch (error) {
      console.error('Error exporting CSV:', error);
      toast.error('Erro ao exportar dados');
    } finally {
      setIsExporting(false);
    }
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="sm" disabled={isExporting}>
          {isExporting ? (
            <>
              <div className="animate-spin rounded-full h-3.5 w-3.5 border-b-2 border-gray-900 mr-2" />
              Exportando...
            </>
          ) : (
            <>
              <Download className="h-4 w-4 mr-2" />
              Exportar
            </>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-56">
        <DropdownMenuLabel>Formato de Exportação</DropdownMenuLabel>
        <DropdownMenuSeparator />

        <DropdownMenuItem onClick={() => exportAsPNG('standard')}>
          <FileImage className="h-4 w-4 mr-2" />
          <div className="flex flex-col">
            <span>PNG Padrão</span>
            <span className="text-xs text-gray-500">150 DPI (para web)</span>
          </div>
        </DropdownMenuItem>

        <DropdownMenuItem onClick={() => exportAsPNG('high')}>
          <FileImage className="h-4 w-4 mr-2" />
          <div className="flex flex-col">
            <span>PNG Alta Resolução</span>
            <span className="text-xs text-gray-500">300 DPI (para publicação)</span>
          </div>
        </DropdownMenuItem>

        <DropdownMenuItem onClick={exportAsSVG}>
          <FileCode className="h-4 w-4 mr-2" />
          <div className="flex flex-col">
            <span>SVG (Vetor)</span>
            <span className="text-xs text-gray-500">Redimensionável</span>
          </div>
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        <DropdownMenuItem onClick={copyToClipboard}>
          <Copy className="h-4 w-4 mr-2" />
          Copiar para Área de Transferência
        </DropdownMenuItem>

        <DropdownMenuItem onClick={exportData}>
          <FileText className="h-4 w-4 mr-2" />
          Exportar Dados (CSV)
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

