}

interface RegressionLine {
  slope: number;
  intercept: number;
  r2: number;
  pValue: number;
  ciUpper?: Array<{ x: number; y: number }>;
  ciLower?: Array<{ x: number; y: number }>;
}

interface QuadrantLabel {
  position: 'topLeft' | 'topRight' | 'bottomLeft' | 'bottomRight';
  label: string;
}

interface InteractiveScatterPlotProps {
  data: DataPoint[];
  xAxisKey: string;
  yAxisKey: string;
  xAxisLabel?: string;
  yAxisLabel?: string;
  title?: string;
  regressionLine?: RegressionLine;
  quadrantLabels?: QuadrantLabel[];
  groupBy?: string;
  highlightOutliers?: boolean;
  outlierThreshold?: number;
  height?: number;
}

export function InteractiveScatterPlot({
  data,
  xAxisKey,
  yAxisKey,
  xAxisLabel,
  yAxisLabel,
  title,
  regressionLine,
  quadrantLabels,
  groupBy,
  highlightOutliers = true,
  outlierThreshold = 0.5,
  height = 400,
}: InteractiveScatterPlotProps) {
  const chartRef = useRef<HTMLDivElement>(null);

  const [settings, setSettings] = useState<ChartSettings>({
    showConfidenceIntervals: true,
    showGridLines: true,
    showDataLabels: false,
    showLegend: true,
    colorScheme: 'telos',
    axisScaleX: 'linear',
    axisScaleY: 'linear',
    opacity: 80,
    fontSize: 12,
  });

  const [selectedPoint, setSelectedPoint] = useState<DataPoint | null>(null);
  const [isDetailOpen, setIsDetailOpen] = useState(false);
  const [hoveredGroup, setHoveredGroup] = useState<string | null>(null);

  const colors = getColorPalette(settings.colorScheme);

  // Group data if groupBy is specified
  const groupedData = groupBy
    ? data.reduce((acc, point) => {
        const groupKey = point[groupBy] as string;
        if (!acc[groupKey]) acc[groupKey] = [];
        acc[groupKey].push(point);
        return acc;
      }, {} as Record<string, DataPoint[]>)
    : { all: data };

  const handlePointClick = (point: DataPoint) => {
    setSelectedPoint(point);
    setIsDetailOpen(true);
  };

  // Calculate regression line points
  const regressionLineData =
    regressionLine && data.length > 0
      ? (() => {
          const xValues = data.map((d) => d[xAxisKey]);
          const minX = Math.min(...xValues);
          const maxX = Math.max(...xValues);
          const points = 100;
          const step = (maxX - minX) / points;

          return Array.from({ length: points }, (_, i) => {
            const x = minX + step * i;
            return {
              x,
              y: regressionLine.slope * x + regressionLine.intercept,
              predicted: true,
            };
          });
        })()
      : [];

  // Calculate confidence band
  const ciUpperData = regressionLine?.ciUpper || [];
  const ciLowerData = regressionLine?.ciLower || [];

  // Identify outliers based on Cook's distance
  const outliers = highlightOutliers
    ? data.filter(
        (d) => d.cooksDistance && d.cooksDistance > outlierThreshold
      )
    : [];

  return (
    <div ref={chartRef} className="space-y-4">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          {title && <h3 className="text-lg font-semibold">{title}</h3>}
          {regressionLine && (
            <div className="flex items-center gap-3 mt-2 text-sm">
              <Badge variant="secondary">
                R² = {regressionLine.r2.toFixed(3)}
              </Badge>
              <Badge
                variant={regressionLine.pValue < 0.05 ? 'default' : 'outline'}
              >
                p = {regressionLine.pValue < 0.001 ? '<0.001' : regressionLine.pValue.toFixed(3)}
              </Badge>
              <span className="text-gray-600">
                y = {regressionLine.slope.toFixed(3)}x +{' '}
                {regressionLine.intercept.toFixed(2)}
              </span>
            </div>
          )}
        </div>

        <div className="flex items-center gap-2">
          <ChartControls
            settings={settings}
            onSettingsChange={setSettings}
          />

          <ChartExportMenu
            chartRef={chartRef}
            chartName={title?.toLowerCase().replace(/\s+/g, '-') || 'scatter-plot'}
          />
        </div>
      </div>

      {/* Outlier Alert */}
      {outliers.length > 0 && (
        <div className="flex items-start gap-2 p-3 bg-orange-50 border border-orange-200 rounded-lg">
          <AlertTriangle className="h-5 w-5 text-orange-600 mt-0.5 flex-shrink-0" />
          <div className="flex-1">
            <div className="font-semibold text-orange-900 text-sm">
              {outliers.length} Outlier{outliers.length > 1 ? 's' : ''} Detectado{outliers.length > 1 ? 's' : ''}
            </div>
            <div className="text-xs text-orange-800 mt-1">
              Pontos com alta influência (Cook's D {'>'} {outlierThreshold}) destacados em vermelho
            </div>
          </div>
        </div>
      )}

      {/* Legend for groups */}
      {settings.showLegend && groupBy && Object.keys(groupedData).length > 1 && (
        <div className="flex flex-wrap items-center gap-3 p-3 bg-gray-50 rounded-lg">
          {Object.keys(groupedData).map((groupKey, index) => {
            const color = colors[index % colors.length];
            const isHovered = hoveredGroup === groupKey;

            return (
              <button
                key={groupKey}
                onMouseEnter={() => setHoveredGroup(groupKey)}
                onMouseLeave={() => setHoveredGroup(null)}
                className={`flex items-center gap-2 px-3 py-1.5 rounded-md transition-all hover:bg-white ${
                  isHovered || hoveredGroup === null ? 'opacity-100' : 'opacity-40'
                }`}
              >
                <div
                  className="w-3 h-3 rounded-full"
                  style={{ backgroundColor: color }}
                />
                <span className="text-sm font-medium">{groupKey}</span>
                <Badge variant="secondary" className="text-xs">
                  n={groupedData[groupKey].length}
                </Badge>
              </button>
            );
          })}
        </div>
      )}

      {/* Chart */}
      <ResponsiveContainer width="100%" height={height}>
        <ScatterChart margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
          {settings.showGridLines && (
            <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
          )}

          <XAxis
            type="number"
            dataKey="x"
            name={xAxisKey}
            label={
              xAxisLabel
                ? { value: xAxisLabel, position: 'insideBottom', offset: -10 }
                : undefined
            }
            tick={{ fontSize: settings.fontSize }}
          />

          <YAxis
            type="number"
            dataKey="y"
            name={yAxisKey}
            label={
              yAxisLabel
                ? { value: yAxisLabel, angle: -90, position: 'insideLeft' }
                : undefined
            }
            tick={{ fontSize: settings.fontSize }}
          />

          <ZAxis range={[50, 200]} />

          <Tooltip
            content={<CustomTooltip type="regression" />}
            cursor={{ strokeDasharray: '3 3' }}
          />

          {/* Quadrant dividers */}
          {quadrantLabels && (
            <>
              <ReferenceLine x={0} stroke="#94a3b8" strokeDasharray="5 5" />
              <ReferenceLine y={0} stroke="#94a3b8" strokeDasharray="5 5" />
            </>
          )}

          {/* Confidence interval band */}
          {settings.showConfidenceIntervals &&
            ciUpperData.length > 0 &&
            ciLowerData.length > 0 && (
              <>
                <Scatter
                  data={ciUpperData}
                  fill={colors[0]}
                  fillOpacity={0.1}
                  shape={() => <></>}
                  line={{ stroke: colors[0], strokeWidth: 1, strokeDasharray: '5 5' }}
                  isAnimationActive={false}
                />
                <Scatter
                  data={ciLowerData}
                  fill={colors[0]}
                  fillOpacity={0.1}
                  shape={() => <></>}
                  line={{ stroke: colors[0], strokeWidth: 1, strokeDasharray: '5 5' }}
                  isAnimationActive={false}
                />
              </>
            )}

          {/* Regression line */}
          {regressionLine && regressionLineData.length > 0 && (
            <Scatter
              data={regressionLineData}
              fill="none"
              line={{ stroke: colors[0], strokeWidth: 2 }}
              shape={() => <></>}
              isAnimationActive={false}
            />
          )}

          {/* Data points by group */}
          {Object.entries(groupedData).map(([groupKey, points], groupIndex) => {
            const color = colors[groupIndex % colors.length];
            const isGroupHovered = hoveredGroup === groupKey || hoveredGroup === null;

            return (
              <Scatter
                key={groupKey}
                name={groupKey}
                data={points.map((p) => ({ ...p, x: p[xAxisKey], y: p[yAxisKey] }))}
                fill={color}
                opacity={(settings.opacity / 100) * (isGroupHovered ? 1 : 0.3)}
                onClick={(data) => handlePointClick(data)}
                className="cursor-pointer"
              >
                {points.map((entry, index) => (
                  <Cell
                    key={`cell-${index}`}
                    fill={
                      entry.outlier || (entry.cooksDistance && entry.cooksDistance > outlierThreshold)
                        ? '#ef4444'
                        : color
                    }
                    stroke={
                      entry.outlier || (entry.cooksDistance && entry.cooksDistance > outlierThreshold)
                        ? '#991b1b'
                        : 'white'
                    }
                    strokeWidth={
                      entry.outlier || (entry.cooksDistance && entry.cooksDistance > outlierThreshold)
                        ? 2
                        : 1
                    }
                  />
                ))}
              </Scatter>
            );
          })}
        </ScatterChart>
      </ResponsiveContainer>

      {/* Hint */}
      <div className="text-xs text-center text-gray-500">
        Clique nos pontos para ver detalhes do paciente
      </div>

      {/* Point Detail Modal */}
      <Dialog open={isDetailOpen} onOpenChange={setIsDetailOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Info className="h-5 w-5" />
              Detalhes do Ponto de Dados
            </DialogTitle>
            <DialogDescription>
              {selectedPoint?.patientId || 'Informações do paciente'}
            </DialogDescription>
          </DialogHeader>

          {selectedPoint && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div className="p-3 border rounded-lg">
                  <div className="text-gray-600 mb-1">{xAxisLabel || xAxisKey}</div>
                  <div className="text-2xl font-bold">{selectedPoint[xAxisKey]}</div>
                </div>

                <div className="p-3 border rounded-lg">
                  <div className="text-gray-600 mb-1">{yAxisLabel || yAxisKey}</div>
                  <div className="text-2xl font-bold">{selectedPoint[yAxisKey]}</div>
                </div>

                {selectedPoint.predicted !== undefined && (
                  <>
                    <div className="p-3 border rounded-lg bg-blue-50">
                      <div className="text-gray-600 mb-1">Valor Predito</div>
                      <div className="text-2xl font-bold text-blue-700">
                        {selectedPoint.predicted.toFixed(2)}
                      </div>
                    </div>

                    <div className="p-3 border rounded-lg">
                      <div className="text-gray-600 mb-1">Resíduo</div>
                      <div
                        className={`text-2xl font-bold ${
                          Math.abs(selectedPoint[yAxisKey] - selectedPoint.predicted) > 2
                            ? 'text-red-600'
                            : 'text-gray-700'
                        }`}
                      >
                        {(selectedPoint[yAxisKey] - selectedPoint.predicted).toFixed(2)}
                      </div>
                    </div>
                  </>
                )}

                {selectedPoint.cooksDistance !== undefined && (
                  <div className="col-span-2 p-3 border rounded-lg">
                    <div className="text-gray-600 mb-1">Cook's Distance</div>
                    <div className="flex items-center justify-between">
                      <div className="text-2xl font-bold">
                        {selectedPoint.cooksDistance.toFixed(4)}
                      </div>
                      {selectedPoint.cooksDistance > outlierThreshold && (
                        <Badge variant="destructive">Outlier</Badge>
                      )}
                    </div>
                  </div>
                )}

                {selectedPoint.group && (
                  <div className="col-span-2 p-3 border rounded-lg bg-gray-50">
                    <div className="text-gray-600 mb-1">Grupo</div>
                    <Badge variant="secondary">{selectedPoint.group}</Badge>
                  </div>
                )}
              </div>

              {/* Additional details */}
              {Object.entries(selectedPoint)
                .filter(
                  ([key]) =>
                    !['x', 'y', xAxisKey, yAxisKey, 'predicted', 'residual', 'cooksDistance', 'group', 'patientId', 'outlier'].includes(key)
                )
                .length > 0 && (
                <div className="border-t pt-4">
                  <h4 className="text-sm font-semibold mb-2">Informações Adicionais</h4>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    {Object.entries(selectedPoint)
                      .filter(
                        ([key]) =>
                          !['x', 'y', xAxisKey, yAxisKey, 'predicted', 'residual', 'cooksDistance', 'group', 'patientId', 'outlier'].includes(key)
                      )
                      .map(([key, value]) => (
                        <div key={key} className="flex justify-between">
                          <span className="text-gray-600">{key}:</span>
                          <span className="font-medium">{String(value)}</span>
                        </div>
                      ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

"use client";

import { useState } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip, BarChart, Bar, XAxis, YAxis, CartesianGrid } from 'recharts';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { AlertCircle, CheckCircle2 } from 'lucide-react';

interface ComplicationsData {
  overall: {
    withComplications: number;
    withoutComplications: number;
    total: number;
    rate: number;
  };
  bySurgeryType: Array<{
    surgeryType: string;
    withComplications: number;
    withoutComplications: number;
    total: number;
    rate: number;
  }>;
}

interface ComplicationsChartProps {
  data: ComplicationsData;
}

const COLORS = {
  withComplications: '#DC2626', // Vermelho
  withoutComplications: '#16A34A', // Verde
};

const SURGERY_LABELS: Record<string, string> = {
  hemorroidectomia: 'Hemorroidectomia',
  fistula: 'Fístula Anal',
  fissura: 'Fissura Anal',
  pilonidal: 'Doença Pilonidal',
};

export function ComplicationsChart({ data }: ComplicationsChartProps) {
  const [selectedSurgeryType, setSelectedSurgeryType] = useState<string | null>(null);

  // Dados para o gráfico de pizza (geral)
  const pieData = [
    {
      name: 'Sem Complicações',
      value: data.overall.withoutComplications,
      color: COLORS.withoutComplications,
    },
    {
      name: 'Com Complicações',
      value: data.overall.withComplications,
      color: COLORS.withComplications,
    },
  ];

  // Dados para o gráfico de barras (por tipo de cirurgia)
  const barData = data.bySurgeryType.map(item => ({
    name: SURGERY_LABELS[item.surgeryType] || item.surgeryType,
    'Com Complicações': item.withComplications,
    'Sem Complicações': item.withoutComplications,
    rate: item.rate,
  }));

  const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white dark:bg-gray-800 p-3 border-2 border-gray-200 dark:border-gray-700 rounded-lg shadow-lg">
          <p className="font-semibold mb-2">{payload[0].payload.name}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} style={{ color: entry.fill }} className="text-sm">
              {entry.name}: {entry.value} pacientes
            </p>
          ))}
          {payload[0].payload.rate !== undefined && (
            <p className="text-sm font-semibold mt-1 pt-1 border-t">
              Taxa: {payload[0].payload.rate}%
            </p>
          )}
        </div>
      );
    }
    return null;
  };

  const CustomLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent }: any) => {
    const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
    const x = cx + radius * Math.cos(-midAngle * Math.PI / 180);
    const y = cy + radius * Math.sin(-midAngle * Math.PI / 180);

    return (
      <text
        x={x}
        y={y}
        fill="white"
        textAnchor={x > cx ? 'start' : 'end'}
        dominantBaseline="central"
        className="font-bold text-sm"
      >
        {`${(percent * 100).toFixed(1)}%`}
      </text>
    );
  };

  return (
    <Card className="border-2 hover:shadow-lg transition-shadow">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <span className="text-2xl">⚕️</span>
          Taxa de Complicações
        </CardTitle>
        <CardDescription>
          Percentual de pacientes com e sem complicações pós-operatórias
        </CardDescription>
      </CardHeader>
      <CardContent>
        <Tabs defaultValue="overview" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="overview">Visão Geral</TabsTrigger>
            <TabsTrigger value="by-type">Por Tipo de Cirurgia</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-4">
            {/* Estatísticas principais */}
            <div className="grid grid-cols-3 gap-4 mb-4">
              <div className="bg-blue-50 dark:bg-blue-950/20 p-4 rounded-lg border-2 border-blue-200">
                <div className="text-sm text-blue-700 dark:text-blue-300 font-medium mb-1">
                  Total de Pacientes
                </div>
                <div className="text-3xl font-bold text-blue-900 dark:text-blue-100">
                  {data.overall.total}
                </div>
              </div>
              <div className="bg-green-50 dark:bg-green-950/20 p-4 rounded-lg border-2 border-green-200">
                <div className="flex items-center gap-2">
                  <CheckCircle2 className="h-4 w-4 text-green-600" />
                  <div className="text-sm text-green-700 dark:text-green-300 font-medium">
                    Sem Complicações
                  </div>
                </div>
                <div className="text-3xl font-bold text-green-900 dark:text-green-100 mt-1">
                  {data.overall.withoutComplications}
                </div>
              </div>
              <div className="bg-red-50 dark:bg-red-950/20 p-4 rounded-lg border-2 border-red-200">
                <div className="flex items-center gap-2">
                  <AlertCircle className="h-4 w-4 text-red-600" />
                  <div className="text-sm text-red-700 dark:text-red-300 font-medium">
                    Com Complicações
                  </div>
                </div>
                <div className="text-3xl font-bold text-red-900 dark:text-red-100 mt-1">
                  {data.overall.withComplications}
                  <span className="text-lg ml-2">({data.overall.rate}%)</span>
                </div>
              </div>
            </div>

            {/* Gráfico de Pizza */}
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={pieData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={CustomLabel}
                  outerRadius={100}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip content={<CustomTooltip />} />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </TabsContent>

          <TabsContent value="by-type" className="space-y-4">
            <ResponsiveContainer width="100%" height={400}>
              <BarChart
                data={barData}
                margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
                <XAxis
                  dataKey="name"
                  stroke="#6b7280"
                  style={{ fontSize: '12px', fontWeight: 600 }}
                  angle={-15}
                  textAnchor="end"
                  height={80}
                />
                <YAxis
                  stroke="#6b7280"
                  style={{ fontSize: '14px', fontWeight: 600 }}
                  label={{ value: 'Número de Pacientes', angle: -90, position: 'insideLeft' }}
                />
                <Tooltip content={<CustomTooltip />} />
                <Legend />
                <Bar
                  dataKey="Sem Complicações"
                  stackId="a"
                  fill={COLORS.withoutComplications}
                  radius={[0, 0, 4, 4]}
                />
                <Bar
                  dataKey="Com Complicações"
                  stackId="a"
                  fill={COLORS.withComplications}
                  radius={[4, 4, 0, 0]}
                />
              </BarChart>
            </ResponsiveContainer>

            {/* Tabela de detalhes */}
            <div className="mt-4 border rounded-lg overflow-hidden">
              <table className="w-full">
                <thead className="bg-gray-100 dark:bg-gray-800">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-semibold">Tipo de Cirurgia</th>
                    <th className="px-4 py-3 text-center text-sm font-semibold">Total</th>
                    <th className="px-4 py-3 text-center text-sm font-semibold">Com Complicações</th>
                    <th className="px-4 py-3 text-center text-sm font-semibold">Taxa</th>
                  </tr>
                </thead>
                <tbody className="divide-y">
                  {data.bySurgeryType.map((item, index) => (
                    <tr key={index} className="hover:bg-gray-50 dark:hover:bg-gray-800/50">
                      <td className="px-4 py-3 text-sm font-medium">
                        {SURGERY_LABELS[item.surgeryType] || item.surgeryType}
                      </td>
                      <td className="px-4 py-3 text-center text-sm">{item.total}</td>
                      <td className="px-4 py-3 text-center text-sm">
                        <span className="font-semibold text-red-600">
                          {item.withComplications}
                        </span>
                      </td>
                      <td className="px-4 py-3 text-center text-sm">
                        <span className={`font-bold ${item.rate > 10 ? 'text-red-600' : 'text-green-600'}`}>
                          {item.rate}%
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </TabsContent>
        </Tabs>

        <div className="mt-4 pt-4 border-t">
          <p className="text-sm text-muted-foreground">
            <strong>Nota:</strong> Complicações incluem qualquer intercorrência reportada no campo
            de descrição cirúrgica ou detectada via follow-ups.
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

