'use client';

import { useState, useEffect, useCallback } from 'react';
import { createPortal } from 'react-dom';

interface TourStep {
  element: string;
  title: string;
  description: string;
}

const DASHBOARD_STEPS: TourStep[] = [
  {
    element: '#dashboard-header',
    title: 'Bem-vindo ao Telos.AI',
    description: 'Este é seu painel de controle médico inteligente. Aqui você acompanha todos os seus pacientes em tempo real com tecnologia de ponta.',
  },
  {
    element: '[data-tutorial="new-patient-btn"]',
    title: 'Novo Paciente',
    description: 'Clique aqui para cadastrar um novo paciente no sistema. O cadastro é rápido e intuitivo, permitindo adicionar todas as informações necessárias.',
  },
  {
    element: '[data-tutorial="research-btn"]',
    title: 'Modo Pesquisa',
    description: 'Organize seus pacientes em grupos de pesquisa científica. Perfeito para estudos clínicos e análise de resultados em larga escala.',
  },
  {
    element: '[data-tutorial="stats-today-surgeries"]',
    title: 'Cirurgias Hoje',
    description: 'Acompanhe em tempo real quantos pacientes foram operados hoje. Este indicador ajuda a gerenciar sua agenda cirúrgica.',
  },
  {
    element: '[data-tutorial="stats-active-patients"]',
    title: 'Pacientes Ativos',
    description: 'Total de pacientes em acompanhamento pós-operatório ativo. Mantenha controle sobre todos os casos em andamento.',
  },
  {
    element: '[data-tutorial="stats-followups-today"]',
    title: 'Follow-ups Hoje',
    description: 'Quantidade de acompanhamentos programados para hoje. Nunca perca um follow-up importante com lembretes automáticos.',
  },
  {
    element: '[data-tutorial="stats-critical-alerts"]',
    title: 'Alertas Críticos',
    description: 'Pacientes que precisam de atenção urgente. O sistema detecta automaticamente situações de risco e alerta você imediatamente.',
  },
  {
    element: '[data-tutorial="search-filters"]',
    title: 'Filtros e Busca',
    description: 'Use filtros avançados para encontrar pacientes rapidamente. Filtre por tipo de cirurgia, status, período ou faça buscas por nome.',
  },
];

function TourContent({ onClose }: { onClose: () => void }) {
  const [currentStep, setCurrentStep] = useState(0);
  const [position, setPosition] = useState({ top: 100, left: 100 });
  const [mounted, setMounted] = useState(false);

  const step = DASHBOARD_STEPS[currentStep];

  useEffect(() => {
    setMounted(true);
  }, []);

  // Calcula posição do popover
  useEffect(() => {
    if (!mounted) return;

    const updatePosition = () => {
      const element = document.querySelector(step.element);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'center' });

        setTimeout(() => {
          const rect = element.getBoundingClientRect();
          const popoverWidth = 420;
          const popoverHeight = 220;

          let top = rect.bottom + 15;
          let left = rect.left + (rect.width / 2) - (popoverWidth / 2);

          // Ajustes de posição
          if (left + popoverWidth > window.innerWidth - 20) {
            left = window.innerWidth - popoverWidth - 20;
          }
          if (top + popoverHeight > window.innerHeight - 20) {
            top = rect.top - popoverHeight - 15;
          }
          if (left < 20) left = 20;
          if (top < 20) top = 20;

          setPosition({ top, left });
        }, 350);
      } else {
        // Fallback: Centralizar
        setPosition({
          top: window.innerHeight / 2 - 110,
          left: window.innerWidth / 2 - 210,
        });
      }
    };

    const timer = setTimeout(updatePosition, 150);
    window.addEventListener('resize', updatePosition);
    return () => {
      clearTimeout(timer);
      window.removeEventListener('resize', updatePosition);
    };
  }, [step.element, mounted]);

  const handleNext = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (currentStep < DASHBOARD_STEPS.length - 1) {
      setCurrentStep(prev => prev + 1);
    } else {
      onClose();
    }
  }, [currentStep, onClose]);

  const handlePrev = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (currentStep > 0) {
      setCurrentStep(prev => prev - 1);
    }
  }, [currentStep]);

  const handleClose = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    onClose();
  }, [onClose]);

  const handleOverlayClick = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  if (!mounted) return null;

  return (
    <div
      id="simple-tour-container"
      onClick={handleOverlayClick}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        zIndex: 2147483646,
      }}
    >
      {/* Overlay escuro */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
        }}
      />

      {/* Popover */}
      <div
        style={{
          position: 'absolute',
          top: position.top,
          left: position.left,
          backgroundColor: '#ffffff',
          borderRadius: '16px',
          padding: '24px',
          width: '420px',
          maxWidth: 'calc(100vw - 40px)',
          boxShadow: '0 20px 60px rgba(0, 0, 0, 0.4), 0 0 0 4px rgba(212, 175, 55, 0.3)',
          border: '2px solid #D4AF37',
          zIndex: 2147483647,
        }}
      >
        {/* Header com indicador de progresso */}
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '16px',
          paddingBottom: '12px',
          borderBottom: '1px solid #E5E7EB'
        }}>
          <div style={{
            fontSize: '12px',
            fontWeight: '600',
            color: '#0A2647',
            backgroundColor: '#F0F4F8',
            padding: '4px 12px',
            borderRadius: '20px'
          }}>
            Passo {currentStep + 1} de {DASHBOARD_STEPS.length}
          </div>

          {/* Botão fechar */}
          <button
            type="button"
            onClick={handleClose}
            style={{
              background: '#F3F4F6',
              border: 'none',
              width: '32px',
              height: '32px',
              borderRadius: '50%',
              fontSize: '20px',
              cursor: 'pointer',
              color: '#6B7280',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              transition: 'all 0.2s',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.background = '#E5E7EB';
              e.currentTarget.style.color = '#374151';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.background = '#F3F4F6';
              e.currentTarget.style.color = '#6B7280';
            }}
          >
            ×
          </button>
        </div>

        {/* Conteúdo */}
        <h3 style={{
          fontSize: '20px',
          fontWeight: '700',
          marginBottom: '12px',
          color: '#0A2647',
          lineHeight: '1.3'
        }}>
          {step.title}
        </h3>
        <p style={{
          fontSize: '15px',
          lineHeight: '1.7',
          color: '#4B5563',
          marginBottom: '24px'
        }}>
          {step.description}
        </p>

        {/* Barra de progresso */}
        <div style={{
          display: 'flex',
          gap: '4px',
          marginBottom: '20px'
        }}>
          {DASHBOARD_STEPS.map((_, idx) => (
            <div
              key={idx}
              style={{
                flex: 1,
                height: '4px',
                borderRadius: '2px',
                backgroundColor: idx <= currentStep ? '#0A2647' : '#E5E7EB',
                transition: 'background-color 0.3s'
              }}
            />
          ))}
        </div>

        {/* Botões de navegação */}
        <div style={{
          display: 'flex',
          gap: '12px',
          justifyContent: 'flex-end'
        }}>
          {currentStep > 0 && (
            <button
              type="button"
              onClick={handlePrev}
              style={{
                padding: '10px 20px',
                backgroundColor: '#F3F4F6',
                color: '#374151',
                border: '1px solid #D1D5DB',
                borderRadius: '8px',
                cursor: 'pointer',
                fontWeight: '600',
                fontSize: '14px',
                transition: 'all 0.2s',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.backgroundColor = '#E5E7EB';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.backgroundColor = '#F3F4F6';
              }}
            >
              Anterior
            </button>
          )}

          <button
            type="button"
            onClick={handleNext}
            style={{
              padding: '10px 24px',
              backgroundColor: '#0A2647',
              color: 'white',
              border: '2px solid #D4AF37',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600',
              fontSize: '14px',
              transition: 'all 0.2s',
              boxShadow: '0 2px 8px rgba(10, 38, 71, 0.3)',
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.backgroundColor = '#0D3156';
              e.currentTarget.style.transform = 'translateY(-1px)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.backgroundColor = '#0A2647';
              e.currentTarget.style.transform = 'translateY(0)';
            }}
          >
            {currentStep < DASHBOARD_STEPS.length - 1 ? 'Próximo' : 'Concluir'}
          </button>
        </div>
      </div>
    </div>
  );
}

export function SimpleTour({ onClose }: { onClose: () => void }) {
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
    // Previne scroll do body enquanto o tour está aberto
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = '';
    };
  }, []);

  if (!mounted) return null;

  // Usa createPortal para renderizar diretamente no body
  return createPortal(
    <TourContent onClose={onClose} />,
    document.body
  );
}

'use client';

import React, { createContext, useContext, useCallback, useEffect, useState } from 'react';
import { useRouter, usePathname } from 'next/navigation';
import { TutorialId, getTutorialSteps, tutorialMetadata } from '@/lib/tutorial-steps';
import { getTutorialAnalytics, TutorialAnalyticsManager } from '@/lib/tutorial-analytics';
import { CustomTutorial, TutorialStep } from './CustomTutorial';

interface TutorialContextType {
  startTutorial: (tutorialId: TutorialId) => void;
  stopTutorial: () => void;
  skipTutorial: (tutorialId: TutorialId) => void;
  resetTutorials: () => void;
  resetTutorial: (tutorialId: TutorialId) => void;
  isTutorialCompleted: (tutorialId: TutorialId) => boolean;
  isOnboardingComplete: () => boolean;
  getSuggestedTutorial: () => TutorialId | null;
  getCompletionRate: () => number;
  isFirstTimeUser: () => boolean;
  currentTutorial: TutorialId | null;
  driverInstance: null; // Mantido para compatibilidade
}

const TutorialContext = createContext<TutorialContextType | undefined>(undefined);

export function useTutorial() {
  const context = useContext(TutorialContext);
  if (!context) {
    throw new Error('useTutorial must be used within TutorialProvider');
  }
  return context;
}

export function TutorialProvider({ children }: { children: React.ReactNode }) {
  const [analyticsManager] = useState<TutorialAnalyticsManager>(() => getTutorialAnalytics());
  const [currentTutorial, setCurrentTutorial] = useState<TutorialId | null>(null);
  const [currentSteps, setCurrentSteps] = useState<TutorialStep[]>([]);
  const [tutorialStartTime, setTutorialStartTime] = useState<number>(0);
  const router = useRouter();
  const pathname = usePathname();

  const startTutorial = useCallback(
    (tutorialId: TutorialId) => {
      // Check if we need to navigate to a different route
      const metadata = tutorialMetadata[tutorialId];
      if (metadata?.route && pathname !== metadata.route) {
        console.log(`Redirecting to ${metadata.route} for tutorial ${tutorialId}`);
        if (typeof window !== 'undefined') {
          sessionStorage.setItem('pending_tutorial', tutorialId);
        }
        router.push(metadata.route);
        return;
      }

      const steps = getTutorialSteps(tutorialId);
      if (steps.length === 0) {
        console.warn(`No steps found for tutorial: ${tutorialId}`);
        return;
      }

      // Converte para o formato do CustomTutorial
      const customSteps: TutorialStep[] = steps.map(step => ({
        element: step.element as string | undefined,
        title: step.popover?.title || '',
        description: step.popover?.description || '',
      }));

      setCurrentTutorial(tutorialId);
      setCurrentSteps(customSteps);
      setTutorialStartTime(Date.now());
      analyticsManager.startTutorial(tutorialId, steps.length);
    },
    [analyticsManager, pathname, router]
  );

  // Check for pending tutorials after navigation
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const pendingTutorial = sessionStorage.getItem('pending_tutorial') as TutorialId | null;
      if (pendingTutorial && pathname === tutorialMetadata[pendingTutorial]?.route) {
        sessionStorage.removeItem('pending_tutorial');
        // Small delay to ensure DOM is ready
        setTimeout(() => {
          startTutorial(pendingTutorial);
        }, 1000);
      }
    }
  }, [pathname, startTutorial]);

  const stopTutorial = useCallback(() => {
    setCurrentTutorial(null);
    setCurrentSteps([]);
  }, []);

  const handleTutorialClose = useCallback(() => {
    if (currentTutorial) {
      const timeSpent = Math.floor((Date.now() - tutorialStartTime) / 1000);
      analyticsManager.skipTutorial(currentTutorial);
    }
    stopTutorial();
  }, [currentTutorial, tutorialStartTime, analyticsManager, stopTutorial]);

  const handleTutorialComplete = useCallback(() => {
    if (currentTutorial) {
      const timeSpent = Math.floor((Date.now() - tutorialStartTime) / 1000);
      analyticsManager.completeTutorial(currentTutorial, timeSpent);
    }
  }, [currentTutorial, tutorialStartTime, analyticsManager]);

  const skipTutorial = useCallback(
    (tutorialId: TutorialId) => {
      analyticsManager.skipTutorial(tutorialId);
      stopTutorial();
    },
    [analyticsManager, stopTutorial]
  );

  const resetTutorials = useCallback(() => {
    analyticsManager.resetAllTutorials();
    stopTutorial();
  }, [analyticsManager, stopTutorial]);

  const resetTutorial = useCallback(
    (tutorialId: TutorialId) => {
      analyticsManager.resetTutorial(tutorialId);
    },
    [analyticsManager]
  );

  const isTutorialCompleted = useCallback(
    (tutorialId: TutorialId) => {
      return analyticsManager.isTutorialCompleted(tutorialId);
    },
    [analyticsManager]
  );

  const isOnboardingComplete = useCallback(() => {
    return analyticsManager.isOnboardingComplete();
  }, [analyticsManager]);

  const getSuggestedTutorial = useCallback(() => {
    return analyticsManager.getSuggestedTutorial();
  }, [analyticsManager]);

  const getCompletionRate = useCallback(() => {
    return analyticsManager.getCompletionRate();
  }, [analyticsManager]);

  const isFirstTimeUser = useCallback(() => {
    return analyticsManager.isFirstTimeUser();
  }, [analyticsManager]);

  // Auto-trigger dashboard tour for first-time users
  useEffect(() => {
    if (typeof window === 'undefined') return;

    // Check if on dashboard page and first time user
    const isDashboard = window.location.pathname === '/dashboard';
    if (isDashboard && isFirstTimeUser()) {
      // Delay to ensure DOM is ready
      const timer = setTimeout(() => {
        if (analyticsManager.shouldShowTutorial('dashboard-tour')) {
          startTutorial('dashboard-tour');
        }
      }, 1500);

      return () => clearTimeout(timer);
    }
  }, [isFirstTimeUser, analyticsManager, startTutorial]);

  const value: TutorialContextType = {
    startTutorial,
    stopTutorial,
    skipTutorial,
    resetTutorials,
    resetTutorial,
    isTutorialCompleted,
    isOnboardingComplete,
    getSuggestedTutorial,
    getCompletionRate,
    isFirstTimeUser,
    currentTutorial,
    driverInstance: null,
  };

  return (
    <TutorialContext.Provider value={value}>
      {children}
      {currentTutorial && currentSteps.length > 0 && (
        <CustomTutorial
          steps={currentSteps}
          onClose={handleTutorialClose}
          onComplete={handleTutorialComplete}
          tutorialId={currentTutorial}
        />
      )}
    </TutorialContext.Provider>
  );
}

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { cn } from "@/lib/utils"

/**
 * Telos.AI Card Variants
 *
 * Standardized card components with consistent styling, shadows, and interactions.
 * Use these instead of base Card for better visual consistency.
 *
 * @example
 * <InteractiveCard onClick={handleClick}>
 *   <CardContent>Click me!</CardContent>
 * </InteractiveCard>
 *
 * <ElevatedCard>
 *   <CardContent>I have more shadow!</CardContent>
 * </ElevatedCard>
 */

// ============================================================================
// CARD VARIANTS
// ============================================================================

const cardVariants = cva(
  "rounded-xl border bg-white text-gray-900 transition-all duration-300",
  {
    variants: {
      variant: {
        default: "border-gray-200 shadow-sm",
        elevated: "border-gray-200 shadow-md hover:shadow-lg",
        bordered: "border-2 border-telos-blue-200 shadow-none",
        interactive: "border-gray-200 shadow-sm hover:shadow-md hover:-translate-y-1 cursor-pointer",
        success: "border-telos-success-200 bg-telos-success-50 shadow-sm",
        warning: "border-telos-warning-200 bg-telos-warning-50 shadow-sm",
        error: "border-telos-error-200 bg-telos-error-50 shadow-sm",
        gold: "border-telos-gold-300 bg-gradient-to-br from-white to-telos-gold-50 shadow-md",
        glass: "border-gray-200/50 bg-white/80 backdrop-blur-sm shadow-lg",
      },
      padding: {
        none: "p-0",
        sm: "p-4",
        md: "p-6",
        lg: "p-8",
      },
    },
    defaultVariants: {
      variant: "default",
      padding: "md",
    },
  }
)

// ============================================================================
// BASE CARD
// ============================================================================

interface BaseCardProps extends React.HTMLAttributes<HTMLDivElement>,
  VariantProps<typeof cardVariants> {}

export function BaseCard({
  variant,
  padding,
  className,
  ...props
}: BaseCardProps) {
  return (
    <div
      className={cn(cardVariants({ variant, padding }), className)}
      {...props}
    />
  )
}

// ============================================================================
// INTERACTIVE CARD
// ============================================================================

interface InteractiveCardProps extends BaseCardProps {
  onClick?: () => void
  href?: string
}

export function InteractiveCard({
  onClick,
  href,
  className,
  children,
  ...props
}: InteractiveCardProps) {
  const Component = href ? "a" : "div"

  return (
    <Component
      className={cn(
        cardVariants({ variant: "interactive" }),
        "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-telos-blue-500 focus-visible:ring-offset-2",
        className
      )}
      onClick={onClick}
      {...(href && { href })}
      {...(props as any)}
    >
      {children}
    </Component>
  )
}

// ============================================================================
// ELEVATED CARD
// ============================================================================

export function ElevatedCard({ className, ...props }: BaseCardProps) {
  return (
    <BaseCard
      variant="elevated"
      className={className}
      {...props}
    />
  )
}

// ============================================================================
// STAT CARD
// ============================================================================

interface StatCardProps extends Omit<BaseCardProps, "variant"> {
  icon?: React.ReactNode
  label: string
  value: string | number
  change?: {
    value: number
    trend: "up" | "down" | "neutral"
  }
  variant?: "default" | "success" | "warning" | "error" | "gold"
}

export function StatCard({
  icon,
  label,
  value,
  change,
  variant = "default",
  className,
  ...props
}: StatCardProps) {
  const trendIcon = {
    up: (
      <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
      </svg>
    ),
    down: (
      <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 17h8m0 0V9m0 8l-8-8-4 4-6-6" />
      </svg>
    ),
    neutral: null,
  }

  const trendColor = {
    up: "text-telos-success-500",
    down: "text-telos-error-500",
    neutral: "text-gray-500",
  }

  return (
    <BaseCard
      variant={variant}
      className={cn("space-y-3", className)}
      {...props}
    >
      <div className="flex items-center justify-between">
        <span className="text-sm font-medium text-gray-600">{label}</span>
        {icon && (
          <div className="p-2 bg-telos-blue-50 rounded-lg text-telos-blue-500">
            {icon}
          </div>
        )}
      </div>

      <div className="space-y-1">
        <div className="text-3xl font-bold text-gray-900">{value}</div>

        {change && (
          <div className={cn("flex items-center gap-1 text-sm font-medium", trendColor[change.trend])}>
            {trendIcon[change.trend]}
            <span>{Math.abs(change.value)}%</span>
            <span className="text-gray-500 font-normal">vs último período</span>
          </div>
        )}
      </div>
    </BaseCard>
  )
}

