"use client"

import { useEffect, useRef, useState, useCallback } from "react"
import { toast } from "@/hooks/use-toast"

// Version for saved data (useful when schema changes)
const AUTOSAVE_VERSION = "1.0.0"

interface AutoSaveOptions {
  key: string // localStorage key
  debounceMs?: number // default 2000ms
  onSave?: (data: any) => void
  onRecover?: (data: any) => void
}

interface AutoSaveReturn {
  isSaving: boolean
  lastSaved: Date | null
  saveNow: () => void
  clearSaved: () => void
  getSavedData: () => any
}

interface SavedData {
  version: string
  timestamp: string
  data: any
}

/**
 * Auto-save hook for form data
 *
 * Features:
 * - Debounced auto-save to localStorage
 * - Visual feedback with saving indicator
 * - Automatic recovery on page reload
 * - Versioning for schema changes
 * - Error handling for localStorage quota
 *
 * @param data - The data to auto-save
 * @param options - Configuration options
 * @returns Auto-save controls and state
 *
 * @example
 * ```tsx
 * const { isSaving, lastSaved, saveNow, clearSaved } = useAutoSave(formData, {
 *   key: 'patient-registration',
 *   debounceMs: 2000,
 *   onRecover: (data) => setFormData(data)
 * })
 * ```
 */
export function useAutoSave(
  data: any,
  options: AutoSaveOptions
): AutoSaveReturn {
  const {
    key,
    debounceMs = 2000,
    onSave,
    onRecover,
  } = options

  const [isSaving, setIsSaving] = useState(false)
  const [lastSaved, setLastSaved] = useState<Date | null>(null)
  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null)
  const isFirstRenderRef = useRef(true)
  const hasRecoveredRef = useRef(false)

  // Get the full localStorage key with prefix
  const getStorageKey = useCallback(() => {
    return `autosave_${key}`
  }, [key])

  /**
   * Save data to localStorage
   */
  const saveToLocalStorage = useCallback((dataToSave: any) => {
    try {
      const savedData: SavedData = {
        version: AUTOSAVE_VERSION,
        timestamp: new Date().toISOString(),
        data: dataToSave,
      }

      localStorage.setItem(getStorageKey(), JSON.stringify(savedData))
      setLastSaved(new Date())
      setIsSaving(false)

      // Call onSave callback if provided
      if (onSave) {
        onSave(dataToSave)
      }
    } catch (error) {
      console.error("Error saving to localStorage:", error)
      setIsSaving(false)

      // Handle quota exceeded error
      if (error instanceof Error && error.name === "QuotaExceededError") {
        toast({
          title: "Erro ao salvar",
          description: "Espaço de armazenamento insuficiente. Limpe o cache do navegador.",
          variant: "destructive",
        })
      } else {
        toast({
          title: "Erro ao salvar",
          description: "Não foi possível salvar os dados automaticamente.",
          variant: "destructive",
        })
      }
    }
  }, [getStorageKey, onSave])

  /**
   * Get saved data from localStorage
   */
  const getSavedData = useCallback((): any => {
    try {
      const stored = localStorage.getItem(getStorageKey())
      if (!stored) return null

      const parsed: SavedData = JSON.parse(stored)

      // Check version compatibility
      if (parsed.version !== AUTOSAVE_VERSION) {
        console.warn(
          `Auto-save version mismatch. Expected ${AUTOSAVE_VERSION}, got ${parsed.version}`
        )
        // Optionally clear incompatible data
        // localStorage.removeItem(getStorageKey())
        // return null
      }

      return parsed.data
    } catch (error) {
      console.error("Error reading from localStorage:", error)
      return null
    }
  }, [getStorageKey])

  /**
   * Clear saved data from localStorage
   */
  const clearSaved = useCallback(() => {
    try {
      localStorage.removeItem(getStorageKey())
      setLastSaved(null)
    } catch (error) {
      console.error("Error clearing localStorage:", error)
    }
  }, [getStorageKey])

  /**
   * Save immediately (bypass debounce)
   */
  const saveNow = useCallback(() => {
    // Cancel pending debounced save
    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current)
      debounceTimerRef.current = null
    }

    setIsSaving(true)
    saveToLocalStorage(data)
  }, [data, saveToLocalStorage])

  /**
   * Recover saved data on mount
   */
  useEffect(() => {
    if (!hasRecoveredRef.current) {
      const savedData = getSavedData()

      if (savedData) {
        // Calculate time elapsed since last save
        try {
          const stored = localStorage.getItem(getStorageKey())
          if (stored) {
            const parsed: SavedData = JSON.parse(stored)
            const savedTime = new Date(parsed.timestamp)
            const now = new Date()
            const minutesElapsed = Math.floor(
              (now.getTime() - savedTime.getTime()) / 1000 / 60
            )

            // Show recovery toast
            toast({
              title: "Dados recuperados",
              description: `Seus dados foram recuperados (salvos há ${minutesElapsed} ${
                minutesElapsed === 1 ? "minuto" : "minutos"
              }).`,
              duration: 5000,
            })

            setLastSaved(savedTime)
          }
        } catch (error) {
          console.error("Error calculating time elapsed:", error)
        }

        // Call recovery callback
        if (onRecover) {
          onRecover(savedData)
        }
      }

      hasRecoveredRef.current = true
    }
  }, [getSavedData, onRecover, getStorageKey])

  /**
   * Auto-save with debounce
   */
  useEffect(() => {
    // Skip on first render (recovery phase)
    if (isFirstRenderRef.current) {
      isFirstRenderRef.current = false
      return
    }

    // Skip if data is empty or null
    if (!data || (typeof data === "object" && Object.keys(data).length === 0)) {
      return
    }

    // Clear existing timer
    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current)
    }

    // Set saving indicator
    setIsSaving(true)

    // Set new debounced timer
    debounceTimerRef.current = setTimeout(() => {
      saveToLocalStorage(data)
    }, debounceMs)

    // Cleanup
    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current)
      }
    }
  }, [data, debounceMs, saveToLocalStorage])

  /**
   * Cleanup on unmount
   */
  useEffect(() => {
    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current)
      }
    }
  }, [])

  return {
    isSaving,
    lastSaved,
    saveNow,
    clearSaved,
    getSavedData,
  }
}

"use client"

import { useEffect, useState } from 'react'

interface FormField {
  value: any
  required?: boolean
  weight?: number // Optional weight for important fields
}

interface UseFormCompletionProps {
  fields: Record<string, FormField>
  onMilestoneReached?: (milestone: 40 | 60 | 80 | 100) => void
}

export function useFormCompletion({ fields, onMilestoneReached }: UseFormCompletionProps) {
  const [completion, setCompletion] = useState(0)
  const [previousCompletion, setPreviousCompletion] = useState(0)

  useEffect(() => {
    const calculateCompletion = () => {
      const fieldEntries = Object.entries(fields)

      // Calculate total weight
      const totalWeight = fieldEntries.reduce((sum, [_, field]) => {
        return sum + (field.weight || 1)
      }, 0)

      // Calculate filled weight
      const filledWeight = fieldEntries.reduce((sum, [_, field]) => {
        const isFilled = isFieldFilled(field.value)
        return sum + (isFilled ? (field.weight || 1) : 0)
      }, 0)

      // Calculate percentage
      const percentage = totalWeight > 0
        ? Math.round((filledWeight / totalWeight) * 100)
        : 0

      return Math.min(100, Math.max(0, percentage))
    }

    const newCompletion = calculateCompletion()
    setCompletion(newCompletion)

    // Check for milestone reached
    if (onMilestoneReached) {
      const milestones: Array<40 | 60 | 80 | 100> = [40, 60, 80, 100]
      const reachedMilestone = milestones.find(
        m => newCompletion >= m && previousCompletion < m
      )

      if (reachedMilestone) {
        onMilestoneReached(reachedMilestone)
      }
    }

    setPreviousCompletion(newCompletion)
  }, [fields, onMilestoneReached, previousCompletion])

  return {
    completion,
    isComplete: completion === 100,
    nextMilestone: getNextMilestone(completion),
    percentageToNextMilestone: getPercentageToNextMilestone(completion)
  }
}

function isFieldFilled(value: any): boolean {
  if (value === null || value === undefined) return false
  if (typeof value === 'string') return value.trim().length > 0
  if (typeof value === 'number') return true
  if (typeof value === 'boolean') return true
  if (Array.isArray(value)) return value.length > 0
  if (typeof value === 'object') return Object.keys(value).length > 0
  return false
}

function getNextMilestone(completion: number): number | null {
  const milestones = [20, 40, 60, 80, 100]
  return milestones.find(m => completion < m) || null
}

function getPercentageToNextMilestone(completion: number): number {
  const nextMilestone = getNextMilestone(completion)
  if (!nextMilestone) return 0
  return nextMilestone - completion
}

/**
 * Hook useNotifications - Gerencia conexão SSE e estado de notificações
 *
 * Este hook conecta ao endpoint SSE, recebe notificações em tempo real,
 * mostra toasts, toca sons para alertas críticos e gerencia o estado local.
 */

'use client';

import { useEffect, useState, useCallback, useRef } from 'react';
import { useToast } from '@/hooks/use-toast';
import { NotificationEvent, shouldPlaySound } from '@/types/notifications';

export function useNotifications() {
  const [notifications, setNotifications] = useState<NotificationEvent[]>([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isConnected, setIsConnected] = useState(false);
  const eventSourceRef = useRef<EventSource | null>(null);
  const { toast } = useToast();

  // Função para tocar som de alerta
  const playNotificationSound = useCallback(() => {
    try {
      // Criar um beep usando Web Audio API
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      oscillator.frequency.value = 800; // Frequência em Hz
      oscillator.type = 'sine';

      gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.5);

      oscillator.start(audioContext.currentTime);
      oscillator.stop(audioContext.currentTime + 0.5);
    } catch (error) {
      console.error('Error playing notification sound:', error);
    }
  }, []);

  // Função para marcar notificação como lida
  const markAsRead = useCallback(async (notificationId: string) => {
    try {
      const response = await fetch(`/api/notifications/${notificationId}/read`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to mark notification as read');
      }

      // Atualizar estado local
      setNotifications((prev) =>
        prev.map((notif) =>
          notif.id === notificationId ? { ...notif, read: true } : notif
        )
      );

      setUnreadCount((prev) => Math.max(0, prev - 1));
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  }, []);

  // Função para marcar todas como lidas
  const markAllAsRead = useCallback(async () => {
    try {
      const response = await fetch('/api/notifications/mark-all-read', {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error('Failed to mark all notifications as read');
      }

      // Atualizar estado local
      setNotifications((prev) =>
        prev.map((notif) => ({ ...notif, read: true }))
      );

      setUnreadCount(0);
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
    }
  }, []);

  // Buscar notificações iniciais
  const fetchNotifications = useCallback(async () => {
    try {
      const response = await fetch('/api/notifications?limit=50');

      if (!response.ok) {
        throw new Error('Failed to fetch notifications');
      }

      const data = await response.json();

      if (data.success) {
        setNotifications(data.notifications);
        setUnreadCount(data.unreadCount);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  }, []);

  // Conectar ao SSE
  useEffect(() => {
    let reconnectTimer: NodeJS.Timeout;
    let shouldReconnect = true;

    const connect = () => {
      // Não conectar se já existe uma conexão
      if (eventSourceRef.current) {
        return;
      }

      console.log('[useNotifications] Connecting to SSE...');

      const eventSource = new EventSource('/api/notifications/stream');
      eventSourceRef.current = eventSource;

      eventSource.onopen = () => {
        console.log('[useNotifications] SSE connection established');
        setIsConnected(true);
      };

      eventSource.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          // Ignorar heartbeats e mensagens de conexão
          if (data.type === 'connected' || !data.id) {
            return;
          }

          const notification: NotificationEvent = {
            ...data,
            timestamp: new Date(data.timestamp),
          };

          console.log('[useNotifications] Notification received:', notification);

          // Adicionar ao estado
          setNotifications((prev) => [notification, ...prev]);

          if (!notification.read) {
            setUnreadCount((prev) => prev + 1);
          }

          // Mostrar toast
          toast({
            title: notification.title,
            description: notification.message,
            variant: notification.priority === 'critical' || notification.priority === 'high'
              ? 'destructive'
              : 'default',
          });

          // Tocar som para notificações críticas/altas
          if (shouldPlaySound(notification.priority)) {
            playNotificationSound();
          }
        } catch (error) {
          console.error('[useNotifications] Error parsing notification:', error);
        }
      };

      eventSource.onerror = (error) => {
        console.error('[useNotifications] SSE error:', error);
        setIsConnected(false);

        // Fechar conexão atual
        eventSource.close();
        eventSourceRef.current = null;

        // Tentar reconectar após 5 segundos
        if (shouldReconnect) {
          console.log('[useNotifications] Reconnecting in 5 seconds...');
          reconnectTimer = setTimeout(() => {
            connect();
          }, 5000);
        }
      };
    };

    // Buscar notificações iniciais
    fetchNotifications();

    // Iniciar conexão SSE
    connect();

    // Cleanup
    return () => {
      shouldReconnect = false;
      clearTimeout(reconnectTimer);

      if (eventSourceRef.current) {
        eventSourceRef.current.close();
        eventSourceRef.current = null;
      }

      setIsConnected(false);
    };
  }, [fetchNotifications, playNotificationSound, toast]);

  return {
    notifications,
    unreadCount,
    isConnected,
    markAsRead,
    markAllAsRead,
    refetch: fetchNotifications,
  };
}

'use client'

import { useEffect, useState } from 'react'

export function usePrefersReducedMotion(): boolean {
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false)

  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)')
    setPrefersReducedMotion(mediaQuery.matches)

    const handleChange = (event: MediaQueryListEvent) => {
      setPrefersReducedMotion(event.matches)
    }

    mediaQuery.addEventListener('change', handleChange)
    return () => mediaQuery.removeEventListener('change', handleChange)
  }, [])

  return prefersReducedMotion
}

import { useState, useEffect, useCallback, useRef } from 'react'

export interface RedFlag {
  id: string
  patient: {
    id: string
    name: string
  }
  surgery: {
    id: string
    type: string
  }
  followUp: {
    dayNumber: number
  }
  response: {
    riskLevel: 'critical' | 'high'
    redFlags: string[]
    createdAt: Date | string
  }
  isViewed: boolean
  lastViewedAt: Date | string | null
}

export interface UseRedFlagsReturn {
  redFlags: RedFlag[]
  count: number
  loading: boolean
  error: string | null
  markAsViewed: (followUpResponseId: string) => Promise<void>
  refresh: () => Promise<void>
}

export function useRedFlags(): UseRedFlagsReturn {
  const [redFlags, setRedFlags] = useState<RedFlag[]>([])
  const [count, setCount] = useState(0)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  // Ref para armazenar o último count de critical flags
  const lastCriticalCountRef = useRef(0)
  // Ref para armazenar se já tocou som nesta sessão
  const hasPlayedSoundRef = useRef(false)

  // Função para tocar som de alerta
  const playAlertSound = useCallback(() => {
    try {
      // Cria um contexto de áudio para garantir compatibilidade
      const AudioContext = window.AudioContext || (window as any).webkitAudioContext
      const audioContext = new AudioContext()

      // Cria um oscilador para gerar o som
      const oscillator = audioContext.createOscillator()
      const gainNode = audioContext.createGain()

      oscillator.connect(gainNode)
      gainNode.connect(audioContext.destination)

      // Configura o som (frequência e volume)
      oscillator.frequency.value = 800 // Hz (tom de alerta)
      gainNode.gain.value = 0.3 // Volume (30%)

      // Toca o som por 200ms
      oscillator.start(audioContext.currentTime)
      oscillator.stop(audioContext.currentTime + 0.2)

      // Segundo bip após 300ms
      setTimeout(() => {
        const oscillator2 = audioContext.createOscillator()
        const gainNode2 = audioContext.createGain()

        oscillator2.connect(gainNode2)
        gainNode2.connect(audioContext.destination)

        oscillator2.frequency.value = 1000
        gainNode2.gain.value = 0.3

        oscillator2.start(audioContext.currentTime)
        oscillator2.stop(audioContext.currentTime + 0.2)
      }, 300)

    } catch (error) {
      console.error('Erro ao tocar som de alerta:', error)
    }
  }, [])

  // Função para buscar red flags
  const fetchRedFlags = useCallback(async () => {
    try {
      const response = await fetch('/api/dashboard/red-flags')
      const data = await response.json()

      if (data.success) {
        const newRedFlags = data.data || []
        const newCount = data.count || 0

        setRedFlags(newRedFlags)
        setCount(newCount)
        setError(null)

        // Verifica se há novos critical flags
        const criticalFlags = newRedFlags.filter(
          (flag: RedFlag) => flag.response.riskLevel === 'critical'
        )
        const newCriticalCount = criticalFlags.length

        // Toca som se:
        // 1. Há critical flags
        // 2. O count de critical aumentou OU ainda não tocou som nesta sessão
        // 3. Não está no primeiro load (loading false)
        if (
          newCriticalCount > 0 &&
          !loading &&
          (newCriticalCount > lastCriticalCountRef.current || !hasPlayedSoundRef.current)
        ) {
          playAlertSound()
          hasPlayedSoundRef.current = true
        }

        lastCriticalCountRef.current = newCriticalCount
      } else {
        setError(data.error?.message || 'Erro ao carregar alertas')
      }
    } catch (err) {
      console.error('Erro ao buscar red flags:', err)
      setError('Erro ao conectar com o servidor')
    } finally {
      setLoading(false)
    }
  }, [loading, playAlertSound])

  // Função para marcar como visualizado
  const markAsViewed = useCallback(async (followUpResponseId: string) => {
    try {
      const response = await fetch('/api/dashboard/red-flags', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ followUpResponseId })
      })

      const data = await response.json()

      if (data.success) {
        // Atualiza a lista removendo o red flag visualizado
        setRedFlags(prev => prev.filter(flag => flag.id !== followUpResponseId))
        setCount(prev => Math.max(0, prev - 1))

        // Atualiza o count de critical se necessário
        const wasCritical = redFlags.find(
          flag => flag.id === followUpResponseId && flag.response.riskLevel === 'critical'
        )

        if (wasCritical) {
          lastCriticalCountRef.current = Math.max(0, lastCriticalCountRef.current - 1)
        }
      } else {
        throw new Error(data.error?.message || 'Erro ao marcar como visualizado')
      }
    } catch (err) {
      console.error('Erro ao marcar red flag como visualizado:', err)
      throw err
    }
  }, [redFlags])

  // Polling a cada 30 segundos
  useEffect(() => {
    // Busca inicial
    fetchRedFlags()

    // Configura polling
    const interval = setInterval(fetchRedFlags, 30000) // 30 segundos

    return () => {
      clearInterval(interval)
    }
  }, [fetchRedFlags])

  return {
    redFlags,
    count,
    loading,
    error,
    markAsViewed,
    refresh: fetchRedFlags
  }
}

