      const d7Date = toBrasiliaTime(d7.scheduledDate);
      expect(d7Date.getDate()).toBe(22);

      // Verificar D+14 (29/01)
      const d14 = followUpsData[6];
      const d14Date = toBrasiliaTime(d14.scheduledDate);
      expect(d14Date.getDate()).toBe(29);
    });
  });
});

