/**
 * Processa resposta do paciente durante questionário usando IA conversacional
 */
export async function processQuestionnaireAnswer(
  phoneNumber: string,
  answer: string
): Promise<{ completed: boolean; nextQuestion?: string; needsDoctorAlert?: boolean }> {
  const conversation = await getOrCreateConversation(phoneNumber);
  const context = conversation.context as ConversationContext;

  // Registrar resposta
  await recordUserMessage(conversation.id, answer);

  // Obter paciente e cirurgia
  const patient = await prisma.patient.findUnique({
    where: { id: conversation.patientId! },
    include: {
      surgeries: {
        orderBy: { date: 'desc' },
        take: 1
      }
    }
  });

  if (!patient || !patient.surgeries[0]) {
    throw new Error('Paciente ou cirurgia não encontrado');
  }

  const surgery = patient.surgeries[0];
  const currentData = context.answers || {};

  // Obter histórico de conversação
  const messageHistory = (conversation.messageHistory as any[]) || [];
  const conversationMessages = messageHistory
    .slice(-10) // Últimas 10 mensagens para contexto
    .map(msg => ({
      role: msg.role === 'system' ? 'assistant' : msg.role,
      content: msg.content,
      timestamp: msg.timestamp
    }));

  // Usar IA conversacional para processar resposta
  const { conductConversation } = await import('./conversational-ai');
  const result = await conductConversation(
    answer,
    patient,
    surgery,
    conversationMessages,
    currentData
  );

  // Enviar imagens se a IA solicitou
  if (result.sendImages) {
    const { sendImage } = await import('./whatsapp');
    const baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'https://proactive-rejoicing-production.up.railway.app';

    try {
      if (result.sendImages.painScale) {
        await sendImage(
          phoneNumber,
          `${baseUrl}/escala-dor.png`,
          'Escala Visual Analógica de Dor (0-10)'
        );
        console.log('✅ Pain scale image sent');
      }

      if (result.sendImages.bristolScale) {
        await sendImage(
          phoneNumber,
          `${baseUrl}/escala-bristol.png`,
          'Escala de Bristol - Tipos de Fezes (1-7)'
        );
        console.log('✅ Bristol scale image sent');
      }
    } catch (error) {
      console.error('❌ Error sending images:', error);
      // Continuar mesmo se falhar
    }
  }

  // Atualizar dados coletados
  await updateConversationState(
    conversation.id,
    result.isComplete ? 'completed' : 'collecting_answers',
    {
      followUpId: context.followUpId,
      answers: result.updatedData
    }
  );

  // Enviar resposta da IA
  await sendMessage(phoneNumber, result.aiResponse);
  await recordSystemMessage(conversation.id, result.aiResponse);

  if (result.isComplete) {
    // Salvar respostas no FollowUp
    if (context.followUpId) {
      await saveQuestionnaireResponse(
        context.followUpId,
        result.updatedData,
        patient.userId,
        result.urgencyLevel
      );
    }

    // Enviar mensagem de agradecimento
    const thankYouMessage =
      '✅ Perfeito! Muito obrigada por compartilhar essas informações comigo.\n\n' +
      'Vou passar tudo para o Dr. João Vitor. Ele vai analisar com atenção e, se necessário, entrará em contato com você.\n\n' +
      'Lembre-se: se sentir qualquer sintoma que te preocupe, pode me mandar mensagem a qualquer momento. Estou aqui para ajudar! 😊\n\n' +
      'Boa recuperação! 💙';

    await sendMessage(phoneNumber, thankYouMessage);
    await recordSystemMessage(conversation.id, thankYouMessage);

    // Resetar conversa para idle após 1 minuto
    setTimeout(async () => {
      await updateConversationState(conversation.id, 'idle', {
        followUpId: undefined,
        answers: undefined,
        templateSent: false
      });
    }, 60000);

    return {
      completed: true,
      needsDoctorAlert: result.needsDoctorAlert
    };
  }

  return {
    completed: false,
    nextQuestion: result.aiResponse,
    needsDoctorAlert: result.needsDoctorAlert
  };
}

/**
 * Salva as respostas do questionário no banco
 */
async function saveQuestionnaireResponse(
  followUpId: string,
  answers: Record<string, any>,
  userId: string,
  urgencyLevel: string = 'low'
) {
  // Buscar follow-up para obter surgeryId e dayNumber
  const followUp = await prisma.followUp.findUnique({
    where: { id: followUpId },
    include: { surgery: true }
  });

  if (!followUp) {
    throw new Error('FollowUp not found');
  }

  // Atualizar status do follow-up
  await prisma.followUp.update({
    where: { id: followUpId },
    data: {
      status: 'responded',
      respondedAt: new Date()
    }
  });

  // Se evacuou e ainda não havia registrado primeira evacuação, registrar
  if (
    answers.bowelMovementSinceLastContact === true &&
    !followUp.surgery.hadFirstBowelMovement
  ) {
    const { recordFirstBowelMovement } = await import('./bowel-movement-tracker');
    await recordFirstBowelMovement(
      followUp.surgeryId,
      followUp.dayNumber,
      answers.painDuringBowelMovement || 0,
      answers.stoolConsistency || 4
    );
    console.log('✅ First bowel movement recorded:', {
      dayNumber: followUp.dayNumber,
      painDuringBM: answers.painDuringBowelMovement,
      stoolConsistency: answers.stoolConsistency
    });
  }

  // Mapear níveis de urgência
  const riskLevelMap: Record<string, string> = {
    'low': 'low',
    'medium': 'medium',
    'high': 'high',
    'critical': 'critical'
  };

  const riskLevel = riskLevelMap[urgencyLevel] || 'low';

  // Criar resposta de follow-up
  await prisma.followUpResponse.create({
    data: {
      userId,
      followUpId,
      questionnaireData: JSON.stringify(answers),
      riskLevel
    }
  });

  console.log('✅ Questionário salvo no banco de dados');
}

/**
 * Reseta conversa para estado inicial
 */
export async function resetConversation(phoneNumber: string) {
  const formattedPhone = phoneNumber.replace(/\D/g, '');

  const conversation = await prisma.conversation.findUnique({
    where: { phoneNumber: formattedPhone }
  });

  if (conversation) {
    await prisma.conversation.update({
      where: { id: conversation.id },
      data: {
        state: 'idle',
        context: {},
        lastSystemMessage: null,
        lastUserMessage: null
      }
    });
  }
}

/**
 * IA Conversacional para Questionários Pós-Operatórios
 * Usa Claude para conversar naturalmente com pacientes
 */

import Anthropic from '@anthropic-ai/sdk';
import { Patient, Surgery } from '@prisma/client';
import { prisma } from './prisma';

const anthropic = new Anthropic({
  apiKey: process.env.ANTHROPIC_API_KEY!,
});

export interface ConversationMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
}

export interface QuestionnaireData {
  // Dor
  pain?: number; // 0-10 na escala visual analógica
  painComparison?: 'better' | 'same' | 'worse'; // Comparação com dia anterior

  // Evacuação
  bowelMovementSinceLastContact?: boolean; // Evacuou desde último contato?
  lastBowelMovement?: string; // Quando foi a última evacuação
  painDuringBowelMovement?: number; // Dor durante evacuação (0-10)
  stoolConsistency?: number; // Bristol Scale 1-7

  // Sangramento
  bleeding?: 'none' | 'minimal' | 'moderate' | 'severe'; // nenhum, leve (papel), moderado (roupa), intenso (vaso)
  bleedingDetails?: string;

  // Urina
  urination?: boolean; // Consegue urinar normalmente
  urinationIssues?: string;

  // Febre
  fever?: boolean;
  feverTemperature?: number; // Temperatura em °C

  // Secreção (D3+)
  discharge?: boolean; // Tem secreção?
  dischargeType?: 'clear' | 'yellowish' | 'purulent' | 'bloody'; // Tipo de secreção
  dischargeAmount?: 'minimal' | 'moderate' | 'abundant';

  // Medicações / Analgesia
  medications?: boolean; // Está tomando conforme prescrito
  medicationIssues?: string;
  painControlledWithMeds?: boolean;
  medicationSideEffects?: string;

  // Atividade (D7+)
  activityLevel?: string;

  // Preocupações gerais
  concerns?: string;

  [key: string]: any;
}

/**
 * Conduz conversa com paciente para coletar dados do questionário
 */
export async function conductConversation(
  userMessage: string,
  patient: Patient,
  surgery: Surgery,
  conversationHistory: ConversationMessage[],
  currentData: QuestionnaireData
): Promise<{
  aiResponse: string;
  updatedData: QuestionnaireData;
  isComplete: boolean;
  needsDoctorAlert: boolean;
  urgencyLevel: string;
  sendImages?: {
    painScale?: boolean;
    bristolScale?: boolean;
  };
}> {
  // Calcular dias pós-operatórios
  const daysPostOp = Math.floor((Date.now() - surgery.date.getTime()) / (1000 * 60 * 60 * 24));

  // Obter contexto do questionário diário
  const { getDailyQuestions } = await import('./daily-questionnaire-flow');
  const dailyQuestions = await getDailyQuestions(surgery.id, daysPostOp + 1);

  // Definir o que ainda precisa ser coletado
  const missingInfo = getMissingInformation(currentData, daysPostOp);

  // Construir prompt para Claude
  const systemPrompt = `Você é uma assistente médica virtual especializada em acompanhamento pós-operatório de cirurgia colorretal.

${dailyQuestions.contextForAI}

CONTEXTO DO PACIENTE:
- Nome: ${patient.name}
- Cirurgia: ${surgery.type}
- Dia pós-operatório: D+${daysPostOp}

⚠️ REGRAS CRÍTICAS - NUNCA VIOLE ESTAS REGRAS:

1. INFORMAÇÕES OBRIGATÓRIAS (devem ser coletadas):
   ${missingInfo.length > 0 ? missingInfo.map(info => `- ${info}`).join('\n') : '✅ Todas as informações já foram coletadas!'}

2. DADOS JÁ COLETADOS:
   ${JSON.stringify(currentData, null, 2)}

3. INSTRUÇÕES ABSOLUTAS:

   a) COLETA ESTRUTURADA MAS NATURAL:
      - SEMPRE colete dados específicos (dor 0-10, sim/não para evacuação, etc)
      - MAS faça isso de forma conversacional, empática e fluida
      - Exemplo: "Como está sua dor hoje? Se 0 é sem dor e 10 é a pior dor que já sentiu, qual número você diria?"

   b) NUNCA SUGIRA OU DIRECIONE RESPOSTAS:
      ❌ PROIBIDO: "Pelo que você disse, parece que sua dor deve ser uns 8, né?"
      ❌ PROIBIDO: "Então posso anotar como 7?"
      ✅ CORRETO: "Entendi. Me diz um número de 0 a 10 para eu anotar?"

   c) ESCALA DE DOR (MUITO IMPORTANTE):
      - SEMPRE pergunte a dor usando escala 0-10
      - Se paciente responder vago ("muita dor", "doendo bastante"):
        * NÃO aceite como resposta final
        * EXPLIQUE a escala novamente
        * INSISTA gentilmente: "Preciso que você me diga um número de 0 a 10 para eu registrar certinho"

      ✅ Exemplo correto:
      Paciente: "Estou com muita dor"
      Você: "Entendo que está com bastante dor. Para eu poder registrar direitinho, preciso que você me diga um número. Se 0 é sem dor nenhuma e 10 é a pior dor que você já sentiu na vida, qual número você diria que está agora?"

      Paciente: "Muito forte mesmo"
      Você: "Sim, percebo que está bem forte. Me ajuda com um número de 0 a 10? Isso é importante para o Dr. João acompanhar sua recuperação."

   d) OUTRAS INFORMAÇÕES:

      EVACUAÇÃO (MUITO IMPORTANTE):
      - Pergunte: "Você evacuou desde a última vez que conversamos?"
      - Se SIM:
        * Primeiro: ENVIAR IMAGEM da escala visual analógica de dor
        * Pergunte dor durante evacuação: "Qual foi a dor durante a evacuação? De 0 a 10"
        * Depois: ENVIAR IMAGEM da Escala de Bristol
        * Pergunte consistência: "Olhando a imagem que acabei de enviar, qual número de 1 a 7 mais se parece com suas fezes?"
      - Se NÃO: pergunte "Quando foi a última vez que você evacuou?"
      - ⚠️ SEMPRE pergunte "evacuou desde a última vez que conversamos?"
      - ⚠️ NUNCA pergunte "evacuou hoje" ou "evacuou desde ontem"
      - ⚠️ NUNCA descreva a escala com texto, SEMPRE enviar a IMAGEM

      SANGRAMENTO:
      - Nenhum
      - Leve (apenas no papel higiênico)
      - Moderado (mancha a roupa íntima)
      - Intenso (encheu o vaso sanitário)

      URINA:
      - Consegue urinar normalmente? Sim/Não
      - Se não: quais dificuldades?

      FEBRE:
      - Teve febre? Sim/Não
      - Se sim: qual temperatura mediu? (em °C)

      SECREÇÃO (APENAS D+3 OU SUPERIOR):
      ${daysPostOp >= 3 ? `
      - Tem saída de secreção pela ferida? Sim/Não
      - Se sim:
        * Cor/aspecto: clara, amarelada, purulenta (pus), sanguinolenta
        * Quantidade: pouca, moderada, muita
      ` : '(Não perguntar - paciente está em D+' + daysPostOp + ')'}

      MEDICAÇÕES E ANALGESIA:
      - Está tomando as medicações conforme prescrito? Sim/Não
      - Sua dor está controlada com as medicações? Sim/Não
      - Tem efeitos colaterais? (náusea, sonolência, constipação, etc)

      COMPARAÇÃO DE DOR (D+2 EM DIANTE):
      ${daysPostOp >= 2 ? `
      - Pergunte: "Comparando com ontem, sua dor hoje está melhor, igual ou pior?"
      ${daysPostOp === 2 ? `
      ⚠️ IMPORTANTE D+2: Se paciente disser que dor PIOROU em relação a D+1:
      - Isso é NORMAL e ESPERADO (bloqueio pudendo terminando após ~48h)
      - TRANQUILIZAR o paciente
      - Explicar que deve melhorar nos próximos dias
      ` : `
      ⚠️ Espera-se melhora progressiva após D+3. Se piorar: investigar e alertar médico.
      `}
      ` : '(Não aplicável em D+1)'}

   e) FLUXO DA CONVERSA:
      - Faça UMA pergunta por vez
      - Espere a resposta completa antes de ir para próxima
      - Se resposta incompleta/vaga: gentilmente peça esclarecimento
      - Quando conseguir informação: confirme e siga para próxima
      - NÃO finalize até ter TODOS os dados necessários

   f) EMPATIA E NATURALIDADE:
      - Seja calorosa, acolhedora
      - Use linguagem simples
      - Demonstre que se importa
      - MAS sempre colete os dados objetivos

4. SINAIS DE ALERTA (RED FLAGS):
   - Dor ≥ 8/10
   - Sangramento volumoso
   - Febre ≥ 38°C
   - Não consegue urinar

   Se detectar: oriente PRONTO-SOCORRO imediatamente

5. ENCERRAMENTO:
   Só finalize quando tiver TODAS as informações.

RESPONDA APENAS COM JSON:
{
  "response": "sua resposta natural para o paciente",
  "extractedInfo": {
    "pain": 7,  // APENAS se paciente deu número específico
    "painDuringBowelMovement": 5,  // Se evacuou e respondeu
    "stoolConsistency": 4,  // Bristol Scale 1-7, se evacuou
    "bowelMovementSinceLastContact": true,  // true/false
    "painComparison": "worse",  // "better"|"same"|"worse" (D+2+)
    "medications": true,
    "painControlledWithMeds": false,
    // ... outros campos conforme coletados
  },
  "sendImages": {
    "painScale": false,  // true se precisa enviar escala de dor
    "bristolScale": false  // true se precisa enviar escala de Bristol
  },
  "isComplete": false,
  "urgency": "low|medium|high|critical",
  "needsDoctorAlert": false
}

⚠️ IMPORTANTE:
- Só incluir em extractedInfo os dados que o paciente EFETIVAMENTE forneceu nesta mensagem.
- Não invente ou assuma valores. Se paciente não respondeu algo, não incluir no JSON.
- Use sendImages.painScale: true ANTES de perguntar sobre dor (em repouso ou durante evacuação)
- Use sendImages.bristolScale: true ANTES de perguntar sobre consistência das fezes`;

  try {
    // Construir mensagens para Claude
    const messages: any[] = [];

    // Adicionar histórico
    conversationHistory.forEach(msg => {
      messages.push({
        role: msg.role,
        content: msg.content
      });
    });

    // Adicionar mensagem atual do usuário
    messages.push({
      role: 'user',
      content: userMessage
    });

    const response = await anthropic.messages.create({
      model: 'claude-3-5-sonnet-20241022',
      max_tokens: 1024,
      temperature: 0.7, // Mais criativa para conversação natural
      system: systemPrompt,
      messages: messages,
    });

    const content = response.content[0];
    if (content.type !== 'text') {
      throw new Error('Unexpected response type from Claude');
    }

    // Extrair JSON da resposta
    const jsonMatch = content.text.match(/\{[\s\S]*\}/);
    if (!jsonMatch) {
      throw new Error('No JSON found in Claude response');
    }

    const result = JSON.parse(jsonMatch[0]);

    // Atualizar dados coletados
    const updatedData = {
      ...currentData,
      ...result.extractedInfo
    };

    return {
      aiResponse: result.response,
      updatedData,
      isComplete: result.isComplete || false,
      needsDoctorAlert: result.needsDoctorAlert || false,
      urgencyLevel: result.urgency || 'low',
      sendImages: result.sendImages
    };

  } catch (error) {
    console.error('Error in conversational AI:', error);

    // Fallback: resposta genérica
    return {
      aiResponse: 'Desculpe, tive um problema técnico. Pode repetir sua resposta, por favor?',
      updatedData: currentData,
      isComplete: false,
      needsDoctorAlert: false,
      urgencyLevel: 'low'
    };
  }
}

/**
 * Determina quais informações ainda faltam coletar
 */
function getMissingInformation(data: QuestionnaireData, daysPostOp: number): string[] {
  const missing: string[] = [];

  // 1. DOR (sempre obrigatório)
  if (data.pain === undefined || data.pain === null) {
    missing.push('Nível de dor ATUAL (0-10 na escala visual analógica)');
  }

  // 2. EVACUAÇÃO
  if (data.bowelMovementSinceLastContact === undefined) {
    missing.push('Se evacuou desde o último contato');
  } else if (data.bowelMovementSinceLastContact === false) {
    // Se não evacuou, perguntar quando foi a última vez
    if (!data.lastBowelMovement) {
      missing.push('Quando foi a última evacuação');
    }
  } else if (data.bowelMovementSinceLastContact === true) {
    // Se evacuou, perguntar a dor durante a evacuação
    if (data.painDuringBowelMovement === undefined || data.painDuringBowelMovement === null) {
      missing.push('Dor durante a evacuação (0-10 na escala visual analógica)');
    }
    // E a consistência das fezes (Bristol Scale)
    if (data.stoolConsistency === undefined || data.stoolConsistency === null) {
      missing.push('Consistência das fezes (Escala de Bristol 1-7)');
    }
  }

  // 3. SANGRAMENTO
  if (!data.bleeding) {
    missing.push('Informações sobre sangramento (nenhum, leve, moderado, intenso)');
  }

  // 4. URINA
  if (data.urination === undefined) {
    missing.push('Se está conseguindo urinar normalmente');
  }

  // 5. FEBRE
  if (data.fever === undefined) {
    missing.push('Se teve febre');
  } else if (data.fever === true && !data.feverTemperature) {
    missing.push('Qual foi a temperatura da febre (em °C)');
  }

  // 6. SECREÇÃO PURULENTA (apenas D+3 ou superior)
  if (daysPostOp >= 3) {
    if (data.discharge === undefined) {
      missing.push('Se tem saída de secreção pela ferida');
    } else if (data.discharge === true) {
      if (!data.dischargeType) {
        missing.push('Aspecto/cor da secreção (clara, amarelada, purulenta, sanguinolenta)');
      }
      if (!data.dischargeAmount) {
        missing.push('Quantidade de secreção (pouca, moderada, muita)');
      }
    }
  }

  // 7. MEDICAÇÕES
  if (data.medications === undefined) {
    missing.push('Se está tomando as medicações conforme prescrito');
  }

  // Concerns é sempre opcional

  return missing;
}

/**
 * Inicia conversa com saudação personalizada
 */
export async function getInitialGreeting(
  patient: Patient,
  surgery: Surgery,
  dayNumber: number,
  phoneNumber: string
): Promise<string> {
  const greeting = getGreeting();
  const firstName = patient.name.split(' ')[0];

  // Obter mensagem de introdução do dia
  const { getIntroductionMessage } = await import('./daily-questionnaire-flow');
  const introMessage = getIntroductionMessage(dayNumber);

  // Enviar imagem da escala de dor ANTES da saudação
  const { sendImage } = await import('./whatsapp');
  try {
    // URL pública da imagem da escala de dor
    // Nota: O arquivo escala-dor.png deve estar em public/
    const imageUrl = `${process.env.NEXT_PUBLIC_APP_URL || 'https://proactive-rejoicing-production.up.railway.app'}/escala-dor.png`;

    await sendImage(
      phoneNumber,
      imageUrl,
      'Escala Visual Analógica de Dor (0-10)'
    );

    console.log('✅ Pain scale image sent before initial greeting');
  } catch (error) {
    console.error('❌ Error sending pain scale image:', error);
    // Continuar mesmo se falhar o envio da imagem
  }

  return `${greeting}, ${firstName}! 👋

Aqui é a assistente de acompanhamento pós-operatório do Dr. João Vitor.

${introMessage}

Vou te fazer algumas perguntas sobre como você está. Pode responder livremente que eu vou anotando tudo certinho. 😊`;
}

/**
 * Retorna saudação apropriada baseada no horário
 */
function getGreeting(): string {
  const hour = new Date().getHours();

  if (hour >= 5 && hour < 12) {
    return 'Bom dia';
  } else if (hour >= 12 && hour < 18) {
    return 'Boa tarde';
  } else {
    return 'Boa noite';
  }
}

/**
 * Fluxo de Questionário Diário Personalizado
 *
 * Define perguntas específicas para cada dia pós-operatório (D+1, D+2, D+3, D+5, D+7, D+10, D+14)
 * Integra:
 * - Rastreamento de primeira evacuação
 * - Análise de tendências de dor
 * - Questionário de analgesia
 * - Perguntas contextuais baseadas no dia
 */

import { Surgery } from '@prisma/client';
import {
  checkBowelMovementStatus,
  getBowelMovementQuestions,
  BowelMovementStatus
} from './bowel-movement-tracker';
import { getAnalgesiaQuestions } from './analgesia-questionnaire';
import { getPainHistory, analyzePainTrend, comparePainWithPreviousDay } from './pain-trend-analyzer';

export interface DailyQuestionSet {
  dayNumber: number;
  questions: QuestionDefinition[];
  contextForAI: string;
  expectedDataFields: string[];
  sendPainScaleImage: boolean;
}

export interface QuestionDefinition {
  id: string;
  category:
    | 'pain'
    | 'bowel_movement'
    | 'bleeding'
    | 'urination'
    | 'fever'
    | 'discharge'
    | 'analgesia'
    | 'general';
  required: boolean;
  text: string;
  followUpLogic?: string;
  contextNote?: string;
}

/**
 * Retorna conjunto de perguntas para um dia específico
 */
export async function getDailyQuestions(
  surgeryId: string,
  dayNumber: number
): Promise<DailyQuestionSet> {
  const surgery = await getSurgery(surgeryId);
  const bowelStatus = await checkBowelMovementStatus(surgeryId, dayNumber);
  const painHistory = await getPainHistory(surgeryId);

  // Perguntas base (presentes em todos os dias)
  const baseQuestions: QuestionDefinition[] = [
    {
      id: 'pain_at_rest',
      category: 'pain',
      required: true,
      text: 'Como está sua dor AGORA, em repouso? Me diz um número de 0 a 10.',
      contextNote:
        'SEMPRE enviar imagem da escala visual analógica ANTES desta pergunta. NUNCA aceitar respostas vagas.'
    }
  ];

  // Perguntas sobre evacuação (variam se já evacuou ou não)
  const bowelQuestions = getBowelMovementQuestions(
    bowelStatus.hadFirstMovement,
    dayNumber
  );

  // Adicionar pergunta sobre evacuação
  baseQuestions.push({
    id: 'bowel_movement',
    category: 'bowel_movement',
    required: true,
    text: bowelQuestions.mainQuestion,
    followUpLogic: `
      Se SIM:
        ${bowelQuestions.followUpIfYes.join('\n        ')}
      Se NÃO:
        ${bowelQuestions.followUpIfNo.join('\n        ')}
    `,
    contextNote: bowelQuestions.contextForAI
  });

  // Perguntas específicas por dia
  const daySpecificQuestions = getDaySpecificQuestions(dayNumber, bowelStatus, painHistory.length > 0);

  // Perguntas comuns a todos os dias (após as específicas)
  const commonQuestions: QuestionDefinition[] = [
    {
      id: 'bleeding',
      category: 'bleeding',
      required: true,
      text: 'Você está tendo sangramento? Se sim, me conta como está:',
      followUpLogic: `
        Classificação:
        - Nenhum: sem sangramento
        - Leve: apenas no papel higiênico
        - Moderado: mancha a roupa íntima
        - Intenso: encheu o vaso sanitário

        ⚠️ Se INTENSO: orientar procurar pronto-socorro IMEDIATAMENTE
      `
    },
    {
      id: 'urination',
      category: 'urination',
      required: true,
      text: 'Você está conseguindo urinar normalmente?',
      followUpLogic: `
        Se NÃO: perguntar quais dificuldades (dor, ardência, retenção)
        ⚠️ Se retenção urinária: orientar procurar pronto-socorro
      `
    },
    {
      id: 'fever',
      category: 'fever',
      required: true,
      text: 'Você teve febre?',
      followUpLogic: `
        Se SIM: perguntar qual foi a temperatura em °C
        ⚠️ Se ≥38°C: alerta médico
      `
    }
  ];

  // Adicionar pergunta sobre secreção se D+3 ou posterior
  if (dayNumber >= 3) {
    commonQuestions.push({
      id: 'discharge',
      category: 'discharge',
      required: true,
      text: 'Você tem saída de secreção (líquido) pela ferida operatória?',
      followUpLogic: `
        Se SIM:
          - Qual a cor/aspecto? (clara, amarelada, purulenta/pus, sanguinolenta)
          - Qual a quantidade? (pouca, moderada, muita)

        ⚠️ Se purulenta (pus) ou quantidade abundante: alerta médico
      `,
      contextNote: 'Secreção purulenta a partir de D+3 pode indicar infecção'
    });
  }

  // Perguntas sobre analgesia (todos os dias)
  const analgesiaQuestions = getAnalgesiaQuestions();
  baseQuestions.push(
    {
      id: 'taking_meds',
      category: 'analgesia',
      required: true,
      text: 'Você está tomando as medicações conforme o médico prescreveu?',
      followUpLogic: `
        Se NÃO: investigar motivo (esqueceu, efeito colateral, não comprou)
        Reforçar importância da analgesia preventiva
      `
    },
    {
      id: 'pain_controlled',
      category: 'analgesia',
      required: true,
      text: 'Sua dor está controlada com as medicações?',
      followUpLogic: `
        Se NÃO: alerta médico para ajuste de prescrição
      `
    },
    {
      id: 'medication_side_effects',
      category: 'analgesia',
      required: false,
      text: 'Você está tendo algum efeito colateral das medicações? (náusea, tontura, sonolência, etc)',
      followUpLogic: `
        Se SIM: investigar quais efeitos
        Se múltiplos efeitos severos: alerta médico
      `
    }
  );

  // Pergunta final sobre preocupações (sempre opcional)
  const finalQuestions: QuestionDefinition[] = [
    {
      id: 'concerns',
      category: 'general',
      required: false,
      text: 'Tem alguma dúvida ou preocupação que gostaria de compartilhar comigo?',
      contextNote: 'Campo livre para o paciente expressar qualquer preocupação'
    }
  ];

  // Montar lista completa de perguntas
  const allQuestions = [
    ...baseQuestions,
    ...daySpecificQuestions,
    ...commonQuestions,
    ...finalQuestions
  ];

  // Contexto para a IA
  const contextForAI = buildContextForAI(dayNumber, bowelStatus, painHistory);

  // Campos esperados no questionário
  const expectedDataFields = allQuestions.filter(q => q.required).map(q => q.id);

  return {
    dayNumber,
    questions: allQuestions,
    contextForAI,
    expectedDataFields,
    sendPainScaleImage: true // SEMPRE enviar imagem da escala antes de perguntas de dor
  };
}

/**
 * Perguntas específicas de cada dia
 */
function getDaySpecificQuestions(
  dayNumber: number,
  bowelStatus: BowelMovementStatus,
  hasPainHistory: boolean
): QuestionDefinition[] {
  const questions: QuestionDefinition[] = [];

  // D+1: Apenas perguntas básicas
  if (dayNumber === 1) {
    // Nenhuma pergunta específica adicional
  }

  // D+2+: Comparar dor com dia anterior
  if (dayNumber >= 2 && hasPainHistory) {
    questions.push({
      id: 'pain_comparison',
      category: 'pain',
      required: false,
      text: 'Comparando com ontem, sua dor hoje está melhor, igual ou pior?',
      contextNote:
        dayNumber === 2
          ? '⚠️ D+2: Aumento de dor é NORMAL (bloqueio pudendo terminando). Não alarmar o paciente.'
          : 'Espera-se melhora progressiva após D+3. Piora significativa requer atenção.'
    });
  }

  // D+7, D+10, D+14: Perguntar sobre retorno às atividades
  if (dayNumber >= 7) {
    questions.push({
      id: 'activity_level',
      category: 'general',
      required: false,
      text: 'Como está sua disposição para as atividades do dia a dia?',
      contextNote: 'Avaliar recuperação funcional do paciente'
    });
  }

  return questions;
}

/**
 * Constrói contexto detalhado para a IA
 */
function buildContextForAI(
  dayNumber: number,
  bowelStatus: BowelMovementStatus,
  painHistory: any[]
): string {
  let context = `
DIA PÓS-OPERATÓRIO: D+${dayNumber}

═══════════════════════════════════════════════════════════════
CONTEXTO CLÍNICO IMPORTANTE
═══════════════════════════════════════════════════════════════
`;

  // Contexto específico do dia
  if (dayNumber === 1) {
    context += `
📅 D+1 - PRIMEIRO DIA PÓS-OPERATÓRIO

Esperado:
- Bloqueio do nervo pudendo AINDA ATIVO (~48h de duração)
- Dor mínima em repouso (0-4/10)
- Paciente pode não ter evacuado ainda (NORMAL)
- Ainda em fase de adaptação pós-cirúrgica

⚠️ Red Flags D+1:
- Dor ≥8/10 (bloqueio pode ter falho)
- Sangramento intenso
- Retenção urinária
- Febre ≥38°C
`;
  } else if (dayNumber === 2) {
    context += `
📅 D+2 - TÉRMINO DO BLOQUEIO PUDENDO

⚠️ MUITO IMPORTANTE:
- Bloqueio anestésico está TERMINANDO (dura ~48h)
- É NORMAL e ESPERADO que a dor AUMENTE em relação a D+1
- Aumento de dor D+1→D+2 NÃO é motivo de alarme
- Espera-se dor entre 2-7/10

Esperado:
- Dor pode estar maior que ontem (isso é OK!)
- Paciente pode estar assustado com o aumento da dor
- Primeira evacuação pode ou não ter ocorrido

Abordagem:
- Se paciente relatar dor maior: TRANQUILIZAR
- Explicar que é esperado devido ao término do bloqueio
- Reforçar que deve melhorar nos próximos dias
- Verificar se está tomando analgésicos corretamente
`;
  } else if (dayNumber === 3) {
    context += `
📅 D+3 - PICO INFLAMATÓRIO

Esperado:
- Dor entre 2-6/10 (pico de resposta inflamatória)
- Deve começar a MELHORAR após este dia
- Paciente deve ter evacuado ou estar próximo (se não, investigar medo)
- Começar perguntas sobre secreção purulenta

⚠️ Red Flags D+3:
- Dor ≥8/10
- Não evacuou e está com medo de dor
- Febre (possível infecção)
`;
  } else if (dayNumber >= 4 && dayNumber <= 7) {
    context += `
📅 D+${dayNumber} - FASE DE MELHORA PROGRESSIVA

Esperado:
- Dor em TENDÊNCIA DE MELHORA (não necessariamente linear)
- Primeira evacuação JÁ DEVE ter ocorrido
- Paciente retomando funções básicas

⚠️ Red Flags D+${dayNumber}:
- Dor ≥7/10 ou piorando
- Ainda não evacuou (≥4 dias sem evacuar)
- Secreção purulenta abundante
- Febre persistente
`;
  } else if (dayNumber >= 8) {
    context += `
📅 D+${dayNumber} - RECUPERAÇÃO AVANÇADA

Esperado:
- Dor leve (0-4/10) ou ausente
- Evacuações regulares
- Retorno gradual às atividades

⚠️ Red Flags D+${dayNumber}:
- Dor persistente ≥5/10
- Sangramento novo ou intensificado
- Sinais de infecção tardia
`;
  }

  // Contexto sobre evacuação
  context += `

═══════════════════════════════════════════════════════════════
SITUAÇÃO DE EVACUAÇÃO
═══════════════════════════════════════════════════════════════

${
    bowelStatus.hadFirstMovement
      ? `✅ Primeira evacuação JÁ OCORREU em D+${bowelStatus.dayNumber}
   - Perguntas de rotina sobre evacuação
   - Se evacuou desde último contato: perguntar dor (0-10) + Bristol Scale`
      : `❌ Primeira evacuação AINDA NÃO OCORREU
   - Dias sem evacuar: ${bowelStatus.daysWithoutMovement}
   - Urgência: ${bowelStatus.urgencyLevel.toUpperCase()}
   - Orientação: ${bowelStatus.message}

   ${bowelStatus.urgencyLevel === 'urgent' ? '⚠️ IMPORTANTE: Avisar médico sobre constipação prolongada' : ''}
   ${bowelStatus.urgencyLevel === 'concern' ? '⚠️ Investigar: paciente está com medo de dor? Tomando laxantes?' : ''}`
  }

LEMBRAR: SEMPRE perguntar "evacuou desde a última vez que conversamos?"
          NUNCA perguntar "evacuou hoje?" ou "evacuou desde ontem?"
`;

  // Contexto sobre dor (se houver histórico)
  if (painHistory.length > 0) {
    const lastPain = painHistory[painHistory.length - 1];
    context += `

═══════════════════════════════════════════════════════════════
HISTÓRICO DE DOR
═══════════════════════════════════════════════════════════════

Última dor registrada: ${lastPain.painAtRest}/10 em D+${lastPain.dayPostOp}

${
      dayNumber === 2
        ? `⚠️ Se dor aumentar em relação a D+1: tranquilizar paciente que é NORMAL`
        : dayNumber >= 3
        ? `⚠️ Espera-se melhora progressiva. Se piorar significativamente: alerta médico`
        : ''
    }
`;
  }

  // Instruções finais
  context += `

═══════════════════════════════════════════════════════════════
INSTRUÇÕES DE CONDUÇÃO
═══════════════════════════════════════════════════════════════

1. ENVIAR IMAGEM da escala visual analógica ANTES de perguntar sobre dor
2. UMA pergunta por vez, aguardar resposta
3. NUNCA sugerir ou direcionar respostas
4. Se resposta vaga: gentilmente insistir em dados específicos
5. Ser EMPÁTICA mas COLETAR DADOS ESTRUTURADOS
6. Red flags: alertar médico imediatamente

IMPORTANTE: Este é um questionário de PESQUISA científica.
           Os dados precisam ser precisos e comparáveis.
`;

  return context;
}

/**
 * Valida se todos os dados obrigatórios foram coletados
 */
export function isQuestionnaireComplete(
  collectedData: Record<string, any>,
  expectedFields: string[]
): boolean {
  for (const field of expectedFields) {
    if (collectedData[field] === undefined || collectedData[field] === null) {
      return false;
    }
  }
  return true;
}

/**
 * Helper para buscar cirurgia
 */
async function getSurgery(surgeryId: string): Promise<Surgery> {
  const { prisma } = await import('./prisma');
  const surgery = await prisma.surgery.findUnique({
    where: { id: surgeryId }
  });

  if (!surgery) {
    throw new Error('Surgery not found');
  }

  return surgery;
}

/**
 * Gera mensagem de introdução para o questionário do dia
 */
export function getIntroductionMessage(dayNumber: number): string {
  const greetings: Record<number, string> = {
    1: 'Como foi sua primeira noite após a cirurgia? Vamos ver como você está hoje.',
    2: 'Chegamos ao segundo dia! Como está se sentindo hoje?',
    3: 'Metade da primeira semana já passou. Como está indo sua recuperação?',
    5: 'Já fazem 5 dias da cirurgia. Vamos ver como está sua evolução.',
    7: 'Uma semana completa! Como você está se sentindo?',
    10: '10 dias de recuperação. Como está se sentindo?',
    14: 'Duas semanas! Estamos quase finalizando o acompanhamento. Como está?'
  };

  return (
    greetings[dayNumber] ||
    `Dia ${dayNumber} pós-operatório. Vamos conversar sobre como você está?`
  );
}

