/**
 * Category labels for display
 */
export const CATEGORY_LABELS: Record<string, string> = {
  dadosBasicos: 'Dados Básicos',
  cirurgia: 'Cirurgia',
  comorbidades: 'Comorbidades',
  medicacoes: 'Medicações',
