/**
 * Create APA-style title page
 */
function createTitlePage(metadata: ResearchMetadata): Paragraph[] {
  const paragraphs: Paragraph[] = [];

  // Running head (removed in APA 7th for student papers, but kept for professional)
  paragraphs.push(
    new Paragraph({
      text: 'Running head: ' + metadata.title.toUpperCase().substring(0, 50),
      alignment: AlignmentType.LEFT,
      spacing: { after: 200 },
    })
  );

  // Title (centered, bold, title case)
  paragraphs.push(
    new Paragraph({
      text: metadata.title,
      heading: HeadingLevel.TITLE,
      alignment: AlignmentType.CENTER,
      spacing: { before: 400, after: 200 },
    })
  );

  // Authors
  metadata.authors.forEach((author) => {
    paragraphs.push(
      new Paragraph({
        text: author,
        alignment: AlignmentType.CENTER,
        spacing: { after: 100 },
      })
    );
  });

  // Institution
  paragraphs.push(
    new Paragraph({
      text: metadata.institution,
      alignment: AlignmentType.CENTER,
      spacing: { after: 200 },
    })
  );

  // Date
  paragraphs.push(
    new Paragraph({
      text: metadata.date.toLocaleDateString('pt-BR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      }),
      alignment: AlignmentType.CENTER,
    })
  );

  return paragraphs;
}

/**
 * Create abstract section
 */
function createAbstract(abstractText: string, keywords: string[]): Paragraph[] {
  const paragraphs: Paragraph[] = [];

  paragraphs.push(
    new Paragraph({
      text: 'Abstract',
      heading: HeadingLevel.HEADING_1,
      alignment: AlignmentType.CENTER,
      spacing: { before: 400, after: 200 },
    })
  );

  paragraphs.push(
    new Paragraph({
      text: abstractText,
      alignment: AlignmentType.JUSTIFIED,
      spacing: { line: 480 }, // Double spacing (240 = single)
    })
  );

  // Keywords
  paragraphs.push(
    new Paragraph({
      children: [
        new TextRun({
          text: 'Keywords: ',
          italics: true,
        }),
        new TextRun({
          text: keywords.join(', '),
        }),
      ],
      spacing: { before: 200 },
    })
  );

  return paragraphs;
}

/**
 * Create APA-formatted table
 */
function createAPATable(data: APATableData): (Paragraph | Table)[] {
  const elements: (Paragraph | Table)[] = [];

  // Table title (italicized)
  elements.push(
    new Paragraph({
      children: [
        new TextRun({
          text: `Table ${data.tableNumber}`,
          italics: true,
          bold: true,
        }),
      ],
      spacing: { before: 400, after: 100 },
    })
  );

  elements.push(
    new Paragraph({
      children: [
        new TextRun({
          text: data.title,
          italics: true,
        }),
      ],
      spacing: { after: 200 },
    })
  );

  // Create table
  const tableRows: TableRow[] = [];

  // Header row
  tableRows.push(
    new TableRow({
      children: data.headers.map(
        (header) =>
          new TableCell({
            children: [
              new Paragraph({
                children: [
                  new TextRun({
                    text: header,
                    bold: true,
                  }),
                ],
              }),
            ],
            borders: {
              top: { style: BorderStyle.SINGLE, size: 1 },
              bottom: { style: BorderStyle.SINGLE, size: 1 },
              left: { style: BorderStyle.NONE, size: 0 },
              right: { style: BorderStyle.NONE, size: 0 },
            },
          })
      ),
    })
  );

  // Data rows
  data.rows.forEach((row) => {
    tableRows.push(
      new TableRow({
        children: row.map(
          (cell) =>
            new TableCell({
              children: [
                new Paragraph({
                  text: String(cell),
                }),
              ],
              borders: {
                top: { style: BorderStyle.NONE, size: 0 },
                bottom: { style: BorderStyle.NONE, size: 0 },
                left: { style: BorderStyle.NONE, size: 0 },
                right: { style: BorderStyle.NONE, size: 0 },
              },
            })
        ),
      })
    );
  });

  const table = new Table({
    rows: tableRows,
    width: {
      size: 100,
      type: WidthType.PERCENTAGE,
    },
    borders: {
      top: { style: BorderStyle.SINGLE, size: 1 },
      bottom: { style: BorderStyle.SINGLE, size: 1 },
      left: { style: BorderStyle.NONE, size: 0 },
      right: { style: BorderStyle.NONE, size: 0 },
      insideHorizontal: { style: BorderStyle.NONE, size: 0 },
      insideVertical: { style: BorderStyle.NONE, size: 0 },
    },
  });

  elements.push(table);

  // Table note
  if (data.note) {
    elements.push(
      new Paragraph({
        children: [
          new TextRun({
            text: 'Note. ',
            italics: true,
          }),
          new TextRun({
            text: data.note,
          }),
        ],
        spacing: { before: 100, after: 200 },
      })
    );
  }

  // Significance notes
  if (data.significanceNotes) {
    elements.push(
      new Paragraph({
        children: [
          new TextRun({
            text: '* ',
            italics: true,
          }),
          new TextRun({
            text: 'p',
            italics: true,
          }),
          new TextRun({
            text: ' < .05. ** ',
          }),
          new TextRun({
            text: 'p',
            italics: true,
          }),
          new TextRun({
            text: ' < .01. *** ',
          }),
          new TextRun({
            text: 'p',
            italics: true,
          }),
          new TextRun({
            text: ' < .001.',
          }),
        ],
        spacing: { after: 400 },
      })
    );
  }

  return elements;
}

/**
 * Create figure with caption
