      console.log(JSON.stringify(sample, null, 2));
    }

  } catch (error) {
    console.error('\n❌ Erro na busca SQL:', error.message);
    console.error('Stack:', error.stack);
  } finally {
    await prisma.$disconnect();
  }
}

