// ============================================
// TIPOS DE NOTIFICAÇÃO
// ============================================

export type NotificationType =
  | 'red_flag_critical'
  | 'red_flag_high'
  | 'red_flag_medium'
  | 'followup_completed'
  | 'followup_overdue'
  | 'patient_created'
  | 'surgery_created'
  | 'system_alert'
  | 'info';

export const NOTIFICATION_TYPE_LABELS: Record<NotificationType, string> = {
  red_flag_critical: 'Alerta Crítico',
  red_flag_high: 'Alerta Alto',
  red_flag_medium: 'Alerta Médio',
  followup_completed: 'Follow-up Respondido',
  followup_overdue: 'Follow-up Atrasado',
  patient_created: 'Novo Paciente',
  surgery_created: 'Nova Cirurgia',
  system_alert: 'Alerta do Sistema',
  info: 'Informação',
};

// ============================================
// PRIORIDADES
// ============================================

export type NotificationPriority = 'low' | 'medium' | 'high' | 'critical';

export const PRIORITY_LABELS: Record<NotificationPriority, string> = {
  low: 'Baixa',
  medium: 'Média',
  high: 'Alta',
  critical: 'Crítica',
};

export const PRIORITY_COLORS: Record<NotificationPriority, string> = {
  low: 'blue',
  medium: 'yellow',
  high: 'orange',
  critical: 'red',
};

// ============================================
// INTERFACE NOTIFICATION
// ============================================

export interface Notification {
  id: string;
  createdAt: Date;
  userId: string;
  type: NotificationType;
  title: string;
  message: string;
  priority: NotificationPriority;
  read: boolean;
  readAt?: Date | null;
  data?: Record<string, any> | null;
  actionUrl?: string | null;
}

