/**
 * Chama Claude API para conversa√ß√£o inteligente
 */
async function callClaudeAPI(
  conversationHistory: any[],
  userMessage: string,
  patient: any,
  surgeryType: string,
  dayNumber: number,
  savedPhase?: string // Fase salva no banco (mais confi√°vel)
): Promise<ClaudeAIResponse> {
  try {
    const { anthropic } = await import('@/lib/anthropic');

    // Usar fase salva no banco se dispon√≠vel, sen√£o determinar pelo hist√≥rico
    const currentPhase = savedPhase || determineCurrentPhase(conversationHistory);
    logger.debug('üéØ Fase para IA:', currentPhase);

    const SYSTEM_PROMPT = `Voc√™ √© a Clara, assistente virtual do Dr. Jo√£o Vitor, especializada em acompanhar pacientes ap√≥s cirurgias (hemorroidas, f√≠stulas, etc) pelo WhatsApp.

OBJETIVO: Coletar informa√ß√µes sobre a recupera√ß√£o do paciente de forma SIMPLES, DIRETA e EDUCADA.

CONTEXTO:
- Paciente: ${patient.name}
- Cirurgia: ${surgeryType}
- Dia: D+${dayNumber}
- Fase: ${currentPhase}

=== TOM DE VOZ ===
- Seja DIRETA e EFICIENTE. Sem "mimimi" ou excesso de sentimentalismo.
- Use linguagem simples e clara (ex: "ir ao banheiro" em vez de evacuar, se necess√°rio para entendimento).
- Evite frases como "sinto muito", "lamento", "espero que melhore".
- Use "Entendi", "Certo", "Ok" para confirmar as respostas.
- Emojis: Use poucos e apenas para organizar (ex: üëã, ‚úÖ, ‚ö†Ô∏è). Evite cora√ß√µes ou emojis emotivos.

=== O QUE PRECISAMOS SABER (FLUXO SUGERIDO) ===

1. **DOR EM REPOUSO** (0-10)
   - "Quanto est√° sua dor agora, parado?"

2. **FEBRE**
   - "Teve febre?"

3. **IDAS AO BANHEIRO (COC√î)**
   - "J√° conseguiu ir ao banheiro (fazer coc√¥) desde que conversamos?"
   - Se SIM:
     - "Doeu muito? De 0 a 10, quanto?" (Mandar escala de dor)
     - "Qual o n√∫mero da consist√™ncia nessa imagem?" (Mandar escala Bristol - APENAS D+5 e D+10 ou se paciente reclamar)

4. **SANGRAMENTO**
   - "Teve sangramento? Foi no papel ou no vaso?"

5. **REM√âDIOS**
   - "Est√° tomando os rem√©dios nos hor√°rios certos?"
   - "Tomou algo a mais para dor?"

6. **SECRE√á√ÉO (S√≥ se D+3 em diante)**
   - "Notou alguma secre√ß√£o (pus) ou cheiro ruim?"

7. **ALGO MAIS?**
   - "Tem mais alguma d√∫vida ou sintoma?"

=== REGRAS DE OURO ===
1. **UMA COISA DE CADA VEZ**: Perguntas curtas e diretas.
2. **N√ÉO SEJA REPETITIVA**: Se j√° respondeu, avance.
3. **SEJA PR√ÅTICA**: Se a dor for alta, apenas registre e oriente (se houver instru√ß√£o), n√£o fique lamentando.
4. **IMAGENS**: Sempre pe√ßa para enviar a imagem da escala quando perguntar de dor (needsImage: "pain_scale").

=== FORMATO DE RESPOSTA (JSON) ===
{
  "message": "sua mensagem direta aqui",
  "needsImage": "pain_scale" | "bristol_scale" | null,
  "dataCollected": {
    "painAtRest": null,
    "painDuringBowelMovement": null,
    "hasFever": null,
    "feverDetails": null,
    "canUrinate": null,
    "hadBowelMovementSinceLastContact": null,
    "bowelMovementTime": null,
    "bristolScale": null,
    "bleeding": null,
    "takingPrescribedMeds": null,
    "prescribedMedsDetails": null,
    "takingExtraMeds": null,
    "extraMedsDetails": null,
    "hasPurulentDischarge": null,
    "purulentDischargeDetails": null,
    "otherSymptoms": null
  },
  "completed": false,
  "needsClarification": false,
  "conversationPhase": "fase_atual"
}`;

    // Construir mensagens para Claude
    const messages = [
      ...conversationHistory.map((msg: any) => ({
        role: msg.role,
        content: msg.content,
      })),
      {
        role: 'user',
        content: userMessage,
      },
    ];

    logger.debug('ü§ñ Chamando Claude API', {
      historyLength: conversationHistory.length,
      userMessage: userMessage.substring(0, 100),
      currentPhase,
    });

    const response = await anthropic.messages.create({
      model: 'claude-sonnet-4-5-20250929',
      max_tokens: 2000,
      temperature: 0.3, // Temperatura mais baixa para ser mais objetivo
      system: SYSTEM_PROMPT,
      messages,
    });

    const responseText = response.content[0].type === 'text'
      ? response.content[0].text
      : '';

    logger.debug('ü§ñ Claude raw response:', responseText);

    // Tentar extrair JSON da resposta (pode ter texto antes/depois)
    const jsonMatch = responseText.match(/\{[\s\S]*\}/);
    if (!jsonMatch) {
      throw new Error('No JSON found in Claude response');
    }

    // Parse e validar resposta da IA
    const parsedJson = JSON.parse(jsonMatch[0]);
    const validatedResponse = validateClaudeResponse(parsedJson);

    if (!validatedResponse) {
      logger.error('‚ùå Claude response validation failed, using parsed data with defaults');
      // Usar dados parseados com valores default se valida√ß√£o falhar
      const aiResponse: ClaudeAIResponse = {
        message: parsedJson.message || 'N√£o entendi. Pode repetir?',
        needsImage: parsedJson.needsImage || null,
        dataCollected: parsedJson.dataCollected || {},
        completed: parsedJson.completed || false,
        needsClarification: parsedJson.needsClarification || false,
        conversationPhase: parsedJson.conversationPhase || currentPhase,
      };
      return aiResponse;
    }

    // Validar dados coletados espec√≠ficos para o dia
    if (validatedResponse.dataCollected && Object.keys(validatedResponse.dataCollected).length > 0) {
      const dayValidation = validatePostOpDataByDay(validatedResponse.dataCollected, dayNumber);
      if (dayValidation.warnings.length > 0) {
        logger.warn('‚ö†Ô∏è PostOp data warnings:', dayValidation.warnings);
      }
      if (!dayValidation.valid) {
        logger.error('‚ùå PostOp data errors:', dayValidation.errors);
      }
    }

    logger.debug('‚úÖ Claude response validated:', {
      completed: validatedResponse.completed,
      needsImage: validatedResponse.needsImage,
      phase: validatedResponse.conversationPhase,
      dataCollected: validatedResponse.dataCollected,
    });

    return validatedResponse;
  } catch (error) {
    logger.error('‚ùå Erro ao chamar Claude API:', error);

    // Log detalhado do erro
    if (error instanceof Error) {
      logger.error('Error name:', error.name);
      logger.error('Error message:', error.message);
      logger.error('Error stack:', error.stack);
    }

    // FALLBACK INTELIGENTE: Tentar interpretar a resposta localmente
    const localInterpretation = interpretResponseLocally(userMessage, conversationHistory, dayNumber);

    if (localInterpretation) {
      logger.debug('üîÑ Usando interpreta√ß√£o local:', localInterpretation);
      return localInterpretation;
    }

    // √öltimo recurso: pedir para repetir (mas N√ÉO reiniciar question√°rio)
    return {
      message: `N√£o consegui entender. Pode responder apenas com o n√∫mero ou sim/n√£o?`,
      needsImage: null,
      dataCollected: {},
      completed: false,
      needsClarification: true,
      conversationPhase: determineCurrentPhase(conversationHistory)
    };
  }
}

/**
 * Determina a fase atual da conversa baseado no hist√≥rico
 * FASES ATUALIZADAS (sem alimenta√ß√£o):
 * 1. collecting_pain_at_rest - Dor em repouso
 * 2. collecting_fever - Febre
 * 3. collecting_fever_temp - Temperatura da febre
 * 4. collecting_urination - Urina
 * 5. collecting_urination_details - Detalhes da urina
 * 6. collecting_bowel - Evacua√ß√£o desde √∫ltima conversa
 * 7. collecting_bowel_time - Hora da evacua√ß√£o
 * 8. collecting_pain_during_bm - Dor DURANTE evacua√ß√£o
 * 9. collecting_bristol - Escala Bristol
 * 10. collecting_bleeding - Sangramento
 * 11. collecting_meds_prescribed - Medica√ß√µes prescritas (dipirona, anti-inflamat√≥rio)
 * 12. collecting_meds_extra - Medica√ß√µes extras al√©m das prescritas
 * 13. collecting_purulent_discharge - Secre√ß√£o purulenta (apenas D+3)
 * 14. collecting_concerns - Preocupa√ß√µes
 */
function determineCurrentPhase(conversationHistory: any[], dayNumber?: number): string {
  // Se n√£o h√° hist√≥rico ou s√≥ tem mensagem inicial, estamos coletando dor em repouso
  if (conversationHistory.length === 0) return 'collecting_pain_at_rest';
  if (conversationHistory.length <= 2) return 'collecting_pain_at_rest';

  // Analisar as √∫ltimas mensagens do assistente para determinar fase
  const assistantMessages = conversationHistory
    .filter(m => m.role === 'assistant')
    .map(m => m.content.toLowerCase());

  if (assistantMessages.length === 0) return 'collecting_pain_at_rest';

  const lastAssistantMsg = assistantMessages[assistantMessages.length - 1];

  // Verificar qual foi a √∫ltima pergunta feita pelo assistente
  // Ordem de verifica√ß√£o importa - mais espec√≠fico primeiro

  // Preocupa√ß√µes finais
  if (lastAssistantMsg.includes('preocupa√ß√£o') || lastAssistantMsg.includes('sintoma') || lastAssistantMsg.includes('√∫ltima pergunta')) {
    return 'collecting_concerns';
  }

  // Secre√ß√£o purulenta (D+3)
  if (lastAssistantMsg.includes('secre√ß√£o') || lastAssistantMsg.includes('pus') || lastAssistantMsg.includes('purulenta')) {
    return 'collecting_purulent_discharge';
  }

  // Medica√ß√µes extras (al√©m das prescritas)
  if (lastAssistantMsg.includes('al√©m') || lastAssistantMsg.includes('outra medica√ß√£o') || lastAssistantMsg.includes('qual medica√ß√£o tomou')) {
    return 'collecting_meds_extra';
  }

  // Medica√ß√µes prescritas
  if (lastAssistantMsg.includes('medica√ß√µes') && lastAssistantMsg.includes('receitadas') || lastAssistantMsg.includes('tomando') && lastAssistantMsg.includes('medica√ß√µes')) {
    return 'collecting_meds_prescribed';
  }

  // Sangramento
  if (lastAssistantMsg.includes('sangr')) {
    return 'collecting_bleeding';
  }

  // Bristol Scale (consist√™ncia das fezes)
  if (lastAssistantMsg.includes('bristol') || lastAssistantMsg.includes('1 a 7') || lastAssistantMsg.includes('consist√™ncia')) {
    return 'collecting_bristol';
  }

  // Dor DURANTE evacua√ß√£o (diferente de dor em repouso)
  if (lastAssistantMsg.includes('dor durante') || lastAssistantMsg.includes('durante a evacua√ß√£o')) {
    return 'collecting_pain_during_bm';
  }

  // Hora da evacua√ß√£o
  if (lastAssistantMsg.includes('que horas') || lastAssistantMsg.includes('horas foi')) {
    return 'collecting_bowel_time';
  }

  // Evacua√ß√£o (desde √∫ltima conversa)
  if (lastAssistantMsg.includes('evacuou') || lastAssistantMsg.includes('evacu') && lastAssistantMsg.includes('√∫ltima')) {
    return 'collecting_bowel';
  }

  // Detalhes da urina
  if (lastAssistantMsg.includes('dificuldade para urinar') || lastAssistantMsg.includes('o que est√° acontecendo')) {
    return 'collecting_urination_details';
  }

  // Urina
  if (lastAssistantMsg.includes('urin') || lastAssistantMsg.includes('xixi')) {
    return 'collecting_urination';
  }

  // Temperatura da febre
