  "PA", "PB", "PR", "PE", "PI", "RJ", "RN", "RS", "RO", "RR", "SC", "SP", "SE", "TO"
]

const registrationSchema = z.object({
  nomeCompleto: z.string().min(3, "Nome completo √© obrigat√≥rio"),
  email: z.string().email("Email inv√°lido"),
  whatsapp: z.string().min(14, "WhatsApp inv√°lido").regex(/^\+55 \(\d{2}\) \d{4,5}-\d{4}$/, "Formato: +55 (XX) XXXXX-XXXX"),
  crm: z.string().min(4, "CRM √© obrigat√≥rio"),
  estado: z.string().min(2, "Selecione o estado"),
  senha: z.string().min(8, "Senha deve ter no m√≠nimo 8 caracteres")
    .regex(/[A-Z]/, "Senha deve conter pelo menos uma letra mai√∫scula")
    .regex(/[a-z]/, "Senha deve conter pelo menos uma letra min√∫scula")
    .regex(/[0-9]/, "Senha deve conter pelo menos um n√∫mero"),
  confirmarSenha: z.string(),
  aceitoTermos: z.boolean().refine(val => val === true, "Voc√™ deve aceitar os termos"),
  acceptedTermsOfService: z.boolean().refine(val => val === true, "Voc√™ deve aceitar os Termos de Uso e Pol√≠tica de Privacidade"),
  aceitoNovidades: z.boolean().optional(),
}).refine((data) => data.senha === data.confirmarSenha, {
  message: "As senhas n√£o coincidem",
  path: ["confirmarSenha"],
})

type RegistrationFormData = z.infer<typeof registrationSchema>

function CadastroMedicoPage() {
  const router = useRouter()
