  const [autoPrint, setAutoPrint] = useState(false);

  useEffect(() => {
    // Auto-print se o par√¢metro print=true estiver na URL
    if (searchParams.get('print') === 'true') {
      setAutoPrint(true);
      setTimeout(() => {
        window.print();
      }, 500);
    }
