"use client"

import { useState, useEffect } from "react"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { useToast } from "@/hooks/use-toast"
import { Loader2, FileText, Star } from "lucide-react"
import { SURGERY_TYPE_LABELS, getTemplateSummary, TemplateData } from "@/lib/template-utils"
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group"
import { Label } from "@/components/ui/label"

interface Template {
  id: string
  name: string
  surgeryType: string
  templateData: string
  templateDataParsed?: TemplateData
  isDefault: boolean
}

interface ApplyTemplateDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  surgeryType: string
  patientId: string
  surgeryId: string
  onSuccess: () => void
}

export function ApplyTemplateDialog({
  open,
  onOpenChange,
  surgeryType,
  patientId,
  surgeryId,
  onSuccess
}: ApplyTemplateDialogProps) {
  const { toast } = useToast()
  const [templates, setTemplates] = useState<Template[]>([])
  const [selectedTemplate, setSelectedTemplate] = useState<string>("")
  const [loading, setLoading] = useState(true)
  const [applying, setApplying] = useState(false)

  useEffect(() => {
    if (open) {
      loadTemplates()
    }
  }, [open, surgeryType])

  async function loadTemplates() {
    try {
      setLoading(true)
      const response = await fetch(`/api/templates?surgeryType=${surgeryType}`)

      if (!response.ok) throw new Error("Erro ao carregar templates")

      const data = await response.json()
      setTemplates(data)

      // Auto-select default template if exists
      const defaultTemplate = data.find((t: Template) => t.isDefault)
      if (defaultTemplate) {
        setSelectedTemplate(defaultTemplate.id)
      }
    } catch (error) {
      toast({
        title: "Erro ao carregar templates",
        description: "N√£o foi poss√≠vel carregar os templates dispon√≠veis",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  async function handleApply() {
    if (!selectedTemplate) {
      toast({
        title: "Selecione um template",
        description: "Por favor, selecione um template para aplicar",
        variant: "destructive"
      })
      return
    }

    try {
      setApplying(true)
      const response = await fetch(`/api/templates/${selectedTemplate}/apply`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ patientId, surgeryId })
      })

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || "Erro ao aplicar template")
      }

      const result = await response.json()

      toast({
        title: "Template aplicado com sucesso",
        description: result.message || "Os dados do template foram aplicados ao paciente"
      })

      onSuccess()
      onOpenChange(false)
    } catch (error: any) {
      toast({
        title: "Erro ao aplicar template",
        description: error.message || "N√£o foi poss√≠vel aplicar o template",
        variant: "destructive"
      })
    } finally {
      setApplying(false)
    }
  }

  const selectedTemplateData = templates.find(t => t.id === selectedTemplate)

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Aplicar Template</DialogTitle>
          <DialogDescription>
            Selecione um template para aplicar ao paciente. Os dados ser√£o mesclados com as informa√ß√µes existentes.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
            </div>
          ) : templates.length === 0 ? (
            <div className="text-center py-8">
              <FileText className="mx-auto h-12 w-12 text-gray-400 mb-4" />
              <p className="text-gray-600">
                Nenhum template dispon√≠vel para {SURGERY_TYPE_LABELS[surgeryType]}
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Crie um template primeiro na p√°gina de Templates
              </p>
            </div>
          ) : (
            <>
              {/* Template Selection */}
              <RadioGroup value={selectedTemplate} onValueChange={setSelectedTemplate}>
                <div className="space-y-3">
                  {templates.map(template => {
                    const summary = template.templateDataParsed
                      ? getTemplateSummary(template.templateDataParsed)
                      : []

                    return (
                      <div
                        key={template.id}
                        className={`border rounded-lg p-4 cursor-pointer transition-colors ${
                          selectedTemplate === template.id
                            ? "border-blue-600 bg-blue-50"
                            : "border-gray-200 hover:border-gray-300"
                        }`}
                        onClick={() => setSelectedTemplate(template.id)}
                      >
                        <div className="flex items-start gap-3">
                          <RadioGroupItem value={template.id} id={template.id} className="mt-1" />
                          <div className="flex-1">
                            <Label
                              htmlFor={template.id}
                              className="text-base font-semibold cursor-pointer flex items-center gap-2"
                            >
                              {template.name}
                              {template.isDefault && (
                                <Star className="h-4 w-4 text-yellow-500 fill-yellow-500" />
                              )}
                            </Label>

                            {summary.length > 0 && (
                              <div className="mt-2 space-y-1">
                                {summary.slice(0, 4).map((item, index) => (
                                  <p key={index} className="text-sm text-gray-600">
                                    ‚Ä¢ {item}
                                  </p>
                                ))}
                                {summary.length > 4 && (
                                  <p className="text-sm text-gray-500 italic">
                                    +{summary.length - 4} mais configura√ß√µes...
                                  </p>
                                )}
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    )
                  })}
                </div>
              </RadioGroup>

              {/* Preview of selected template */}
              {selectedTemplateData && selectedTemplateData.templateDataParsed && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mt-4">
                  <h4 className="font-semibold text-blue-900 mb-2">
                    Preview do Template
                  </h4>
                  <div className="text-sm text-blue-800 space-y-1">
                    {getTemplateSummary(selectedTemplateData.templateDataParsed).map((item, index) => (
                      <p key={index}>‚Ä¢ {item}</p>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={applying}
          >
            Cancelar
          </Button>
          <Button
            onClick={handleApply}
            disabled={!selectedTemplate || applying || loading}
          >
            {applying ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Aplicando...
              </>
            ) : (
              "Aplicar Template"
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

"use client"

import { useEffect, useState } from "react"
import { CheckCircle2, Loader2 } from "lucide-react"

interface AutoSaveIndicatorProps {
  isSaving: boolean
  lastSaved: Date | null
  className?: string
}

/**
 * Visual indicator for auto-save status
 *
 * Shows:
 * - "Salvando..." with spinner when saving
 * - "Salvo h√° X segundos/minutos" when saved
 * - Nothing if never saved
 *
 * @example
 * ```tsx
 * <AutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 * ```
 */
export function AutoSaveIndicator({
  isSaving,
  lastSaved,
  className = "",
}: AutoSaveIndicatorProps) {
  const [timeAgo, setTimeAgo] = useState<string>("")

  // Update time ago every second
  useEffect(() => {
    if (!lastSaved) {
      setTimeAgo("")
      return
    }

    const updateTimeAgo = () => {
      const now = new Date()
      const secondsElapsed = Math.floor(
        (now.getTime() - lastSaved.getTime()) / 1000
      )

      if (secondsElapsed < 5) {
        setTimeAgo("agora mesmo")
      } else if (secondsElapsed < 60) {
        setTimeAgo(`h√° ${secondsElapsed} segundo${secondsElapsed === 1 ? "" : "s"}`)
      } else if (secondsElapsed < 3600) {
        const minutes = Math.floor(secondsElapsed / 60)
        setTimeAgo(`h√° ${minutes} minuto${minutes === 1 ? "" : "s"}`)
      } else {
        const hours = Math.floor(secondsElapsed / 3600)
        setTimeAgo(`h√° ${hours} hora${hours === 1 ? "" : "s"}`)
      }
    }

    // Update immediately
    updateTimeAgo()

    // Update every second
    const interval = setInterval(updateTimeAgo, 1000)

    return () => clearInterval(interval)
  }, [lastSaved])

  // Don't render if never saved and not saving
  if (!isSaving && !lastSaved) {
    return null
  }

  return (
    <div
      className={`flex items-center gap-2 text-sm transition-all duration-200 ${className}`}
      role="status"
      aria-live="polite"
    >
      {isSaving ? (
        <>
          <Loader2 className="h-4 w-4 animate-spin text-blue-600" />
          <span className="text-gray-600 font-medium">Salvando...</span>
        </>
      ) : lastSaved ? (
        <>
          <CheckCircle2 className="h-4 w-4 text-green-600" />
          <span className="text-gray-600">
            <span className="text-green-600 font-medium">Salvo</span> {timeAgo}
          </span>
        </>
      ) : null}
    </div>
  )
}

/**
 * Floating auto-save indicator (top-right position)
 *
 * Minimal, non-intrusive design that floats in the top-right corner
 *
 * @example
 * ```tsx
 * <FloatingAutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 * ```
 */
export function FloatingAutoSaveIndicator({
  isSaving,
  lastSaved,
}: AutoSaveIndicatorProps) {
  // Don't render if never saved and not saving
  if (!isSaving && !lastSaved) {
    return null
  }

  return (
    <div
      className="fixed top-4 right-4 z-50 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 px-4 py-2"
      role="status"
      aria-live="polite"
    >
      <AutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
    </div>
  )
}

/**
 * Inline auto-save indicator for forms
 *
 * Designed to be placed inside form headers or cards
 *
 * @example
 * ```tsx
 * <CardHeader>
 *   <div className="flex items-center justify-between">
 *     <CardTitle>Patient Registration</CardTitle>
 *     <InlineAutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 *   </div>
 * </CardHeader>
 * ```
 */
export function InlineAutoSaveIndicator({
  isSaving,
  lastSaved,
}: AutoSaveIndicatorProps) {
  return (
    <AutoSaveIndicator
      isSaving={isSaving}
      lastSaved={lastSaved}
      className="ml-auto"
    />
  )
}

