import { extractTemplateFromSurgery, SURGERY_TYPE_LABELS } from "@/lib/template-utils"

interface SaveAsTemplateDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  patientData: any
  onSuccess: () => void
}

export function SaveAsTemplateDialog({
  open,
  onOpenChange,
  patientData,
  onSuccess
}: SaveAsTemplateDialogProps) {
  const { toast } = useToast()
  const [templateName, setTemplateName] = useState("")
  const [isDefault, setIsDefault] = useState(false)
  const [saving, setSaving] = useState(false)

  async function handleSave() {
    if (!templateName.trim()) {
      toast({
        title: "Nome obrigat√≥rio",
        description: "Por favor, digite um nome para o template",
        variant: "destructive"
      })
      return
    }

    try {
      setSaving(true)

      // Extract template data from patient data
      const templateData = extractTemplateFromSurgery(patientData)

      // Create template
      const response = await fetch("/api/templates", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: templateName,
          surgeryType: patientData.surgery.type,
          templateData,
          isDefault
        })
      })

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || "Erro ao salvar template")
      }

      toast({
        title: "Template salvo com sucesso",
        description: `"${templateName}" foi salvo e pode ser aplicado a outros pacientes`
      })

      // Reset form
      setTemplateName("")
      setIsDefault(false)

      onSuccess()
      onOpenChange(false)
    } catch (error: any) {
      toast({
        title: "Erro ao salvar template",
        description: error.message || "N√£o foi poss√≠vel salvar o template",
        variant: "destructive"
      })
    } finally {
      setSaving(false)
    }
  }

  // Generate a suggested name
  const suggestedName = patientData
    ? `Minha ${SURGERY_TYPE_LABELS[patientData.surgery.type]?.toLowerCase()} padr√£o`
    : ""

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>Salvar como Template</DialogTitle>
          <DialogDescription>
            Salve as configura√ß√µes atuais como um template para reutilizar em outros pacientes
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {patientData && (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
              <p className="text-sm text-blue-900">
                <strong>Tipo de cirurgia:</strong> {SURGERY_TYPE_LABELS[patientData.surgery.type]}
              </p>
              <p className="text-sm text-blue-700 mt-1">
                Os dados espec√≠ficos do paciente (nome, datas, etc) n√£o ser√£o salvos, apenas as configura√ß√µes de procedimento, anestesia e prescri√ß√£o.
              </p>
            </div>
          )}

          <div className="space-y-2">
            <Label htmlFor="templateName">Nome do Template</Label>
            <Input
              id="templateName"
              placeholder={suggestedName}
              value={templateName}
              onChange={(e) => setTemplateName(e.target.value)}
              onFocus={(e) => {
                if (!templateName && suggestedName) {
                  setTemplateName(suggestedName)
                }
              }}
            />
            <p className="text-xs text-gray-500">
              Ex: "Minha hemorroidectomia padr√£o", "F√≠stula - protocolo Dr. Jo√£o"
            </p>
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox
              id="isDefault"
              checked={isDefault}
              onCheckedChange={(checked) => setIsDefault(checked as boolean)}
            />
            <Label
              htmlFor="isDefault"
              className="text-sm font-normal cursor-pointer"
            >
              Definir como template padr√£o para este tipo de cirurgia
            </Label>
          </div>

          {isDefault && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
              <p className="text-sm text-yellow-800">
                Este template ser√° automaticamente sugerido ao criar novos pacientes com {SURGERY_TYPE_LABELS[patientData?.surgery.type]?.toLowerCase()}.
              </p>
            </div>
          )}
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={saving}
          >
            Cancelar
          </Button>
          <Button onClick={handleSave} disabled={saving}>
            {saving ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Salvando...
              </>
            ) : (
              <>
                <Save className="mr-2 h-4 w-4" />
                Salvar Template
              </>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

"use client";

import { useState } from "react";
import { ArrowUpDown, ArrowUp, ArrowDown } from "lucide-react";
import { Button } from "@/components/ui/button";

export interface Column<T> {
  key: string;
  label: string;
  sortable?: boolean;
  render?: (value: any, row: T) => React.ReactNode;
  width?: string;
}

interface DataTableProps<T> {
  columns: Column<T>[];
  data: T[];
  keyField: keyof T;
  onRowClick?: (row: T) => void;
}

type SortDirection = "asc" | "desc" | null;

export function DataTable<T extends Record<string, any>>({
  columns,
  data,
  keyField,
  onRowClick,
}: DataTableProps<T>) {
  const [sortKey, setSortKey] = useState<string | null>(null);
  const [sortDirection, setSortDirection] = useState<SortDirection>(null);

  const handleSort = (key: string) => {
    if (sortKey === key) {
      // Cycling through: asc -> desc -> null
      if (sortDirection === "asc") {
        setSortDirection("desc");
      } else if (sortDirection === "desc") {
        setSortKey(null);
        setSortDirection(null);
      }
    } else {
      setSortKey(key);
      setSortDirection("asc");
    }
  };

  const sortedData = [...data].sort((a, b) => {
    if (!sortKey || !sortDirection) return 0;

    const aValue = a[sortKey];
    const bValue = b[sortKey];

    if (aValue === bValue) return 0;

    const comparison = aValue > bValue ? 1 : -1;
    return sortDirection === "asc" ? comparison : -comparison;
  });

  const getSortIcon = (columnKey: string) => {
    if (sortKey !== columnKey) {
      return <ArrowUpDown className="ml-2 h-4 w-4 text-gray-400" />;
    }
    return sortDirection === "asc" ? (
      <ArrowUp className="ml-2 h-4 w-4 text-blue-600" />
    ) : (
      <ArrowDown className="ml-2 h-4 w-4 text-blue-600" />
    );
  };

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-gray-50 border-b">
            <tr>
              {columns.map((column) => (
                <th
                  key={column.key}
                  className={`px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider ${
                    column.width || ""
                  }`}
                >
                  {column.sortable ? (
                    <Button
                      variant="ghost"
                      onClick={() => handleSort(column.key)}
                      className="h-auto p-0 hover:bg-transparent font-medium"
                    >
                      {column.label}
                      {getSortIcon(column.key)}
                    </Button>
                  ) : (
                    column.label
                  )}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {sortedData.length === 0 ? (
              <tr>
                <td
                  colSpan={columns.length}
                  className="px-6 py-8 text-center text-gray-500"
                >
                  Nenhum registro encontrado
                </td>
              </tr>
            ) : (
              sortedData.map((row) => (
                <tr
                  key={String(row[keyField])}
                  onClick={() => onRowClick?.(row)}
                  className={`${
                    onRowClick ? "cursor-pointer hover:bg-gray-50" : ""
                  } transition-colors`}
                >
                  {columns.map((column) => (
                    <td key={column.key} className="px-6 py-4 whitespace-nowrap">
                      {column.render
                        ? column.render(row[column.key], row)
                        : row[column.key]}
                    </td>
                  ))}
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

