              >
                <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
                <XAxis
                  dataKey="name"
                  stroke="#6b7280"
                  style={{ fontSize: '12px', fontWeight: 600 }}
                  angle={-15}
                  textAnchor="end"
                  height={80}
                />
                <YAxis
                  stroke="#6b7280"
                  style={{ fontSize: '14px', fontWeight: 600 }}
                  label={{ value: 'N√∫mero de Pacientes', angle: -90, position: 'insideLeft' }}
                />
                <Tooltip content={<CustomTooltip />} />
                <Legend />
                <Bar
                  dataKey="Sem Complica√ß√µes"
                  stackId="a"
                  fill={COLORS.withoutComplications}
                  radius={[0, 0, 4, 4]}
                />
                <Bar
                  dataKey="Com Complica√ß√µes"
                  stackId="a"
                  fill={COLORS.withComplications}
                  radius={[4, 4, 0, 0]}
                />
              </BarChart>
            </ResponsiveContainer>

            {/* Tabela de detalhes */}
            <div className="mt-4 border rounded-lg overflow-hidden">
              <table className="w-full">
