'use client';

/**
 * Enable Notifications Prompt Component
 * Modal bonito pedindo permiss√£o para ativar notifica√ß√µes push
 */

import { useState, useEffect } from 'react';
import { Bell, X, Check } from 'lucide-react';
import {
  isPushNotificationSupported,
  getNotificationPermission,
  subscribeToPush,
  isSubscribedToPush,
  sendTestNotification,
} from '@/lib/push-notifications';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { toast } from 'sonner';

const STORAGE_KEY = 'notifications-prompt-dismissed';
const STORAGE_ENABLED_KEY = 'notifications-enabled';

export function EnableNotificationsPrompt() {
  const [open, setOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [isSupported, setIsSupported] = useState(false);
  const [alreadySubscribed, setAlreadySubscribed] = useState(false);

  useEffect(() => {
    checkNotificationStatus();
  }, []);

  async function checkNotificationStatus() {
    // Verificar suporte
    const supported = isPushNotificationSupported();
    setIsSupported(supported);

    if (!supported) {
      return;
    }

    // Verificar se j√° est√° inscrito
    const subscribed = await isSubscribedToPush();
    setAlreadySubscribed(subscribed);

    // Verificar se j√° foi dispensado ou ativado
    const dismissed = localStorage.getItem(STORAGE_KEY);
    const enabled = localStorage.getItem(STORAGE_ENABLED_KEY);

    // Se j√° ativou ou j√° est√° inscrito, n√£o mostrar
    if (enabled === 'true' || subscribed) {
      return;
    }

    // Se foi dispensado h√° menos de 7 dias, n√£o mostrar
    if (dismissed) {
      const dismissedDate = new Date(dismissed);
      const daysSinceDismissed = (Date.now() - dismissedDate.getTime()) / (1000 * 60 * 60 * 24);
      if (daysSinceDismissed < 7) {
        return;
      }
    }

    // Verificar permiss√£o atual
    const permission = getNotificationPermission();

    // Se foi negada, n√£o mostrar (usu√°rio precisa habilitar manualmente)
    if (permission === 'denied') {
      return;
    }

    // Se j√° foi concedida mas n√£o est√° inscrito, inscrever automaticamente
    if (permission === 'granted' && !subscribed) {
      await handleEnableNotifications();
      return;
    }

    // Mostrar prompt ap√≥s 5 segundos (para n√£o ser invasivo)
    setTimeout(() => {
      setOpen(true);
    }, 5000);
  }

  async function handleEnableNotifications() {
    setLoading(true);
    try {
      await subscribeToPush();

      // Marcar como ativado
      localStorage.setItem(STORAGE_ENABLED_KEY, 'true');

      // Enviar notifica√ß√£o de teste
      try {
        await sendTestNotification();
        toast.success('Notifica√ß√µes ativadas com sucesso!', {
          description: 'Voc√™ receber√° alertas importantes sobre seus pacientes.',
        });
      } catch (error) {
        toast.success('Notifica√ß√µes ativadas!', {
          description: 'Voc√™ receber√° alertas importantes sobre seus pacientes.',
        });
      }

      setOpen(false);
      setAlreadySubscribed(true);
    } catch (error: any) {
      console.error('Error enabling notifications:', error);

      if (error.message.includes('negada')) {
        toast.error('Permiss√£o negada', {
          description: 'Por favor, habilite as notifica√ß√µes nas configura√ß√µes do navegador.',
        });
      } else {
        toast.error('Erro ao ativar notifica√ß√µes', {
          description: 'Tente novamente mais tarde.',
        });
      }
    } finally {
      setLoading(false);
    }
  }

  function handleDismiss() {
    // Salvar data de dispensa
    localStorage.setItem(STORAGE_KEY, new Date().toISOString());
    setOpen(false);

    toast.info('Voc√™ pode ativar as notifica√ß√µes depois nas configura√ß√µes', {
      duration: 5000,
    });
  }

  // N√£o renderizar se n√£o for suportado ou j√° estiver inscrito
  if (!isSupported || alreadySubscribed) {
    return null;
  }

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <div className="mx-auto mb-4 flex h-16 w-16 items-center justify-center rounded-full bg-blue-100 dark:bg-blue-900/20">
            <Bell className="h-8 w-8 text-blue-600 dark:text-blue-400" />
          </div>
          <DialogTitle className="text-center text-xl">
            Ativar Notifica√ß√µes Push
          </DialogTitle>
          <DialogDescription className="text-center text-base">
            Receba alertas importantes em tempo real sobre seus pacientes
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-3">
            <BenefitItem
              icon={<Bell className="h-5 w-5" />}
              title="Red Flags Detectados"
              description="Seja alertado imediatamente quando um paciente reportar sintomas preocupantes"
            />
            <BenefitItem
              icon={<Check className="h-5 w-5" />}
              title="Respostas de Pacientes"
              description="Receba notifica√ß√£o quando um paciente responder ao question√°rio"
            />
            <BenefitItem
              icon={<Bell className="h-5 w-5" />}
              title="Funciona Offline"
              description="Notifica√ß√µes chegam mesmo com o app fechado ou dispositivo em standby"
            />
          </div>

          <div className="rounded-lg bg-blue-50 p-4 dark:bg-blue-900/10">
            <p className="text-sm text-blue-900 dark:text-blue-100">
              <strong>Privacidade:</strong> Suas notifica√ß√µes s√£o criptografadas e apenas
              voc√™ as receber√°. Voc√™ pode desativar a qualquer momento.
            </p>
          </div>
        </div>

        <DialogFooter className="flex-col gap-2 sm:flex-row">
          <Button
            variant="outline"
            onClick={handleDismiss}
            className="w-full sm:w-auto"
          >
            Agora N√£o
          </Button>
          <Button
            onClick={handleEnableNotifications}
            disabled={loading}
            className="w-full sm:w-auto"
          >
            {loading ? 'Ativando...' : 'Ativar Notifica√ß√µes'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

function BenefitItem({
  icon,
  title,
  description,
}: {
  icon: React.ReactNode;
  title: string;
  description: string;
}) {
  return (
    <div className="flex gap-3">
      <div className="flex h-10 w-10 shrink-0 items-center justify-center rounded-lg bg-blue-100 text-blue-600 dark:bg-blue-900/20 dark:text-blue-400">
        {icon}
      </div>
      <div className="flex-1">
        <h4 className="font-medium text-sm">{title}</h4>
        <p className="text-sm text-muted-foreground">{description}</p>
      </div>
    </div>
  );
}

