      2: "Forma de salsicha com caro√ßos",
      3: "Salsicha com rachaduras",
      4: "Forma de cobra, lisa",
      5: "Peda√ßos macios",
      6: "Peda√ßos fofos, pastoso",
      7: "L√≠quido, sem peda√ßos",
    }
    return descriptions[scale] || ""
  }

  const getBleedingLabel = (bleeding?: string) => {
    if (!bleeding) return null
    const labels: Record<string, { text: string; color: string }> = {
      none: { text: "Sem sangramento", color: "text-green-600" },
      paper: { text: "S√≥ no papel", color: "text-yellow-600" },
      bowl: { text: "No vaso", color: "text-orange-600" },
      clots: { text: "Com co√°gulos", color: "text-red-600" },
    }
    return labels[bleeding]
  }

  const getRedFlagLabel = (flag: string): string => {
    const labels: Record<string, string> = {
      febre_alta: "Febre alta",
      dor_intensa: "Dor intensa",
      sangramento_ativo: "Sangramento ativo",
      retencao_urinaria: "Reten√ß√£o urin√°ria",
      sem_evacuacao: "Sem evacua√ß√£o",
      incontinencia_fecal: "Incontin√™ncia fecal",
      secrecao_purulenta: "Secre√ß√£o purulenta",
      edema_importante: "Edema importante",
      nausea_vomito: "N√°usea/v√¥mito",
      dispneia: "Dispneia",
      alteracao_consciencia: "Altera√ß√£o consci√™ncia",
    }
    return labels[flag] || flag
  }

  const responseDate = new Date(response.createdAt)

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <Card
        className={`
          border-l-4 transition-all duration-200 hover:shadow-md cursor-pointer
          ${response.riskLevel === 'critical' ? 'border-l-red-500 bg-red-50/50' : ''}
          ${response.riskLevel === 'high' ? 'border-l-orange-500 bg-orange-50/50' : ''}
          ${response.riskLevel === 'medium' ? 'border-l-yellow-500 bg-yellow-50/30' : ''}
          ${response.riskLevel === 'low' ? 'border-l-green-500' : ''}
          ${isLatest ? 'ring-2 ring-blue-200' : ''}
        `}
        onClick={() => setExpanded(!expanded)}
      >
        <CardContent className="p-4">
          {/* Header sempre vis√≠vel */}
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Badge
                variant="outline"
                className="text-sm font-bold px-3 py-1"
                style={{ backgroundColor: '#0A2647', color: 'white' }}
              >
                D+{response.dayNumber}
              </Badge>

              <Badge className={`${getRiskColor(response.riskLevel)} font-semibold`}>
                {getRiskLabel(response.riskLevel)}
              </Badge>

              {isLatest && (
                <Badge className="bg-blue-500 text-white">
                  Mais recente
                </Badge>
              )}
            </div>

            <div className="flex items-center gap-2 text-sm text-gray-500">
              <Clock className="h-4 w-4" />
              <span title={format(responseDate, "dd/MM/yyyy HH:mm", { locale: ptBR })}>
                {formatDistanceToNow(responseDate, { locale: ptBR, addSuffix: true })}
              </span>
              {expanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
            </div>
          </div>

          {/* Resumo r√°pido sempre vis√≠vel */}
          <div className="flex flex-wrap gap-4 mt-3">
            {data.painAtRest !== undefined && (
              <div className="flex items-center gap-1.5">
                <Activity className="h-4 w-4 text-blue-600" />
                <span className="text-sm">
                  Dor repouso: <strong className={data.painAtRest >= 7 ? 'text-red-600' : ''}>{data.painAtRest}/10</strong>
                </span>
              </div>
            )}

            {data.hasFever && data.temperature && (
              <div className="flex items-center gap-1.5">
                <Thermometer className="h-4 w-4 text-red-500" />
                <span className="text-sm text-red-600 font-semibold">
                  {data.temperature}¬∞C
                </span>
              </div>
            )}

            {response.redFlags.length > 0 && (
              <div className="flex items-center gap-1.5">
                <AlertTriangle className="h-4 w-4 text-red-500" />
                <span className="text-sm text-red-600 font-semibold">
                  {response.redFlags.length} alerta{response.redFlags.length > 1 ? 's' : ''}
                </span>
              </div>
            )}
          </div>

          {/* Conte√∫do expandido */}
          <AnimatePresence>
            {expanded && (
              <motion.div
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: "auto", opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
                transition={{ duration: 0.2 }}
                className="overflow-hidden"
              >
                <div className="mt-4 pt-4 border-t border-gray-200 space-y-4">
                  {/* Dados de Dor */}
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    {/* Dor em repouso */}
                    <div className="bg-white rounded-lg p-3 border shadow-sm">
                      <div className="flex items-center gap-2 text-gray-600 mb-1">
                        <Activity className="h-4 w-4" />
                        <span className="text-xs font-medium">Dor em repouso</span>
                      </div>
                      <div className={`text-2xl font-bold ${(data.painAtRest || 0) >= 7 ? 'text-red-600' : (data.painAtRest || 0) >= 4 ? 'text-yellow-600' : 'text-green-600'}`}>
                        {data.painAtRest ?? '-'}/10
                      </div>
                    </div>

                    {/* Dor durante evacua√ß√£o */}
                    {data.hadBowelMovement && (
                      <div className="bg-white rounded-lg p-3 border shadow-sm">
                        <div className="flex items-center gap-2 text-gray-600 mb-1">
                          <Activity className="h-4 w-4" />
                          <span className="text-xs font-medium">Dor evacua√ß√£o</span>
                        </div>
                        <div className={`text-2xl font-bold ${(data.painDuringEvacuation || 0) >= 7 ? 'text-red-600' : (data.painDuringEvacuation || 0) >= 4 ? 'text-yellow-600' : 'text-green-600'}`}>
                          {data.painDuringEvacuation ?? '-'}/10
                        </div>
                      </div>
                    )}

                    {/* Temperatura */}
                    <div className="bg-white rounded-lg p-3 border shadow-sm">
                      <div className="flex items-center gap-2 text-gray-600 mb-1">
                        <Thermometer className="h-4 w-4" />
                        <span className="text-xs font-medium">Temperatura</span>
                      </div>
                      {data.hasFever ? (
                        <div className="text-2xl font-bold text-red-600">
                          {data.temperature}¬∞C
                        </div>
                      ) : (
                        <div className="text-2xl font-bold text-green-600">
                          Normal
                        </div>
                      )}
                    </div>

                    {/* Bristol */}
                    {data.bristolScale && (
                      <div className="bg-white rounded-lg p-3 border shadow-sm">
                        <div className="flex items-center gap-2 text-gray-600 mb-1">
                          <span className="text-xs font-medium">Escala Bristol</span>
                        </div>
                        <div className="text-2xl font-bold text-gray-800">
                          {data.bristolScale}
                        </div>
                        <div className="text-xs text-gray-500">
                          {getBristolDescription(data.bristolScale)}
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Evacua√ß√£o */}
                  <div className="bg-white rounded-lg p-3 border shadow-sm">
                    <div className="flex items-center gap-2 mb-2">
                      {data.hadBowelMovement ? (
                        <CheckCircle2 className="h-5 w-5 text-green-500" />
                      ) : (
                        <AlertTriangle className="h-5 w-5 text-yellow-500" />
                      )}
                      <span className="font-medium">
                        {data.hadBowelMovement ? 'Evacuou' : 'N√£o evacuou'}
                      </span>
                      {data.bowelMovementTime && (
                        <span className="text-gray-500 text-sm">
                          √†s {data.bowelMovementTime}
                        </span>
                      )}
                    </div>

                    {data.bleeding && (
                      <div className="flex items-center gap-2 mt-2">
                        <Droplets className="h-4 w-4" />
                        <span className={`text-sm ${getBleedingLabel(data.bleeding)?.color}`}>
                          {getBleedingLabel(data.bleeding)?.text}
                        </span>
                      </div>
                    )}
                  </div>

                  {/* Medica√ß√µes */}
                  <div className="bg-white rounded-lg p-3 border shadow-sm">
                    <div className="flex items-center gap-2 mb-2">
                      <Pill className="h-5 w-5 text-purple-500" />
                      <span className="font-medium">Medica√ß√µes</span>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      {data.usedPrescribedMeds && (
                        <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
                          Usou prescritas
                        </Badge>
                      )}
                      {data.usedExtraMeds && (
                        <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">
                          Usou extras
                        </Badge>
                      )}
                      {!data.usedPrescribedMeds && !data.usedExtraMeds && (
                        <Badge variant="outline" className="bg-gray-50 text-gray-700 border-gray-200">
                          N√£o usou analg√©sicos
                        </Badge>
                      )}
                    </div>
                    {data.extraMedsDetails && (
                      <p className="text-sm text-gray-600 mt-2">
                        Detalhes: {data.extraMedsDetails}
                      </p>
                    )}
                  </div>

                  {/* Red Flags */}
                  {response.redFlags.length > 0 && (
                    <div className="bg-red-50 rounded-lg p-3 border border-red-200">
                      <div className="flex items-center gap-2 mb-2">
                        <AlertTriangle className="h-5 w-5 text-red-500" />
                        <span className="font-medium text-red-900">Alertas Detectados</span>
                      </div>
                      <div className="flex flex-wrap gap-2">
                        {response.redFlags.map((flag, idx) => (
                          <Badge key={idx} variant="destructive" className="bg-red-500">
                            {getRedFlagLabel(flag)}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* An√°lise da IA */}
                  {response.aiAnalysis && (
                    <div className="bg-blue-50 rounded-lg p-3 border border-blue-200">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="font-medium text-blue-900">An√°lise da IA</span>
                      </div>
                      <p className="text-sm text-blue-800 whitespace-pre-wrap">
                        {response.aiAnalysis}
                      </p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </CardContent>
      </Card>
    </motion.div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Skeleton } from "@/components/ui/skeleton"
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  ChartOptions,
  ChartData,
} from 'chart.js'
import { Line } from 'react-chartjs-2'
import {
  Activity,
  TrendingDown,
  TrendingUp,
  Minus,
  AlertTriangle,
  RefreshCw,
} from "lucide-react"

// Registrar componentes do Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
)

interface PainData {
  day: number
  date: string
  painAtRest: number | null
  painDuringEvacuation: number | null
  hasRedFlag: boolean
}

interface PainEvolutionChartProps {
  patientId: string
  baselinePain?: number | null
}

export function PainEvolutionChart({ patientId, baselinePain }: PainEvolutionChartProps) {
  const [data, setData] = useState<PainData[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [showEvacuation, setShowEvacuation] = useState(true)

  const fetchPainData = async () => {
    try {
      setError(null)
      const response = await fetch(`/api/paciente/${patientId}/timeline`)

      if (!response.ok) {
        throw new Error('Falha ao carregar dados de dor')
      }

      const result = await response.json()

      if (result.success) {
        // Extrair dados de dor do timeline
        const painDataMap = new Map<number, PainData>()

        // Dias padr√£o de follow-up
        const followUpDays = [1, 2, 3, 5, 7, 10, 14]
        followUpDays.forEach(day => {
          painDataMap.set(day, {
            day,
            date: '',
            painAtRest: null,
            painDuringEvacuation: null,
            hasRedFlag: false,
          })
        })

        // Preencher com dados reais
        result.data.timeline
          .filter((event: any) => event.type === 'followup' && event.metadata?.riskLevel)
          .forEach((event: any) => {
            const dayMatch = event.title.match(/D\+(\d+)/)
            if (dayMatch) {
              const day = parseInt(dayMatch[1])
              const questionnaireData = event.metadata?.questionnaireData
              const hasRedFlags = (event.metadata?.redFlags || []).length > 0

              if (painDataMap.has(day) || followUpDays.includes(day)) {
                painDataMap.set(day, {
                  day,
                  date: new Date(event.date).toLocaleDateString('pt-BR'),
                  painAtRest: questionnaireData?.painAtRest ?? null,
                  painDuringEvacuation: questionnaireData?.painDuringEvacuation ?? null,
                  hasRedFlag: hasRedFlags || event.metadata?.riskLevel === 'critical' || event.metadata?.riskLevel === 'high',
                })
              }
            }
          })

        const sortedData = Array.from(painDataMap.values()).sort((a, b) => a.day - b.day)
        setData(sortedData)
      } else {
        throw new Error(result.error?.message || 'Erro desconhecido')
      }
    } catch (err) {
      console.error('Erro ao carregar dados de dor:', err)
      setError(err instanceof Error ? err.message : 'Erro ao carregar dados')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPainData()
  }, [patientId])

  // Calcular tend√™ncia
  const calculateTrend = () => {
    const validData = data.filter(d => d.painAtRest !== null)
    if (validData.length < 2) return 'neutral'

    const firstHalf = validData.slice(0, Math.ceil(validData.length / 2))
    const secondHalf = validData.slice(Math.floor(validData.length / 2))

    const avgFirst = firstHalf.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / firstHalf.length
    const avgSecond = secondHalf.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / secondHalf.length

    if (avgSecond < avgFirst - 0.5) return 'improving'
    if (avgSecond > avgFirst + 0.5) return 'worsening'
    return 'stable'
  }

  const trend = calculateTrend()

  // Calcular m√©dia de dor
  const avgPain = () => {
    const validData = data.filter(d => d.painAtRest !== null)
    if (validData.length === 0) return null
    return (validData.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / validData.length).toFixed(1)
  }

  // Dados para o gr√°fico
  const chartData: ChartData<'line'> = {
    labels: data.map(d => `D+${d.day}`),
    datasets: [
      {
        label: 'Dor em repouso',
        data: data.map(d => d.painAtRest),
        borderColor: '#0A2647',
        backgroundColor: 'rgba(10, 38, 71, 0.1)',
        fill: true,
        tension: 0.3,
        pointRadius: data.map(d => d.hasRedFlag ? 8 : 5),
        pointBackgroundColor: data.map(d =>
          d.painAtRest === null ? 'transparent' :
          d.hasRedFlag ? '#DC2626' :
          (d.painAtRest || 0) >= 7 ? '#DC2626' :
          (d.painAtRest || 0) >= 4 ? '#F59E0B' :
          '#10B981'
        ),
        pointBorderColor: data.map(d =>
          d.painAtRest === null ? 'transparent' :
          d.hasRedFlag ? '#DC2626' :
          '#0A2647'
        ),
        pointBorderWidth: data.map(d => d.hasRedFlag ? 3 : 2),
        pointHoverRadius: 8,
        spanGaps: true,
      },
      ...(showEvacuation ? [{
        label: 'Dor durante evacua√ß√£o',
        data: data.map(d => d.painDuringEvacuation),
        borderColor: '#D4AF37',
        backgroundColor: 'rgba(212, 175, 55, 0.1)',
        fill: false,
        tension: 0.3,
        borderDash: [5, 5],
        pointRadius: 4,
        pointBackgroundColor: data.map(d =>
          d.painDuringEvacuation === null ? 'transparent' :
          (d.painDuringEvacuation || 0) >= 7 ? '#DC2626' :
          (d.painDuringEvacuation || 0) >= 4 ? '#F59E0B' :
          '#10B981'
        ),
        pointBorderColor: '#D4AF37',
        spanGaps: true,
      }] : []),
    ],
  }

  const chartOptions: ChartOptions<'line'> = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom' as const,
        labels: {
          usePointStyle: true,
          padding: 20,
          font: {
            family: 'Inter, sans-serif',
            size: 12,
          },
        },
      },
      tooltip: {
        backgroundColor: 'rgba(10, 38, 71, 0.95)',
        titleFont: {
          family: 'Inter, sans-serif',
          size: 14,
          weight: 'bold',
        },
        bodyFont: {
          family: 'Inter, sans-serif',
          size: 13,
        },
        padding: 12,
        cornerRadius: 8,
        callbacks: {
          label: function(context) {
            const dataIndex = context.dataIndex
            const painData = data[dataIndex]
            const value = context.parsed.y

            if (value === null) return ''

            let label = `${context.dataset.label}: ${value}/10`

            if (painData?.hasRedFlag) {
              label += ' ‚ö†Ô∏è ALERTA'
            }

            return label
          },
          afterBody: function(context) {
            const dataIndex = context[0].dataIndex
            const painData = data[dataIndex]

            if (painData?.date) {
              return [`Data: ${painData.date}`]
            }
            return []
          },
        },
      },
    },
    scales: {
      y: {
        min: 0,
        max: 10,
        ticks: {
          stepSize: 2,
          font: {
            family: 'Inter, sans-serif',
          },
          callback: function(value) {
            return `${value}`
          },
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
        },
        title: {
          display: true,
          text: 'N√≠vel de dor (EVA)',
          font: {
            family: 'Inter, sans-serif',
            size: 12,
          },
        },
      },
      x: {
        grid: {
          display: false,
        },
        ticks: {
          font: {
            family: 'Inter, sans-serif',
          },
        },
      },
    },
    interaction: {
      intersect: false,
      mode: 'index',
    },
    elements: {
      line: {
        borderWidth: 3,
      },
    },
  }

  // Nota: Baseline de dor √© exibido nos cards de resumo abaixo do gr√°fico

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <Skeleton className="h-6 w-48" />
            <Skeleton className="h-9 w-24" />
          </div>
        </CardHeader>
        <CardContent>
          <Skeleton className="h-64 w-full" />
        </CardContent>
      </Card>
    )
  }

  if (error) {
    return (
      <Card className="border-red-200 bg-red-50">
        <CardContent className="py-8 text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-red-700 font-medium">{error}</p>
          <Button onClick={fetchPainData} variant="outline" className="mt-4">
            <RefreshCw className="h-4 w-4 mr-2" />
            Tentar novamente
          </Button>
        </CardContent>
      </Card>
    )
  }

  const hasData = data.some(d => d.painAtRest !== null)

  return (
    <Card className="overflow-hidden">
      <CardHeader className="bg-gradient-to-r from-indigo-50 to-purple-50 border-b">
        <div className="flex items-center justify-between flex-wrap gap-4">
          <div className="flex items-center gap-3">
            <div
              className="p-2 rounded-lg"
              style={{ backgroundColor: '#0A2647' }}
            >
              <Activity className="h-5 w-5 text-white" />
            </div>
            <div>
              <CardTitle className="text-lg">Evolu√ß√£o da Dor</CardTitle>
              <p className="text-sm text-gray-500 mt-1">
                Escala Visual Anal√≥gica (0-10)
              </p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {/* Tend√™ncia */}
            {hasData && (
              <Badge
                variant="outline"
                className={`
                  ${trend === 'improving' ? 'bg-green-50 text-green-700 border-green-200' : ''}
                  ${trend === 'worsening' ? 'bg-red-50 text-red-700 border-red-200' : ''}
                  ${trend === 'stable' ? 'bg-gray-50 text-gray-700 border-gray-200' : ''}
                `}
              >
                {trend === 'improving' && <TrendingDown className="h-3 w-3 mr-1" />}
                {trend === 'worsening' && <TrendingUp className="h-3 w-3 mr-1" />}
                {trend === 'stable' && <Minus className="h-3 w-3 mr-1" />}
                {trend === 'improving' ? 'Melhorando' : trend === 'worsening' ? 'Piorando' : 'Est√°vel'}
              </Badge>
            )}

            {/* M√©dia */}
            {avgPain() && (
              <Badge
                variant="outline"
                className="bg-blue-50 text-blue-700 border-blue-200"
              >
                M√©dia: {avgPain()}/10
              </Badge>
            )}

            {/* Toggle evacua√ß√£o */}
            <Button
              variant={showEvacuation ? "default" : "outline"}
              size="sm"
              onClick={() => setShowEvacuation(!showEvacuation)}
              style={showEvacuation ? { backgroundColor: '#D4AF37' } : {}}
            >
              Evacua√ß√£o
            </Button>
          </div>
        </div>

        {/* Legenda de cores */}
        <div className="flex flex-wrap gap-3 mt-4">
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-green-500" />
            <span>Dor leve (0-3)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-yellow-500" />
            <span>Dor moderada (4-6)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-red-500" />
            <span>Dor intensa (7-10)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full border-2 border-red-500 bg-white" />
            <span>Com alerta</span>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-4">
        {!hasData ? (
          <div className="text-center py-12">
            <Activity className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-700 mb-2">
              Sem dados de dor ainda
            </h3>
            <p className="text-gray-500 text-sm">
