const headingVariants = cva("font-semibold text-telos-blue-500", {
  variants: {
    level: {
      1: "text-4xl lg:text-5xl leading-tight tracking-tight",
      2: "text-3xl lg:text-4xl leading-tight",
      3: "text-2xl lg:text-3xl leading-snug",
