        type: 'choice',
        options: ['Nenhuma', 'Clara', 'Serosa', 'Purulenta', 'Abundante'],
        required: true,
        redFlagTrigger: (answer) => answer === 'Purulenta' || answer === 'Abundante',
      });
    }
