  // Para cisto pilonidal, perguntar sobre a ferida
  if (surgeryType === 'pilonidal' && dayNumber >= 2) {
    questionnaire.questions.push({
      id: 'wound_status',
      question: 'Como est√° o local da cirurgia (regi√£o sacrococc√≠gea)?',
      type: 'choice',
      options: ['Cicatrizando bem', 'Vermelhid√£o leve', 'Incha√ßo', 'Vermelhid√£o intensa/calor'],
      required: true,
      redFlagTrigger: (answer) => answer === 'Vermelhid√£o intensa/calor',
    });
  }
}

