#!/usr/bin/env node

/**
 * API Verification Script
 * Verifies that all API endpoints and utilities are properly created
 */

const fs = require('fs');
const path = require('path');

const COLORS = {
  green: '\x1b[32m',
  red: '\x1b[31m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  reset: '\x1b[0m',
};

function checkFile(filePath) {
  const fullPath = path.join(process.cwd(), filePath);
  const exists = fs.existsSync(fullPath);

  if (exists) {
    const stats = fs.statSync(fullPath);
    const sizeKB = (stats.size / 1024).toFixed(2);
    console.log(`${COLORS.green}‚úì${COLORS.reset} ${filePath} (${sizeKB} KB)`);
    return true;
  } else {
    console.log(`${COLORS.red}‚úó${COLORS.reset} ${filePath} ${COLORS.red}(MISSING)${COLORS.reset}`);
    return false;
  }
}

function checkDirectory(dirPath) {
  const fullPath = path.join(process.cwd(), dirPath);
  const exists = fs.existsSync(fullPath);

  if (exists) {
    console.log(`${COLORS.green}‚úì${COLORS.reset} ${dirPath}/`);
    return true;
  } else {
    console.log(`${COLORS.red}‚úó${COLORS.reset} ${dirPath}/ ${COLORS.red}(MISSING)${COLORS.reset}`);
    return false;
  }
}

console.log(`\n${COLORS.blue}=== API Verification Script ===${COLORS.reset}\n`);

// Check API endpoints
console.log(`${COLORS.yellow}API Endpoints:${COLORS.reset}`);
const endpoints = [
  'app/api/paciente/[id]/route.ts',
  'app/api/paciente/[id]/completeness/route.ts',
  'app/api/paciente/[id]/timeline/route.ts',
  'app/api/pacientes/route.ts',
