  try {
    // 1. Buscar projeto
    console.log('üîç [1/5] Buscando projeto...');

    const projectRes = await makeRequest({
      hostname: 'api.vercel.com',
      path: `/v9/projects/${PROJECT_NAME}?teamId=${TEAM_SLUG}`,
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

