  RESEND_API_KEY: 're_placeholder_key'
};

// Fun√ß√£o para obter o token do Vercel
function getVercelToken() {
  try {
    const fs = require('fs');
    let configPath;

    // Tentar m√∫ltiplos caminhos poss√≠veis
    const possiblePaths = [
      `${process.env.APPDATA}\\com.vercel.cli\\Data\\auth.json`,
      `${process.env.USERPROFILE}\\.vercel\\auth.json`,
      `${process.env.HOME}/.vercel/auth.json`
    ];

    for (const path of possiblePaths) {
      if (fs.existsSync(path)) {
        configPath = path;
        break;
      }
    }

    if (!configPath) {
      throw new Error('Arquivo de autentica√ß√£o n√£o encontrado');
    }

    const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));
    return config.token;
  } catch (error) {
    console.error('‚ùå Erro ao obter token do Vercel:', error.message);
    console.log('üí° Execute: vercel login');
    process.exit(1);
  }
}

// Fun√ß√£o para fazer requisi√ß√£o HTTPS
function httpsRequest(options, data) {
  return new Promise((resolve, reject) => {
    const req = https.request(options, (res) => {
      let body = '';
      res.on('data', (chunk) => body += chunk);
      res.on('end', () => {
        try {
          resolve({
            statusCode: res.statusCode,
            body: body ? JSON.parse(body) : {}
          });
        } catch (e) {
          resolve({
            statusCode: res.statusCode,
            body: body
          });
        }
      });
    });

    req.on('error', reject);
    if (data) req.write(JSON.stringify(data));
    req.end();
  });
}

// Fun√ß√£o para obter informa√ß√µes do projeto
async function getProjectInfo(token) {
  const options = {
    hostname: 'api.vercel.com',
    path: '/v9/projects/sistema-pos-operatorio',
    method: 'GET',
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  };

  const response = await httpsRequest(options);

  if (response.statusCode !== 200) {
    throw new Error(`Erro ao obter projeto: ${JSON.stringify(response.body)}`);
  }

  return response.body;
}

// Fun√ß√£o para adicionar vari√°vel de ambiente
async function addEnvVar(token, projectId, key, value) {
  const options = {
    hostname: 'api.vercel.com',
    path: `/v10/projects/${projectId}/env`,
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  };

  const data = {
    key: key,
    value: value,
    type: 'encrypted',
    target: ['production']
  };

  const response = await httpsRequest(options, data);

  return response;
}

// Fun√ß√£o principal
async function main() {
  console.log('üöÄ Iniciando configura√ß√£o de vari√°veis de ambiente no Vercel...\n');

  // Obter token
  const token = getVercelToken();
  console.log('‚úÖ Token do Vercel obtido com sucesso\n');

  // Obter informa√ß√µes do projeto
  console.log('üì¶ Obtendo informa√ß√µes do projeto...');
  const project = await getProjectInfo(token);
  console.log(`‚úÖ Projeto encontrado: ${project.name} (ID: ${project.id})\n`);

  // Adicionar vari√°veis
  const total = Object.keys(envVars).length;
  let count = 0;
  let success = 0;
  let skipped = 0;
  let errors = 0;

  console.log(`üìù Adicionando ${total} vari√°veis de ambiente...\n`);

  for (const [key, value] of Object.entries(envVars)) {
    count++;
    process.stdout.write(`[${count}/${total}] ${key}... `);

    try {
      const response = await addEnvVar(token, project.id, key, value);

      if (response.statusCode === 200 || response.statusCode === 201) {
        console.log('‚úÖ');
        success++;
      } else if (response.statusCode === 409) {
        console.log('‚ö†Ô∏è  (j√° existe)');
        skipped++;
      } else {
        console.log(`‚ùå (${response.statusCode})`);
        if (response.body.error) {
          console.log(`   Erro: ${response.body.error.message}`);
        }
        errors++;
      }
    } catch (error) {
      console.log(`‚ùå (${error.message})`);
      errors++;
    }

    // Pequeno delay para evitar rate limiting
    await new Promise(resolve => setTimeout(resolve, 100));
  }

  // Resumo
  console.log('\n' + '='.repeat(60));
  console.log('üìä RESUMO:');
  console.log(`   ‚úÖ Adicionadas: ${success}`);
  console.log(`   ‚ö†Ô∏è  J√° existiam: ${skipped}`);
  console.log(`   ‚ùå Erros: ${errors}`);
  console.log(`   üìù Total: ${total}`);
  console.log('='.repeat(60) + '\n');

  if (success > 0 || skipped === total) {
    console.log('‚úÖ Configura√ß√£o conclu√≠da com sucesso!');
    console.log('\nüìã Pr√≥ximos passos:');
    console.log('   1. Verificar vari√°veis: vercel env ls production');
    console.log('   2. Fazer redeploy: vercel --prod\n');
  } else {
    console.log('‚ö†Ô∏è  Configura√ß√£o conclu√≠da com erros');
    console.log('   Verifique os erros acima e tente novamente\n');
  }
}

// Executar
main().catch(error => {
  console.error('\n‚ùå Erro fatal:', error.message);
  process.exit(1);
});

