// ============================================
// VALIDATORS
// ============================================

export function isNotificationType(value: string): value is NotificationType {
  return [
    'red_flag_critical',
    'red_flag_high',
    'red_flag_medium',
    'followup_completed',
    'followup_overdue',
    'patient_created',
    'surgery_created',
    'system_alert',
    'info',
  ].includes(value);
}

export function isNotificationPriority(value: string): value is NotificationPriority {
  return ['low', 'medium', 'high', 'critical'].includes(value);
}

// ============================================
// HELPERS
// ============================================

export function getPriorityFromRiskLevel(riskLevel: string): NotificationPriority {
  switch (riskLevel.toLowerCase()) {
    case 'critical':
      return 'critical';
    case 'high':
      return 'high';
    case 'medium':
      return 'medium';
    case 'low':
    default:
      return 'low';
  }
}

export function getNotificationTypeFromRiskLevel(riskLevel: string): NotificationType {
  switch (riskLevel.toLowerCase()) {
    case 'critical':
      return 'red_flag_critical';
    case 'high':
      return 'red_flag_high';
    case 'medium':
      return 'red_flag_medium';
    default:
      return 'info';
  }
}

export function shouldPlaySound(priority: NotificationPriority): boolean {
  return priority === 'critical' || priority === 'high';
}

export function formatNotificationTime(date: Date): string {
  const now = new Date();
  const diff = now.getTime() - new Date(date).getTime();
  const seconds = Math.floor(diff / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);

  if (days > 0) return `${days}d atr√°s`;
  if (hours > 0) return `${hours}h atr√°s`;
  if (minutes > 0) return `${minutes}min atr√°s`;
  return 'agora';
}

