"use client"

import { useState, useEffect } from "react"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { useToast } from "@/hooks/use-toast"
import { Loader2, FileText, Star } from "lucide-react"
import { SURGERY_TYPE_LABELS, getTemplateSummary, TemplateData } from "@/lib/template-utils"
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group"
import { Label } from "@/components/ui/label"

interface Template {
  id: string
  name: string
  surgeryType: string
  templateData: string
  templateDataParsed?: TemplateData
  isDefault: boolean
}

interface ApplyTemplateDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  surgeryType: string
  patientId: string
  surgeryId: string
  onSuccess: () => void
}

export function ApplyTemplateDialog({
  open,
  onOpenChange,
  surgeryType,
  patientId,
  surgeryId,
  onSuccess
}: ApplyTemplateDialogProps) {
  const { toast } = useToast()
  const [templates, setTemplates] = useState<Template[]>([])
  const [selectedTemplate, setSelectedTemplate] = useState<string>("")
  const [loading, setLoading] = useState(true)
  const [applying, setApplying] = useState(false)

  useEffect(() => {
    if (open) {
      loadTemplates()
    }
  }, [open, surgeryType])

  async function loadTemplates() {
    try {
      setLoading(true)
      const response = await fetch(`/api/templates?surgeryType=${surgeryType}`)

      if (!response.ok) throw new Error("Erro ao carregar templates")

      const data = await response.json()
      setTemplates(data)

      // Auto-select default template if exists
      const defaultTemplate = data.find((t: Template) => t.isDefault)
      if (defaultTemplate) {
        setSelectedTemplate(defaultTemplate.id)
      }
    } catch (error) {
      toast({
        title: "Erro ao carregar templates",
        description: "Não foi possível carregar os templates disponíveis",
        variant: "destructive"
      })
    } finally {
      setLoading(false)
    }
  }

  async function handleApply() {
    if (!selectedTemplate) {
      toast({
        title: "Selecione um template",
        description: "Por favor, selecione um template para aplicar",
        variant: "destructive"
      })
      return
    }

    try {
      setApplying(true)
      const response = await fetch(`/api/templates/${selectedTemplate}/apply`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ patientId, surgeryId })
      })

      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || "Erro ao aplicar template")
      }

      const result = await response.json()

      toast({
        title: "Template aplicado com sucesso",
        description: result.message || "Os dados do template foram aplicados ao paciente"
      })

      onSuccess()
      onOpenChange(false)
    } catch (error: any) {
      toast({
        title: "Erro ao aplicar template",
        description: error.message || "Não foi possível aplicar o template",
        variant: "destructive"
      })
    } finally {
      setApplying(false)
    }
  }

  const selectedTemplateData = templates.find(t => t.id === selectedTemplate)

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Aplicar Template</DialogTitle>
          <DialogDescription>
            Selecione um template para aplicar ao paciente. Os dados serão mesclados com as informações existentes.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
            </div>
          ) : templates.length === 0 ? (
            <div className="text-center py-8">
              <FileText className="mx-auto h-12 w-12 text-gray-400 mb-4" />
              <p className="text-gray-600">
                Nenhum template disponível para {SURGERY_TYPE_LABELS[surgeryType]}
              </p>
              <p className="text-sm text-gray-500 mt-2">
                Crie um template primeiro na página de Templates
              </p>
            </div>
          ) : (
            <>
              {/* Template Selection */}
              <RadioGroup value={selectedTemplate} onValueChange={setSelectedTemplate}>
                <div className="space-y-3">
                  {templates.map(template => {
                    const summary = template.templateDataParsed
                      ? getTemplateSummary(template.templateDataParsed)
                      : []

                    return (
                      <div
                        key={template.id}
                        className={`border rounded-lg p-4 cursor-pointer transition-colors ${
                          selectedTemplate === template.id
                            ? "border-blue-600 bg-blue-50"
                            : "border-gray-200 hover:border-gray-300"
                        }`}
                        onClick={() => setSelectedTemplate(template.id)}
                      >
                        <div className="flex items-start gap-3">
                          <RadioGroupItem value={template.id} id={template.id} className="mt-1" />
                          <div className="flex-1">
                            <Label
                              htmlFor={template.id}
                              className="text-base font-semibold cursor-pointer flex items-center gap-2"
                            >
                              {template.name}
                              {template.isDefault && (
                                <Star className="h-4 w-4 text-yellow-500 fill-yellow-500" />
                              )}
                            </Label>

                            {summary.length > 0 && (
                              <div className="mt-2 space-y-1">
                                {summary.slice(0, 4).map((item, index) => (
                                  <p key={index} className="text-sm text-gray-600">
                                    • {item}
                                  </p>
                                ))}
                                {summary.length > 4 && (
                                  <p className="text-sm text-gray-500 italic">
                                    +{summary.length - 4} mais configurações...
                                  </p>
                                )}
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    )
                  })}
                </div>
              </RadioGroup>

              {/* Preview of selected template */}
              {selectedTemplateData && selectedTemplateData.templateDataParsed && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mt-4">
                  <h4 className="font-semibold text-blue-900 mb-2">
                    Preview do Template
                  </h4>
                  <div className="text-sm text-blue-800 space-y-1">
                    {getTemplateSummary(selectedTemplateData.templateDataParsed).map((item, index) => (
                      <p key={index}>• {item}</p>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={applying}
          >
            Cancelar
          </Button>
          <Button
            onClick={handleApply}
            disabled={!selectedTemplate || applying || loading}
          >
            {applying ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Aplicando...
              </>
            ) : (
              "Aplicar Template"
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  )
}

"use client"

import { useEffect, useState } from "react"
import { CheckCircle2, Loader2 } from "lucide-react"

interface AutoSaveIndicatorProps {
  isSaving: boolean
  lastSaved: Date | null
  className?: string
}

/**
 * Visual indicator for auto-save status
 *
 * Shows:
 * - "Salvando..." with spinner when saving
 * - "Salvo há X segundos/minutos" when saved
 * - Nothing if never saved
 *
 * @example
 * ```tsx
 * <AutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 * ```
 */
export function AutoSaveIndicator({
  isSaving,
  lastSaved,
  className = "",
}: AutoSaveIndicatorProps) {
  const [timeAgo, setTimeAgo] = useState<string>("")

  // Update time ago every second
  useEffect(() => {
    if (!lastSaved) {
      setTimeAgo("")
      return
    }

    const updateTimeAgo = () => {
      const now = new Date()
      const secondsElapsed = Math.floor(
        (now.getTime() - lastSaved.getTime()) / 1000
      )

      if (secondsElapsed < 5) {
        setTimeAgo("agora mesmo")
      } else if (secondsElapsed < 60) {
        setTimeAgo(`há ${secondsElapsed} segundo${secondsElapsed === 1 ? "" : "s"}`)
      } else if (secondsElapsed < 3600) {
        const minutes = Math.floor(secondsElapsed / 60)
        setTimeAgo(`há ${minutes} minuto${minutes === 1 ? "" : "s"}`)
      } else {
        const hours = Math.floor(secondsElapsed / 3600)
        setTimeAgo(`há ${hours} hora${hours === 1 ? "" : "s"}`)
      }
    }

    // Update immediately
    updateTimeAgo()

    // Update every second
    const interval = setInterval(updateTimeAgo, 1000)

    return () => clearInterval(interval)
  }, [lastSaved])

  // Don't render if never saved and not saving
  if (!isSaving && !lastSaved) {
    return null
  }

  return (
    <div
      className={`flex items-center gap-2 text-sm transition-all duration-200 ${className}`}
      role="status"
      aria-live="polite"
    >
      {isSaving ? (
        <>
          <Loader2 className="h-4 w-4 animate-spin text-blue-600" />
          <span className="text-gray-600 font-medium">Salvando...</span>
        </>
      ) : lastSaved ? (
        <>
          <CheckCircle2 className="h-4 w-4 text-green-600" />
          <span className="text-gray-600">
            <span className="text-green-600 font-medium">Salvo</span> {timeAgo}
          </span>
        </>
      ) : null}
    </div>
  )
}

/**
 * Floating auto-save indicator (top-right position)
 *
 * Minimal, non-intrusive design that floats in the top-right corner
 *
 * @example
 * ```tsx
 * <FloatingAutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 * ```
 */
export function FloatingAutoSaveIndicator({
  isSaving,
  lastSaved,
}: AutoSaveIndicatorProps) {
  // Don't render if never saved and not saving
  if (!isSaving && !lastSaved) {
    return null
  }

  return (
    <div
      className="fixed top-4 right-4 z-50 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 px-4 py-2"
      role="status"
      aria-live="polite"
    >
      <AutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
    </div>
  )
}

/**
 * Inline auto-save indicator for forms
 *
 * Designed to be placed inside form headers or cards
 *
 * @example
 * ```tsx
 * <CardHeader>
 *   <div className="flex items-center justify-between">
 *     <CardTitle>Patient Registration</CardTitle>
 *     <InlineAutoSaveIndicator isSaving={isSaving} lastSaved={lastSaved} />
 *   </div>
 * </CardHeader>
 * ```
 */
export function InlineAutoSaveIndicator({
  isSaving,
  lastSaved,
}: AutoSaveIndicatorProps) {
  return (
    <AutoSaveIndicator
      isSaving={isSaving}
      lastSaved={lastSaved}
      className="ml-auto"
    />
  )
}

"use client"

import React, { useEffect, useState } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { CheckCircle2, Lock, Sparkles, TrendingUp, Award, Star } from 'lucide-react'
import confetti from 'canvas-confetti'

interface CompletenessIncentiveProps {
  currentCompletion: number // 0-100
  onContinue?: () => void
  showConfetti?: boolean
}

const benefits = {
  20: ["Follow-ups básicos habilitados"],
  40: ["Relatórios simples disponíveis"],
  60: ["Análises preditivas ativadas", "Alertas personalizados"],
  80: ["Exportação completa de dados", "Gráficos avançados"],
  100: ["Todos os recursos desbloqueados", "Pontuação máxima de qualidade", "Elegível para pesquisas"]
}

const levels = [
  { threshold: 0, label: "Dados básicos", color: "#EF4444", bgColor: "#FEE2E2", emoji: "🔴" },
  { threshold: 21, label: "Informações essenciais", color: "#F97316", bgColor: "#FFEDD5", emoji: "🟠" },
  { threshold: 41, label: "Bom progresso", color: "#EAB308", bgColor: "#FEF3C7", emoji: "🟡" },
  { threshold: 61, label: "Quase completo", color: "#84CC16", bgColor: "#ECFCCB", emoji: "🟢" },
  { threshold: 81, label: "Excelente!", color: "#22C55E", bgColor: "#DCFCE7", emoji: "✨" },
  { threshold: 100, label: "Cadastro perfeito!", color: "#FFD700", bgColor: "#FEF9C3", emoji: "🏆" }
]

const motivationalMessages = {
  0: "Vamos começar! Cada informação conta.",
  21: "Ótimo começo! Continue assim.",
  41: "Você está indo muito bem!",
  61: "Quase lá! Não desista agora.",
  81: "Incrível! Falta muito pouco.",
  100: "Perfeito! Cadastro 100% completo!"
}

export function CompletenessIncentive({
  currentCompletion,
  onContinue,
  showConfetti = true
}: CompletenessIncentiveProps) {
  const [previousCompletion, setPreviousCompletion] = useState(currentCompletion)
  const [showMilestone, setShowMilestone] = useState(false)

  // Determine current level
  const currentLevel = [...levels].reverse().find(level => currentCompletion >= level.threshold) || levels[0]

  // Get motivational message
  const message = Object.entries(motivationalMessages)
    .reverse()
    .find(([threshold]) => currentCompletion >= parseInt(threshold))?.[1] || motivationalMessages[0]

  // Check for milestone reached
  useEffect(() => {
    const milestones = [40, 60, 80, 100]
    const reachedMilestone = milestones.find(
      m => currentCompletion >= m && previousCompletion < m
    )

    if (reachedMilestone) {
      setShowMilestone(true)

      // Trigger confetti for 100%
      if (reachedMilestone === 100 && showConfetti) {
        triggerConfetti()
      }

      setTimeout(() => setShowMilestone(false), 3000)
    }

    setPreviousCompletion(currentCompletion)
  }, [currentCompletion, previousCompletion, showConfetti])

  const triggerConfetti = () => {
    const duration = 3000
    const end = Date.now() + duration

    const frame = () => {
      confetti({
        particleCount: 3,
        angle: 60,
        spread: 55,
        origin: { x: 0 },
        colors: ['#0066CC', '#00A3E0', '#FFD700', '#FFA500']
      })
      confetti({
        particleCount: 3,
        angle: 120,
        spread: 55,
        origin: { x: 1 },
        colors: ['#0066CC', '#00A3E0', '#FFD700', '#FFA500']
      })

      if (Date.now() < end) {
        requestAnimationFrame(frame)
      }
    }
    frame()
  }

  // Get unlocked and locked benefits
  const allBenefitLevels = [20, 40, 60, 80, 100]
  const unlockedBenefits: { level: number; items: string[] }[] = []
  const nextBenefit = allBenefitLevels.find(level => currentCompletion < level)

  allBenefitLevels.forEach(level => {
    if (currentCompletion >= level) {
      unlockedBenefits.push({ level, items: benefits[level as keyof typeof benefits] })
    }
  })

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="relative"
    >
      {/* Milestone Celebration Overlay */}
      <AnimatePresence>
        {showMilestone && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="absolute inset-0 z-50 flex items-center justify-center bg-black/20 backdrop-blur-sm rounded-2xl"
          >
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: [0, 1.2, 1] }}
              transition={{ duration: 0.5 }}
              className="bg-white rounded-2xl p-8 shadow-2xl text-center"
            >
              <motion.div
                animate={{ rotate: [0, 360] }}
                transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              >
                <Award className="w-20 h-20 mx-auto mb-4" style={{ color: currentLevel.color }} />
              </motion.div>
              <h3 className="text-2xl font-bold mb-2">Parabéns!</h3>
              <p className="text-gray-600">Você alcançou {currentCompletion}% de completude!</p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main Card */}
      <div className="bg-gradient-to-br from-white to-gray-50 rounded-2xl shadow-lg p-6 border border-gray-200">
        <div className="flex items-start gap-6">
          {/* Progress Ring */}
          <div className="flex-shrink-0">
            <div className="relative w-32 h-32">
              {/* Background circle */}
              <svg className="w-32 h-32 transform -rotate-90">
                <circle
                  cx="64"
                  cy="64"
                  r="56"
                  stroke="#E5E7EB"
                  strokeWidth="8"
                  fill="none"
                />
                {/* Progress circle */}
                <motion.circle
                  cx="64"
                  cy="64"
                  r="56"
                  stroke={currentLevel.color}
                  strokeWidth="8"
                  fill="none"
                  strokeLinecap="round"
                  initial={{ strokeDasharray: "0 352" }}
                  animate={{
                    strokeDasharray: `${(currentCompletion / 100) * 352} 352`,
                  }}
                  transition={{ duration: 1, ease: "easeOut" }}
                  style={{
                    filter: currentCompletion === 100 ? 'drop-shadow(0 0 8px rgba(255, 215, 0, 0.8))' : 'none'
                  }}
                />
              </svg>

              {/* Center content */}
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                <motion.div
                  key={currentCompletion}
                  initial={{ scale: 0.5, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  className="text-3xl font-bold"
                  style={{ color: currentLevel.color }}
                >
                  {currentCompletion}%
                </motion.div>
                <motion.div
                  animate={{
                    scale: currentCompletion === 100 ? [1, 1.2, 1] : 1,
                  }}
                  transition={{
                    duration: 0.5,
                    repeat: currentCompletion === 100 ? Infinity : 0,
                    repeatDelay: 1
                  }}
                  className="text-2xl"
                >
                  {currentLevel.emoji}
                </motion.div>
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 min-w-0">
            {/* Level Badge */}
            <motion.div
              key={currentLevel.label}
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              className="inline-flex items-center gap-2 px-3 py-1 rounded-full text-sm font-semibold mb-3"
              style={{
                backgroundColor: currentLevel.bgColor,
                color: currentLevel.color
              }}
            >
              <Sparkles className="w-4 h-4" />
              {currentLevel.label}
            </motion.div>

            {/* Motivational Message */}
            <h3 className="text-xl font-bold text-gray-900 mb-2">
              {message}
            </h3>

            {/* Progress to next milestone */}
            {nextBenefit && currentCompletion < 100 && (
              <div className="flex items-center gap-2 text-sm text-gray-600 mb-4">
                <TrendingUp className="w-4 h-4" />
                <span>
                  Faltam {nextBenefit - currentCompletion}% para desbloquear novos recursos
                </span>
              </div>
            )}

            {/* Unlocked Benefits */}
            {unlockedBenefits.length > 0 && (
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center gap-2">
                  <CheckCircle2 className="w-4 h-4 text-green-500" />
                  Recursos Desbloqueados
                </h4>
                <div className="space-y-1">
                  {unlockedBenefits.flatMap(b => b.items).map((benefit, idx) => (
                    <motion.div
                      key={idx}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: idx * 0.1 }}
                      className="flex items-center gap-2 text-sm text-gray-600"
                    >
                      <div className="w-1.5 h-1.5 rounded-full bg-green-500" />
                      {benefit}
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            {/* Next Benefits Preview */}
            {nextBenefit && currentCompletion < 100 && (
              <div className="mb-4">
                <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center gap-2">
                  <Lock className="w-4 h-4 text-gray-400" />
                  Próximos Desbloqueios ({nextBenefit}%)
                </h4>
                <div className="space-y-1">
                  {benefits[nextBenefit as keyof typeof benefits].map((benefit, idx) => (
                    <motion.div
                      key={idx}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: idx * 0.1 }}
                      className="flex items-center gap-2 text-sm text-gray-400"
                    >
                      <div className="w-1.5 h-1.5 rounded-full bg-gray-300" />
                      {benefit}
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            {/* CTA Button */}
            {currentCompletion < 100 && onContinue && (
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={onContinue}
                className="w-full bg-gradient-to-r from-[#0066CC] to-[#00A3E0] text-white font-semibold py-3 px-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow flex items-center justify-center gap-2"
              >
                <Sparkles className="w-5 h-5" />
                Continue preenchendo
                <motion.span
                  animate={{ x: [0, 4, 0] }}
                  transition={{ duration: 1, repeat: Infinity }}
                >
                  →
                </motion.span>
              </motion.button>
            )}

            {/* Perfect completion message */}
            {currentCompletion === 100 && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-gradient-to-r from-yellow-50 to-orange-50 border-2 border-yellow-300 rounded-xl p-4 text-center"
              >
                <div className="flex items-center justify-center gap-2 text-lg font-bold text-yellow-700 mb-1">
                  <Star className="w-6 h-6 fill-yellow-500 text-yellow-500" />
                  Cadastro Perfeito!
                  <Star className="w-6 h-6 fill-yellow-500 text-yellow-500" />
                </div>
                <p className="text-sm text-yellow-600">
                  Você desbloqueou todos os recursos da plataforma!
                </p>
              </motion.div>
            )}
          </div>
        </div>

        {/* Progress Bar (mobile fallback) */}
        <div className="mt-6 md:hidden">
          <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
            <motion.div
              initial={{ width: 0 }}
              animate={{ width: `${currentCompletion}%` }}
              transition={{ duration: 1, ease: "easeOut" }}
              className="h-full rounded-full"
              style={{ backgroundColor: currentLevel.color }}
            />
          </div>
        </div>
      </div>
    </motion.div>
  )
}

"use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { FileText, Download, Loader2 } from "lucide-react"
import { toast } from "sonner"

interface ExportPatientPDFButtonProps {
  patientId: string
  patientName: string
  variant?: "default" | "outline" | "ghost"
  size?: "default" | "sm" | "lg" | "icon"
  showIcon?: boolean
  showText?: boolean
}

export function ExportPatientPDFButton({
  patientId,
  patientName,
  variant = "outline",
  size = "sm",
  showIcon = true,
  showText = true,
}: ExportPatientPDFButtonProps) {
  const [isExporting, setIsExporting] = useState(false)

  async function handleExport() {
    setIsExporting(true)

    try {
      const response = await fetch("/api/export/patient-pdf", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ patientId }),
      })

      if (!response.ok) {
        const data = await response.json()
        throw new Error(data.error || "Erro ao exportar PDF")
      }

      // Baixa o PDF
      const blob = await response.blob()
      const url = window.URL.createObjectURL(blob)
      const a = document.createElement("a")
      a.href = url
      a.download = `paciente-${patientName.replace(/\s/g, "_")}-${new Date().toISOString().split("T")[0]}.pdf`
      document.body.appendChild(a)
      a.click()
      window.URL.revokeObjectURL(url)
      document.body.removeChild(a)

      toast.success("PDF exportado com sucesso!")
    } catch (error: any) {
      console.error("Erro ao exportar PDF:", error)
      toast.error(error.message || "Erro ao exportar PDF")
    } finally {
      setIsExporting(false)
    }
  }

  return (
    <Button
      variant={variant}
      size={size}
      onClick={handleExport}
      disabled={isExporting}
      title="Exportar relatório em PDF"
    >
      {isExporting ? (
        <>
          {showIcon && <Loader2 className="h-4 w-4 animate-spin" />}
          {showText && size !== "icon" && <span className="ml-2">Exportando...</span>}
        </>
      ) : (
        <>
          {showIcon && <FileText className="h-4 w-4" />}
          {showText && size !== "icon" && <span className="ml-2">Exportar PDF</span>}
        </>
      )}
    </Button>
  )
}

'use client';

/**
 * FollowUpStatus Component
 * Displays follow-up timeline, status, and actions
 */

import { useState } from 'react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  CheckCircle2,
  Clock,
  Send,
  XCircle,
  AlertTriangle,
  Eye,
  RefreshCw,
  Calendar,
} from 'lucide-react';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';

interface FollowUp {
  id: string;
  dayNumber: number;
  scheduledDate: Date | string;
  status: 'pending' | 'sent' | 'responded' | 'overdue' | 'skipped';
  sentAt?: Date | string | null;
  respondedAt?: Date | string | null;
  responses?: FollowUpResponse[];
}

interface FollowUpResponse {
  id: string;
  createdAt: Date | string;
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  redFlags?: string | null;
  aiResponse?: string | null;
}

interface FollowUpStatusProps {
  followUps: FollowUp[];
  onResend?: (followUpId: string) => Promise<void>;
  onViewResponses?: (followUpId: string) => void;
  isLoading?: boolean;
}

export function FollowUpStatus({
  followUps,
  onResend,
  onViewResponses,
  isLoading = false,
}: FollowUpStatusProps) {
  const [resendingId, setResendingId] = useState<string | null>(null);

  const handleResend = async (followUpId: string) => {
    if (!onResend) return;

    setResendingId(followUpId);
    try {
      await onResend(followUpId);
    } finally {
      setResendingId(null);
    }
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">Acompanhamento Pós-Operatório</h3>
        <Badge variant="outline">
          {followUps.length} {followUps.length === 1 ? 'Follow-up' : 'Follow-ups'}
        </Badge>
      </div>

      {/* Timeline */}
      <div className="relative space-y-4">
        {followUps.map((followUp, index) => {
          const isLast = index === followUps.length - 1;
          const latestResponse = followUp.responses?.[0];

          return (
            <div key={followUp.id} className="relative">
              {/* Timeline line */}
              {!isLast && (
                <div className="absolute left-4 top-10 h-full w-0.5 bg-border" />
              )}

              <Card className="p-4">
                <div className="flex items-start gap-4">
                  {/* Status Icon */}
                  <div className="relative z-10">
                    <StatusIcon status={followUp.status} riskLevel={latestResponse?.riskLevel} />
                  </div>

                  {/* Content */}
                  <div className="flex-1 space-y-2">
                    <div className="flex items-start justify-between gap-2">
                      <div>
                        <div className="flex items-center gap-2">
                          <h4 className="font-semibold">Dia {followUp.dayNumber}</h4>
                          <StatusBadge status={followUp.status} />
                          {latestResponse && (
                            <RiskBadge riskLevel={latestResponse.riskLevel} />
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground">
                          <Calendar className="inline h-3 w-3 mr-1" />
                          Agendado para{' '}
                          {format(new Date(followUp.scheduledDate), 'dd/MM/yyyy', {
                            locale: ptBR,
                          })}
                        </p>
                      </div>

                      {/* Actions */}
                      <div className="flex gap-2">
                        {followUp.status === 'responded' && onViewResponses && (
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => onViewResponses(followUp.id)}
                          >
                            <Eye className="h-4 w-4" />
                            Ver respostas
                          </Button>
                        )}

                        {(followUp.status === 'pending' ||
                          followUp.status === 'sent' ||
                          followUp.status === 'overdue') &&
                          onResend && (
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => handleResend(followUp.id)}
                              disabled={resendingId === followUp.id || isLoading}
                            >
                              <RefreshCw
                                className={`h-4 w-4 ${
                                  resendingId === followUp.id ? 'animate-spin' : ''
                                }`}
                              />
                              {followUp.status === 'pending' ? 'Enviar' : 'Reenviar'}
                            </Button>
                          )}
                      </div>
                    </div>

                    {/* Timestamps */}
                    <div className="text-xs text-muted-foreground space-y-1">
                      {followUp.sentAt && (
                        <div>
                          Enviado em{' '}
                          {format(new Date(followUp.sentAt), "dd/MM/yyyy 'às' HH:mm", {
                            locale: ptBR,
                          })}
                        </div>
                      )}
                      {followUp.respondedAt && (
                        <div>
                          Respondido em{' '}
                          {format(new Date(followUp.respondedAt), "dd/MM/yyyy 'às' HH:mm", {
                            locale: ptBR,
                          })}
                        </div>
                      )}
                    </div>

                    {/* Red Flags */}
                    {latestResponse && latestResponse.redFlags && (
                      <div className="mt-2 p-2 rounded-md bg-destructive/10 border border-destructive/20">
                        <div className="flex items-start gap-2">
                          <AlertTriangle className="h-4 w-4 text-destructive mt-0.5" />
                          <div className="flex-1">
                            <p className="text-sm font-medium text-destructive">
                              Sinais de alerta detectados:
                            </p>
                            <ul className="text-xs mt-1 space-y-0.5">
                              {JSON.parse(latestResponse.redFlags).map(
                                (flag: string, i: number) => (
                                  <li key={i}>• {flag}</li>
                                )
                              )}
                            </ul>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </Card>
            </div>
          );
        })}
      </div>

      {followUps.length === 0 && (
        <Card className="p-8 text-center">
          <Clock className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
          <p className="text-muted-foreground">
            Nenhum follow-up agendado ainda.
          </p>
        </Card>
      )}
    </div>
  );
}

/**
 * Status Icon Component
 */
function StatusIcon({
  status,
  riskLevel,
}: {
  status: string;
  riskLevel?: string;
}) {
  const iconClassName = 'h-8 w-8';

  switch (status) {
    case 'responded':
      if (riskLevel === 'critical' || riskLevel === 'high') {
        return <AlertTriangle className={`${iconClassName} text-destructive`} />;
      }
      return <CheckCircle2 className={`${iconClassName} text-green-500`} />;

    case 'sent':
      return <Send className={`${iconClassName} text-blue-500`} />;

    case 'overdue':
      return <XCircle className={`${iconClassName} text-orange-500`} />;

    case 'skipped':
      return <XCircle className={`${iconClassName} text-gray-400`} />;

    case 'pending':
    default:
      return <Clock className={`${iconClassName} text-gray-400`} />;
  }
}

/**
 * Status Badge Component
 */
function StatusBadge({ status }: { status: string }) {
  const variants: Record<string, { label: string; variant: any }> = {
    pending: { label: 'Pendente', variant: 'secondary' },
    sent: { label: 'Enviado', variant: 'default' },
    responded: { label: 'Respondido', variant: 'default' },
    overdue: { label: 'Atrasado', variant: 'destructive' },
    skipped: { label: 'Pulado', variant: 'outline' },
  };

  const config = variants[status] || { label: status, variant: 'outline' };

  return <Badge variant={config.variant}>{config.label}</Badge>;
}

/**
 * Risk Badge Component
 */
function RiskBadge({ riskLevel }: { riskLevel: string }) {
  const variants: Record<
    string,
    { label: string; className: string }
  > = {
    low: {
      label: 'Baixo risco',
      className: 'bg-green-100 text-green-800 border-green-200',
    },
    medium: {
      label: 'Risco moderado',
      className: 'bg-yellow-100 text-yellow-800 border-yellow-200',
    },
    high: {
      label: 'Risco alto',
      className: 'bg-orange-100 text-orange-800 border-orange-200',
    },
    critical: {
      label: 'Risco crítico',
      className: 'bg-red-100 text-red-800 border-red-200',
    },
  };

  const config = variants[riskLevel] || {
    label: riskLevel,
    className: 'bg-gray-100 text-gray-800 border-gray-200',
  };

  return (
    <Badge variant="outline" className={config.className}>
      {config.label}
    </Badge>
  );
}

