"use client";

import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell } from 'recharts';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Clock, CheckCircle, Send, AlertTriangle, XCircle } from 'lucide-react';

interface FollowUpsStatusData {
  status: string;
  count: number;
}

interface FollowUpsStatusChartProps {
  data: FollowUpsStatusData[];
  onStatusClick?: (status: string) => void;
}

const STATUS_CONFIG: Record<string, { label: string; color: string; icon: any; bgColor: string }> = {
  pending: {
    label: 'Pendente',
    color: '#F59E0B',
    icon: Clock,
    bgColor: '#FEF3C7',
  },
  sent: {
    label: 'Enviado',
    color: '#3B82F6',
    icon: Send,
    bgColor: '#DBEAFE',
  },
  responded: {
    label: 'Respondido',
    color: '#16A34A',
    icon: CheckCircle,
    bgColor: '#D1FAE5',
  },
  overdue: {
    label: 'Atrasado',
    color: '#DC2626',
    icon: AlertTriangle,
    bgColor: '#FEE2E2',
  },
  skipped: {
    label: 'Ignorado',
    color: '#6B7280',
    icon: XCircle,
    bgColor: '#F3F4F6',
  },
};

export function FollowUpsStatusChart({ data, onStatusClick }: FollowUpsStatusChartProps) {
  // Preparar dados para o gráfico
  const chartData = data.map(item => ({
    status: STATUS_CONFIG[item.status]?.label || item.status,
    count: item.count,
    color: STATUS_CONFIG[item.status]?.color || '#6B7280',
    originalStatus: item.status,
  }));

  // Calcular total
  const total = data.reduce((sum, item) => sum + item.count, 0);

  const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      const percentage = total > 0 ? ((data.count / total) * 100).toFixed(1) : '0.0';

      return (
        <div className="bg-white dark:bg-gray-800 p-3 border-2 border-gray-200 dark:border-gray-700 rounded-lg shadow-lg">
          <p className="font-semibold mb-2">{data.status}</p>
          <p className="text-sm">
            <span className="font-semibold">{data.count}</span> follow-ups
          </p>
          <p className="text-sm text-muted-foreground">
            {percentage}% do total
          </p>
        </div>
      );
    }
    return null;
  };

  const handleBarClick = (data: any) => {
    if (onStatusClick) {
      onStatusClick(data.originalStatus);
    }
  };

  return (
    <Card className="border-2 hover:shadow-lg transition-shadow">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <span className="text-2xl">📨</span>
          Follow-ups por Status
        </CardTitle>
        <CardDescription>
          Distribuição dos follow-ups de acordo com seu status atual
        </CardDescription>
      </CardHeader>
      <CardContent>
        {/* Estatísticas em Cards */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-3 mb-6">
          {data.map((item) => {
            const config = STATUS_CONFIG[item.status];
            if (!config) return null;

            const Icon = config.icon;
            const percentage = total > 0 ? ((item.count / total) * 100).toFixed(0) : '0';

            return (
              <div
                key={item.status}
                className="p-4 rounded-lg border-2 hover:shadow-md transition-all cursor-pointer"
                style={{ backgroundColor: config.bgColor, borderColor: config.color }}
                onClick={() => onStatusClick && onStatusClick(item.status)}
              >
                <div className="flex items-center gap-2 mb-2">
                  <Icon className="h-4 w-4" style={{ color: config.color }} />
                  <span className="text-xs font-semibold" style={{ color: config.color }}>
                    {config.label}
                  </span>
                </div>
                <div className="text-2xl font-bold" style={{ color: config.color }}>
                  {item.count}
                </div>
                <div className="text-xs text-muted-foreground mt-1">
                  {percentage}% do total
                </div>
              </div>
            );
          })}
        </div>

        {/* Gráfico de Barras Horizontal */}
        <ResponsiveContainer width="100%" height={300}>
          <BarChart
            data={chartData}
            layout="vertical"
            margin={{ top: 5, right: 30, left: 100, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" horizontal={false} />
            <XAxis
              type="number"
              stroke="#6b7280"
              style={{ fontSize: '14px', fontWeight: 600 }}
            />
            <YAxis
              dataKey="status"
              type="category"
              stroke="#6b7280"
              style={{ fontSize: '14px', fontWeight: 600 }}
              width={100}
            />
            <Tooltip content={<CustomTooltip />} />
            <Bar
              dataKey="count"
              radius={[0, 8, 8, 0]}
              onClick={handleBarClick}
              cursor="pointer"
            >
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>

        {/* Resumo */}
        <div className="mt-6 pt-4 border-t">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm font-semibold text-gray-700 dark:text-gray-300">
              Total de Follow-ups
            </span>
            <Badge variant="secondary" className="text-lg px-3 py-1">
              {total}
            </Badge>
          </div>

          {/* Alertas */}
          {data.find(d => d.status === 'overdue') && data.find(d => d.status === 'overdue')!.count > 0 && (
            <div className="bg-red-50 dark:bg-red-950/20 border border-red-300 rounded-lg p-3 mt-3">
              <div className="flex items-start gap-2">
                <AlertTriangle className="h-5 w-5 text-red-600 flex-shrink-0 mt-0.5" />
                <div>
                  <p className="font-semibold text-red-900 dark:text-red-100 text-sm">
                    Atenção: Follow-ups Atrasados
                  </p>
                  <p className="text-xs text-red-800 dark:text-red-200 mt-1">
                    Existem {data.find(d => d.status === 'overdue')!.count} follow-ups que passaram
                    da data programada e ainda não foram enviados.
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="mt-4 pt-4 border-t">
          <p className="text-sm text-muted-foreground">
            <strong>Dica:</strong> Clique em qualquer barra ou card para filtrar o dashboard
            por esse status.
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

interface PainEvolutionChartProps {
  data: Array<{
    day: string;
    [key: string]: number | string | null;
  }>;
}

const SURGERY_COLORS: Record<string, string> = {
  hemorroidectomia: '#DC2626', // Vermelho
  fistula: '#2563EB', // Azul
  fissura: '#16A34A', // Verde
  pilonidal: '#D97706', // Laranja
};

const SURGERY_LABELS: Record<string, string> = {
  hemorroidectomia: 'Hemorroidectomia',
  fistula: 'Fístula Anal',
  fissura: 'Fissura Anal',
  pilonidal: 'Doença Pilonidal',
};

export function PainEvolutionChart({ data }: PainEvolutionChartProps) {
  // Extrair tipos de cirurgia dos dados
  const surgeryTypes = data.length > 0
    ? Object.keys(data[0]).filter(key => key !== 'day')
    : [];

  // Custom tooltip
  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white dark:bg-gray-800 p-3 border-2 border-gray-200 dark:border-gray-700 rounded-lg shadow-lg">
          <p className="font-semibold mb-2">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} style={{ color: entry.color }} className="text-sm">
              {SURGERY_LABELS[entry.dataKey] || entry.dataKey}: {entry.value?.toFixed(1)} / 10
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <Card className="border-2 hover:shadow-lg transition-shadow">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <span className="text-2xl">📊</span>
          Evolução da Dor Média
        </CardTitle>
        <CardDescription>
          Nível médio de dor reportado pelos pacientes ao longo dos dias pós-operatórios (escala 0-10)
        </CardDescription>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart
            data={data}
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
            <XAxis
              dataKey="day"
              stroke="#6b7280"
              style={{ fontSize: '14px', fontWeight: 600 }}
            />
            <YAxis
              domain={[0, 10]}
              ticks={[0, 2, 4, 6, 8, 10]}
              stroke="#6b7280"
              style={{ fontSize: '14px', fontWeight: 600 }}
              label={{ value: 'Dor (0-10)', angle: -90, position: 'insideLeft' }}
            />
            <Tooltip content={<CustomTooltip />} />
            <Legend
              wrapperStyle={{ paddingTop: '20px' }}
              formatter={(value) => SURGERY_LABELS[value] || value}
            />
            {surgeryTypes.map((type) => (
              <Line
                key={type}
                type="monotone"
                dataKey={type}
                stroke={SURGERY_COLORS[type] || '#6b7280'}
                strokeWidth={3}
                dot={{ fill: SURGERY_COLORS[type] || '#6b7280', r: 5 }}
                activeDot={{ r: 8 }}
                connectNulls
              />
            ))}
          </LineChart>
        </ResponsiveContainer>

        {/* Legenda adicional */}
        <div className="mt-4 pt-4 border-t">
          <p className="text-sm text-muted-foreground">
            <strong>Interpretação:</strong> Valores mais altos indicam maior intensidade de dor.
            Uma diminuição gradual é esperada ao longo dos dias.
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from 'recharts';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Thermometer, Droplet, Zap, AlertTriangle, AlertCircle } from 'lucide-react';

interface RedFlagsData {
  category: string;
  count: number;
}

interface RedFlagsChartProps {
  data: RedFlagsData[];
}

const RED_FLAG_CONFIG: Record<string, { label: string; color: string; icon: any; severity: 'critical' | 'high' | 'medium' }> = {
  febre: {
    label: 'Febre',
    color: '#DC2626',
    icon: Thermometer,
    severity: 'critical',
  },
  sangramento: {
    label: 'Sangramento',
    color: '#DC2626',
    icon: Droplet,
    severity: 'critical',
  },
  dor_intensa: {
    label: 'Dor Intensa',
    color: '#F59E0B',
    icon: Zap,
    severity: 'high',
  },
  retencao_urinaria: {
    label: 'Retenção Urinária',
    color: '#F59E0B',
    icon: AlertTriangle,
    severity: 'high',
  },
  outros: {
    label: 'Outros',
    color: '#6B7280',
    icon: AlertCircle,
    severity: 'medium',
  },
};

export function RedFlagsChart({ data }: RedFlagsChartProps) {
  // Preparar dados para o gráfico
  const chartData = data
    .map(item => ({
      category: RED_FLAG_CONFIG[item.category]?.label || item.category,
      count: item.count,
      color: RED_FLAG_CONFIG[item.category]?.color || '#6B7280',
      severity: RED_FLAG_CONFIG[item.category]?.severity || 'medium',
      originalCategory: item.category,
    }))
    .filter(item => item.count > 0) // Filtrar categorias com 0 ocorrências
    .sort((a, b) => b.count - a.count); // Ordenar por frequência

  // Calcular total
  const total = data.reduce((sum, item) => sum + item.count, 0);

  // Identificar os mais comuns
  const mostCommon = chartData.slice(0, 3);

  const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      const percentage = total > 0 ? ((data.count / total) * 100).toFixed(1) : '0.0';

      return (
        <div className="bg-white dark:bg-gray-800 p-3 border-2 border-gray-200 dark:border-gray-700 rounded-lg shadow-lg">
          <p className="font-semibold mb-2">{data.category}</p>
          <p className="text-sm">
            <span className="font-semibold">{data.count}</span> ocorrências
          </p>
          <p className="text-sm text-muted-foreground">
            {percentage}% do total
          </p>
          <Badge
            variant={data.severity === 'critical' ? 'destructive' : data.severity === 'high' ? 'default' : 'secondary'}
            className="mt-2 text-xs"
          >
            Severidade: {data.severity === 'critical' ? 'Crítica' : data.severity === 'high' ? 'Alta' : 'Média'}
          </Badge>
        </div>
      );
    }
    return null;
  };

  return (
    <Card className="border-2 hover:shadow-lg transition-shadow">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <span className="text-2xl">🚨</span>
          Red Flags Detectados
        </CardTitle>
        <CardDescription>
          Frequência de alertas críticos identificados pela IA nos follow-ups
        </CardDescription>
      </CardHeader>
      <CardContent>
        {/* Alertas principais em destaque */}
        {total > 0 ? (
          <>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              {mostCommon.map((item) => {
                const config = RED_FLAG_CONFIG[item.originalCategory];
                if (!config) return null;

                const Icon = config.icon;
                const percentage = total > 0 ? ((item.count / total) * 100).toFixed(0) : '0';

                return (
                  <div
                    key={item.originalCategory}
                    className={`p-4 rounded-lg border-2 ${
                      item.severity === 'critical'
                        ? 'bg-red-50 border-red-300'
                        : item.severity === 'high'
                        ? 'bg-orange-50 border-orange-300'
                        : 'bg-gray-50 border-gray-300'
                    }`}
                  >
                    <div className="flex items-center gap-2 mb-2">
                      <Icon className="h-5 w-5" style={{ color: item.color }} />
                      <span className="text-sm font-semibold" style={{ color: item.color }}>
                        {item.category}
                      </span>
                    </div>
                    <div className="text-3xl font-bold" style={{ color: item.color }}>
                      {item.count}
                    </div>
                    <div className="text-xs text-muted-foreground mt-1">
                      {percentage}% dos alertas
                    </div>
                    <Badge
                      variant={item.severity === 'critical' ? 'destructive' : 'secondary'}
                      className="mt-2 text-xs"
                    >
                      {item.severity === 'critical' ? 'Crítico' : item.severity === 'high' ? 'Alta' : 'Média'}
                    </Badge>
                  </div>
                );
              })}
            </div>

            {/* Gráfico de Barras */}
            <ResponsiveContainer width="100%" height={350}>
              <BarChart
                data={chartData}
                margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
                <XAxis
                  dataKey="category"
                  stroke="#6b7280"
                  style={{ fontSize: '14px', fontWeight: 600 }}
                  angle={-15}
                  textAnchor="end"
                  height={80}
                />
                <YAxis
                  stroke="#6b7280"
                  style={{ fontSize: '14px', fontWeight: 600 }}
                  label={{ value: 'Número de Ocorrências', angle: -90, position: 'insideLeft' }}
                />
                <Tooltip content={<CustomTooltip />} />
                <Bar dataKey="count" radius={[8, 8, 0, 0]}>
                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>

            {/* Resumo e Insights */}
            <div className="mt-6 pt-4 border-t space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm font-semibold text-gray-700 dark:text-gray-300">
                  Total de Red Flags
                </span>
                <Badge variant="destructive" className="text-lg px-3 py-1">
                  {total}
                </Badge>
              </div>

              {/* Alerta se houver muitos red flags críticos */}
              {chartData.filter(d => d.severity === 'critical').reduce((sum, d) => sum + d.count, 0) > 0 && (
                <div className="bg-red-50 dark:bg-red-950/20 border border-red-300 rounded-lg p-3">
                  <div className="flex items-start gap-2">
                    <AlertCircle className="h-5 w-5 text-red-600 flex-shrink-0 mt-0.5" />
                    <div>
                      <p className="font-semibold text-red-900 dark:text-red-100 text-sm">
                        Atenção: Alertas Críticos Detectados
                      </p>
                      <p className="text-xs text-red-800 dark:text-red-200 mt-1">
                        {chartData.filter(d => d.severity === 'critical').reduce((sum, d) => sum + d.count, 0)} pacientes
                        reportaram sintomas críticos (febre ou sangramento). Verifique imediatamente.
                      </p>
                    </div>
                  </div>
                </div>
              )}

              {/* Insights */}
              {mostCommon.length > 0 && (
                <div className="bg-blue-50 dark:bg-blue-950/20 border border-blue-300 rounded-lg p-3">
                  <p className="text-sm text-blue-900 dark:text-blue-100">
                    <strong>Insight:</strong> O red flag mais comum é <strong>{mostCommon[0].category}</strong> ({mostCommon[0].count} ocorrências).
                    {mostCommon[0].severity === 'critical' && ' Este é um alerta crítico que requer ação imediata.'}
                  </p>
                </div>
              )}
            </div>
          </>
        ) : (
          <div className="py-12 text-center">
            <div className="text-6xl mb-4">🎉</div>
            <h3 className="text-xl font-semibold text-gray-900 dark:text-gray-100 mb-2">
              Nenhum Red Flag Detectado
            </h3>
            <p className="text-muted-foreground">
              Parabéns! Não há alertas críticos no período selecionado.
              Seus pacientes estão se recuperando bem.
            </p>
          </div>
        )}

        <div className="mt-4 pt-4 border-t">
          <p className="text-sm text-muted-foreground">
            <strong>Nota:</strong> Red flags são identificados automaticamente pela IA ao analisar
            as respostas dos pacientes nos follow-ups. Alertas críticos requerem atenção imediata.
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

