"use client";

import React, { useState, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Button } from '@/components/ui/button';
import { ResearchRequiredIndicator } from '@/components/ResearchRequiredBadge';

interface DadosBasicosSectionProps {
  patient: any;
  onUpdate: (data: any) => void;
  onComplete: (isComplete: boolean) => void;
  isResearchParticipant?: boolean;
}

export function DadosBasicosSection({ patient, onUpdate, onComplete, isResearchParticipant = false }: DadosBasicosSectionProps) {
  const [formData, setFormData] = useState({
    name: patient?.name || '',
    cpf: patient?.cpf || '',
    dateOfBirth: patient?.dateOfBirth || '',
    sex: patient?.sex || '',
    phone: patient?.phone || '',
    email: patient?.email || '',
    hospital: patient?.hospital || '',
    surgeryDuration: patient?.surgeryDuration || ''
  });

  const [age, setAge] = useState<number | null>(null);

  // Calculate age from date of birth
  useEffect(() => {
    if (formData.dateOfBirth) {
      const birthDate = new Date(formData.dateOfBirth);
      const today = new Date();
      let calculatedAge = today.getFullYear() - birthDate.getFullYear();
      const monthDiff = today.getMonth() - birthDate.getMonth();

      if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
        calculatedAge--;
      }

      setAge(calculatedAge);
    } else {
      setAge(null);
    }
  }, [formData.dateOfBirth]);

  // Check if form is complete
  useEffect(() => {
    const isComplete = !!(
      formData.name &&
      formData.cpf &&
      formData.dateOfBirth &&
      formData.sex &&
      formData.phone &&
      formData.hospital
    );
    onComplete(isComplete);
  }, [formData, onComplete]);

  // Update parent on any change
  useEffect(() => {
    onUpdate(formData);
  }, [formData, onUpdate]);

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  // CPF mask
  const formatCPF = (value: string) => {
    const cleaned = value.replace(/\D/g, '');
    const match = cleaned.match(/^(\d{0,3})(\d{0,3})(\d{0,3})(\d{0,2})$/);
    if (match) {
      return [match[1], match[2], match[3], match[4]]
        .filter(Boolean)
        .join('.')
        .replace(/\.(\d{2})$/, '-$1');
    }
    return value;
  };

  const handleCPFChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const formatted = formatCPF(e.target.value);
    handleChange('cpf', formatted);
  };

  // Phone mask (WhatsApp)
  const formatPhone = (value: string) => {
    const cleaned = value.replace(/\D/g, '');
    const match = cleaned.match(/^(\d{0,2})(\d{0,5})(\d{0,4})$/);
    if (match) {
      const formatted = [match[1], match[2], match[3]]
        .filter(Boolean)
        .join(' ')
        .replace(/^(\d{2}) /, '($1) ')
        .replace(/ (\d{4})$/, '-$1');
      return formatted;
    }
    return value;
  };

  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const formatted = formatPhone(e.target.value);
    handleChange('phone', formatted);
  };

  return (
    <Card className="p-6">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Dados Básicos do Paciente</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Nome Completo */}
        <div className="md:col-span-2">
          <Label htmlFor="name" className="text-sm font-medium text-gray-700">
            Nome Completo <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Input
            id="name"
            type="text"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            placeholder="Nome completo do paciente"
            className="mt-1"
            required
          />
        </div>

        {/* CPF */}
        <div>
          <Label htmlFor="cpf" className="text-sm font-medium text-gray-700">
            CPF <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Input
            id="cpf"
            type="text"
            value={formData.cpf}
            onChange={handleCPFChange}
            placeholder="000.000.000-00"
            maxLength={14}
            className="mt-1"
            required
          />
        </div>

        {/* Data de Nascimento */}
        <div>
          <Label htmlFor="dateOfBirth" className="text-sm font-medium text-gray-700">
            Data de Nascimento <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <div className="flex gap-2 items-center">
            <Input
              id="dateOfBirth"
              type="date"
              value={formData.dateOfBirth}
              onChange={(e) => handleChange('dateOfBirth', e.target.value)}
              className="mt-1 flex-1"
              required
            />
            {age !== null && (
              <span className="text-sm font-medium text-gray-600 mt-1">
                {age} anos
              </span>
            )}
          </div>
        </div>

        {/* Sexo */}
        <div>
          <Label htmlFor="sex" className="text-sm font-medium text-gray-700">
            Sexo <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Select value={formData.sex} onValueChange={(value) => handleChange('sex', value)}>
            <SelectTrigger className="mt-1">
              <SelectValue placeholder="Selecione o sexo" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="masculino">Masculino</SelectItem>
              <SelectItem value="feminino">Feminino</SelectItem>
              <SelectItem value="outro">Outro</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Telefone (WhatsApp) */}
        <div>
          <Label htmlFor="phone" className="text-sm font-medium text-gray-700">
            Telefone/WhatsApp <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Input
            id="phone"
            type="tel"
            value={formData.phone}
            onChange={handlePhoneChange}
            placeholder="(00) 00000-0000"
            maxLength={15}
            className="mt-1"
            required
          />
        </div>

        {/* Email */}
        <div>
          <Label htmlFor="email" className="text-sm font-medium text-gray-700">
            Email <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Input
            id="email"
            type="email"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            placeholder="email@exemplo.com"
            className="mt-1"
          />
        </div>

        {/* Hospital */}
        <div className="md:col-span-2">
          <Label htmlFor="hospital" className="text-sm font-medium text-gray-700">
            Hospital <ResearchRequiredIndicator isResearchParticipant={isResearchParticipant} />
          </Label>
          <Input
            id="hospital"
            type="text"
            value={formData.hospital}
            onChange={(e) => handleChange('hospital', e.target.value)}
            placeholder="Nome do hospital onde a cirurgia foi realizada"
            className="mt-1"
            required
          />
        </div>

        {/* Duração da Cirurgia */}
        <div>
          <Label htmlFor="surgeryDuration" className="text-sm font-medium text-gray-700">
            Duração da Cirurgia (minutos)
          </Label>
          <Input
            id="surgeryDuration"
            type="number"
            value={formData.surgeryDuration}
            onChange={(e) => handleChange('surgeryDuration', e.target.value)}
            placeholder="Ex: 45"
            min="0"
            className="mt-1"
          />
        </div>
      </div>
    </Card>
  );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';

interface DescricaoCompletaSectionProps {
  patient: any;
  onUpdate: (data: any) => void;
  onComplete: (isComplete: boolean) => void;
}

export function DescricaoCompletaSection({ patient, onUpdate, onComplete }: DescricaoCompletaSectionProps) {
  const [description, setDescription] = useState(patient?.surgicalDescription || '');

  // Check completion - no required fields, always complete
  useEffect(() => {
    onComplete(true);
  }, [onComplete]);

  // Update parent
  useEffect(() => {
    onUpdate({ surgicalDescription: description });
  }, [description, onUpdate]);

  return (
    <Card className="p-6">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Descrição Completa da Cirurgia</h2>

      <div>
        <Label htmlFor="surgicalDescription" className="text-sm font-medium text-gray-700">
          Descrição Operatória Detalhada
        </Label>
        <p className="text-sm text-gray-600 mt-1 mb-2">
          Descreva detalhadamente todos os procedimentos realizados durante a cirurgia, incluindo:
          posição do paciente, antissepsia, técnica cirúrgica, achados intraoperatórios,
          hemostasia, síntese, curativos e qualquer outra informação relevante.
        </p>

        <Textarea
          id="surgicalDescription"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Ex: Paciente posicionado em decúbito ventral, sob anestesia raquiana. Realizada antissepsia da região perianal com clorexidina alcoólica. Identificadas três mamilos hemorroidários nas posições anterior esquerda, lateral direita e posterior. Realizada hemorroidectomia pela técnica de Ferguson modificada por Campos, utilizando bisturi elétrico para dissecção e hemostasia. Ressecção dos mamilos com preservação da ponte mucosa entre os mesmos. Hemostasia cuidadosa com pontos absorvíveis. Não houve intercorrências. Paciente encaminhado à sala de recuperação em boas condições..."
          className="mt-1 min-h-[400px]"
          rows={20}
        />

        <div className="mt-4 text-sm text-gray-600">
          <p className="font-medium mb-2">Sugestões do que incluir na descrição:</p>
          <ul className="list-disc list-inside space-y-1 ml-2">
            <li>Posicionamento do paciente</li>
            <li>Antissepsia e paramentação</li>
            <li>Achados ao exame físico inicial</li>
            <li>Técnica cirúrgica detalhada (passo a passo)</li>
            <li>Instrumentos e dispositivos utilizados</li>
            <li>Achados intraoperatórios relevantes</li>
            <li>Método de hemostasia</li>
            <li>Técnica de síntese (se aplicável)</li>
            <li>Curativos e drenos (se aplicável)</li>
            <li>Intercorrências ou complicações</li>
            <li>Estado do paciente ao final do procedimento</li>
          </ul>
        </div>
      </div>

      <div className="mt-6 bg-blue-50 border-l-4 border-blue-500 p-4">
        <p className="text-sm text-blue-800">
          <strong>Importante:</strong> Esta é a descrição cirúrgica completa e oficial do
          procedimento. Será utilizada para documentação médica, relatórios e laudos. Seja
          detalhado e preciso nas informações.
        </p>
      </div>
    </Card>
  );
}

"use client";

import React, { useState, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Textarea } from '@/components/ui/textarea';

interface DetalhesCirurgicosSectionProps {
  patient: any;
  onUpdate: (data: any) => void;
  onComplete: (isComplete: boolean) => void;
}

export function DetalhesCirurgicosSection({ patient, onUpdate, onComplete }: DetalhesCirurgicosSectionProps) {
  const [surgeryType, setSurgeryType] = useState(patient?.surgeryType || 'hemorroidectomia');

  // Hemorrhoidectomy fields
  const [techniques, setTechniques] = useState<string[]>(patient?.hemorrhoidectomy?.techniques || []);
  const [energyType, setEnergyType] = useState(patient?.hemorrhoidectomy?.energyType || '');
  const [mamillaeResected, setMamillaeResected] = useState(patient?.hemorrhoidectomy?.mamillaeResected || '');
  const [mamillaePositions, setMamillaePositions] = useState(patient?.hemorrhoidectomy?.mamillaePositions || '');
  const [hemorrhoidType, setHemorrhoidType] = useState(patient?.hemorrhoidectomy?.type || '');
  const [internalGrade, setInternalGrade] = useState(patient?.hemorrhoidectomy?.internalGrade || '');
  const [externalDetails, setExternalDetails] = useState(patient?.hemorrhoidectomy?.externalDetails || '');

  // Fistula fields
  const [fistulaType, setFistulaType] = useState(patient?.fistula?.type || '');
  const [fistulaTechnique, setFistulaTechnique] = useState(patient?.fistula?.technique || '');
  const [fistulaTracts, setFistulaTracts] = useState(patient?.fistula?.tracts || '');
  const [setonUsed, setSetonUsed] = useState(patient?.fistula?.setonUsed || false);
  const [setonMaterial, setSetonMaterial] = useState(patient?.fistula?.setonMaterial || '');

  // Fissure fields
  const [fissureType, setFissureType] = useState(patient?.fissure?.type || '');
  const [fissureLocation, setFissureLocation] = useState(patient?.fissure?.location || '');
  const [fissureTechniques, setFissureTechniques] = useState(patient?.fissure?.techniques || '');

  // Pilonidal fields
  const [pilonidalTechnique, setPilonidalTechnique] = useState(patient?.pilonidal?.technique || '');

  // Common fields
  const [complications, setComplications] = useState(patient?.surgicalComplications || '');
  const [recoveryMinutes, setRecoveryMinutes] = useState(patient?.recoveryRoomMinutes || '');
  const [sameDayDischarge, setSameDayDischarge] = useState(patient?.sameDayDischarge || false);
  const [hospitalizationDays, setHospitalizationDays] = useState(patient?.hospitalizationDays || '');

  // Check completion
  useEffect(() => {
    let isComplete = false;

    if (surgeryType === 'hemorroidectomia') {
      isComplete = !!(techniques.length > 0 && energyType && mamillaeResected);
    } else if (surgeryType === 'fistula') {
      isComplete = !!(fistulaType && fistulaTechnique);
    } else if (surgeryType === 'fissura') {
      isComplete = !!(fissureType && fissureLocation);
    } else if (surgeryType === 'cisto_pilonidal') {
      isComplete = !!pilonidalTechnique;
    }

    onComplete(isComplete);
  }, [surgeryType, techniques, energyType, mamillaeResected, fistulaType, fistulaTechnique, fissureType, fissureLocation, pilonidalTechnique, onComplete]);

  // Update parent
  useEffect(() => {
    const data: any = {
      surgeryType,
      surgicalComplications: complications,
      recoveryRoomMinutes: recoveryMinutes,
      sameDayDischarge,
      hospitalizationDays
    };

    if (surgeryType === 'hemorroidectomia') {
      data.hemorrhoidectomy = {
        techniques,
        energyType,
        mamillaeResected,
        mamillaePositions,
        type: hemorrhoidType,
        internalGrade,
        externalDetails
      };
    } else if (surgeryType === 'fistula') {
      data.fistula = {
        type: fistulaType,
        technique: fistulaTechnique,
        tracts: fistulaTracts,
        setonUsed,
        setonMaterial
      };
    } else if (surgeryType === 'fissura') {
      data.fissure = {
        type: fissureType,
        location: fissureLocation,
        techniques: fissureTechniques
      };
    } else if (surgeryType === 'cisto_pilonidal') {
      data.pilonidal = {
        technique: pilonidalTechnique
      };
    }

    onUpdate(data);
  }, [surgeryType, techniques, energyType, mamillaeResected, mamillaePositions, hemorrhoidType, internalGrade, externalDetails, fistulaType, fistulaTechnique, fistulaTracts, setonUsed, setonMaterial, fissureType, fissureLocation, fissureTechniques, pilonidalTechnique, complications, recoveryMinutes, sameDayDischarge, hospitalizationDays, onUpdate]);

  const handleTechniqueToggle = (technique: string) => {
    setTechniques(prev =>
      prev.includes(technique)
        ? prev.filter(t => t !== technique)
        : [...prev, technique]
    );
  };

  return (
    <Card className="p-6">
      <h2 className="text-2xl font-semibold mb-6 text-gray-800">Detalhes Cirúrgicos</h2>

      {/* Surgery Type */}
      <div className="mb-6">
        <Label htmlFor="surgeryType" className="text-sm font-medium text-gray-700">
          Tipo de Cirurgia <span className="text-red-500">*</span>
        </Label>
        <Select value={surgeryType} onValueChange={setSurgeryType}>
          <SelectTrigger className="mt-1">
            <SelectValue placeholder="Selecione o tipo de cirurgia" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="hemorroidectomia">Hemorroidectomia</SelectItem>
            <SelectItem value="fistula">Fístula Anorretal</SelectItem>
            <SelectItem value="fissura">Fissura Anal</SelectItem>
            <SelectItem value="cisto_pilonidal">Cisto Pilonidal</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Hemorrhoidectomy Details */}
      {surgeryType === 'hemorroidectomia' && (
        <div className="space-y-4 border-t pt-4">
          <h3 className="text-lg font-semibold text-gray-700">Detalhes da Hemorroidectomia</h3>

          {/* Technique (multi-select) */}
          <div>
            <Label className="text-sm font-medium text-gray-700">
