            {shouldShowError('sex') ? (
              <FieldValidationMessage error={fieldErrors.sex} />
            ) : shouldShowWarning('sex') ? (
              <FieldValidationMessage warning={fieldWarnings.sex} />
            ) : null}
          </div>
        </CardContent>
      </Card>

      {/* Surgery Data Section */}
