        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Dados Cirúrgicos</CardTitle>
            {sectionSummary['Dados Cirúrgicos'] && (
              <SectionValidationSummary
                sectionName="Dados Cirúrgicos"
                {...sectionSummary['Dados Cirúrgicos']}
              />
