            </div>
            <Input
              id="surgeryType"
              value={formData.surgeryType}
              onChange={(e) => handleFieldChange('surgeryType', e.target.value)}
              onBlur={() => handleFieldBlur('surgeryType')}
              placeholder="Ex: Apendicectomia"
              className={shouldShowError('surgeryType') ? 'border-red-500' : ''}
            />
            {shouldShowError('surgeryType') && (
              <FieldValidationMessage error={fieldErrors.surgeryType} />
            )}
          </div>

          {/* Surgery Date */}
          <div id="field-surgeryDate" className="space-y-2">
            <div className="flex items-center gap-2">
              <Label htmlFor="surgeryDate">Data da Cirurgia</Label>
              <FieldRequirementBadge fieldName="surgeryDate" context={context} />
            </div>
            <Input
              id="surgeryDate"
              type="date"
              value={formData.surgeryDate}
              onChange={(e) => handleFieldChange('surgeryDate', e.target.value)}
              onBlur={() => handleFieldBlur('surgeryDate')}
              className={shouldShowError('surgeryDate') ? 'border-red-500' : ''}
            />
            {shouldShowError('surgeryDate') && (
              <FieldValidationMessage error={fieldErrors.surgeryDate} />
            )}
          </div>

          {/* Surgery Details */}
          <div id="field-surgeryDetails" className="space-y-2">
            <div className="flex items-center gap-2">
              <Label htmlFor="surgeryDetails">Detalhes da Cirurgia</Label>
              <FieldRequirementBadge fieldName="surgeryDetails" context={context} />
            </div>
            <Input
              id="surgeryDetails"
              value={formData.surgeryDetails}
              onChange={(e) => handleFieldChange('surgeryDetails', e.target.value)}
              onBlur={() => handleFieldBlur('surgeryDetails')}
              placeholder="Informações adicionais sobre a cirurgia"
              className={shouldShowError('surgeryDetails') ? 'border-red-500' : ''}
            />
            {shouldShowWarning('surgeryDetails') && (
              <FieldValidationMessage warning={fieldWarnings.surgeryDetails} />
            )}
          </div>
        </CardContent>
      </Card>

      {/* Validation Summary */}
      <ValidationSummary
        errors={formatValidationErrors(validation.errors)}
        missingRequired={validation.missingRequired.map(getFieldLabel)}
        missingRecommended={validation.missingRecommended?.map(getFieldLabel)}
      />

      {/* Submit Button */}
      <div className="flex gap-4">
        <Button
          type="submit"
          size="lg"
          disabled={!validation.isValid && isResearchParticipant}
          className="flex-1"
        >
          {validation.isValid ? 'Cadastrar Paciente' : 'Preencha os campos obrigatórios'}
        </Button>
        {!isResearchParticipant && !validation.isValid && (
          <Button
            type="submit"
            variant="outline"
            size="lg"
          >
            Salvar Rascunho
          </Button>
        )}
      </div>
    </form>
  );
}

"use client"

import { useState } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Badge } from "@/components/ui/badge"
import { Brain, AlertTriangle, CheckCircle, Activity, Loader2 } from "lucide-react"
import { Progress } from "@/components/ui/progress"

interface ComplicationRiskPredictorProps {
  patientData: {
    idade: number
    sexo: string
    comorbidades: string
    tipo_cirurgia: string
    duracao_minutos?: number
    bloqueio_pudendo?: number
    dor_d1: number
    retencao_urinaria?: number
    febre?: number
    sangramento_intenso?: number
  }
}

interface PredictionResult {
  probability: number
  prediction: number
  risk_level: "low" | "medium" | "high" | "critical"
  risk_label: string
  recommendation: string
  top_risk_factors: {
    name: string
    contribution: number
  }[]
}

export function ComplicationRiskPredictor({ patientData }: ComplicationRiskPredictorProps) {
  const [prediction, setPrediction] = useState<PredictionResult | null>(null)
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState("")

  async function handlePredict() {
    setIsLoading(true)
    setError("")
    setPrediction(null)

    try {
      const response = await fetch("/api/ml/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(patientData),
      })

      if (!response.ok) {
        const data = await response.json()
        throw new Error(data.error || "Erro ao fazer predição")
      }

      const data = await response.json()
      setPrediction(data)
    } catch (err: any) {
      setError(err.message || "Erro ao fazer predição")
    } finally {
      setIsLoading(false)
    }
  }

  const getRiskColor = (level: string) => {
    switch (level) {
      case "critical":
        return "bg-red-600"
      case "high":
        return "bg-orange-500"
      case "medium":
        return "bg-yellow-500"
      case "low":
        return "bg-green-500"
      default:
        return "bg-gray-500"
    }
  }

  const getRiskIcon = (level: string) => {
    switch (level) {
      case "critical":
      case "high":
        return <AlertTriangle className="h-5 w-5" />
      case "medium":
        return <Activity className="h-5 w-5" />
      case "low":
        return <CheckCircle className="h-5 w-5" />
      default:
        return null
    }
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center gap-2">
          <Brain className="h-6 w-6 text-primary" />
          <div>
            <CardTitle>Predição de Complicações (ML)</CardTitle>
            <CardDescription>
              Modelo de Machine Learning prevê risco de complicações pós-operatórias
            </CardDescription>
          </div>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        {!prediction && !isLoading && (
          <Button onClick={handlePredict} className="w-full" disabled={isLoading}>
            <Brain className="mr-2 h-4 w-4" />
            Analisar Risco com IA
          </Button>
        )}

        {isLoading && (
          <div className="flex items-center justify-center py-8">
            <Loader2 className="h-8 w-8 animate-spin text-primary" />
            <span className="ml-2">Analisando dados com IA...</span>
          </div>
        )}

        {error && (
          <Alert variant="destructive">
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {prediction && (
          <div className="space-y-4">
            {/* Probabilidade */}
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Probabilidade de Complicação</span>
                <span className="text-2xl font-bold">{(prediction.probability * 100).toFixed(1)}%</span>
              </div>
              <Progress value={prediction.probability * 100} className="h-3" />
            </div>

            {/* Nível de Risco */}
            <div className="flex items-center gap-3 p-4 rounded-lg bg-muted">
              <div className={`p-2 rounded-full ${getRiskColor(prediction.risk_level)} text-white`}>
                {getRiskIcon(prediction.risk_level)}
              </div>
              <div className="flex-1">
                <div className="font-semibold">Nível de Risco: {prediction.risk_label}</div>
                <div className="text-sm text-muted-foreground">{prediction.recommendation}</div>
              </div>
            </div>

            {/* Principais Fatores de Risco */}
            <div className="space-y-2">
              <div className="font-semibold text-sm">Principais Fatores Contribuintes:</div>
              {prediction.top_risk_factors.map((factor, index) => (
                <div key={index} className="flex items-center justify-between text-sm">
                  <span className="text-muted-foreground">
                    {index + 1}. {factor.name.replace(/_/g, " ")}
                  </span>
                  <Badge variant="outline">{(factor.contribution * 100).toFixed(1)}%</Badge>
                </div>
              ))}
            </div>

            {/* Ações */}
            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={handlePredict}
                className="flex-1"
              >
                Recalcular
              </Button>
              <Button
                variant="default"
                onClick={() => window.print()}
                className="flex-1"
              >
                Imprimir Relatório
              </Button>
            </div>

            {/* Disclaimer */}
            <Alert>
              <AlertDescription className="text-xs">
                ⚠️ <strong>Importante:</strong> Esta predição é baseada em modelos estatísticos e deve ser
                usada como ferramenta auxiliar na tomada de decisão clínica. Sempre considere o julgamento
                clínico e as diretrizes médicas estabelecidas.
              </AlertDescription>
            </Alert>
          </div>
        )}
      </CardContent>
    </Card>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Skeleton } from "@/components/ui/skeleton"
import { FollowUpResponseCard } from "./FollowUpResponseCard"
import {
  MessageCircle,
  RefreshCw,
  Filter,
  AlertTriangle,
  CheckCircle2,
  Clock,
  ChevronDown,
} from "lucide-react"
import { motion, AnimatePresence } from "framer-motion"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

interface FollowUpResponse {
  id: string
  followUpId: string
  dayNumber: number
  createdAt: string | Date
  riskLevel: 'low' | 'medium' | 'high' | 'critical'
  redFlags: string[]
  questionnaireData?: any
  aiAnalysis?: string
}

interface FollowUpSummary {
  id: string
  dayNumber: number
  scheduledDate: string | Date
  status: 'pending' | 'sent' | 'responded' | 'overdue'
  sentAt?: string | Date
  respondedAt?: string | Date
  responseCount: number
  latestRiskLevel?: string
  hasRedFlags: boolean
}

interface TimelineData {
  responses: FollowUpResponse[]
  summary: FollowUpSummary[]
  stats: {
    totalFollowUps: number
    completedFollowUps: number
    pendingFollowUps: number
    overdueFollowUps: number
    highRiskResponses: number
  }
}

interface ConversationTimelineProps {
  patientId: string
}

type FilterType = 'all' | 'alerts' | 'pending'

export function ConversationTimeline({ patientId }: ConversationTimelineProps) {
  const [data, setData] = useState<TimelineData | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [filter, setFilter] = useState<FilterType>('all')
  const [refreshing, setRefreshing] = useState(false)

  const fetchTimeline = async () => {
    try {
      setError(null)
      const response = await fetch(`/api/paciente/${patientId}/timeline`)

      if (!response.ok) {
        throw new Error('Falha ao carregar timeline')
      }

      const result = await response.json()

      if (result.success) {
        // Transformar dados do timeline para o formato esperado
        const responses: FollowUpResponse[] = result.data.timeline
          .filter((event: any) => event.type === 'followup' && event.metadata?.riskLevel)
          .map((event: any) => ({
            id: event.id,
            followUpId: event.metadata?.followUpId || event.id,
            dayNumber: parseInt(event.title.replace(/\D/g, '')) || 0,
            createdAt: event.date,
            riskLevel: event.metadata?.riskLevel || 'low',
            redFlags: event.metadata?.redFlags || [],
            questionnaireData: event.metadata?.questionnaireData,
            aiAnalysis: event.metadata?.aiAnalysis,
          }))
          .sort((a: FollowUpResponse, b: FollowUpResponse) =>
            new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
          )

        setData({
          responses,
          summary: result.data.followUpSummary || [],
          stats: result.data.stats || {
            totalFollowUps: 0,
            completedFollowUps: 0,
            pendingFollowUps: 0,
            overdueFollowUps: 0,
            highRiskResponses: 0,
          },
        })
      } else {
        throw new Error(result.error?.message || 'Erro desconhecido')
      }
    } catch (err) {
      console.error('Erro ao carregar timeline:', err)
      setError(err instanceof Error ? err.message : 'Erro ao carregar dados')
    } finally {
      setLoading(false)
      setRefreshing(false)
    }
  }

  useEffect(() => {
    fetchTimeline()
  }, [patientId])

  const handleRefresh = () => {
    setRefreshing(true)
    fetchTimeline()
  }

  const filteredResponses = data?.responses.filter(response => {
    if (filter === 'alerts') {
      return response.riskLevel === 'high' || response.riskLevel === 'critical'
    }
    return true
  }) || []

  const pendingFollowUps = data?.summary.filter(s => s.status === 'pending' || s.status === 'sent') || []

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'responded': return <CheckCircle2 className="h-4 w-4 text-green-500" />
      case 'pending': return <Clock className="h-4 w-4 text-yellow-500" />
      case 'sent': return <MessageCircle className="h-4 w-4 text-blue-500" />
      case 'overdue': return <AlertTriangle className="h-4 w-4 text-red-500" />
      default: return null
    }
  }

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'responded': return 'Respondido'
      case 'pending': return 'Pendente'
      case 'sent': return 'Enviado'
      case 'overdue': return 'Atrasado'
      default: return status
    }
  }

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <Skeleton className="h-6 w-48" />
            <Skeleton className="h-9 w-24" />
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          {[1, 2, 3].map((i) => (
            <Skeleton key={i} className="h-32 w-full" />
          ))}
        </CardContent>
      </Card>
    )
  }

  if (error) {
    return (
      <Card className="border-red-200 bg-red-50">
        <CardContent className="py-8 text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-red-700 font-medium">{error}</p>
          <Button onClick={handleRefresh} variant="outline" className="mt-4">
            <RefreshCw className="h-4 w-4 mr-2" />
            Tentar novamente
          </Button>
        </CardContent>
      </Card>
    )
  }

  return (
    <Card className="overflow-hidden">
      <CardHeader className="bg-gradient-to-r from-blue-50 to-indigo-50 border-b">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div
              className="p-2 rounded-lg"
              style={{ backgroundColor: '#0A2647' }}
            >
              <MessageCircle className="h-5 w-5 text-white" />
            </div>
            <div>
              <CardTitle className="text-lg">Acompanhamento via WhatsApp</CardTitle>
              <p className="text-sm text-gray-500 mt-1">
                {data?.stats.completedFollowUps || 0} de {data?.stats.totalFollowUps || 0} follow-ups respondidos
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {/* Filtro */}
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline" size="sm" className="gap-2">
                  <Filter className="h-4 w-4" />
                  {filter === 'all' ? 'Todos' : filter === 'alerts' ? 'Alertas' : 'Pendentes'}
                  <ChevronDown className="h-3 w-3" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => setFilter('all')}>
                  Todos ({data?.responses.length || 0})
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => setFilter('alerts')}>
                  <AlertTriangle className="h-4 w-4 mr-2 text-red-500" />
                  Com alertas ({data?.stats.highRiskResponses || 0})
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>

            {/* Refresh */}
            <Button
              variant="outline"
              size="sm"
              onClick={handleRefresh}
              disabled={refreshing}
            >
              <RefreshCw className={`h-4 w-4 ${refreshing ? 'animate-spin' : ''}`} />
            </Button>
          </div>
        </div>

        {/* Stats rápidos */}
        {data && (
          <div className="flex flex-wrap gap-3 mt-4">
            <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
              <CheckCircle2 className="h-3 w-3 mr-1" />
              {data.stats.completedFollowUps} respondidos
            </Badge>
            {data.stats.pendingFollowUps > 0 && (
              <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">
                <Clock className="h-3 w-3 mr-1" />
                {data.stats.pendingFollowUps} pendentes
              </Badge>
            )}
            {data.stats.overdueFollowUps > 0 && (
              <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200">
                <AlertTriangle className="h-3 w-3 mr-1" />
                {data.stats.overdueFollowUps} atrasados
              </Badge>
            )}
            {data.stats.highRiskResponses > 0 && (
              <Badge variant="destructive">
                {data.stats.highRiskResponses} alertas
              </Badge>
            )}
          </div>
        )}
      </CardHeader>

      <CardContent className="p-4">
        {/* Pendentes */}
        {pendingFollowUps.length > 0 && filter !== 'alerts' && (
          <div className="mb-6">
            <h4 className="text-sm font-semibold text-gray-500 mb-3 flex items-center gap-2">
              <Clock className="h-4 w-4" />
              Aguardando resposta
            </h4>
            <div className="flex flex-wrap gap-2">
              {pendingFollowUps.map((followUp) => (
                <Badge
                  key={followUp.id}
                  variant="outline"
                  className={`
                    ${followUp.status === 'sent' ? 'bg-blue-50 border-blue-200 text-blue-700' : ''}
                    ${followUp.status === 'pending' ? 'bg-gray-50 border-gray-200 text-gray-700' : ''}
                  `}
                >
                  {getStatusIcon(followUp.status)}
                  <span className="ml-1">D+{followUp.dayNumber}</span>
                  <span className="ml-1 text-xs opacity-70">({getStatusLabel(followUp.status)})</span>
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Lista de respostas */}
        {filteredResponses.length === 0 ? (
          <div className="text-center py-12">
            <MessageCircle className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-700 mb-2">
              {filter === 'alerts' ? 'Nenhum alerta encontrado' : 'Nenhuma resposta ainda'}
            </h3>
            <p className="text-gray-500 text-sm">
              {filter === 'alerts'
                ? 'Não há respostas com alertas de alto risco.'
                : 'As respostas dos follow-ups aparecerão aqui quando o paciente responder.'}
            </p>
          </div>
        ) : (
          <div className="space-y-3">
            <h4 className="text-sm font-semibold text-gray-500 mb-3">
              {filter === 'alerts' ? 'Respostas com alertas' : 'Histórico de respostas'}
            </h4>
            <AnimatePresence mode="popLayout">
              {filteredResponses.map((response, index) => (
                <FollowUpResponseCard
                  key={response.id}
                  response={response}
                  isLatest={index === 0}
                />
              ))}
            </AnimatePresence>
          </div>
        )}
      </CardContent>
    </Card>
  )
}

"use client"

import { Card, CardContent } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { format, formatDistanceToNow } from "date-fns"
import { ptBR } from "date-fns/locale"
import {
  Thermometer,
  Droplets,
  Activity,
  Pill,
  AlertTriangle,
  CheckCircle2,
  Clock,
  ChevronDown,
  ChevronUp,
} from "lucide-react"
import { useState } from "react"
import { motion, AnimatePresence } from "framer-motion"

interface QuestionnaireData {
  painAtRest?: number
  painDuringEvacuation?: number
  hadBowelMovement?: boolean
  bowelMovementTime?: string
  bristolScale?: number
  bleeding?: string
  hasFever?: boolean
  temperature?: number
  usedPrescribedMeds?: boolean
  usedExtraMeds?: boolean
  extraMedsDetails?: string
  hasPurulentDischarge?: boolean
  [key: string]: any
}

interface FollowUpResponse {
  id: string
  followUpId: string
  dayNumber: number
  createdAt: string | Date
  riskLevel: 'low' | 'medium' | 'high' | 'critical'
  redFlags: string[]
  questionnaireData?: QuestionnaireData
  aiAnalysis?: string
}

interface FollowUpResponseCardProps {
  response: FollowUpResponse
  isLatest?: boolean
}

export function FollowUpResponseCard({ response, isLatest = false }: FollowUpResponseCardProps) {
  const [expanded, setExpanded] = useState(isLatest)
  const data = response.questionnaireData || {}

  const getRiskColor = (level: string) => {
    switch (level) {
      case 'critical': return 'bg-red-500 text-white'
      case 'high': return 'bg-orange-500 text-white'
      case 'medium': return 'bg-yellow-500 text-black'
      default: return 'bg-green-500 text-white'
    }
  }

  const getRiskLabel = (level: string) => {
    switch (level) {
      case 'critical': return 'CRÍTICO'
      case 'high': return 'ALTO'
      case 'medium': return 'MÉDIO'
      default: return 'BAIXO'
    }
  }

  const getBristolDescription = (scale?: number) => {
    if (!scale) return null
    const descriptions: Record<number, string> = {
      1: "Caroços duros separados",
      2: "Forma de salsicha com caroços",
      3: "Salsicha com rachaduras",
      4: "Forma de cobra, lisa",
      5: "Pedaços macios",
      6: "Pedaços fofos, pastoso",
      7: "Líquido, sem pedaços",
    }
    return descriptions[scale] || ""
  }

  const getBleedingLabel = (bleeding?: string) => {
    if (!bleeding) return null
    const labels: Record<string, { text: string; color: string }> = {
      none: { text: "Sem sangramento", color: "text-green-600" },
      paper: { text: "Só no papel", color: "text-yellow-600" },
      bowl: { text: "No vaso", color: "text-orange-600" },
      clots: { text: "Com coágulos", color: "text-red-600" },
    }
    return labels[bleeding]
  }

  const getRedFlagLabel = (flag: string): string => {
    const labels: Record<string, string> = {
      febre_alta: "Febre alta",
      dor_intensa: "Dor intensa",
      sangramento_ativo: "Sangramento ativo",
      retencao_urinaria: "Retenção urinária",
      sem_evacuacao: "Sem evacuação",
      incontinencia_fecal: "Incontinência fecal",
      secrecao_purulenta: "Secreção purulenta",
      edema_importante: "Edema importante",
      nausea_vomito: "Náusea/vômito",
      dispneia: "Dispneia",
      alteracao_consciencia: "Alteração consciência",
    }
    return labels[flag] || flag
  }

  const responseDate = new Date(response.createdAt)

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <Card
        className={`
          border-l-4 transition-all duration-200 hover:shadow-md cursor-pointer
          ${response.riskLevel === 'critical' ? 'border-l-red-500 bg-red-50/50' : ''}
          ${response.riskLevel === 'high' ? 'border-l-orange-500 bg-orange-50/50' : ''}
          ${response.riskLevel === 'medium' ? 'border-l-yellow-500 bg-yellow-50/30' : ''}
          ${response.riskLevel === 'low' ? 'border-l-green-500' : ''}
          ${isLatest ? 'ring-2 ring-blue-200' : ''}
        `}
        onClick={() => setExpanded(!expanded)}
      >
        <CardContent className="p-4">
          {/* Header sempre visível */}
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Badge
                variant="outline"
                className="text-sm font-bold px-3 py-1"
                style={{ backgroundColor: '#0A2647', color: 'white' }}
              >
                D+{response.dayNumber}
              </Badge>

              <Badge className={`${getRiskColor(response.riskLevel)} font-semibold`}>
                {getRiskLabel(response.riskLevel)}
              </Badge>

              {isLatest && (
                <Badge className="bg-blue-500 text-white">
                  Mais recente
                </Badge>
              )}
            </div>

            <div className="flex items-center gap-2 text-sm text-gray-500">
              <Clock className="h-4 w-4" />
              <span title={format(responseDate, "dd/MM/yyyy HH:mm", { locale: ptBR })}>
                {formatDistanceToNow(responseDate, { locale: ptBR, addSuffix: true })}
              </span>
              {expanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
            </div>
          </div>

          {/* Resumo rápido sempre visível */}
          <div className="flex flex-wrap gap-4 mt-3">
            {data.painAtRest !== undefined && (
              <div className="flex items-center gap-1.5">
                <Activity className="h-4 w-4 text-blue-600" />
                <span className="text-sm">
                  Dor repouso: <strong className={data.painAtRest >= 7 ? 'text-red-600' : ''}>{data.painAtRest}/10</strong>
                </span>
              </div>
            )}

            {data.hasFever && data.temperature && (
              <div className="flex items-center gap-1.5">
                <Thermometer className="h-4 w-4 text-red-500" />
                <span className="text-sm text-red-600 font-semibold">
                  {data.temperature}°C
                </span>
              </div>
            )}

            {response.redFlags.length > 0 && (
              <div className="flex items-center gap-1.5">
                <AlertTriangle className="h-4 w-4 text-red-500" />
                <span className="text-sm text-red-600 font-semibold">
                  {response.redFlags.length} alerta{response.redFlags.length > 1 ? 's' : ''}
                </span>
              </div>
            )}
          </div>

          {/* Conteúdo expandido */}
          <AnimatePresence>
            {expanded && (
              <motion.div
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: "auto", opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
                transition={{ duration: 0.2 }}
                className="overflow-hidden"
              >
                <div className="mt-4 pt-4 border-t border-gray-200 space-y-4">
                  {/* Dados de Dor */}
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    {/* Dor em repouso */}
                    <div className="bg-white rounded-lg p-3 border shadow-sm">
                      <div className="flex items-center gap-2 text-gray-600 mb-1">
                        <Activity className="h-4 w-4" />
                        <span className="text-xs font-medium">Dor em repouso</span>
                      </div>
                      <div className={`text-2xl font-bold ${(data.painAtRest || 0) >= 7 ? 'text-red-600' : (data.painAtRest || 0) >= 4 ? 'text-yellow-600' : 'text-green-600'}`}>
                        {data.painAtRest ?? '-'}/10
                      </div>
                    </div>

                    {/* Dor durante evacuação */}
                    {data.hadBowelMovement && (
                      <div className="bg-white rounded-lg p-3 border shadow-sm">
                        <div className="flex items-center gap-2 text-gray-600 mb-1">
                          <Activity className="h-4 w-4" />
                          <span className="text-xs font-medium">Dor evacuação</span>
                        </div>
                        <div className={`text-2xl font-bold ${(data.painDuringEvacuation || 0) >= 7 ? 'text-red-600' : (data.painDuringEvacuation || 0) >= 4 ? 'text-yellow-600' : 'text-green-600'}`}>
                          {data.painDuringEvacuation ?? '-'}/10
                        </div>
                      </div>
                    )}

                    {/* Temperatura */}
                    <div className="bg-white rounded-lg p-3 border shadow-sm">
                      <div className="flex items-center gap-2 text-gray-600 mb-1">
                        <Thermometer className="h-4 w-4" />
                        <span className="text-xs font-medium">Temperatura</span>
                      </div>
                      {data.hasFever ? (
                        <div className="text-2xl font-bold text-red-600">
                          {data.temperature}°C
                        </div>
                      ) : (
                        <div className="text-2xl font-bold text-green-600">
                          Normal
                        </div>
                      )}
                    </div>

                    {/* Bristol */}
                    {data.bristolScale && (
                      <div className="bg-white rounded-lg p-3 border shadow-sm">
                        <div className="flex items-center gap-2 text-gray-600 mb-1">
                          <span className="text-xs font-medium">Escala Bristol</span>
                        </div>
                        <div className="text-2xl font-bold text-gray-800">
                          {data.bristolScale}
                        </div>
                        <div className="text-xs text-gray-500">
                          {getBristolDescription(data.bristolScale)}
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Evacuação */}
                  <div className="bg-white rounded-lg p-3 border shadow-sm">
                    <div className="flex items-center gap-2 mb-2">
                      {data.hadBowelMovement ? (
                        <CheckCircle2 className="h-5 w-5 text-green-500" />
                      ) : (
                        <AlertTriangle className="h-5 w-5 text-yellow-500" />
                      )}
                      <span className="font-medium">
                        {data.hadBowelMovement ? 'Evacuou' : 'Não evacuou'}
                      </span>
                      {data.bowelMovementTime && (
                        <span className="text-gray-500 text-sm">
                          às {data.bowelMovementTime}
                        </span>
                      )}
                    </div>

                    {data.bleeding && (
                      <div className="flex items-center gap-2 mt-2">
                        <Droplets className="h-4 w-4" />
                        <span className={`text-sm ${getBleedingLabel(data.bleeding)?.color}`}>
                          {getBleedingLabel(data.bleeding)?.text}
                        </span>
                      </div>
                    )}
                  </div>

                  {/* Medicações */}
                  <div className="bg-white rounded-lg p-3 border shadow-sm">
                    <div className="flex items-center gap-2 mb-2">
                      <Pill className="h-5 w-5 text-purple-500" />
                      <span className="font-medium">Medicações</span>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      {data.usedPrescribedMeds && (
                        <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">
                          Usou prescritas
                        </Badge>
                      )}
                      {data.usedExtraMeds && (
                        <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">
                          Usou extras
                        </Badge>
                      )}
                      {!data.usedPrescribedMeds && !data.usedExtraMeds && (
                        <Badge variant="outline" className="bg-gray-50 text-gray-700 border-gray-200">
                          Não usou analgésicos
                        </Badge>
                      )}
                    </div>
                    {data.extraMedsDetails && (
                      <p className="text-sm text-gray-600 mt-2">
                        Detalhes: {data.extraMedsDetails}
                      </p>
                    )}
                  </div>

                  {/* Red Flags */}
                  {response.redFlags.length > 0 && (
                    <div className="bg-red-50 rounded-lg p-3 border border-red-200">
                      <div className="flex items-center gap-2 mb-2">
                        <AlertTriangle className="h-5 w-5 text-red-500" />
                        <span className="font-medium text-red-900">Alertas Detectados</span>
                      </div>
                      <div className="flex flex-wrap gap-2">
                        {response.redFlags.map((flag, idx) => (
                          <Badge key={idx} variant="destructive" className="bg-red-500">
                            {getRedFlagLabel(flag)}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Análise da IA */}
                  {response.aiAnalysis && (
                    <div className="bg-blue-50 rounded-lg p-3 border border-blue-200">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="font-medium text-blue-900">Análise da IA</span>
                      </div>
                      <p className="text-sm text-blue-800 whitespace-pre-wrap">
                        {response.aiAnalysis}
                      </p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </CardContent>
      </Card>
    </motion.div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Skeleton } from "@/components/ui/skeleton"
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  ChartOptions,
  ChartData,
} from 'chart.js'
import { Line } from 'react-chartjs-2'
import {
  Activity,
  TrendingDown,
  TrendingUp,
  Minus,
  AlertTriangle,
  RefreshCw,
} from "lucide-react"

// Registrar componentes do Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
)

interface PainData {
  day: number
  date: string
  painAtRest: number | null
  painDuringEvacuation: number | null
  hasRedFlag: boolean
}

interface PainEvolutionChartProps {
  patientId: string
  baselinePain?: number | null
}

export function PainEvolutionChart({ patientId, baselinePain }: PainEvolutionChartProps) {
  const [data, setData] = useState<PainData[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [showEvacuation, setShowEvacuation] = useState(true)

  const fetchPainData = async () => {
    try {
      setError(null)
      const response = await fetch(`/api/paciente/${patientId}/timeline`)

      if (!response.ok) {
        throw new Error('Falha ao carregar dados de dor')
      }

      const result = await response.json()

      if (result.success) {
        // Extrair dados de dor do timeline
        const painDataMap = new Map<number, PainData>()

        // Dias padrão de follow-up
        const followUpDays = [1, 2, 3, 5, 7, 10, 14]
        followUpDays.forEach(day => {
          painDataMap.set(day, {
            day,
            date: '',
            painAtRest: null,
            painDuringEvacuation: null,
            hasRedFlag: false,
          })
        })

        // Preencher com dados reais
        result.data.timeline
          .filter((event: any) => event.type === 'followup' && event.metadata?.riskLevel)
          .forEach((event: any) => {
            const dayMatch = event.title.match(/D\+(\d+)/)
            if (dayMatch) {
              const day = parseInt(dayMatch[1])
              const questionnaireData = event.metadata?.questionnaireData
              const hasRedFlags = (event.metadata?.redFlags || []).length > 0

              if (painDataMap.has(day) || followUpDays.includes(day)) {
                painDataMap.set(day, {
                  day,
                  date: new Date(event.date).toLocaleDateString('pt-BR'),
                  painAtRest: questionnaireData?.painAtRest ?? null,
                  painDuringEvacuation: questionnaireData?.painDuringEvacuation ?? null,
                  hasRedFlag: hasRedFlags || event.metadata?.riskLevel === 'critical' || event.metadata?.riskLevel === 'high',
                })
              }
            }
          })

        const sortedData = Array.from(painDataMap.values()).sort((a, b) => a.day - b.day)
        setData(sortedData)
      } else {
        throw new Error(result.error?.message || 'Erro desconhecido')
      }
    } catch (err) {
      console.error('Erro ao carregar dados de dor:', err)
      setError(err instanceof Error ? err.message : 'Erro ao carregar dados')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPainData()
  }, [patientId])

  // Calcular tendência
  const calculateTrend = () => {
    const validData = data.filter(d => d.painAtRest !== null)
    if (validData.length < 2) return 'neutral'

    const firstHalf = validData.slice(0, Math.ceil(validData.length / 2))
    const secondHalf = validData.slice(Math.floor(validData.length / 2))

    const avgFirst = firstHalf.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / firstHalf.length
    const avgSecond = secondHalf.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / secondHalf.length

    if (avgSecond < avgFirst - 0.5) return 'improving'
    if (avgSecond > avgFirst + 0.5) return 'worsening'
    return 'stable'
  }

  const trend = calculateTrend()

  // Calcular média de dor
  const avgPain = () => {
    const validData = data.filter(d => d.painAtRest !== null)
    if (validData.length === 0) return null
    return (validData.reduce((sum, d) => sum + (d.painAtRest || 0), 0) / validData.length).toFixed(1)
  }

  // Dados para o gráfico
  const chartData: ChartData<'line'> = {
    labels: data.map(d => `D+${d.day}`),
    datasets: [
      {
        label: 'Dor em repouso',
        data: data.map(d => d.painAtRest),
        borderColor: '#0A2647',
        backgroundColor: 'rgba(10, 38, 71, 0.1)',
        fill: true,
        tension: 0.3,
        pointRadius: data.map(d => d.hasRedFlag ? 8 : 5),
        pointBackgroundColor: data.map(d =>
          d.painAtRest === null ? 'transparent' :
          d.hasRedFlag ? '#DC2626' :
          (d.painAtRest || 0) >= 7 ? '#DC2626' :
          (d.painAtRest || 0) >= 4 ? '#F59E0B' :
          '#10B981'
        ),
        pointBorderColor: data.map(d =>
          d.painAtRest === null ? 'transparent' :
          d.hasRedFlag ? '#DC2626' :
          '#0A2647'
        ),
        pointBorderWidth: data.map(d => d.hasRedFlag ? 3 : 2),
        pointHoverRadius: 8,
        spanGaps: true,
      },
      ...(showEvacuation ? [{
        label: 'Dor durante evacuação',
        data: data.map(d => d.painDuringEvacuation),
        borderColor: '#D4AF37',
        backgroundColor: 'rgba(212, 175, 55, 0.1)',
        fill: false,
        tension: 0.3,
        borderDash: [5, 5],
        pointRadius: 4,
        pointBackgroundColor: data.map(d =>
          d.painDuringEvacuation === null ? 'transparent' :
          (d.painDuringEvacuation || 0) >= 7 ? '#DC2626' :
          (d.painDuringEvacuation || 0) >= 4 ? '#F59E0B' :
          '#10B981'
        ),
        pointBorderColor: '#D4AF37',
        spanGaps: true,
      }] : []),
    ],
  }

  const chartOptions: ChartOptions<'line'> = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom' as const,
        labels: {
          usePointStyle: true,
          padding: 20,
          font: {
            family: 'Inter, sans-serif',
            size: 12,
          },
        },
      },
      tooltip: {
        backgroundColor: 'rgba(10, 38, 71, 0.95)',
        titleFont: {
          family: 'Inter, sans-serif',
          size: 14,
          weight: 'bold',
        },
        bodyFont: {
          family: 'Inter, sans-serif',
          size: 13,
        },
        padding: 12,
        cornerRadius: 8,
        callbacks: {
          label: function(context) {
            const dataIndex = context.dataIndex
            const painData = data[dataIndex]
            const value = context.parsed.y

            if (value === null) return ''

            let label = `${context.dataset.label}: ${value}/10`

            if (painData?.hasRedFlag) {
              label += ' ⚠️ ALERTA'
            }

            return label
          },
          afterBody: function(context) {
            const dataIndex = context[0].dataIndex
            const painData = data[dataIndex]

            if (painData?.date) {
              return [`Data: ${painData.date}`]
            }
            return []
          },
        },
      },
    },
    scales: {
      y: {
        min: 0,
        max: 10,
        ticks: {
          stepSize: 2,
          font: {
            family: 'Inter, sans-serif',
          },
          callback: function(value) {
            return `${value}`
          },
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)',
        },
        title: {
          display: true,
          text: 'Nível de dor (EVA)',
          font: {
            family: 'Inter, sans-serif',
            size: 12,
          },
        },
      },
      x: {
        grid: {
          display: false,
        },
        ticks: {
          font: {
            family: 'Inter, sans-serif',
          },
        },
      },
    },
    interaction: {
      intersect: false,
      mode: 'index',
    },
    elements: {
      line: {
        borderWidth: 3,
      },
    },
  }

  // Nota: Baseline de dor é exibido nos cards de resumo abaixo do gráfico

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <Skeleton className="h-6 w-48" />
            <Skeleton className="h-9 w-24" />
          </div>
        </CardHeader>
        <CardContent>
          <Skeleton className="h-64 w-full" />
        </CardContent>
      </Card>
    )
  }

  if (error) {
    return (
      <Card className="border-red-200 bg-red-50">
        <CardContent className="py-8 text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-red-700 font-medium">{error}</p>
          <Button onClick={fetchPainData} variant="outline" className="mt-4">
            <RefreshCw className="h-4 w-4 mr-2" />
            Tentar novamente
          </Button>
        </CardContent>
      </Card>
    )
  }

  const hasData = data.some(d => d.painAtRest !== null)

  return (
    <Card className="overflow-hidden">
      <CardHeader className="bg-gradient-to-r from-indigo-50 to-purple-50 border-b">
        <div className="flex items-center justify-between flex-wrap gap-4">
          <div className="flex items-center gap-3">
            <div
              className="p-2 rounded-lg"
              style={{ backgroundColor: '#0A2647' }}
            >
              <Activity className="h-5 w-5 text-white" />
            </div>
            <div>
              <CardTitle className="text-lg">Evolução da Dor</CardTitle>
              <p className="text-sm text-gray-500 mt-1">
                Escala Visual Analógica (0-10)
              </p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {/* Tendência */}
            {hasData && (
              <Badge
                variant="outline"
                className={`
                  ${trend === 'improving' ? 'bg-green-50 text-green-700 border-green-200' : ''}
                  ${trend === 'worsening' ? 'bg-red-50 text-red-700 border-red-200' : ''}
                  ${trend === 'stable' ? 'bg-gray-50 text-gray-700 border-gray-200' : ''}
                `}
              >
                {trend === 'improving' && <TrendingDown className="h-3 w-3 mr-1" />}
                {trend === 'worsening' && <TrendingUp className="h-3 w-3 mr-1" />}
                {trend === 'stable' && <Minus className="h-3 w-3 mr-1" />}
                {trend === 'improving' ? 'Melhorando' : trend === 'worsening' ? 'Piorando' : 'Estável'}
              </Badge>
            )}

            {/* Média */}
            {avgPain() && (
              <Badge
                variant="outline"
                className="bg-blue-50 text-blue-700 border-blue-200"
              >
                Média: {avgPain()}/10
              </Badge>
            )}

            {/* Toggle evacuação */}
            <Button
              variant={showEvacuation ? "default" : "outline"}
              size="sm"
              onClick={() => setShowEvacuation(!showEvacuation)}
              style={showEvacuation ? { backgroundColor: '#D4AF37' } : {}}
            >
              Evacuação
            </Button>
          </div>
        </div>

        {/* Legenda de cores */}
        <div className="flex flex-wrap gap-3 mt-4">
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-green-500" />
            <span>Dor leve (0-3)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-yellow-500" />
            <span>Dor moderada (4-6)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full bg-red-500" />
            <span>Dor intensa (7-10)</span>
          </div>
          <div className="flex items-center gap-1.5 text-xs">
            <div className="w-3 h-3 rounded-full border-2 border-red-500 bg-white" />
            <span>Com alerta</span>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-4">
        {!hasData ? (
          <div className="text-center py-12">
            <Activity className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-700 mb-2">
              Sem dados de dor ainda
            </h3>
            <p className="text-gray-500 text-sm">
              Os dados de dor aparecerão aqui quando o paciente responder aos follow-ups.
            </p>
          </div>
        ) : (
          <div className="h-72 md:h-80">
            <Line data={chartData} options={chartOptions} />
          </div>
        )}

        {/* Cards de resumo */}
        {hasData && (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mt-4">
            {/* Pico de dor */}
            <div className="bg-red-50 rounded-lg p-3 border border-red-100">
              <p className="text-xs text-red-600 font-medium">Pico de dor</p>
              <p className="text-2xl font-bold text-red-700">
                {Math.max(...data.filter(d => d.painAtRest !== null).map(d => d.painAtRest || 0))}/10
              </p>
              <p className="text-xs text-red-500">
                D+{data.find(d => d.painAtRest === Math.max(...data.filter(d => d.painAtRest !== null).map(d => d.painAtRest || 0)))?.day || '-'}
              </p>
            </div>

            {/* Última dor */}
            <div className="bg-blue-50 rounded-lg p-3 border border-blue-100">
              <p className="text-xs text-blue-600 font-medium">Última medição</p>
              <p className="text-2xl font-bold text-blue-700">
                {data.filter(d => d.painAtRest !== null).slice(-1)[0]?.painAtRest ?? '-'}/10
              </p>
              <p className="text-xs text-blue-500">
                D+{data.filter(d => d.painAtRest !== null).slice(-1)[0]?.day || '-'}
              </p>
            </div>

            {/* Medições */}
            <div className="bg-gray-50 rounded-lg p-3 border border-gray-100">
              <p className="text-xs text-gray-600 font-medium">Medições</p>
              <p className="text-2xl font-bold text-gray-700">
                {data.filter(d => d.painAtRest !== null).length}
              </p>
              <p className="text-xs text-gray-500">de {data.length} dias</p>
            </div>

            {/* Alertas */}
            <div className="bg-orange-50 rounded-lg p-3 border border-orange-100">
              <p className="text-xs text-orange-600 font-medium">Alertas</p>
              <p className="text-2xl font-bold text-orange-700">
                {data.filter(d => d.hasRedFlag).length}
              </p>
              <p className="text-xs text-orange-500">dias com alerta</p>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  )
}

'use client';

import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { toast } from 'sonner';
import {
  FileText,
  File,
  Download,
  Settings,
  CheckCircle2,
  Loader2,
  Eye,
  FileSpreadsheet,
  X,
} from 'lucide-react';
import { getAllTemplates, ExportTemplate, TemplateType } from '@/lib/export-templates';
import { Packer } from 'docx';

// ============================================
// TYPE DEFINITIONS
// ============================================

export interface ExportReportModalProps {
  researchId: string;
  researchTitle: string;
  trigger?: React.ReactNode;
  onExportComplete?: (filename: string) => void;
}

export interface ExportConfiguration {
  format: 'docx' | 'pdf';
  template: string;
  sections: {
    anova: boolean;
    chiSquare: boolean;
    regression: boolean;
    survival: boolean;
    rawData: boolean;
    demographics: boolean;
  };
  style: 'apa' | 'vancouver' | 'custom';
  includeCharts: boolean;
  chartResolution: 'standard' | 'high' | 'print';
  anonymize: boolean;
  language: 'pt' | 'en';
}

// ============================================
// COMPONENT
// ============================================

export default function ExportReportModal({
  researchId,
  researchTitle,
  trigger,
  onExportComplete,
}: ExportReportModalProps) {
  const [open, setOpen] = useState(false);
  const [activeTab, setActiveTab] = useState<'format' | 'content' | 'style'>('format');
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [showPreview, setShowPreview] = useState(false);

  // Configuration state
  const [config, setConfig] = useState<ExportConfiguration>({
    format: 'docx',
    template: 'publication-full',
    sections: {
      anova: true,
      chiSquare: true,
      regression: true,
      survival: true,
      rawData: false,
      demographics: true,
    },
    style: 'apa',
    includeCharts: true,
    chartResolution: 'print',
    anonymize: true,
    language: 'pt',
  });

  const templates = getAllTemplates();
  const selectedTemplate = templates.find((t) => t.id === config.template);

  // Update template when format changes
  useEffect(() => {
    if (config.format === 'pdf' && config.template === 'publication-full') {
      setConfig((prev) => ({ ...prev, template: 'progress-report' }));
    }
  }, [config.format]);

  const handleExport = async () => {
    setIsExporting(true);
    setExportProgress(0);

    try {
      // Progress simulation
      const progressInterval = setInterval(() => {
        setExportProgress((prev) => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return prev;
          }
          return prev + 10;
        });
      }, 200);

      // Call export API
      const response = await fetch('/api/export-research/report', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          researchId,
          config,
        }),
      });

      clearInterval(progressInterval);
      setExportProgress(100);

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Erro ao gerar relatório');
      }

      // Download file
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;

      const extension = config.format === 'docx' ? 'docx' : 'pdf';
      const templateName = selectedTemplate?.name.replace(/\s/g, '_') || 'report';
      const filename = `${researchTitle.replace(/\s/g, '_')}_${templateName}_${new Date().toISOString().split('T')[0]}.${extension}`;

      a.download = filename;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      toast.success(`Relatório exportado com sucesso: ${filename}`);

      if (onExportComplete) {
        onExportComplete(filename);
      }

      setTimeout(() => {
        setOpen(false);
        setIsExporting(false);
        setExportProgress(0);
      }, 1000);
    } catch (error) {
      console.error('Export error:', error);
      toast.error(error instanceof Error ? error.message : 'Erro ao exportar relatório');
      setIsExporting(false);
      setExportProgress(0);
    }
  };

  const handlePreview = () => {
    setShowPreview(true);
    toast.info('Gerando preview...', { duration: 1000 });
  };

  const updateSection = (section: keyof ExportConfiguration['sections'], value: boolean) => {
    setConfig((prev) => ({
      ...prev,
      sections: {
        ...prev.sections,
        [section]: value,
      },
    }));
  };

  const selectAllSections = () => {
    setConfig((prev) => ({
      ...prev,
      sections: {
        anova: true,
        chiSquare: true,
        regression: true,
        survival: true,
        rawData: true,
        demographics: true,
      },
    }));
  };

  const deselectAllSections = () => {
    setConfig((prev) => ({
      ...prev,
      sections: {
        anova: false,
        chiSquare: false,
        regression: false,
        survival: false,
        rawData: false,
        demographics: false,
      },
    }));
  };

  const getSelectedSectionsCount = () => {
    return Object.values(config.sections).filter(Boolean).length;
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {trigger || (
          <Button variant="outline" size="lg">
            <Download className="mr-2 h-5 w-5" />
            Exportar Relatório
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl flex items-center gap-2">
            <FileText className="h-6 w-6" />
            Exportar Relatório de Pesquisa
          </DialogTitle>
          <DialogDescription>
            Configure o formato e conteúdo do relatório para exportação profissional
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as any)} className="mt-4">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="format" className="flex items-center gap-2">
              <File className="h-4 w-4" />
              Formato
            </TabsTrigger>
            <TabsTrigger value="content" className="flex items-center gap-2">
              <Settings className="h-4 w-4" />
              Conteúdo
            </TabsTrigger>
            <TabsTrigger value="style" className="flex items-center gap-2">
              <FileSpreadsheet className="h-4 w-4" />
              Estilo
            </TabsTrigger>
          </TabsList>

          {/* FORMAT TAB */}
          <TabsContent value="format" className="space-y-6">
            {/* Export Format */}
            <div className="space-y-3">
              <Label className="text-base font-semibold">Formato de Exportação</Label>
              <RadioGroup
                value={config.format}
                onValueChange={(value) => setConfig({ ...config, format: value as 'docx' | 'pdf' })}
              >
                <div className="grid grid-cols-2 gap-4">
                  <div
                    className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                      config.format === 'docx'
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => setConfig({ ...config, format: 'docx' })}
                  >
                    <RadioGroupItem value="docx" id="format-docx" />
                    <div className="flex-1">
                      <Label
                        htmlFor="format-docx"
                        className="cursor-pointer font-semibold flex items-center gap-2"
                      >
                        <FileText className="h-4 w-4" />
                        Word (.docx)
                      </Label>
                      <p className="text-sm text-gray-600 mt-1">
                        Documento editável com formatação APA. Ideal para manuscritos científicos.
                      </p>
                      <Badge variant="outline" className="mt-2">
                        Recomendado para publicações
                      </Badge>
                    </div>
                  </div>

                  <div
                    className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                      config.format === 'pdf'
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => setConfig({ ...config, format: 'pdf' })}
                  >
                    <RadioGroupItem value="pdf" id="format-pdf" />
                    <div className="flex-1">
                      <Label
                        htmlFor="format-pdf"
                        className="cursor-pointer font-semibold flex items-center gap-2"
                      >
                        <File className="h-4 w-4" />
                        PDF
                      </Label>
                      <p className="text-sm text-gray-600 mt-1">
                        Documento fixo com layout profissional. Ideal para compartilhamento.
                      </p>
                      <Badge variant="outline" className="mt-2">
                        Pronto para impressão
                      </Badge>
                    </div>
                  </div>
                </div>
              </RadioGroup>
            </div>

            <Separator />

            {/* Template Selection */}
            <div className="space-y-3">
              <Label className="text-base font-semibold">Tipo de Relatório</Label>
              <Select value={config.template} onValueChange={(value) => setConfig({ ...config, template: value })}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {templates.map((template) => (
                    <SelectItem key={template.id} value={template.id}>
                      <div className="flex flex-col items-start">
                        <span className="font-medium">{template.name}</span>
                        <span className="text-xs text-gray-500">{template.description}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {selectedTemplate && (
                <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  <h4 className="font-semibold text-blue-900 mb-2">{selectedTemplate.name}</h4>
                  <p className="text-sm text-blue-800 mb-3">{selectedTemplate.description}</p>
                  <div className="space-y-1">
                    <p className="text-xs font-semibold text-blue-900">Seções incluídas:</p>
                    <div className="flex flex-wrap gap-1">
                      {selectedTemplate.sections.slice(0, 5).map((section) => (
                        <Badge key={section.id} variant="secondary" className="text-xs">
                          {section.title}
                        </Badge>
                      ))}
                      {selectedTemplate.sections.length > 5 && (
                        <Badge variant="secondary" className="text-xs">
                          +{selectedTemplate.sections.length - 5} mais
                        </Badge>
                      )}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </TabsContent>

          {/* CONTENT TAB */}
          <TabsContent value="content" className="space-y-6">
            <div className="flex items-center justify-between">
              <Label className="text-base font-semibold">Selecionar Seções</Label>
              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={selectAllSections}>
                  Selecionar Todas
                </Button>
                <Button variant="outline" size="sm" onClick={deselectAllSections}>
                  Limpar
                </Button>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              {/* ANOVA Results */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.anova ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('anova', !config.sections.anova)}
              >
                <Checkbox
                  id="section-anova"
                  checked={config.sections.anova}
                  onCheckedChange={(checked) => updateSection('anova', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-anova" className="cursor-pointer font-semibold">
                    Resultados ANOVA
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Análise de variância entre grupos, testes post-hoc e tamanhos de efeito
                  </p>
                </div>
              </div>

              {/* Chi-Square Analysis */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.chiSquare ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('chiSquare', !config.sections.chiSquare)}
              >
                <Checkbox
                  id="section-chisquare"
                  checked={config.sections.chiSquare}
                  onCheckedChange={(checked) => updateSection('chiSquare', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-chisquare" className="cursor-pointer font-semibold">
                    Análise Qui-Quadrado
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Testes de associação para variáveis categóricas
                  </p>
                </div>
              </div>

              {/* Regression Models */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.regression ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('regression', !config.sections.regression)}
              >
                <Checkbox
                  id="section-regression"
                  checked={config.sections.regression}
                  onCheckedChange={(checked) => updateSection('regression', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-regression" className="cursor-pointer font-semibold">
                    Modelos de Regressão
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Análises preditivas e relações entre variáveis
                  </p>
                </div>
              </div>

              {/* Survival Analysis */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.survival ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('survival', !config.sections.survival)}
              >
                <Checkbox
                  id="section-survival"
                  checked={config.sections.survival}
                  onCheckedChange={(checked) => updateSection('survival', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-survival" className="cursor-pointer font-semibold">
                    Análise de Sobrevivência
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Curvas Kaplan-Meier e modelos de Cox
                  </p>
                </div>
              </div>

              {/* Demographics */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.demographics ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('demographics', !config.sections.demographics)}
              >
                <Checkbox
                  id="section-demographics"
                  checked={config.sections.demographics}
                  onCheckedChange={(checked) => updateSection('demographics', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-demographics" className="cursor-pointer font-semibold">
                    Demografia dos Pacientes
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Características basais e distribuição por grupo
                  </p>
                </div>
              </div>

              {/* Raw Data Tables */}
              <div
                className={`flex items-start space-x-3 rounded-lg border-2 p-4 cursor-pointer transition-all ${
                  config.sections.rawData ? 'border-blue-500 bg-blue-50' : 'border-gray-200'
                }`}
                onClick={() => updateSection('rawData', !config.sections.rawData)}
              >
                <Checkbox
                  id="section-rawdata"
                  checked={config.sections.rawData}
                  onCheckedChange={(checked) => updateSection('rawData', checked as boolean)}
                />
                <div className="flex-1">
                  <Label htmlFor="section-rawdata" className="cursor-pointer font-semibold">
                    Tabelas de Dados Brutos
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Dados individuais para apêndices (pode aumentar tamanho do arquivo)
                  </p>
                </div>
              </div>
            </div>

            <div className="p-3 bg-gray-50 border border-gray-200 rounded-lg">
              <p className="text-sm text-gray-700">
                <strong>{getSelectedSectionsCount()}</strong> de 6 seções selecionadas
              </p>
            </div>
          </TabsContent>

          {/* STYLE TAB */}
          <TabsContent value="style" className="space-y-6">
            {/* Citation Style */}
            <div className="space-y-3">
              <Label className="text-base font-semibold">Estilo de Citação</Label>
              <RadioGroup value={config.style} onValueChange={(value) => setConfig({ ...config, style: value as any })}>
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="apa" id="style-apa" />
                    <Label htmlFor="style-apa" className="cursor-pointer">
                      APA 7ª Edição (American Psychological Association)
                    </Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="vancouver" id="style-vancouver" />
                    <Label htmlFor="style-vancouver" className="cursor-pointer">
                      Vancouver (International Committee of Medical Journal Editors)
                    </Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="custom" id="style-custom" />
                    <Label htmlFor="style-custom" className="cursor-pointer">
                      Personalizado
                    </Label>
                  </div>
                </div>
              </RadioGroup>
            </div>

            <Separator />

            {/* Charts */}
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <Label className="text-base font-semibold">Gráficos e Figuras</Label>
                <Checkbox
                  id="include-charts"
                  checked={config.includeCharts}
                  onCheckedChange={(checked) => setConfig({ ...config, includeCharts: checked as boolean })}
                />
              </div>

              {config.includeCharts && (
                <div className="space-y-2 pl-4 border-l-2 border-blue-500">
                  <Label className="text-sm">Resolução dos Gráficos</Label>
                  <RadioGroup
                    value={config.chartResolution}
                    onValueChange={(value) => setConfig({ ...config, chartResolution: value as any })}
                  >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="standard" id="res-standard" />
                      <Label htmlFor="res-standard" className="cursor-pointer text-sm">
                        Padrão (72 DPI) - Arquivos menores
                      </Label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="high" id="res-high" />
                      <Label htmlFor="res-high" className="cursor-pointer text-sm">
                        Alta (150 DPI) - Melhor qualidade
                      </Label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="print" id="res-print" />
                      <Label htmlFor="res-print" className="cursor-pointer text-sm">
                        Impressão (300 DPI) - Qualidade máxima
                      </Label>
                    </div>
                  </RadioGroup>
                </div>
              )}
            </div>

            <Separator />

            {/* Privacy */}
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <div>
                  <Label className="text-base font-semibold">Anonimização de Dados</Label>
                  <p className="text-sm text-gray-600">
                    Remover nomes e informações identificáveis dos pacientes
                  </p>
                </div>
                <Checkbox
                  id="anonymize"
                  checked={config.anonymize}
                  onCheckedChange={(checked) => setConfig({ ...config, anonymize: checked as boolean })}
                />
              </div>
            </div>

            <Separator />

            {/* Language */}
            <div className="space-y-3">
              <Label className="text-base font-semibold">Idioma</Label>
              <RadioGroup value={config.language} onValueChange={(value) => setConfig({ ...config, language: value as any })}>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="pt" id="lang-pt" />
                  <Label htmlFor="lang-pt" className="cursor-pointer">
                    Português (Brasil)
                  </Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="en" id="lang-en" />
                  <Label htmlFor="lang-en" className="cursor-pointer">
                    English (US)
                  </Label>
                </div>
              </RadioGroup>
            </div>
          </TabsContent>
        </Tabs>

        {/* Export Progress */}
        {isExporting && (
          <div className="space-y-3 p-4 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium text-blue-900">Gerando relatório...</span>
              <span className="text-sm font-semibold text-blue-700">{exportProgress}%</span>
            </div>
            <Progress value={exportProgress} className="h-2" />
            <p className="text-xs text-blue-700">
              {exportProgress < 30 && 'Coletando dados da pesquisa...'}
              {exportProgress >= 30 && exportProgress < 60 && 'Gerando tabelas estatísticas...'}
              {exportProgress >= 60 && exportProgress < 90 && 'Renderizando gráficos...'}
              {exportProgress >= 90 && 'Finalizando documento...'}
            </p>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex justify-between items-center pt-4 border-t">
          <Button variant="outline" onClick={handlePreview} disabled={isExporting}>
            <Eye className="mr-2 h-4 w-4" />
            Visualizar Preview
          </Button>

          <div className="flex gap-2">
            <Button variant="outline" onClick={() => setOpen(false)} disabled={isExporting}>
              Cancelar
            </Button>
            <Button
              onClick={handleExport}
              disabled={isExporting || getSelectedSectionsCount() === 0}
              style={{ backgroundColor: '#0A2647', color: 'white' }}
            >
              {isExporting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Exportando...
                </>
              ) : (
                <>
                  <Download className="mr-2 h-4 w-4" />
                  Exportar Relatório
                </>
              )}
            </Button>
          </div>
        </div>

        {/* File Size Estimate */}
        <div className="text-xs text-gray-500 text-center">
          Tamanho estimado do arquivo:{' '}
          {config.includeCharts && config.chartResolution === 'print'
            ? '5-15 MB'
            : config.includeCharts
            ? '2-5 MB'
            : '< 1 MB'}
        </div>
      </DialogContent>
    </Dialog>
  );
}

