interface ListProps extends React.HTMLAttributes<HTMLUListElement | HTMLOListElement> {
  ordered?: boolean
  variant?: "default" | "checkmarks" | "bullets" | "numbers"
}

export function List({
  ordered = false,
  variant = "default",
  className,
  children,
  ...props
}: ListProps) {
  const Component = ordered ? "ol" : "ul"

  const variantClasses = {
    default: ordered ? "list-decimal" : "list-disc",
    checkmarks: "",
    bullets: "list-disc",
    numbers: "list-decimal",
  }

  return (
    <Component
      className={cn(
        "space-y-2 pl-6 text-gray-700",
        variantClasses[variant],
        variant === "checkmarks" && "list-none pl-0",
        className
      )}
      {...props}
    >
      {variant === "checkmarks"
        ? React.Children.map(children, (child) => (
            <li className="flex items-start gap-2">
              <svg
                className="h-5 w-5 text-telos-success-500 mt-0.5 shrink-0"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M5 13l4 4L19 7"
                />
              </svg>
              {child}
            </li>
          ))
        : children}
    </Component>
  )
}

