    if (!allowedRoles.includes(user.role)) {
      return NextResponse.json(
        buildErrorResponse(
          'Forbidden',
          `This action requires one of the following roles: ${allowedRoles.join(', ')}`
        ),
        { status: 403 }
      );
    }

