  };
}

// ============================================
// RATE LIMITING MIDDLEWARE (Prepared for implementation)
// ============================================

interface RateLimitConfig {
  maxRequests: number;
  windowMs: number;
}

const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

/**
 * Rate limiting middleware - Basic implementation
 * For production, consider using a Redis-based solution
 */
export async function withRateLimit(
  request: NextRequest,
  config: RateLimitConfig,
  handler: (request: NextRequest) => Promise<NextResponse>
): Promise<NextResponse> {
  try {
    // Get client identifier (IP address or user ID)
    const identifier =
      request.headers.get('x-forwarded-for') ||
      request.headers.get('x-real-ip') ||
      'unknown';

    const now = Date.now();
    const rateLimitData = rateLimitStore.get(identifier);

    if (rateLimitData) {
      // Check if window has expired
      if (now > rateLimitData.resetTime) {
        // Reset the counter
        rateLimitStore.set(identifier, {
          count: 1,
          resetTime: now + config.windowMs,
        });
      } else if (rateLimitData.count >= config.maxRequests) {
        // Rate limit exceeded
        const retryAfter = Math.ceil((rateLimitData.resetTime - now) / 1000);
        return NextResponse.json(
          buildErrorResponse(
            'Rate limit exceeded',
            `Too many requests. Please try again in ${retryAfter} seconds.`,
            'RATE_LIMIT_EXCEEDED'
          ),
          {
            status: 429,
            headers: {
              'Retry-After': retryAfter.toString(),
              'X-RateLimit-Limit': config.maxRequests.toString(),
              'X-RateLimit-Remaining': '0',
              'X-RateLimit-Reset': rateLimitData.resetTime.toString(),
            },
          }
        );
      } else {
        // Increment counter
        rateLimitData.count++;
        rateLimitStore.set(identifier, rateLimitData);
      }
    } else {
      // First request from this identifier
      rateLimitStore.set(identifier, {
        count: 1,
        resetTime: now + config.windowMs,
      });
    }

    // Clean up old entries periodically (simple cleanup)
    if (rateLimitStore.size > 10000) {
      const keysToDelete: string[] = [];
      rateLimitStore.forEach((value, key) => {
        if (now > value.resetTime) {
          keysToDelete.push(key);
        }
      });
      keysToDelete.forEach((key) => rateLimitStore.delete(key));
    }

    return handler(request);
  } catch (error) {
    console.error('Rate limiting error:', error);
    // On error, allow the request to proceed
    return handler(request);
  }
}

// ============================================
// CORS MIDDLEWARE
// ============================================

export interface CorsConfig {
  allowedOrigins: string[];
  allowedMethods: string[];
  allowedHeaders: string[];
  credentials: boolean;
}

const defaultCorsConfig: CorsConfig = {
  allowedOrigins: ['*'],
  allowedMethods: ['GET', 'POST', 'PUT', 'PATCH', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization'],
  credentials: true,
};

export function withCors(
  request: NextRequest,
  handler: (request: NextRequest) => Promise<NextResponse>,
  config: Partial<CorsConfig> = {}
): Promise<NextResponse> {
  const corsConfig = { ...defaultCorsConfig, ...config };

  // Handle preflight requests
  if (request.method === 'OPTIONS') {
    return Promise.resolve(
      new NextResponse(null, {
        status: 204,
        headers: {
          'Access-Control-Allow-Origin': corsConfig.allowedOrigins.join(', '),
          'Access-Control-Allow-Methods': corsConfig.allowedMethods.join(', '),
          'Access-Control-Allow-Headers': corsConfig.allowedHeaders.join(', '),
          'Access-Control-Allow-Credentials': corsConfig.credentials.toString(),
          'Access-Control-Max-Age': '86400',
        },
      })
    );
  }

  return handler(request);
}

// ============================================
// REQUEST VALIDATION MIDDLEWARE
// ============================================

export async function withValidation<T>(
  request: NextRequest,
  schema: any,
  handler: (request: NextRequest, data: T) => Promise<NextResponse>
): Promise<NextResponse> {
  try {
    const body = await request.json();
    const validatedData = schema.parse(body);
    return handler(request, validatedData);
  } catch (error: any) {
    if (error.name === 'ZodError') {
      return NextResponse.json(
        buildErrorResponse(
          'Validation error',
          error.errors.map((e: any) => `${e.path.join('.')}: ${e.message}`).join(', '),
          'VALIDATION_ERROR'
        ),
        { status: 400 }
      );
    }

    return NextResponse.json(
      buildErrorResponse(
        'Invalid request body',
        error instanceof Error ? error.message : 'Unknown error',
        'INVALID_REQUEST'
      ),
      { status: 400 }
    );
  }
}

// ============================================
// ERROR HANDLING MIDDLEWARE
// ============================================

export async function withErrorHandler(
  handler: () => Promise<NextResponse>
): Promise<NextResponse> {
  try {
    return await handler();
  } catch (error) {
    console.error('API Error:', error);

    // Prisma errors
    if (error instanceof Error && error.message.includes('Prisma')) {
      return NextResponse.json(
        buildErrorResponse(
          'Database error',
          'An error occurred while accessing the database',
          'DATABASE_ERROR'
        ),
        { status: 500 }
      );
    }

    // Generic error
    return NextResponse.json(
      buildErrorResponse(
        'Internal server error',
        error instanceof Error ? error.message : 'Unknown error',
        'INTERNAL_ERROR'
      ),
      { status: 500 }
    );
  }
}

// ============================================
// LOGGING MIDDLEWARE
// ============================================

export async function withLogging(
  request: NextRequest,
  handler: (request: NextRequest) => Promise<NextResponse>
): Promise<NextResponse> {
  const startTime = Date.now();
  const method = request.method;
  const url = request.url;

  console.log(`[API] ${method} ${url} - Started`);

  try {
    const response = await handler(request);
    const duration = Date.now() - startTime;
    console.log(`[API] ${method} ${url} - ${response.status} (${duration}ms)`);
    return response;
  } catch (error) {
    const duration = Date.now() - startTime;
    console.error(`[API] ${method} ${url} - Error (${duration}ms)`, error);
    throw error;
  }
}

// ============================================
// COMPOSITE MIDDLEWARE
// ============================================

/**
 * Combine multiple middleware functions
 */
export function compose(
  ...middlewares: Array<
    (
      request: NextRequest,
      next: (request: NextRequest) => Promise<NextResponse>
    ) => Promise<NextResponse>
  >
) {
  return async (
    request: NextRequest,
    handler: (request: NextRequest) => Promise<NextResponse>
  ): Promise<NextResponse> => {
    let index = 0;

    const next = async (req: NextRequest): Promise<NextResponse> => {
      if (index >= middlewares.length) {
        return handler(req);
      }
      const middleware = middlewares[index++];
      return middleware(req, next);
    };

    return next(request);
  };
}

import { Patient, Surgery } from '@prisma/client';

// ============================================
// PAGINATION HELPERS
// ============================================

export interface PaginationParams {
  page: number;
  limit: number;
}

export interface PaginationResult {
  skip: number;
  take: number;
}

export function paginate(page: number = 1, limit: number = 10): PaginationResult {
  const validPage = Math.max(1, page);
  const validLimit = Math.min(Math.max(1, limit), 100); // Max 100 items per page

  return {
    skip: (validPage - 1) * validLimit,
    take: validLimit,
  };
}

export function buildPaginationMeta(
  total: number,
  page: number,
  limit: number
) {
  const totalPages = Math.ceil(total / limit);

  return {
    total,
    page,
    limit,
    totalPages,
    hasNextPage: page < totalPages,
    hasPreviousPage: page > 1,
  };
}

// ============================================
// SEARCH QUERY BUILDER
// ============================================

export function buildSearchQuery(search: string) {
  if (!search || search.trim() === '') {
    return {};
  }

  const searchTerm = search.trim();

  return {
    OR: [
      { name: { contains: searchTerm, mode: 'insensitive' as const } },
      { phone: { contains: searchTerm } },
      { cpf: { contains: searchTerm } },
    ],
  };
}

// ============================================
// COMPLETENESS CALCULATOR
// ============================================

export interface PatientWithRelations {
  comorbidities?: any[];
  medications?: any[];
  surgery?: {
    details?: any;
    anesthesia?: any;
    preOp?: any;
    postOp?: any;
  } | null;
}

export function calculateCompleteness(patient: PatientWithRelations): number {
  let percentage = 20; // Base from express form

  // +10% if comorbidities filled
  if (patient.comorbidities && patient.comorbidities.length > 0) {
    percentage += 10;
  }

  // +10% if medications filled
  if (patient.medications && patient.medications.length > 0) {
    percentage += 10;
  }

  // +20% if surgery details filled
  if (patient.surgery?.details) {
    percentage += 20;
  }

  // +15% if anesthesia filled
  if (patient.surgery?.anesthesia) {
    percentage += 15;
  }

  // +10% if pre-op filled
  if (patient.surgery?.preOp) {
    percentage += 10;
  }

  // +10% if prescription filled
  if (patient.surgery?.postOp) {
    percentage += 10;
  }

  // +5% if full description filled
  if (patient.surgery?.details?.fullDescription) {
    percentage += 5;
  }

  return Math.min(percentage, 100);
}

// ============================================
// VALIDATION HELPERS
// ============================================

export function isValidCuid(id: string): boolean {
  // CUID format: c + 24 alphanumeric characters
  return /^c[a-z0-9]{24}$/i.test(id);
}

export function isValidDate(dateString: string): boolean {
  const date = new Date(dateString);
  return date instanceof Date && !isNaN(date.getTime());
}

// ============================================
// ERROR RESPONSE BUILDER
// ============================================

export interface ApiError {
  error: string;
  details?: string;
  code?: string;
}

export function buildErrorResponse(
  error: string,
  details?: string,
  code?: string
): ApiError {
  return {
    error,
    details,
    code,
  };
}

// ============================================
// FILTER BUILDERS
// ============================================

export interface PatientFilters {
  surgeryType?: string;
  status?: string;
  completeness?: string; // low (<50), medium (50-79), high (>=80)
  dateFrom?: string;
  dateTo?: string;
}

export function buildPatientFilters(filters: PatientFilters) {
  const where: any = {};

  if (filters.surgeryType) {
    where.surgeries = {
      some: {
        type: filters.surgeryType,
      },
    };
  }

  if (filters.status) {
    where.surgeries = {
      some: {
        ...where.surgeries?.some,
        status: filters.status,
      },
    };
  }

  if (filters.dateFrom || filters.dateTo) {
    where.surgeries = {
      some: {
        ...where.surgeries?.some,
        date: {
          ...(filters.dateFrom && { gte: new Date(filters.dateFrom) }),
          ...(filters.dateTo && { lte: new Date(filters.dateTo) }),
        },
      },
    };
  }

  return where;
}

export function buildCompletenessFilter(completeness: string) {
  switch (completeness) {
    case 'low':
      return { lt: 50 };
    case 'medium':
      return { gte: 50, lt: 80 };
    case 'high':
      return { gte: 80 };
    default:
      return undefined;
  }
}

// ============================================
// TIMELINE HELPERS
// ============================================

export interface TimelineEvent {
  id: string;
  type: 'surgery' | 'followup' | 'consent' | 'update';
  date: Date;
  title: string;
  description: string;
  metadata?: any;
}

export function buildTimelineEvents(patient: any): TimelineEvent[] {
  const events: TimelineEvent[] = [];

  // Add surgery event
  if (patient.surgeries && patient.surgeries.length > 0) {
    patient.surgeries.forEach((surgery: any) => {
      events.push({
        id: surgery.id,
        type: 'surgery',
        date: surgery.date,
        title: `Cirurgia: ${surgery.type}`,
        description: surgery.hospital || 'Hospital não informado',
        metadata: {
          durationMinutes: surgery.durationMinutes,
          dataCompleteness: surgery.dataCompleteness,
        },
      });
    });
  }

  // Add follow-up events
  if (patient.followUps && patient.followUps.length > 0) {
    patient.followUps.forEach((followUp: any) => {
      events.push({
        id: followUp.id,
        type: 'followup',
        date: followUp.respondedAt || followUp.sentAt || followUp.scheduledDate,
        title: `Follow-up D+${followUp.dayNumber}`,
        description: `Status: ${followUp.status}`,
        metadata: {
          dayNumber: followUp.dayNumber,
          status: followUp.status,
          responseCount: followUp.responses?.length || 0,
        },
      });
    });
  }

  // Add consent terms
  if (patient.consentTerms && patient.consentTerms.length > 0) {
    patient.consentTerms.forEach((consent: any) => {
      events.push({
        id: consent.id,
        type: 'consent',
        date: consent.signedDate || consent.createdAt,
        title: `Termo: ${consent.termType}`,
        description: consent.signedPhysically ? 'Assinado fisicamente' : 'Não assinado',
        metadata: {
          termType: consent.termType,
          signedPhysically: consent.signedPhysically,
        },
      });
    });
  }

  // Sort by date (newest first)
  events.sort((a, b) => b.date.getTime() - a.date.getTime());

  return events;
}

// ============================================
// SANITIZATION
// ============================================

export function sanitizeInput(input: string): string {
  // Remove potential SQL injection characters and XSS
  return input.trim().replace(/[<>\"'`;]/g, '');
}

export function sanitizeSearchTerm(search: string): string {
  // Allow only alphanumeric, spaces, and common punctuation
  return search.trim().replace(/[^a-zA-Z0-9\s\-_@.]/g, '');
}

