// Função para anonimizar dados de um paciente
export function anonymizePatient(patient: PatientExportData, index: number): PatientExportData {
  return {
    ...patient,
    id: `P${String(index + 1).padStart(4, '0')}`, // P0001, P0002, etc
    name: undefined,
    cpf: undefined,
    phone: undefined,
    surgeries: patient.surgeries.map((surgery, surgIdx) => ({
      ...surgery,
      id: `S${String(index + 1).padStart(4, '0')}-${surgIdx + 1}`, // S0001-1, S0001-2, etc
    })),
  };
}

// Função para calcular idade a partir da data de nascimento
export function calculateAge(dateOfBirth: Date): number {
  const today = new Date();
  let age = today.getFullYear() - dateOfBirth.getFullYear();
  const monthDiff = today.getMonth() - dateOfBirth.getMonth();

  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < dateOfBirth.getDate())) {
    age--;
  }

  return age;
}

// Formatar dados para planilha de dados brutos
export function formatRawData(patients: PatientExportData[], filters: ExportFilters): any[] {
  const rows: any[] = [];

  patients.forEach((patient) => {
    patient.surgeries.forEach((surgery) => {
      const baseRow: any = {};

      // ID do paciente (sempre incluído)
      baseRow['ID_Paciente'] = patient.id;

      // Dados demográficos
      if (filters.fields.demographic) {
        baseRow['Idade'] = patient.age;
        baseRow['Sexo'] = patient.sex;
      }

      // Dados identificáveis (apenas se não anonimizado)
      if (filters.fields.identifiable && !filters.anonymize) {
        baseRow['Nome'] = patient.name;
        baseRow['CPF'] = patient.cpf;
        baseRow['Telefone'] = patient.phone;
      }

      // Detalhes cirúrgicos
      if (filters.fields.surgeryDetails) {
        baseRow['ID_Cirurgia'] = surgery.id;
        baseRow['Tipo_Cirurgia'] = surgery.type;
        baseRow['Data_Cirurgia'] = surgery.date.toISOString().split('T')[0];
        baseRow['Hospital'] = surgery.hospital;
        baseRow['Duracao_Min'] = surgery.durationMinutes;

        // Anestesia
        if (surgery.anesthesia) {
          baseRow['Tipo_Anestesia'] = surgery.anesthesia.type;
          baseRow['Bloqueio_Pudendo'] = surgery.anesthesia.pudendoBlock ? 'Sim' : 'Não';
        }

        // Pré-operatório
        if (surgery.preOp) {
          baseRow['Botox_Usado'] = surgery.preOp.botoxUsed ? 'Sim' : 'Não';
          baseRow['Preparo_Intestinal'] = surgery.preOp.intestinalPrep ? 'Sim' : 'Não';
        }
      }

      // Comorbidades
      if (filters.fields.comorbidities) {
        baseRow['Comorbidades'] = surgery.comorbidities.join('; ');
        baseRow['Num_Comorbidades'] = surgery.comorbidities.length;
      }

      // Complicações
      if (filters.fields.complications) {
        baseRow['Complicacoes'] = surgery.complications || '';
      }

      // NPS
      if (filters.fields.nps) {
        baseRow['NPS'] = surgery.nps;
      }

      // Trajetória de dor (D+1 a D+14)
      if (filters.fields.painTrajectory) {
        const painByDay: { [key: number]: number | null } = {};

        surgery.followUps.forEach((followUp) => {
          if (followUp.painLevel !== undefined && followUp.painLevel !== null) {
            painByDay[followUp.dayNumber] = followUp.painLevel;
          }
        });

        // Dias padrão de follow-up: D+1, D+2, D+3, D+5, D+7, D+10, D+14
        [1, 2, 3, 5, 7, 10, 14].forEach((day) => {
          baseRow[`Dor_D${day}`] = painByDay[day] ?? null;
        });

        // Estatísticas de dor
        const painValues = Object.values(painByDay).filter(v => v !== null) as number[];
        if (painValues.length > 0) {
          baseRow['Dor_Media'] = (painValues.reduce((a, b) => a + b, 0) / painValues.length).toFixed(2);
          baseRow['Dor_Maxima'] = Math.max(...painValues);
          baseRow['Dor_Minima'] = Math.min(...painValues);
        }
      }

      // Dados de follow-up
      const completedFollowUps = surgery.followUps.filter(f => f.status === 'responded').length;
      baseRow['FollowUps_Completos'] = completedFollowUps;
      baseRow['Taxa_Adesao'] = `${((completedFollowUps / 7) * 100).toFixed(1)}%`;

      // Red flags
      const allRedFlags = surgery.followUps
        .flatMap(f => f.redFlags || [])
        .filter((v, i, a) => a.indexOf(v) === i); // unique
      baseRow['Red_Flags'] = allRedFlags.join('; ');

      rows.push(baseRow);
    });
  });

  return rows;
}

// Formatar dados para planilha de estatísticas descritivas
export function formatStatistics(patients: PatientExportData[], filters: ExportFilters): any[] {
  const stats: any[] = [];

  // Estatísticas demográficas
  if (filters.fields.demographic) {
    const ages = patients.map(p => p.age).filter(a => a !== undefined) as number[];
    const sexCounts = patients.reduce((acc, p) => {
      if (p.sex) {
        acc[p.sex] = (acc[p.sex] || 0) + 1;
      }
      return acc;
    }, {} as Record<string, number>);

    stats.push({ Categoria: 'DADOS DEMOGRÁFICOS', Medida: '', Valor: '' });
    stats.push({ Categoria: 'Total de Pacientes', Medida: 'N', Valor: patients.length });

    if (ages.length > 0) {
      stats.push({ Categoria: 'Idade', Medida: 'Média', Valor: (ages.reduce((a, b) => a + b, 0) / ages.length).toFixed(1) });
      stats.push({ Categoria: 'Idade', Medida: 'Desvio Padrão', Valor: calculateStdDev(ages).toFixed(1) });
      stats.push({ Categoria: 'Idade', Medida: 'Mínima', Valor: Math.min(...ages) });
      stats.push({ Categoria: 'Idade', Medida: 'Máxima', Valor: Math.max(...ages) });
      stats.push({ Categoria: 'Idade', Medida: 'Mediana', Valor: calculateMedian(ages).toFixed(1) });
    }

    Object.entries(sexCounts).forEach(([sex, count]) => {
      stats.push({ Categoria: 'Sexo', Medida: sex, Valor: `${count} (${((count / patients.length) * 100).toFixed(1)}%)` });
    });

    stats.push({ Categoria: '', Medida: '', Valor: '' });
  }

  // Estatísticas por tipo de cirurgia
  if (filters.fields.surgeryDetails) {
    const allSurgeries = patients.flatMap(p => p.surgeries);
    const surgeryTypeCounts = allSurgeries.reduce((acc, s) => {
      acc[s.type] = (acc[s.type] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    stats.push({ Categoria: 'TIPO DE CIRURGIA', Medida: '', Valor: '' });
    Object.entries(surgeryTypeCounts).forEach(([type, count]) => {
      stats.push({ Categoria: type, Medida: 'N', Valor: `${count} (${((count / allSurgeries.length) * 100).toFixed(1)}%)` });
    });

    stats.push({ Categoria: '', Medida: '', Valor: '' });
  }

  // Estatísticas de dor
  if (filters.fields.painTrajectory) {
    stats.push({ Categoria: 'TRAJETÓRIA DE DOR', Medida: '', Valor: '' });

    [1, 2, 3, 5, 7, 10, 14].forEach((day) => {
      const painValues: number[] = [];

      patients.forEach(patient => {
        patient.surgeries.forEach(surgery => {
          const followUp = surgery.followUps.find(f => f.dayNumber === day);
          if (followUp?.painLevel !== undefined && followUp?.painLevel !== null) {
            painValues.push(followUp.painLevel);
          }
        });
      });

      if (painValues.length > 0) {
        stats.push({
          Categoria: `Dor D+${day}`,
          Medida: 'Média ± DP',
          Valor: `${(painValues.reduce((a, b) => a + b, 0) / painValues.length).toFixed(2)} ± ${calculateStdDev(painValues).toFixed(2)}`
        });
        stats.push({
          Categoria: `Dor D+${day}`,
          Medida: 'Mediana [Min-Max]',
          Valor: `${calculateMedian(painValues).toFixed(1)} [${Math.min(...painValues)}-${Math.max(...painValues)}]`
        });
        stats.push({
          Categoria: `Dor D+${day}`,
          Medida: 'N respostas',
          Valor: painValues.length
        });
      }
    });

    stats.push({ Categoria: '', Medida: '', Valor: '' });
  }

  // Estatísticas de comorbidades
  if (filters.fields.comorbidities) {
    const allComorbidities = patients.flatMap(p =>
      p.surgeries.flatMap(s => s.comorbidities)
    );
    const comorbidityNames = allComorbidities.filter((v, i, a) => a.indexOf(v) === i);
    const comorbidCounts = comorbidityNames.reduce((acc, name) => {
      acc[name] = allComorbidities.filter(c => c === name).length;
      return acc;
    }, {} as Record<string, number>);

    stats.push({ Categoria: 'COMORBIDADES', Medida: '', Valor: '' });
    Object.entries(comorbidCounts)
      .sort((a, b) => b[1] - a[1])
      .forEach(([name, count]) => {
        stats.push({
          Categoria: name,
          Medida: 'Frequência',
          Valor: `${count} (${((count / patients.length) * 100).toFixed(1)}%)`
        });
      });

    stats.push({ Categoria: '', Medida: '', Valor: '' });
  }

  // Estatísticas de NPS
  if (filters.fields.nps) {
    const npsValues = patients.flatMap(p =>
      p.surgeries.map(s => s.nps).filter(n => n !== undefined)
    ) as number[];

    if (npsValues.length > 0) {
      stats.push({ Categoria: 'NET PROMOTER SCORE (NPS)', Medida: '', Valor: '' });
      stats.push({
        Categoria: 'NPS',
        Medida: 'Média',
        Valor: (npsValues.reduce((a, b) => a + b, 0) / npsValues.length).toFixed(1)
      });
      stats.push({
        Categoria: 'NPS',
        Medida: 'N respostas',
        Valor: npsValues.length
      });

      const promoters = npsValues.filter(n => n >= 9).length;
      const passives = npsValues.filter(n => n >= 7 && n < 9).length;
      const detractors = npsValues.filter(n => n < 7).length;

      stats.push({ Categoria: 'Promotores (9-10)', Medida: '%', Valor: ((promoters / npsValues.length) * 100).toFixed(1) });
      stats.push({ Categoria: 'Neutros (7-8)', Medida: '%', Valor: ((passives / npsValues.length) * 100).toFixed(1) });
      stats.push({ Categoria: 'Detratores (0-6)', Medida: '%', Valor: ((detractors / npsValues.length) * 100).toFixed(1) });
      stats.push({
        Categoria: 'NPS Score',
        Medida: '(% Promotores - % Detratores)',
        Valor: (((promoters - detractors) / npsValues.length) * 100).toFixed(1)
      });
    }
  }

  return stats;
}

// Formatar dados para matriz de trajetória de dor (paciente x dia)
export function formatPainTrajectory(patients: PatientExportData[]): any[] {
  const rows: any[] = [];

  patients.forEach((patient) => {
    patient.surgeries.forEach((surgery) => {
      const row: any = {
        ID_Paciente: patient.id,
        ID_Cirurgia: surgery.id,
        Tipo_Cirurgia: surgery.type,
        Data_Cirurgia: surgery.date.toISOString().split('T')[0],
      };

      // Adicionar dor para cada dia
      [1, 2, 3, 5, 7, 10, 14].forEach((day) => {
        const followUp = surgery.followUps.find(f => f.dayNumber === day);
        row[`D+${day}`] = followUp?.painLevel ?? null;
      });

      rows.push(row);
    });
  });

  return rows;
}

// Calcular desvio padrão
function calculateStdDev(values: number[]): number {
  if (values.length === 0) return 0;

  const mean = values.reduce((a, b) => a + b, 0) / values.length;
  const squaredDiffs = values.map(v => Math.pow(v - mean, 2));
  const variance = squaredDiffs.reduce((a, b) => a + b, 0) / values.length;

  return Math.sqrt(variance);
}

// Calcular mediana
function calculateMedian(values: number[]): number {
  if (values.length === 0) return 0;

  const sorted = [...values].sort((a, b) => a - b);
  const mid = Math.floor(sorted.length / 2);

  if (sorted.length % 2 === 0) {
    return (sorted[mid - 1] + sorted[mid]) / 2;
  } else {
    return sorted[mid];
  }
}

// Gerar arquivo Excel com múltiplas abas
export async function generateExcelFile(patients: PatientExportData[], filters: ExportFilters): Promise<Buffer> {
  // Dynamic import to reduce initial bundle size
  const XLSX = await import('xlsx');

  const workbook = XLSX.utils.book_new();

  // Aba 1: Dados brutos
  const rawData = formatRawData(patients, filters);
  const rawSheet = XLSX.utils.json_to_sheet(rawData);
  XLSX.utils.book_append_sheet(workbook, rawSheet, 'Dados Brutos');

  // Aba 2: Estatísticas descritivas
  const statsData = formatStatistics(patients, filters);
  const statsSheet = XLSX.utils.json_to_sheet(statsData);
  XLSX.utils.book_append_sheet(workbook, statsSheet, 'Estatísticas');

  // Aba 3: Trajetória de dor (se selecionado)
  if (filters.fields.painTrajectory) {
    const painData = formatPainTrajectory(patients);
    const painSheet = XLSX.utils.json_to_sheet(painData);
    XLSX.utils.book_append_sheet(workbook, painSheet, 'Trajetória de Dor');
  }

  // Converter para buffer
  const excelBuffer = XLSX.write(workbook, { type: 'buffer', bookType: 'xlsx' });
  return excelBuffer;
}

// Gerar arquivo CSV
export async function generateCSVFile(patients: PatientExportData[], filters: ExportFilters): Promise<string> {
  // Dynamic import to reduce initial bundle size
  const XLSX = await import('xlsx');

  const rawData = formatRawData(patients, filters);
  const workbook = XLSX.utils.book_new();
  const worksheet = XLSX.utils.json_to_sheet(rawData);
  XLSX.utils.book_append_sheet(workbook, worksheet, 'Dados');

  // Converter para CSV
  const csv = XLSX.utils.sheet_to_csv(worksheet);
  return csv;
}

/**
 * Follow-up Analyzer - Sistema de Análise com Claude AI
 * Analisa respostas de follow-up e gera insights clínicos
 *
 * Funcionalidades:
 * - Análise de respostas via Claude AI
 * - Detecção automática de red flags
 * - Geração de respostas empáticas
 * - Alertas ao médico quando necessário
 */

import Anthropic from '@anthropic-ai/sdk';
import { getAnalysisPrompt, type AnalysisPromptParams } from './ai-prompts';
import { SurgeryType, detectRedFlags } from './surgery-templates';

// ============================================
// TYPES
// ============================================

export interface AnalysisResult {
  status: 'NORMAL' | 'ATENÇÃO' | 'URGENTE' | 'EMERGÊNCIA';
  riskLevel: 'low' | 'medium' | 'high' | 'critical';
  redFlags: string[];
  analise: string;
  raciocinioClinico?: string;
  recomendacoes: string[];
  respostaEmpática: string;
  alertarMedico: boolean;
  urgencia: 'baixa' | 'média' | 'alta' | 'crítica';
}

export interface FollowUpAnalysisParams {
  surgeryType: SurgeryType;
  dayNumber: number;
  answers: Record<string, any>;
  patientName: string;
  patientAge?: number;
  hasComorbidities?: boolean;
  useCache?: boolean;
}

// ============================================
// CONFIGURAÇÃO ANTHROPIC
// ============================================

const ANTHROPIC_API_KEY = process.env.ANTHROPIC_API_KEY;
const MODEL = 'claude-sonnet-4-5-20250929';

/**
 * Verifica se a API está configurada
 */
export function isAIConfigured(): boolean {
  return !!ANTHROPIC_API_KEY;
}

/**
 * Cria cliente Anthropic
 */
function getAnthropicClient(): Anthropic {
  if (!ANTHROPIC_API_KEY) {
    throw new Error('ANTHROPIC_API_KEY não configurada');
  }

  return new Anthropic({
    apiKey: ANTHROPIC_API_KEY,
  });
}

// ============================================
// ANÁLISE PRINCIPAL
// ============================================

/**
 * Analisa respostas de follow-up usando Claude AI
 */
export async function analyzeFollowUpResponse(
  params: FollowUpAnalysisParams
): Promise<AnalysisResult> {
  try {
    const { surgeryType, dayNumber, answers, patientName, patientAge, hasComorbidities, useCache = true } = params;

    // Verificar configuração
    if (!isAIConfigured()) {
      console.warn('AI não configurada, usando análise simplificada');
      return getSimplifiedAnalysis(params);
    }

    // Detectar red flags localmente primeiro
    const localRedFlags = detectRedFlags(surgeryType, dayNumber, answers);

    // Gerar prompt
    const prompt = getAnalysisPrompt({
      surgeryType,
      dayNumber,
      answers,
      patientName,
      patientAge,
      hasComorbidities,
    });

    // Chamar Claude AI
    const anthropic = getAnthropicClient();

    const message = await anthropic.messages.create({
      model: MODEL,
      max_tokens: 2048,
      temperature: 0.3, // Mais conservador para análise médica
      messages: [
        {
          role: 'user',
          content: prompt,
        },
      ],
    });

    // Extrair resposta
    const responseText = message.content[0].type === 'text'
      ? message.content[0].text
      : '';

    // Parse JSON da resposta
    let analysis: AnalysisResult;
    try {
      // Remover markdown se presente
      const cleanedResponse = responseText
        .replace(/```json\n?/g, '')
        .replace(/```\n?/g, '')
        .trim();

      analysis = JSON.parse(cleanedResponse);
    } catch (parseError) {
      console.error('Erro ao fazer parse da resposta AI:', parseError);
      console.error('Resposta recebida:', responseText);

      // Fallback para análise simplificada
      return getSimplifiedAnalysis(params);
    }

    // Validar e enriquecer resultado
    analysis = validateAndEnrichAnalysis(analysis, localRedFlags);

    // Log para auditoria
    console.log('Análise AI completada:', {
      surgeryType,
      day: dayNumber,
      status: analysis.status,
      riskLevel: analysis.riskLevel,
      redFlagsCount: analysis.redFlags.length,
    });

    return analysis;

  } catch (error) {
    console.error('Erro na análise de follow-up:', error);

    // Fallback para análise simplificada em caso de erro
    return getSimplifiedAnalysis(params);
  }
}

/**
 * Análise simplificada sem IA (fallback)
 */
function getSimplifiedAnalysis(params: FollowUpAnalysisParams): AnalysisResult {
  const { surgeryType, dayNumber, answers, patientName } = params;

  // Detectar red flags localmente
  const redFlags = detectRedFlags(surgeryType, dayNumber, answers);

  // Determinar nível de risco baseado em red flags
  let riskLevel: AnalysisResult['riskLevel'] = 'low';
  let status: AnalysisResult['status'] = 'NORMAL';
  let urgencia: AnalysisResult['urgencia'] = 'baixa';
  let alertarMedico = false;

  // Análise de dor
  const painLevel = answers.dor !== undefined ? parseInt(answers.dor) : 0;

  // Análise de febre
  const hasFebre = answers.febre === true || answers.febre === 'Sim';
  const temperatura = answers.temperatura ? parseFloat(answers.temperatura) : 0;

  // Análise de sangramento
  const sangramento = answers.sangramento || 'Não';

  if (redFlags.length >= 3 || hasFebre && temperatura >= 38.5 || sangramento === 'Intenso') {
    riskLevel = 'critical';
    status = 'EMERGÊNCIA';
    urgencia = 'crítica';
    alertarMedico = true;
  } else if (redFlags.length >= 2 || hasFebre || sangramento === 'Moderado' || painLevel >= 9) {
    riskLevel = 'high';
    status = 'URGENTE';
    urgencia = 'alta';
    alertarMedico = true;
  } else if (redFlags.length >= 1 || painLevel >= 7) {
    riskLevel = 'medium';
    status = 'ATENÇÃO';
    urgencia = 'média';
    alertarMedico = false;
  }

  // Gerar mensagens
  const analise = `Análise automática (IA não disponível): ${redFlags.length} red flag(s) detectado(s). ` +
    `Dor: ${painLevel}/10, Sangramento: ${sangramento}, Febre: ${hasFebre ? 'Sim' : 'Não'}.`;

  const recomendacoes = generateSimpleRecommendations(status, redFlags, answers);

  const respostaEmpática = generateSimpleEmpatheticResponse(
    patientName.split(' ')[0],
    status,
    dayNumber
  );

  return {
    status,
    riskLevel,
    redFlags: redFlags.map(flag => `Red flag detectado: ${flag}`),
    analise,
    recomendacoes,
    respostaEmpática,
    alertarMedico,
    urgencia,
  };
}

/**
 * Valida e enriquece resultado da análise AI
 */
function validateAndEnrichAnalysis(
  analysis: AnalysisResult,
  localRedFlags: string[]
): AnalysisResult {
  // Garantir que todos os campos obrigatórios existem
  const validated: AnalysisResult = {
    status: analysis.status || 'ATENÇÃO',
    riskLevel: analysis.riskLevel || 'medium',
    redFlags: analysis.redFlags || [],
    analise: analysis.analise || 'Análise não disponível',
    raciocinioClinico: analysis.raciocinioClinico,
    recomendacoes: analysis.recomendacoes || [],
    respostaEmpática: analysis.respostaEmpática || 'Obrigado por responder. Em breve retornaremos.',
    alertarMedico: analysis.alertarMedico ?? false,
    urgencia: analysis.urgencia || 'baixa',
  };

  // Adicionar red flags locais que podem ter sido perdidos
  localRedFlags.forEach(flag => {
    if (!validated.redFlags.some(rf => rf.includes(flag))) {
      validated.redFlags.push(`Detectado localmente: ${flag}`);
    }
  });

  // Ajustar alertarMedico baseado em status
  if (validated.status === 'URGENTE' || validated.status === 'EMERGÊNCIA') {
    validated.alertarMedico = true;
  }

  return validated;
}

/**
 * Gera recomendações simples
 */
function generateSimpleRecommendations(
  status: AnalysisResult['status'],
  redFlags: string[],
  answers: Record<string, any>
): string[] {
  const recommendations: string[] = [];

  if (status === 'EMERGÊNCIA') {
    recommendations.push('⚠️ ATENÇÃO: Procure atendimento médico IMEDIATAMENTE');
    recommendations.push('Vá ao pronto-socorro ou entre em contato com seu médico AGORA');
    return recommendations;
  }

  if (status === 'URGENTE') {
    recommendations.push('Entre em contato com seu médico o mais breve possível');
    recommendations.push('Não aguarde a consulta de retorno agendada');
  }

  if (answers.dor >= 8) {
    recommendations.push('Tomar medicação para dor conforme prescrito');
    recommendations.push('Se dor não melhorar, entrar em contato com médico');
  }

  if (answers.febre === true) {
    recommendations.push('Medir temperatura a cada 4 horas');
    recommendations.push('Se febre > 38°C persistir, contactar médico');
  }

  if (answers.sangramento === 'Moderado' || answers.sangramento === 'Intenso') {
    recommendations.push('Observar quantidade de sangramento');
    recommendations.push('Se aumentar ou não parar, procurar atendimento');
  }

  if (answers.medicacoes !== 'Sim, todas') {
    recommendations.push('Importante tomar todas as medicações prescritas');
    recommendations.push('Seguir horários e dosagens recomendadas');
  }

  if (recommendations.length === 0) {
    recommendations.push('Continuar cuidados habituais');
    recommendations.push('Manter higiene local adequada');
    recommendations.push('Tomar medicações conforme orientado');
    recommendations.push('Retorno conforme agendado');
  }

  return recommendations;
}

/**
 * Gera resposta empática simples
 */
function generateSimpleEmpatheticResponse(
  firstName: string,
  status: AnalysisResult['status'],
  dayNumber: number
): string {
  if (status === 'EMERGÊNCIA') {
    return `${firstName}, sua situação requer atenção médica IMEDIATA. ` +
      `Por favor, procure o pronto-socorro ou entre em contato com seu médico AGORA. ` +
      `Não espere. Sua saúde é prioridade.`;
  }

  if (status === 'URGENTE') {
    return `${firstName}, obrigado por responder. Identifiquei alguns sinais que precisam de atenção médica. ` +
      `Por favor, entre em contato com seu médico o mais breve possível, sem aguardar a consulta de retorno. ` +
      `Ele poderá avaliar melhor sua situação e orientar os próximos passos.`;
  }

  if (status === 'ATENÇÃO') {
    return `${firstName}, obrigado por responder ao questionário do dia ${dayNumber}. ` +
      `Alguns pontos merecem atenção, mas não há urgência. Continue tomando suas medicações corretamente. ` +
      `Se houver piora dos sintomas, entre em contato com seu médico. Continue se cuidando!`;
  }

  return `${firstName}, obrigado por responder! Sua recuperação está dentro do esperado para o dia ${dayNumber}. ` +
    `Continue com os cuidados e medicações conforme orientado. ` +
    `Qualquer dúvida ou mudança significativa, estou aqui para ajudar. Melhoras!`;
}

// ============================================
// FUNÇÕES AUXILIARES
// ============================================

/**
 * Converte nível de risco para cor
 */
export function getRiskLevelColor(riskLevel: AnalysisResult['riskLevel']): string {
  const colors: Record<AnalysisResult['riskLevel'], string> = {
    low: '#22c55e', // green-500
    medium: '#f59e0b', // amber-500
    high: '#ef4444', // red-500
    critical: '#dc2626', // red-600
  };

  return colors[riskLevel];
}

/**
 * Converte status para emoji
 */
export function getStatusEmoji(status: AnalysisResult['status']): string {
  const emojis: Record<AnalysisResult['status'], string> = {
    'NORMAL': '✅',
    'ATENÇÃO': '⚠️',
    'URGENTE': '🚨',
    'EMERGÊNCIA': '🆘',
  };

  return emojis[status];
}

/**
 * Formata análise para WhatsApp
 */
export function formatAnalysisForWhatsApp(analysis: AnalysisResult): string {
  const emoji = getStatusEmoji(analysis.status);

  let message = `${emoji} *${analysis.status}*\n\n`;
  message += `${analysis.respostaEmpática}\n\n`;

  if (analysis.recomendacoes.length > 0) {
    message += `*Recomendações:*\n`;
    analysis.recomendacoes.forEach((rec, i) => {
      message += `${i + 1}. ${rec}\n`;
    });
  }

  return message;
}

/**
 * Formata análise para dashboard médico
 */
export function formatAnalysisForDashboard(analysis: AnalysisResult): string {
  let html = `<div class="analysis-result risk-${analysis.riskLevel}">`;
  html += `<h3>${getStatusEmoji(analysis.status)} ${analysis.status}</h3>`;
  html += `<p><strong>Análise:</strong> ${analysis.analise}</p>`;

  if (analysis.redFlags.length > 0) {
    html += `<div class="red-flags">`;
    html += `<h4>🚩 Red Flags Detectados:</h4><ul>`;
    analysis.redFlags.forEach(flag => {
      html += `<li>${flag}</li>`;
    });
    html += `</ul></div>`;
  }

  if (analysis.recomendacoes.length > 0) {
    html += `<div class="recommendations">`;
    html += `<h4>💡 Recomendações:</h4><ul>`;
    analysis.recomendacoes.forEach(rec => {
      html += `<li>${rec}</li>`;
    });
    html += `</ul></div>`;
  }

  html += `</div>`;
  return html;
}

/**
 * Machine Learning Prediction Service
 *
 * Integra predições de risco de complicações pós-operatórias
 * usando modelo de ML externo via API Python.
 *
 * IMPORTANTE: Este serviço NÃO bloqueia o fluxo principal.
 * Se a API ML falhar, o cadastro continua normalmente.
 */

import { Patient, Surgery } from '@prisma/client'

// ============================================
// TIPOS E INTERFACES
// ============================================

export interface MLPredictionInput {
  // Dados do paciente
  age?: number | null
  sex?: string | null

  // Tipo de cirurgia
  surgeryType: string

  // Comorbidades (se disponível)
  hasComorbidities?: boolean
  comorbidityCount?: number

  // Medicações (se disponível)
  medicationCount?: number

  // Outros fatores de risco
  [key: string]: any
}

export interface MLPredictionResult {
  risk: number // 0.0 a 1.0
  level: 'low' | 'medium' | 'high'
  features: {
    importance: Record<string, number>
    values: Record<string, any>
  }
  modelVersion: string
  timestamp: Date
}

export interface MLPredictionError {
  error: string
  timestamp: Date
}

// ============================================
// CONFIGURAÇÕES
// ============================================

const ML_API_BASE_URL = process.env.ML_API_URL || 'http://localhost:8000'
const ML_API_TIMEOUT = 5000 // 5 segundos
const ML_MODEL_VERSION = process.env.ML_MODEL_VERSION || '1.0.0'

// Thresholds para classificação de risco
const RISK_THRESHOLDS = {
  LOW: 0.3,    // 0.0 - 0.3 = baixo risco
  MEDIUM: 0.6, // 0.3 - 0.6 = médio risco
  // > 0.6 = alto risco
}

// ============================================
// FUNÇÕES AUXILIARES
// ============================================

/**
 * Classifica o nível de risco baseado na probabilidade
 */
function classifyRiskLevel(probability: number): 'low' | 'medium' | 'high' {
  if (probability < RISK_THRESHOLDS.LOW) return 'low'
  if (probability < RISK_THRESHOLDS.MEDIUM) return 'medium'
  return 'high'
}

/**
 * Prepara os dados do paciente para enviar ao modelo ML
 */
function prepareMLInput(
  surgery: Surgery,
  patient: Patient,
  additionalData?: any
): MLPredictionInput {
  return {
    age: patient.age,
    sex: patient.sex,
    surgeryType: surgery.type,
    hasComorbidities: additionalData?.comorbidityCount > 0,
    comorbidityCount: additionalData?.comorbidityCount || 0,
    medicationCount: additionalData?.medicationCount || 0,
    // Adicione outros campos conforme necessário
    ...additionalData,
  }
}

/**
 * Faz chamada HTTP com timeout
 */
async function fetchWithTimeout(
  url: string,
  options: RequestInit,
  timeout: number
): Promise<Response> {
  const controller = new AbortController()
  const timeoutId = setTimeout(() => controller.abort(), timeout)

  try {
    const response = await fetch(url, {
      ...options,
      signal: controller.signal,
    })
    clearTimeout(timeoutId)
    return response
  } catch (error) {
    clearTimeout(timeoutId)
    throw error
  }
}

// ============================================
// FUNÇÃO PRINCIPAL DE PREDIÇÃO
// ============================================

/**
 * Prediz o risco de complicações pós-operatórias
 *
 * @param surgery - Dados da cirurgia
 * @param patient - Dados do paciente
 * @param additionalData - Dados adicionais (comorbidades, medicações, etc)
 * @returns Resultado da predição ou null se falhar
 *
 * IMPORTANTE: Esta função é não-bloqueante. Se falhar, retorna null
 * e o sistema continua funcionando normalmente.
 */
export async function predictComplicationRisk(
  surgery: Surgery,
  patient: Patient,
  additionalData?: any
): Promise<MLPredictionResult | null> {
  const startTime = Date.now()

  try {
    // Preparar dados de entrada
    const input = prepareMLInput(surgery, patient, additionalData)

    console.log('[ML] Iniciando predição de risco:', {
      patientId: patient.id,
      surgeryId: surgery.id,
      surgeryType: surgery.type,
      timestamp: new Date().toISOString(),
    })

    // Chamar API de ML
    const response = await fetchWithTimeout(
      `${ML_API_BASE_URL}/api/ml/predict`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(input),
      },
      ML_API_TIMEOUT
    )

    if (!response.ok) {
      throw new Error(`ML API retornou status ${response.status}`)
    }

    const data = await response.json()

    // Validar resposta
    if (typeof data.risk !== 'number' || data.risk < 0 || data.risk > 1) {
      throw new Error('Resposta inválida da API ML: risco fora do intervalo [0,1]')
    }

    // Classificar nível de risco
    const riskLevel = classifyRiskLevel(data.risk)

    const result: MLPredictionResult = {
      risk: data.risk,
      level: riskLevel,
      features: {
        importance: data.feature_importance || {},
        values: input,
      },
      modelVersion: data.model_version || ML_MODEL_VERSION,
      timestamp: new Date(),
    }

    const elapsedTime = Date.now() - startTime
    console.log('[ML] Predição concluída com sucesso:', {
      patientId: patient.id,
      surgeryId: surgery.id,
      risk: result.risk,
      level: result.level,
      elapsedTimeMs: elapsedTime,
    })

    return result

  } catch (error) {
    const elapsedTime = Date.now() - startTime

    // Log de erro mas NÃO propaga (não-bloqueante)
    console.error('[ML] Erro ao predizer risco (não-bloqueante):', {
      patientId: patient.id,
      surgeryId: surgery.id,
      error: error instanceof Error ? error.message : String(error),
      elapsedTimeMs: elapsedTime,
    })

    // Retornar null em caso de erro
    return null
  }
}

// ============================================
// PREDIÇÃO ASYNC (FIRE-AND-FORGET)
// ============================================

/**
 * Executa predição em background sem bloquear o fluxo
 * Ideal para usar após cadastro de paciente
 *
 * @param surgery - Dados da cirurgia
 * @param patient - Dados do paciente
 * @param onSuccess - Callback opcional para sucesso
 * @param onError - Callback opcional para erro
 */
export function predictComplicationRiskAsync(
  surgery: Surgery,
  patient: Patient,
  additionalData?: any,
  onSuccess?: (result: MLPredictionResult) => void,
  onError?: (error: Error) => void
): void {
  // Fire-and-forget: não espera resultado
  predictComplicationRisk(surgery, patient, additionalData)
    .then((result) => {
      if (result && onSuccess) {
        onSuccess(result)
      }
    })
    .catch((error) => {
      if (onError) {
        onError(error)
      }
    })
}

// ============================================
// UTILIDADES PARA VISUALIZAÇÃO
// ============================================

/**
 * Formata o risco como percentual para exibição
 */
export function formatRiskPercentage(risk: number): string {
  return `${Math.round(risk * 100)}%`
}

/**
 * Retorna a cor apropriada para o nível de risco
 */
export function getRiskColor(level: 'low' | 'medium' | 'high'): string {
  const colors = {
    low: '#22c55e',    // green-500
    medium: '#eab308', // yellow-500
    high: '#ef4444',   // red-500
  }
  return colors[level]
}

/**
 * Retorna o texto descritivo para o nível de risco
 */
export function getRiskLabel(level: 'low' | 'medium' | 'high'): string {
  const labels = {
    low: 'Baixo Risco',
    medium: 'Risco Moderado',
    high: 'Alto Risco',
  }
  return labels[level]
}

/**
 * Retorna os principais fatores de risco ordenados por importância
 */
export function getTopRiskFactors(
  features: MLPredictionResult['features'],
  topN: number = 5
): Array<{ name: string; importance: number; value: any }> {
  const { importance, values } = features

  return Object.entries(importance)
    .map(([name, importanceValue]) => ({
      name,
      importance: importanceValue,
      value: values[name],
    }))
    .sort((a, b) => b.importance - a.importance)
    .slice(0, topN)
}

