/**
 * Gera um resumo legível do template
 */
export function getTemplateSummary(templateData: TemplateData): string[] {
  const summary: string[] = []

  // Anestesia
  if (templateData.anesthesia?.type) {
    summary.push(`Anestesia: ${templateData.anesthesia.type}`)
    if (templateData.anesthesia.pudendoBlock) {
      summary.push(`Bloqueio pudendo: ${templateData.anesthesia.pudendoTechnique}`)
    }
  }

  // Detalhes cirúrgicos
  if (templateData.surgeryDetails) {
    const details = templateData.surgeryDetails

    if (details.hemorrhoidTechnique?.length) {
      summary.push(`Técnica: ${details.hemorrhoidTechnique.join(", ")}`)
    }
    if (details.hemorrhoidEnergyType) {
      summary.push(`Energia: ${details.hemorrhoidEnergyType}`)
    }
    if (details.fistulaType) {
      summary.push(`Tipo: ${details.fistulaType}`)
    }
    if (details.fistulaTechnique) {
      summary.push(`Técnica: ${details.fistulaTechnique}`)
    }
  }

  // Prescrição
  if (templateData.prescription) {
    const { ointments, medications } = templateData.prescription

    if (ointments?.length) {
      summary.push(`${ointments.length} pomada(s)`)
    }
    if (medications?.length) {
      summary.push(`${medications.length} medicação(ões)`)
    }
  }

  return summary
}

/**
 * WhatsApp Business API Client
 * Integração com Meta Cloud API para envio de mensagens
 */

import { Patient, Surgery, FollowUp } from '@prisma/client';

import { prisma } from '@/lib/prisma';

const WHATSAPP_API_URL = 'https://graph.facebook.com/v21.0';
const PHONE_NUMBER_ID = process.env.WHATSAPP_PHONE_NUMBER_ID!;

/**
 * Obtém o token do WhatsApp do banco de dados ou variável de ambiente
 */
async function getWhatsAppToken(): Promise<string> {
  try {
    // Tentar buscar do banco primeiro
    const config = await prisma.systemConfig.findUnique({
      where: { key: 'WHATSAPP_ACCESS_TOKEN' }
    });

    if (config?.value) {
      return config.value;
    }
  } catch (error) {
    console.warn('⚠️ Failed to fetch WhatsApp token from DB, falling back to env var:', error);
  }

  // Fallback para variável de ambiente
  return process.env.WHATSAPP_ACCESS_TOKEN || '';
}

export interface WhatsAppMessage {
  to: string;
  type: 'text' | 'template' | 'interactive' | 'image';
  text?: {
    body: string;
  };
  template?: {
    name: string;
    language: {
      code: string;
    };
    components?: any[];
  };
  interactive?: {
    type: 'button' | 'list';
    body: {
      text: string;
    };
    action: any;
  };
  image?: {
    link: string;
    caption?: string;
  };
}

export interface WhatsAppResponse {
  messaging_product: string;
  contacts: Array<{
    input: string;
    wa_id: string;
  }>;
  messages: Array<{
    id: string;
  }>;
}

/**
 * Envia imagem via WhatsApp
 */
export async function sendImage(
  to: string,
  imageUrl: string,
  caption?: string
): Promise<WhatsAppResponse> {
  try {
    const formattedPhone = formatPhoneNumber(to);
    const token = await getWhatsAppToken();

    console.log('📸 Sending WhatsApp image:', {
      to: formattedPhone,
      imageUrl,
      hasCaption: !!caption,
    });

    const payload = {
      messaging_product: 'whatsapp',
      to: formattedPhone,
      type: 'image',
      image: {
        link: imageUrl,
        ...(caption && { caption })
      }
    };

    const response = await fetch(
      `${WHATSAPP_API_URL}/${PHONE_NUMBER_ID}/messages`,
      {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      }
    );

    if (!response.ok) {
      const error = await response.json();
      console.error('❌ WhatsApp Image API Error:', error);
      throw new Error(`WhatsApp Image API Error: ${JSON.stringify(error)}`);
    }

    const data = await response.json();
    console.log('✅ WhatsApp image sent successfully:', data);
    return data;
  } catch (error) {
    console.error('❌ Error sending WhatsApp image:', error);
    throw error;
  }
