/**
 * Create figure with caption
 */
function createFigure(chart: ChartImage): (Paragraph | ImageRun)[] {
  const elements: any[] = [];

  // Image
  elements.push(
    new Paragraph({
      children: [
        new ImageRun({
          data: chart.data,
          transformation: {
            width: chart.width,
            height: chart.height,
          },
        } as any),
      ],
      alignment: AlignmentType.CENTER,
      spacing: { before: 400, after: 200 },
    })
  );

  // Caption
  elements.push(
    new Paragraph({
      children: [
        new TextRun({
          text: `Figure ${chart.figureNumber}. `,
          italics: true,
          bold: true,
        }),
        new TextRun({
          text: chart.caption,
          italics: true,
        }),
      ],
      alignment: AlignmentType.LEFT,
      spacing: { after: 400 },
    })
  );

  return elements;
}

