const docs = [
  'docs/API_DOCUMENTATION.md',
  'docs/API_IMPLEMENTATION_REPORT.md',
  'docs/API_QUICK_REFERENCE.md',
];

let docsOk = true;
docs.forEach(file => {
  if (!checkFile(file)) docsOk = false;
});

// Check existing files
